[7503] = {"La plus grande race de chasseurs", nil, {"Rendez ce livre à ses légitimes propriétaires."}},
[7504] = {"Sainte Bolognaise : Ce que la lumière ne vous dit pas", nil, {"Rendez ce livre à ses légitimes propriétaires."}},
[7505] = {"Le horion de givre et vous", nil, {"Rendez ce livre à ses légitimes propriétaires."}},
[7506] = {"Le Rêve d'Emeraude...", nil, {"Rendez ce livre à ses légitimes propriétaires."}},
[7562] = {"Mor'zul Porte-sang", nil, {"Allez voir Mor'zul Porte-sang à l'Autel des Tempêtes, dans les Steppes Ardentes."}},
[7563] = {"Rage de sang", nil, {"Apportez 30 bouteilles de Sang de bêtes déchaînées à Mor'zul Porte-sang dans les Steppes Ardentes."}},
[7581] = {"Les ancrages de la prison", nil, {"Rendez-vous à Hache-Tripes en Féralas et récupérez 15 doses de Sang de satyre sur les satyres Follengeance qui habitent le quartier de Crochebois. Lorsque ce sera fait, retournez voir Daio à la Balafre impure."}},
[7582] = {"Les parois de la prison", nil, {"Rendez-vous à la gorge du Sombre murmure, au Berceau-de-l'Hiver, et récupérez 5 Larmes de l'Hederine sur les démons Hederine qui occupent la gorge. Lorsque ce sera fait, retournez voir Levandre à la Balafre impure."}},
[7602] = {"La gangressence parfaite", nil, {"Démonet vous a demandé de lui apporter trois Gangressences parfaites, venant de trois régions distinctes.\n\nLes satyres Legashi d'Azshara, les légionnaires de Jaedenar et les gangregardes sentinelles des terres Foudroyées détiennent la Gangressence parfaite de leur région.\n\nRécupérez les Gangressences parfaites et rapportez-les à Démonet, à Gangrebois."}},
[7603] = {"Le noyau infernal de Kroshius", nil, {"Fouillez le Val Grêlé pour trouver les restes de Kroshius. Quand vous les aurez localisés, utilisez le Gangrefeu à proximité et attendez le retour de Kroshius. Quand l'Infernal se sera réveillé, tuez-le et prenez le Noyau infernal sur son cadavre.\n\nRapportez le Noyau infernal de Kroshius à Niby le Tout-Puissant, à Gangrebois."}},
[7604] = {"Un contrat contraignant", nil, {"Apportez le Contrat de la Confrérie du thorium à Lokhtos Sombrescompte, si vous voulez recevoir les plans du marteau de sulfuron."}},
[7622] = {"L'équilibre de la Lumière et de l'Ombre", nil, {"Sauvez 50 Paysans avant que 15 d'entre eux aient été tués. Si vous y parvenez, allez parler à Eris Havrefeu.\n\nConsultez le Panneau des morts pour voir combien de paysans ont été tués."}},
[7623] = {"Le seigneur Creuseplaie", nil, {"Achetez des Potions des ténèbres à Gorzeeki à l'Autel des Tempêtes, dans les Steppes Ardentes.\n\nServez-vous-en pour vous rendre à Jaedenar, dans Gangrebois et parlez au seigneur Creuseplaie à l'intérieur du Fort des Ombres."}},
[7625] = {"Poussière d'étoile xorothienne", nil, {"Achetez de la Poussière d’étoile xorothienne à Ur’dan dans le Fort des Ombres à Jaedenar, dans Gangrebois. Apportez-la à Gorzeeki Fouregard dans les Steppes Ardentes."}},
[7626] = {"La Cloche de Dethmoora", nil, {"Apportez 2 Elixirs de puissance de l'ombre à Gorzeeki Fouregard dans les Steppes ardentes."}},
[7627] = {"La Roue de la Marche noire", nil, {"Apportez 6 Grands éclats brillants et 25 charges de Minerai de sombrefer à Gorzeeki dans les Steppes ardentes."}},
[7628] = {"La Chandelle de l'apocalypse", nil, {"Apportez 3 Écailles de dragon noir à Gorzeeki Fouregard dans les Steppes Ardentes."}},
[7629] = {"Livraison de diablotin", nil, {"Apportez le Diablotin en bouteille au laboratoire d'alchimie de la Scholomance. Une fois le parchemin créé, rapportez la bouteille à Gorzeeki Fouregard."}},
[7630] = {"L'arcanite", nil, {"Apporter 1 Barre d'arcanite à Gorzeeki dans les Steppes ardentes."}},
[7631] = {"Le destrier de l'effroi de Xoroth", nil, {"Lisez les Instructions de Mor'zul. Invoquez un Destrier de l'effroi xorothien, vainquez-le, et liez son esprit."}},
[7632] = {"La feuille ancienne", nil, {"Trouvez le propriétaire de la Feuille ancienne pétrifiée. Bonne chance, Bomdiyada, le monde est vaste."}},
[7634] = {"Feuille d'Ancien cousue de tendons", nil, {"L'Ancien Hastat vous demande de lui ramener un Tendon de dragon bleu adulte. Si vous en trouvez un, ramenez-le-lui à Gangrebois."}},
[7635] = {"La bonne corde", nil, {"L'Ancien Stoma vous demande de lui ramener un Tendon de dragon noir adulte. Si vous en trouvez un, ramenez-le à Stoma à Gangrebois."}},
[7636] = {"Le bâton des Anciens", nil, {"Vous devez trouver et tuer quatre corrupteurs démoniaques :\n\nSimona la Séductrice.\n\nKlinfran l'Enragé.\n\nSolenor le Tueur.\n\nArtorius le Néfaste.\n\nDétruisez ces créatures et retournez voir l'Ancien Vartus à Gangrebois.\n\nVous trouverez des indices sur leurs faits et gestes sur l'Ecorce pétrifiée qui se trouve dans votre inventaire.\n\nVous DEVEZ agir seul."}},
[7637] = {"L'accent sur le sacrifice", nil, {"Rendez-vous à Forgefer et procurez-vous l'Encensoir d'exorcisme du grand prêtre Rohan. Pour vous le procurer, il vous faudra verser une donation de 150 pièces d'or."}},
[7638] = {"Le seigneur Grayson Brisombre", nil, {"Parler au seigneur Grayson Brisombre dans le Quartier de la Cathédrale de Hurlevent."}},
[7639] = {"Des preuves de jugement", nil, {"Ramener l'Encensoir d'exorcisme au seigneur Grayson Brisombre, dans le Quartier de cathédrale de Hurlevent."}},
[7640] = {"Exorciser Val-Terreur", nil, {"Servez-vous de l'Encensoir d'exorcisme pour chasser les esprits qui tourmentent Val-Terreur. Lorsque vous aurez tué 25 Esprits de Val-Terreur, retournez voir le seigneur Grayson Brisombre dans le Quartier de la cathédrale de Hurlevent."}},
[7641] = {"L'œuvre de Grimand Elmore", nil, {"Parlez à Grimand Elmore dans le Quartier des Nains de Hurlevent."}},
[7642] = {"Une ribambelle d'objets", nil, {"Apporter 40 Etoffes runiques, 6 Barres d'arcanite, 10 Larmes d'Arthas, 5 Fioles d'eau sacrée de Stratholme, et 150 pièces d'or à Grimand Elmore, au Quartier des nains de Hurlevent."}},
[7643] = {"L'ancien esprit équin", nil, {"Procurez-vous un aliment pour chevaux capable de nourrir un esprit équin. Il semble que Merideth Carlson, à Austrivage, sache en fabriquer.\n\nEnsuite, rendez-vous au donjon de Hache-tripes en Féralas, et tuez Tendris Crochebois. Sa mort libérera l'Ancien esprit équin. Nourrissez-le avec l'aliment spécial afin de l'apaiser. Enfin, donnez-lui le Caparaçon d'arcanite pour qu'il le bénisse."}},
[7645] = {"Fourrage enrichi en manne", nil, {"Récupérer 20 Biscuits de manne enrichis, l'ingrédient vital qui permet de préparer du Fourrage enrichi en manne, pour le compte de Merideth Carlson de Southore dans les contreforts de Hautebrande. L'Aube d'argent est la seule à fabriquer ces biscuits.\n\nVous devrez également lui donner 50 pièces d'or pour apaiser sa susceptibilité."}},
[7647] = {"Jugement et rédemption", nil, {"Utilisez le Clairvoyant au coeur du sous-sol du Grand ossuaire, dans la Scholomance. Cela attirera des esprits que vous devrez juger. Lorsqu'ils seront vaincus, le Chevalier de la mort Ravassombre apparaîtra. Vainquez-le et purifiez l'âme du destrier déchu.\n\nEnsuite, donnez l'Ame rachetée du destrier et le Caparaçon enchanté et béni au Destrier déchu de Ravassombre."}},
[7649] = {"Les plates en thorium enchanté - Volume I", nil, {"Rendez ce livre à ses légitimes propriétaires."}},
[7650] = {"Les plates en thorium enchanté - Volume II", nil, {"Rendez ce livre à ses légitimes propriétaires."}},
[7666] = {"Retour au Grand ossuaire", nil, nil},
[7667] = {"Assistance matérielle", nil, {"Si vous voulez aider Sagorne Rôdeur-des-crêtes de la vallée de la Sagesse d'Orgrimmar, apportez-lui un Diamant d'Azeroth et un Diamant noir en parfait état."}},
[7670] = {"Le seigneur Grayson Brisombre", nil, {"Parler au seigneur Grayson Brisombre dans le Quartier de la Cathédrale de Hurlevent."}},
[7681] = {"Hunter test quest", nil, {"Nourrissez votre familier, puis parlez au donneur de quête."}},
[7682] = {"Hunter test quest2", nil, {"Nourrissez votre familier, puis parlez au donneur de quête."}},
[7701] = {"ON RECHERCHE : Surveillant Maltorius", nil, {"Loin dans les profondeurs de la fosse aux Scories dans le Chaudron, dans la Gorge des Vents brûlants, vous trouverez le nain traître du clan Sombrefer répondant au nom de surveillant Maltorius.\n\nTuez le surveillant Maltorius et prenez sa tête. Apportez-la au capitaine de guet Lolo Heurteloin dans la Gorge des Vents brûlants pour la récompense."}},
[7702] = {"Privez-les de sommeil", nil, {"Loin dans les profondeurs de la fosse aux Scories, dans le Chaudron, vous trouverez le dortoir des nains Sombrefer.\n\nAllez dans leur dortoir et volez 20 Oreillers Sombrefer. Revenez voir Evonice Fumesuie dans la gorge des Vents brûlants quand vous aurez accompli cette tâche."}},
[7703] = {"Affaires inachevées chez les Gordok", nil, {"Trouvez le Gantelet de puissance gordok et rendez-le au Capitaine Kromcrush à Hache-tripes.\n\nSelon Kromcrush, les histoires des « vieux » racontent que Tortheldrin - un « vilain » elfe qui se fait appeler prince - l'a volé à l'un des rois gordok."}},
[7704] = {"Regardez un peu la taille !", nil, {"Apportez l'Oreiller de la Femme de chambre Pincetaie à Evonice Fumesuie dans la Gorge des Vents brûlants."}},
[7721] = {"Du carburant pour la zappette", nil, {"Récupérez 10 Noyaux élémentaires d'eau sur les Elémentaires marins et les Embruns le long de la Côte Oubliée de Féralas, et rapportez-les à Zorbin Poudrozieu, près du quai là-bas."}},
[7722] = {"Le flux embrasé", nil, {"Quelque part dans la fosse aux Scories du Chaudron, vous trouverez les Plans secrets : Flux embrasé dérobés par le surveillant Maltorius. Trouvez ces plans et rapportez-les au Maître forgeron Brûlotin, dans la Gorge des Vents brûlants."}},
[7723] = {"Maudits gros doigts", nil, {"Hansel Lourdemains veut que vous tuiez 20 Golems de guerre lourds.\n\nLes golems de guerre lourds errent dans les régions qui entourent le Chaudron dans la Gorge des Vents brûlants. Quand vous en aurez tué assez, revenez voir Hansel dans la Gorge des Vents brûlants."}},
[7724] = {"Une menace incandescente", nil, {"Hansel Lourdemains veut que vous tuiez 20 Grandes araignées de lave dans la gorge des Vents brûlants. Vous pouvez trouver les Grandes araignées de lave dans les plateaux et les mares de lave de la gorge des Vents brûlants.\n\nRevenez voir Hansel dans la gorge des Vents brûlants quand vous aurez tué assez de Grandes araignées de lave."}},
[7727] = {"Incendosaures ? N'importosaures, plutôt", nil, {"Hansel Lourdemains veut que vous tuiez 20 Incendosaures.\n\nOn peut les trouver dans les bassins de lave de la Fosse aux scories, qui occupe une partie du Chaudron de la gorge des Vents brûlants.\n\nRetournez voir Hansel Lourdemains dans la gorge des Vents brûlants lorsque vous aurez tué suffisamment d'Incendosaures."}},
[7728] = {"OBJETS VOLES : Tuyère et longue-vue", nil, {"Trouvez la Tuyère de forge et la Longue-vue du guetteur et rapportez-les au sous-chef Scrange dans la gorge des Vents brûlants.\n\nLes seules informations dont vous disposez sur ces objets sont les suivantes : ils ont été dérobés par les nains Sombrefer, la Tuyère de forge est un outil utilisé par les forgerons et la Longue-vue du guetteur est un outil de surveillance inestimable pour les guetteurs."}},
[7729] = {"OFFRE D'EMPLOI : Réduire la concurrence", nil, {"Tuez 15 Sous-chefs Sombrefer et 15 Esclavagistes Sombrefer. Revenez voir le sous-chef Scrange dans la gorge des Vents brûlants quand vous aurez accompli cette tâche."}},
[7730] = {"Infestation de Zukk'ash", nil, {"Rapportez 20 Carapaces de Zukk'ash à Hadoken Rôdeur-agile au Camp Mojache en Féralas."}},
[7731] = {"Fouet-cuisant", nil, {"Battez Fouet-cuisant dans le Gouffre Grouillant et rapportez ses glandes à Hadoken Rôdeur-agile au Camp Mojache en Féralas."}},
[7733] = {"Qualité améliorée", nil, {"Fournir 10 Peaux de yéti griffe féroce à Pratt McGrubben au bastion de Pennelune, en Féralas."}},
[7734] = {"Qualité améliorée", nil, {"Fournir 10 Peaux de yéti griffe féroce à Jangdor Rôdeur-agile au Camp Mojache, en Féralas."}},
[7737] = {"Se faire encore mieux accepter", nil, {"Conditions :"}},
[7761] = {"Les ordres de Main-noire", nil, {"Cet orc est stupide. Il semble que vous deviez trouver un moyen d'obtenir la Marque de Drakkisath pour accéder à l'Orbe de commandement.\n\nD'après la lettre, la Marque est gardée par le général Drakkisath. Vous devriez peut-être enquêter."}},
[7781] = {"Le seigneur de Rochenoire", nil, {"Apportez la Tête de Nefarian au roi Varian Wrynn à Hurlevent."}},
[7782] = {"Le seigneur de Rochenoire", nil, {"Aller dans la vallée des Héros de Hurlevent et parler au grand maréchal Pont-de-Pierre."}},
[7783] = {"Le seigneur de Rochenoire", nil, {"Apportez la Tête de Nefarian à Garrosh à Orgrimmar."}},
[7784] = {"Le seigneur de Rochenoire", nil, {"Parlez au Seigneur Runthak à Orgrimmar. Il vous attend dans la Vallée de la Force."}},
[7785] = {"Examiner le Calice", nil, {"Examiner le Calice de la renaissance et le rapporter au généralissime Demitrian."}},
[7786] = {"Tonneraan le Cherchevent", nil, {"Pour libérer Tonneraan le Cherchevent de sa prison, vous devez présenter au Généralissime Demitrian les moitiés droite et gauche des Liens du Cherchevent, 10 Barres d'élémentium enchantées, et l'Essence du seigneur du Feu, en Silithus."}},
[7787] = {"Éveille-toi, Lame-tonnerre !", nil, {"Présenter la Lame du baiser du vent dormante au généralissime Demitrian."}},
