[8274] = {"Quête test d’élimination", nil, {"Kill 5 Murlocs and come back to the test character."}},
[8275] = {"La reconquête de Silithus", nil, {"Parlez à l'Imploratrice céleste Corne-fière au Fort cénarien, en Silithus."}},
[8276] = {"La reconquête de Silithus", nil, {"Parlez à l'Imploratrice céleste Corne-fière au Fort cénarien, en Silithus."}},
[8277] = {"Venin mortel du désert", nil, {"Beetix Kalefragile au Fort cénarien en Silithus veut que vous ramassiez 8 Dards de scorpide Cinglepierre et 8 Crocs de Glisseuse du sable. Retournez la voir quand vous aurez terminé."}},
[8278] = {"Le dernier espoir de Noggle", nil, {"Beetix Kalefragile, du Fort cénarien en Silithus, veut que vous ramassiez 3 Dards de pinceur Cinglepierre, 3 Dards d'écorcheur Cinglepierre et 3 Crocs de traqueuse des rochers."}},
[8279] = {"Le lexique du Crépuscule", nil, {"Apportez les trois chapitres du Lexique du Crépuscule à l'ermite Ortell, en Silithus."}},
[8280] = {"Sécuriser les lignes d'approvisionnement", nil, {"L'Imploratrice céleste Corne-fière au Fort cénarien en Silithus veut que vous tuiez 15 Frappeurs térébrants."}},
[8281] = {"Améliorer la sécurité", nil, {"L'Imploratrice céleste Corne-fière au Fort cénarien en Silithus veut que vous tuiez 20 Ecraseurs térébrants."}},
[8282] = {"La sacoche perdue de Noggle", nil, {"Récupérez la Sacoche de Noggle dans les montagnes au sud de Silithus et rapportez-la-lui au Fort cénarien."}},
[8283] = {"On recherche : Poigne-de-mort, Terreur des sables", nil, {"Apporter la Pince de Poigne-de-mort à Vish Kozus, capitaine de la garde."}},
[8284] = {"Le mystère du Crépuscule", nil, {"La géologue Fablemort du Fort cénarien en Silithus veut que vous rassembliez 8 Fragments de la tablette du Crépuscule."}},
[8285] = {"Le déserteur", nil, {"Apportez la Tablette réparée à l'ermite Ortell à Silithus."}},
[8286] = {"Ce que demain apportera", nil, {"Rendez-vous dans les Grottes du temps en Tanaris et trouvez Anachronos, la progéniture de Nozdormu."}},
[8287] = {"Un terrible but", nil, {"Remettez la Transcription décodée de la tablette au commandant Mar'alith."}},
[8288] = {"Il ne peut y en avoir qu'un", nil, {"Ramenez la Tête du seigneur des couvées Lanistaire à Baristolth des Sables changeants, au Fort cénarien en Silithus."}},
[8301] = {"Le Chemin des Justes", nil, {"Récupérez 200 Fragments de carapaces de silithides et retournez voir Baristolth."}},
[8302] = {"La Main des Justes", nil, {"Récupérez 200 Fragments de carapaces de silithides et retournez voir Baristolth."}},
[8303] = {"Anachronos", nil, {"Cherchez Anachronos dans les Grottes du temps à Tanaris."}},
[8304] = {"Chère Natalia", nil, {"Interrogez Frankal et Rutgar au Campement de Barbe-de-bronze pour savoir où se trouve Natalia Mar’alith."}},
[8305] = {"Des souvenirs oubliés depuis longtemps", nil, {"Trouvez la Larme cristalline, en Silithus, et contemplez ses profondeurs."}},
[8306] = {"L'antre de la folie", nil, {"Le Commandant Mar'alith au Fort cénarien en Silithus veut que vous retrouviez sa bien-aimée, Natalia. Selon les informations que vous avez réunies, la Ruche'Regal au sud serait l'endroit où vous pourriez trouver Dame Natalia Mar'alith.\n\nN'oubliez pas de rendre visite aux nains au camp de Barbe-de-bronze avant de vous aventurer dans la ruche. Ils ont peut-être d'autres tâches à vous confier et des conseils pour vous.\n\nEt Bomdiyada, souvenez-vous des mots du commandant : « Faites votre devoir… »"}},
[8307] = {"Recette du désert", nil, {"Cherchez des informations sur la façon de cuisiner la Chair de Ver des sables au camp du Marteau du crépuscule à l'ouest du Fort cénarien."}},
[8308] = {"La lettre perdue de Brann Barbe-de-bronze", nil, {"Apporter la Lettre de Brann Barbe-de-bronze au Campement de Barbe-de-bronze dans le sud de Silithus."}},
[8309] = {"À la poursuite des glyphes", nil, {"Utilisez la Trousse de transcription du géologue pour obtenir une Retranscription de la Ruche’Regal à partir du Cristal orné de glyphes de la Ruche’Regal."}},
[8310] = {"Déchiffrer le code", nil, {"Obtenez un Cerveau de silithide intact provenant de la Ruche’Regal. N’importe quel silithide de cette ruche fera l’affaire."}},
[8311] = {"Des bonbons de la Sanssaint pour Jesper !", nil, {"Parler aux aubergistes de Hurlevent, de Forgefer, de Darnassus, et à Talvash del Kissel à Forgefer. Accomplissez les blagues qu'ils vous demanderont de faire en échange de bonbons.\n\nRetournez voir Jesper devant Hurlevent avec un Massepain de Darnassus, une Boule de gomme de Gnomeregan, du Nougat de Hurlevent et un Bonbon à la menthe de Forgefer."}},
[8312] = {"Des bonbons de la Sanssaint pour Spoops !", nil, {"Parler aux aubergistes d'Orgrimmar, de Fossoyeuse, des Pitons-du-Tonnerre, et à Kali Remik au village de Sen'jin. Accomplissez les blagues qu'ils vous demanderont de faire en échange de bonbons.\n\nRetournez voir Spoops dans les Ruines de Lordaeron avec un Massepain des Pitons-du-Tonnerre, une Boule de gomme sombrelance, du Nougat d'Orgrimmar et un Bonbon à la menthe de Fossoyeuse."}},
[8313] = {"Partager la connaissance", nil, {"Remettre la Page de recette déchirée à Calandrath, à l'auberge du Fort cénarien."}},
[8314] = {"Dévoiler le mystère", nil, {"Remettez le Prisme cristallin orné de glyphes à la géologue Fablemort au Fort cénarien, en Silithus."}},
[8315] = {"L'appel", nil, {"La géologue Fablemort, au Fort cénarien en Silithus, veut que vous récupériez le Mécanisme de déverrouillage du cristal sur le Maître des clés.\n\nElle vous a demandé de vous rendre près des Restes de Grakkarond, au sud du Fort cénarien, et d'y dessiner les glyphes d'appel dans le sable. Si le Maître des clés apparaît, tuez-le et récupérez le Mécanisme de déverrouillage du cristal. Ensuite, si vous avez réussi, retournez voir la géologue Fablemort.\n\nElle vous a conseillé d'emmener une armée, Scanner."}},
[8317] = {"Un coup de main à la cuisine", nil, {"Calandrath, du Fort cénarien, a besoin d'une fournée de 10 Boulettes fumées du désert."}},
[8318] = {"Communications secrètes", nil, {"Apportez 10 Textes du Crépuscule cryptés à Bor Crin-sauvage, au Fort cénarien."}},
[8319] = {"Les textes du Crépuscule cryptés", nil, {"Conditions :"}},
[8320] = {"Les géoseigneurs du Crépuscule", nil, {"Tuez 10 Géoseigneurs du Crépuscule, puis retournez voir Huum Crin-sauvage au Fort cénarien."}},
[8321] = {"Vyral le Vil", nil, {"Tuez Vyral le Vil et prenez-lui sa Chevalière."}},
[8322] = {"Les œufs pourris", nil, {"Placez les Œufs pourris dans le tonneau de bière principal de l'auberge d'Austrivage."}},
[8323] = {"Les vrais croyants", nil, {"L'ermite Ortell veut que vous lui apportiez 10 Textes du Crépuscule cryptés."}},
[8324] = {"Je crois encore...", nil, {"Conditions :"}},
[8325] = {"La reconquête de l'île de Haut-Soleil", nil, {"Tuez 6 Wyrms de mana. Ils se trouvent près des Cristaux ardents, non loin de la tour et du belvédère."}},
[8326] = {"Des mesures regrettables", nil, {"Collectez 8 Colliers de lynx sur des Lynx Preste-pattes et des Jeunes Preste-pattes. Lorsque vous aurez terminé, retournez voir la magistrice Erona sur l'île de Haut-soleil."}},
[8327] = {"Se présenter à Lanthan Perilon", nil, {"Parlez à Lanthan Perilon sur l'île de Haut-soleil. Il se tient près du sentier qui part de la Flèche solaire, vers l'ouest."}},
[8328] = {"Le maître des mages", nil, {"Parlez à Julia Heurtesoleil, le maître des mages, à l'intérieur de la Flèche solaire de l'île de Haut-soleil."}},
[8329] = {"La formation de guerrier", nil, {"Parlez à Delios, le maître des guerriers, à la Flèche solaire sur l’Île de Haut-Soleil."}},
[8330] = {"Les possessions de Solanian", nil, {"Récupérez l'Orbe de divination de Solanian, son Parchemin de magie du Fléau et son Journal. Ils se trouvent sur l'île de Haut-soleil, non loin de l'étang, de la fontaine et de l'un des cristaux ardents. Lorsque vous les aurez tous, restituez-les au Veilleur du puits à la Flèche solaire de l'île de Haut-soleil."}},
[8332] = {"Les ducs du Conseil", nil, {"Apportez 1 Grand éclat brillant et 3 Blasons abyssaux à Aurel Feuilledor, au Fort cénarien."}},
[8334] = {"Agression", nil, {"Tuez 7 Sylveniers et 7 Sylveniers farouches, puis retournez voir Lanthan Perilon sur l'île de Haut-soleil."}},
[8335] = {"Felendren le Banni", nil, {"Tuez 8 Âmes en peine arcaniques et 2 Âmes en peine arcaniques contaminées, ainsi que Felendren le Banni. Ils se trouvent à l'académie de Falthrien. Ramenez la Tête de Felendren à Lanthan Perilon sur l'île de Haut-soleil."}},
[8336] = {"Pour une poignée d'esquilles", nil, {"Récupérer 6 Esquilles arcaniques sur n'importe quelle créature qui utilise du mana, puis retourner voir l'arcaniste Ithanas sur l'île de Haut-soleil."}},
[8337] = {"<INUTILISÉ>", nil, {"Conditions :"}},
[8338] = {"L'esquille arcanique contaminée", nil, {"Remettre l'Esquille arcanique contaminée à l'arcaniste Helion, au belvédère de la Flèche solaire, sur l'île de Haut-soleil."}},
[8341] = {"Les seigneurs du conseil", nil, {"Apportez 5 Grands éclats brillants et 3 Chevalières abyssales à Aurel Feuilledor, au Fort cénarien."}},
[8345] = {"Le sanctuaire de Dath'Remar", nil, {"Lisez la plaque au Sanctuaire de Dath'Remar puis retournez auprès du veilleur du puits Solanian, à la Flèche solaire de l'île de Haut-soleil."}},
[8346] = {"L'insatiable soif", nil, {"Servez-vous de votre compétence raciale Torrent arcanique sur un wyrm de mana, puis revenez voir l’arcaniste Helion."}},
[8347] = {"Aider les estafettes", nil, {"Parler à l'estafette Alarion, près du pont qui mène au sud de l'île de Haut-soleil."}},
[8348] = {"La chevalière des ducs", nil, {"Apportez la Chevalière abyssale à Bor Crin-sauvage, au Fort cénarien."}},
[8349] = {"Bor Crin-sauvage", nil, {"Parlez à Bor Crin-sauvage au Fort cénarien."}},
[8350] = {"Achever la livraison", nil, {"Remettez le Paquet d'Erona à l'aubergiste Delaniel, à l'auberge située sur la place de l'épervier dans le bois des Chants éternels."}},
[8352] = {"Le sceptre du Conseil", nil, {"Apportez un Sceptre abyssal à Bor Crin-sauvage, au Fort cénarien."}},
[8353] = {"Caqueter pour un bonbon à la menthe", nil, {"Utilisez l'emote /poulet devant l'aubergiste Brassefeu, et vous recevrez en échange un bonbon à la menthe de Forgefer !"}},
[8354] = {"Caqueter pour un bonbon à la menthe", nil, {"Utilisez l'emote /poulet devant l'aubergiste Norman, et vous recevrez en échange un bonbon à la menthe de Fossoyeuse !"}},
[8356] = {"Montrer ses muscles pour du nougat", nil, {"Utilisez l'emote /frime devant l'aubergiste Allison, et vous recevrez en échange du nougat de Hurlevent !"}},
[8357] = {"Danser pour du massepain", nil, {"Utilisez l'emote /danser devant l'aubergiste Saelienne, et vous recevrez en échange du massepain de Darnassus !"}},
[8358] = {"Boule de gomme en approche", nil, {"Utilisez l'emote /tchou devant Kali Remik, et vous recevrez en échange une Boule de gomme sombrelance !"}},
[8359] = {"Montrer ses muscles pour du nougat", nil, {"Utilisez l'emote /frime devant l'aubergiste Gryshka, et vous recevrez en échange du nougat d'Orgrimmar !"}},
[8360] = {"Danser pour du massepain", nil, {"Utilisez l'emote /danser devant l'aubergiste Pala, et vous recevrez en échange du massepain des Pitons-du-Tonnerre !"}},
[8361] = {"Des contacts abyssaux", nil, {"Bor Crin-sauvage, du Fort cénarien en Silithus, veut que vous détruisiez un Templier abyssal et que vous lui rameniez un Blason abyssal comme preuve de votre exploit."}},
[8365] = {"Ohé, des chapeaux de pirate !", nil, {"Hautaine Lamodiste veut que vous lui rapportiez 20 Chapeaux de pirate des mers du Sud à Port Gentepression, en Tanaris."}},
[8366] = {"Le racket des mers du Sud", nil, {"Le chef de la sécurité Vizisanie du port Gentepression, en Tanaris, veut que vous tuiez 10 Pirates des mers du Sud, 10 Flibustiers des mers du Sud, 10 Portefaix des mers du Sud et 10 Bretteurs des mers du Sud."}},
[8367] = {"Pour le plus grand des honneurs", nil, {"Apportez 1 Marque d’honneur de chaque champ de bataille à un Porteguerre de la Horde, dans n'importe quelle capitale de la Horde ou à Shattrath."}},
[8368] = {"La bataille du goulet des Chanteguerres", nil, {"Apportez 3 Marques d'honneur Chanteguerre à l'un des porteguerres de la Horde, devant les champs de bataille."}},
[8369] = {"Les envahisseurs de la vallée d'Alterac", nil, {"Apportez 3 Marques d'honneur de la vallée d'Alterac à l'un des porteguerres de la Horde, devant les champs de bataille."}},
[8370] = {"La conquête du bassin Arathi", nil, {"Apportez 3 Marques d'honneur du bassin Arathi à l'un des porteguerres de la Horde, devant les champs de bataille."}},
[8371] = {"Des efforts concertés", nil, {"Apportez 1 Marque d’honneur de chaque champ de bataille à un Général de brigade de l'Alliance, dans n'importe quelle capitale de l'Alliance, à Dalaran ou à Shattrath."}},
[8372] = {"La lutte pour le goulet des Chanteguerres", nil, {"Apportez 3 Marques d'honneur du goulet des Chanteguerres à un général de brigade de l'Alliance, devant les champs de bataille."}},
[8373] = {"La puissance du pin", nil, {"Utilisez le Nettoyeur de boules puantes pour retirer les Boules puantes que les Réprouvés ont lancées sur Austrivage. Retournez voir le sergent Hartman à Austrivage quand vous aurez terminé."}},
