[8410] = {"La maîtrise des éléments", nil, {"Rassembler un échantillon d'air, de feu, de terre et d'eau pour Bath'rah la Vigie des vents."}},
[8412] = {"[PÉRIMÉ]Le totem des esprits", nil, {"Apportez les différents morceaux du Totem des esprits à Bath'rah la Vigie des Vents."}},
[8413] = {"Vaudou !", nil, {"Apportez les Plumes vaudou à Bath'rah la Vigie des vents."}},
[8414] = {"La dissipation du mal", nil, {"Récupérez 20 Pierres du Fléau des serviteurs et apportez-les au grand prêtre Thel'danis dans les Maleterres de l'Ouest."}},
[8415] = {"La pointe du Noroît", nil, {"Parler au commandant Ashlam Vaillepoing au Camp du Noroît."}},
[8417] = {"Un esprit troublé", nil, {"Parlez au Héros de la Horde tombé au combat, à l'entrée des Terres Foudroyées."}},
[8418] = {"Forger la pierre de pouvoir", nil, {"Apportez des Plumes vaudou ambres, bleues et vertes."}},
[8419] = {"La demande d'un diablotin", nil, {"Apportez un morceau de Gangrétoffe à Démonet à Gangrebois."}},
[8420] = {"Chaud et piquant", nil, {"Apportez un morceau de gangrétoffe à Démonet à Gangrebois."}},
[8421] = {"L'anti-étoffe des héros", nil, {"Apporter 10 morceaux de Bois pourri et 4 Essences de Vénéneuse à Démonet, en Gangrebois."}},
[8422] = {"Les trolls d'un même plumage...", nil, {"Apporter 6 Plumes vaudou des trolls du Temple englouti."}},
[8423] = {"Fraternité entre guerriers", nil, {"Tuez 7 Sangliers infernaux dans les Terres Foudroyées, et revenez voir le Héros de la Horde tombé au combat."}},
[8424] = {"La guerre contre les liges d'ombre", nil, {"Tuez les Liges d'ombre dans les Terres Foudroyées et revenez voir le Héros de la Horde tombé au combat."}},
[8425] = {"Plumes vaudou", nil, {"Apportez les Plumes vaudou des trolls du Temple englouti au Héros de la Horde tombé au combat."}},
