[8596] = {"Les bottillons de l'Oracle", nil, {"Ramener des Manchettes de commandement qiraji, 2 Idoles de la mort, 5 Scarabées en bronze et 5 Scarabées d'or à Kandrostrasz, à Ahn'Qiraj. Pour terminer cette quête, il faut aussi que la Progéniture de Nozdormu soit au moins Neutre à votre égard."}},
[8597] = {"Le draconique pour les nuls", nil, {"Retrouver le livre de Narain Divinambolesque, enfoui sur une île des mers du Sud."}},
[8600] = {"La Horde a besoin de cuir robuste !", nil, {"Apportez 10 morceaux de Cuir robuste au Docteur Serratus dans la Vallée des Esprits d'Orgrimmar."}},
[8602] = {"Les espauliers d'implorateur des tempêtes", nil, {"Apportez des Manchettes de domination qiraji, 2 Idoles de la vie, 5 Scarabées d'or et 5 Scarabées de cristal à Andorgos, à Ahn'Qiraj. Pour terminer cette quête, il faut aussi que la Progéniture de Nozdormu soit au moins Neutre à votre égard."}},
[8603] = {"Les habits de l'Oracle", nil, {"Apportez la Carcasse du Dieu très ancien, 2 Idoles de la mort, 5 Scarabées de pierre et 5 Scarabées de cristal à Vethsera, à l'intérieur d'Ahn'Qiraj. Pour terminer cette quête, vous devez être Honoré par la Progéniture de Nozdormu."}},
[8604] = {"La Horde a besoin de bandages en laine !", nil, {"Apportez 20 Bandages en laine au Guérisseur Long-coureur dans la Vallée des Esprits d'Orgrimmar."}},
[8606] = {"Un leurre !", nil, {"Narain Divinambolesque, en Tanaris, veut que vous vous rendiez au Berceau-de-l'hiver et que vous posiez le Sac d'or à l'endroit indiqué par les voleurs de livres."}},
[8607] = {"La Horde a besoin de bandages en tisse-mage !", nil, {"Apportez 20 Bandages en tisse-mage à Dame Callow dans la Vallée des Esprits d'Orgrimmar."}},
[8609] = {"La Horde a besoin de bandages en étoffe runique !", nil, {"Apportez 20 Bandages en étoffe runique à la Garde de pierre Sabot-d'argile dans la Vallée des Esprits d'Orgrimmar."}},
[8611] = {"La Horde a besoin de steaks de loup !", nil, {"Apportez 20 steaks de loup à la garde de sang Brutepoix dans la Vallée des Esprits d'Orgrimmar."}},
[8613] = {"La Horde a besoin de jaunes-queues tachetés !", nil, {"Ramener 20 jaunes-queues tachetés au pêcheur Lin'do dans la Vallée des esprits d'Orgrimmar."}},
[8615] = {"La Horde a besoin de pains de saumon !", nil, {"Apportez 20 Pains de saumon au chef Griffe-tranchante dans la Vallée des Esprits d'Orgrimmar."}},
[8617] = {"La Horde a besoin de pierres de Magma !", nil, {"Ramener 50 Pierres de Magma roussies au général Zog, en Durotar."}},
[8619] = {"L'Ancien Gouffre-du-matin", nil, nil},
[8620] = {"Le seul remède", nil, {"Retrouver les 8 chapitres perdus du \"Draconique pour les nuls\" et les combiner avec la Reliure magique, puis ramener l'exemplaire réparé du \"Draconique pour les nuls, volume II\" à Narain Divinambolesque, en Tanaris."}},
[8621] = {"Les protège-tibias d'implorateur de tempête", nil, {"Apportez des Manchettes de domination qiraji, 2 Idoles du sage, 5 Scarabées en bronze et 5 Scarabées d'argile à Kandrostrasz, à Ahn'Qiraj. Pour terminer cette quête, il faut aussi que la Progéniture de Nozdormu soit au moins Neutre à votre égard."}},
