[8921] = {"L'alambic ectoplasmique", nil, {"Retournez voir Mux Brouillemana à Gadgetzan avec 1 Convertisseur d'arcanite délicat, 4 Essences éternelles supérieures, 10 Huiles de pierre-écaille, 25 Cendres volcaniques et 40 pièces d'or."}},
[8922] = {"Un objet surnaturel", nil, {"Apportez le Récipient de sang scellé à Mux Brouillemana, à Gadgetzan en Tanaris."}},
[8923] = {"Un objet surnaturel", nil, {"Remettez le Récipient de venin scellé à Mux Brouillemana, à Gadgetzan."}},
[8924] = {"La chasse aux ectoplasmes", nil, {"Utilisez l'Alambic ectoplasmique à proximité de morts-vivants désincarnés pour recueillir 12 Ectoplasmes brûlés en Silithus, 12 Ectoplasmes glacés au Berceau-de-l'hiver et 12 Ectoplasmes stables dans les Maleterres de l'est. Remettez à Mux Brouillemana à Gadgetzan. Il veut aussi récupérer l'Alambic ectoplasmique."}},
[8925] = {"Une source d'énergie portable", nil, {"Trouvez le Seigneur-magma Bokk dans les Steppes ardentes, prenez son Cœur de magma et rapportez-le à Mux Brouillemana, à Gadgetzan."}},
[8927] = {"Une juste compensation", nil, {"Apportez une Ceinture du Cœur-sauvage et une paire de Gants du Cœur-sauvage à Mokvar à Orgrimmar."}},
[8930] = {"À la recherche d'Anthion", nil, {"Utilisez le Révélateur de fantômes extradimensionnel à l'entrée de Stratholme, et parlez au fantôme d'Anthion Harmon."}},
[8945] = {"La supplique du mort", nil, {"Entrez à Stratholme et sauvez Ysida Harmon du baron Vaillefendre."}},
[8946] = {"Une preuve de vie", nil, {"Remettre le Médaillon d'Ysida à Anthion Harmon, dans les Maleterres de l'est."}},
[8947] = {"L'étonnante requête d'Anthion", nil, {"Remettre 3 Barres de sombrefer, 20 Cuirs enchantés, 3 Etoffes lunaires et 4 Peaux robustes traitées à Anthion Harmon dans les Maleterres de l'est."}},
[8949] = {"La vendetta de Falrin", nil, {"Collecter 25 Perles de guerre ogres à l'intérieur de Hache-tripes ou du Pic Rochenoire, puis revenir voir Falrin Sculpteflore, à l'intérieur de l'Athenaeum à Hache-tripes."}},
[8953] = {"Les adieux d'Anthion", nil, {"Retournez voir Deliana à Forgefer avec des Bottes de magistère, des Jambières de magistère et un Mantelet de magistère."}},
[8957] = {"Les adieux d'Anthion", nil, {"Retournez voir Mokvar à Orgrimmar avec des Bottes des éléments, un Kilt des éléments et des Espauliers des éléments."}},
[8959] = {"Les adieux d'Anthion", nil, {"Retournez voir Deliana à Forgefer avec des Bottes de vaillance, des Cuissards de vaillance et des Spallières de vaillance."}},
[8960] = {"Le triste destin de Bodley", nil, {"Rendez-vous au mont Rochenoire, près du pic Rochenoire, et utilisez le Révélateur de fantômes extradimensionnel pour trouver Bodley."}},
[8961] = {"Les trois rois des flammes", nil, {"Récupérez l'Incendicite d'Incendius, la Braise du prophète ardent sur le pyrogarde Prophète ardent et les Cendres de Cendres, ainsi qu'un Brasero béni, puis retournez voir Bodley au mont Rochenoire."}},
[8968] = {"Le morceau gauche de l'amulette du seigneur Valthalak", nil, {"Utilisez le Brasero de signal pour invoquer Jarien et Sothos et les tuer. Retournez voir Bodley, à l'intérieur du mont Rochenoire, avec le Morceau gauche de l'amulette du seigneur Valthalak et le Brasero de signal."}},
[8969] = {"Le morceau gauche de l'amulette du seigneur Valthalak", nil, {"Utilisez le Brasero de signal pour invoquer l'esprit de Kormok, puis tuez-le. Retournez voir Bodley, à l'intérieur du mont Rochenoire, avec le Morceau gauche de l'amulette du seigneur Valthalak et le Brasero de signal."}},
[8970] = {"Votre avenir passe par l'île d'Alcaz", nil, {"Récupérez 20 Varechs de sang sur les nagas Strashaz, puis retournez voir Bodley au mont Rochenoire."}},
