[8994] = {"Derniers préparatifs", nil, {"Collectez 40 Brassards rochenoire et un Flacon de pouvoir suprême. Ensuite, retournez voir Bodley au mont Rochenoire."}},
[8995] = {"Mea culpa, seigneur Valthalak", nil, {"Utilisez le Brasero de signal pour invoquer le seigneur Valthalak. Tuez-le, puis utilisez l'Amulette du seigneur Valthalak sur le corps. Ensuite, rendez l'amulette à l'Esprit du seigneur Valthalak."}},
[9001] = {"Garder le meilleur pour la fin", nil, {"Donnez votre Collerette de magistère et votre Robe de magistère à Deliana."}},
[9003] = {"Garder le meilleur pour la fin", nil, {"Donnez votre Collerette de dévot et votre Robe de dévot à Deliana."}},
[9013] = {"Garder le meilleur pour la fin", nil, {"Donnez votre Heaume de vaillance et votre Cuirasse de vaillance à Mokvar."}},
[9015] = {"Le défi", nil, {"Rendez-vous dans le Cercle de la loi, dans les profondeurs de Rochenoire, et placez la Bannière de provocation en son centre lorsque vous serez condamnée par le Juge supérieur Mornepierre. Tuez Theldren et ses gladiateurs, puis retournez voir Anthion Harmon dans les Maleterres de l'est avec le premier morceau de l'amulette du seigneur Valthalak."}},
[9016] = {"Les adieux d'Anthion", nil, {"Retournez voir Mokvar à Orgrimmar avec des bottes du Cœur-sauvage, un Kilt du Cœur-sauvage et des Spallières du Cœur-sauvage."}},
