[9120] = {"La chute de Kel'Thuzad", nil, {"Apportez le Phylactère de Kel'Thuzad à la chapelle de l'Espoir de Lumière dans les Maleterres de l'est."}},
[9121] = {"Naxxramas, la citadelle de l'effroi", nil, {"L'archimage Angela Dosantos à la chapelle de l'Espoir de Lumière dans les Maleterres de l'Est veut 5 Cristaux des arcanes, 2 Cristaux de nexus, 1 Orbe de piété et 60 pièces d'or. Vous devez aussi être Honoré auprès de l'Aube d'argent."}},
[9122] = {"Naxxramas, la citadelle de l'effroi", nil, {"L'archimage Angela Dosantos à la chapelle de l'Espoir de Lumière dans les Maleterres de l'Est veut 2 Cristaux des arcanes, 1 Cristal de nexus et 30 pièces d'or. Vous devez aussi être Révéré auprès de l'Aube d'argent."}},
[9123] = {"Naxxramas, la citadelle de l'effroi", nil, {"L'archimage Angela Dosantos à la chapelle de l'Espoir de Lumière dans les Maleterres de l'Est vous accordera gratuitement l'Occultation arcanique. Vous devez être Exaltée auprès de l'Aube d'argent."}},
[9124] = {"L'armure de traqueur des cryptes ne se fabrique pas toute seule", nil, {"Le veneur Leopold, à la chapelle de l'Espoir de Lumière dans les Maleterres de l'est, veut que vous lui apportiez 30 Morceaux de démon des cryptes."}},
[9126] = {"Des morceaux pour la faucheuse d'os", nil, {"Rohan l'assassin, à la chapelle de l'Espoir de Lumière dans les Maleterres de l'est, veut 30 Fragments d'os."}},
[9128] = {"L'équation élémentaire", nil, {"L'archimage Angela Dosantos à la chapelle de l'Espoir de Lumière dans les Maleterres de l'Est veut 30 Noyaux des éléments."}},
[9130] = {"Des marchandises de Lune-d'argent", nil, {"Remettez la Demande de Sathiel au maître du ciel Brillaube, à Brise-Clémente."}},
[9131] = {"Armer un cuirassier", nil, {"Korfax, de la chapelle de l'Espoir de Lumière dans les Maleterres de l'est, veut 30 Morceaux de sombrefer."}},
[9133] = {"Un vol jusqu'à Lune-d'argent", nil, {"Achetez un vol pour Lune-d’argent et remettez la Demande de Sathiel à Sathren Aube-azur, à Lune-d’argent."}},
[9134] = {"La maîtresse du ciel Tombejour", nil, {"Apporter les Marchandises de Sathiel à la maîtresse du ciel Tombejour, juste devant la Porte du Berger, à Lune-d’argent."}},
[9135] = {"Le retour vers Sathiel", nil, {"Achetez un vol de retour vers Brise-Clémente dans les Bois des Chants éternels, et remettez les Marchandises de Sathiel à Sathiel."}},
[9136] = {"La flore sauvage", nil, {"Rayne de la chapelle de l'Espoir de Lumière dans les Maleterres de l'est veut 30 Palmes sauvages."}},
[9138] = {"Le village de Solcouronne", nil, {"Tuez 10 Gardes nérubis, puis retournez voir l'arcaniste Vandril à Tranquillien dans les Terres fantômes."}},
[9139] = {"Brume-d'or", nil, {"Vaincre 6 Fantômes quel'dorei et 4 Âmes en peine quel'dorei au village de Brume-d'or, puis retourner voir l'arcaniste Vandril à Tranquillien, dans les Terres fantômes."}},
[9140] = {"Coursevent", nil, {"Collectez 6 Substances fantasmatiques et 4 Fragments de gargouille. Rapportez-les à l'arcaniste Vandril à Tranquillien dans les Terres fantômes."}},
[9141] = {"On m'appelle « Le Coq »", nil, {"Le commandant d'ordonnance Metz, à la chapelle de l'Espoir de Lumière dans les Maleterres de l'est, veut une Marque de valeur de l'Aube d'argent."}},
[9143] = {"S'occuper de Zeb'Sora", nil, {"Collecter 6 Oreilles de trolls de Zeb'Sora et les remettre au Forestier Valanna dans les Terres fantômes."}},
[9144] = {"Disparu dans les Terres fantômes", nil, {"Suivez la route qui part du village de Brise-clémente vers le sud. Lorsque vous approcherez les Terres fantômes, cherchez le Courrier Aube-glorieuse."}},
[9145] = {"Il faut aider le forestier Valanna !", nil, {"Trouver le forestier Valanna et le lieutenant Tomathren sur la rive du lac Elrendar, dans les Terres fantômes, près du village de Solcouronne."}},
[9146] = {"Un rapport pour le capitaine Hélios", nil, {"Parler au capitaine Hélios à l'enclave des Pérégrins, sur la rive sud du lac Elrendar, dans les Terres fantômes."}},
[9147] = {"Le courrier blessé", nil, {"L'apothicaire Thedra du bois des Chants éternels veut que vous lui apportiez 4 Échantillons de sang pestiféré pris sur les bêtes des Terres fantômes."}},
[9148] = {"Livraison à Tranquillien", nil, {"Apportez la Lettre de Lune-d'argent à l'arcaniste Vandril à Tranquillien, dans les Terres fantômes."}},
[9149] = {"La côte pestiférée", nil, {"Apportez 6 Epines dorsales de murlocs pestiférés à l'apothicaire Renzithen, à Tranquillien dans les Terres fantômes."}},
[9150] = {"Les dépouilles du passé", nil, {"Le magistère Darenis veut que vous vous rendiez au sanctum de la Lune, à l'ouest de Tranquillien, et que vous récupériez 8 Essences de mana cristallisées sur les Dévoreurs arcaniques et les Changemana qui occupent les lieux."}},
[9151] = {"Le sanctum du Soleil", nil, {"Parlez au magistère Idonis au sanctum du Soleil dans les Terres fantômes."}},
[9152] = {"Les marchandises de Tombal", nil, {"Retrouver la Marchandise de Rathis Tombal et la lui rapporter à Tranquillien dans les Terres fantômes."}},
[9153] = {"Sous les ombres", nil, {"Surveillez votre carte pour trouver une région subissant une attaque du Fléau. Allez-y et endommagez une nécropole en exterminant les créatures du Fléau aux alentours. Rapportez ensuite 10 de leurs Runes nécrotiques au commandant Thomas Helleran, à la chapelle de l'Espoir de Lumière dans les Maleterres de l'est."}},
[9154] = {"La chapelle de l'Espoir de Lumière", nil, {"Apportez l'Annonce de l'appel aux armes au gardien des registres, à la chapelle de l'Espoir de Lumière dans les Maleterres de l'est."}},
[9155] = {"Dans la Malebrèche", nil, {"Tuez 10 Affameurs ressuscités et 10 Cannibales dégingandés dans la Malebrèche. Retournez voir le nécrotraqueur Rathiel à Tranquillien dans les Terres fantômes pour obtenir une récompense."}},
[9156] = {"On recherche : Putrepoing et Luzran", nil, {"Recherchez Putrepoing et Luzran dans les Terres fantômes et tuez-les. Rapportez leurs têtes au nécrotraqueur Rathiel à Tranquillien pour empocher une prime."}},
