[9978] = {"Par tous les moyens nécessaires", nil, {"Trouvez Empoor sur la route entre Tuurem et Shattrath, dans la Forêt de Terokkar. Puis convainquez-le de vous dire ce qu'il sait."}},
[9979] = {"Le marchand des vents Lathrai", nil, {"Parlez au marchand des vents Lathrai près de la Taverne de la Fin du monde dans la ville basse à Shattrath."}},
[9982] = {"Il se fait appeler Altruis...", nil, {"Si vous cherchez à affronter la Légion ardente, allez trouver Altruis le Souffrant dans l'ouest de Nagrand."}},
[9983] = {"Il se fait appeler Altruis...", nil, {"Si vous cherchez à affronter la Légion ardente, allez trouver Altruis le Souffrant dans l'ouest de Nagrand."}},
[9984] = {"L'hôte de la cité cachée", nil, {"Apportez les Ordres de Luanga à Andarl au Bastion allérien, dans la Forêt de Terokkar."}},
[9986] = {"S'opposer aux arakkoa", nil, {"Tuez Ashkaz au Voile Reskk, Ayit au Voile Shienor et Urdak au Voile Skith, puis retournez voir Thander au Bastion allérien."}},
[9987] = {"S'opposer aux arakkoa", nil, {"Tuez Ashkaz au Voile Reskk, Ayit au Voile Shienor et Urdak au Voile Skith, puis retournez voir Rokag au fort des Brise-pierres."}},
[9988] = {"Le meilleur ami des élégants", nil, {"Apportez 30 Plumes d'arakkoa à Ros'eleth au Bastion allérien."}},
[9990] = {"Enquête à Tuurem", nil, {"Trouvez la Boîte scellée puis apportez-la au lieur de terre Tavgren juste à l'extérieur du Fourré cénarien dans la forêt de Terokkar."}},
[9991] = {"Observer le terrain", nil, {"Asseyez-vous et profitez du voyage. Parlez à Altruis quand vous aurez terminé l'observation."}},
[9992] = {"Graines d'olemba", nil, {"Ramassez 30 Graines d'olemba et apportez-les à Ros'eleth au Bastion allérien."}},
[9993] = {"Huile de graines d'olemba", nil, {"Apportez 30 Graines d'olemba à Rakoria au fort des Brise-pierres."}},
[9994] = {"Que sont ces choses ?", nil, {"Apportez la Boîte de pièces détachées à Jenai Murmétoile au Bastion allérien dans la forêt de Terokkar."}},
[9995] = {"Que sont ces choses ?", nil, {"Apportez la Boîte de pièces détachées à Tooki au fort des Brise-pierres dans la forêt de Terokkar."}},
[9996] = {"L'attaque de la halte Aile-de-feu", nil, {"Tuez 10 Défenseurs Aile-de-feu, 10 Garde-sang Aile-de-feu et 10 Démonistes Aile-de-feu. Puis retournez au Poste allérien dans la forêt de Terokkar pour faire votre rapport au lieutenant Meridian."}},
[9997] = {"L'attaque de la halte Aile-de-feu", nil, {"Tuez 10 Défenseurs Aile-de-feu, 10 Garde-sang Aile-de-feu et 10 Démonistes Aile-de-feu. Puis retournez au camp des Brise-pierres dans la forêt de Terokkar pour faire votre rapport au sergent Chawni."}},
[9998] = {"Des voisins encombrants", nil, {"Tuez 12 Péons redimensionnés puis faites votre rapport à Theloria Capedombre au Poste allérien."}},
[9999] = {"Gagner du temps", nil, {"Altruis le Souffrant dans l'ouest de Nagrand vous a demandé de tuer 2 Légionnaires gangregarde, 3 Ingénieurs mo'arg et 8 Bricoleurs gan'arg."}},
[10000] = {"Une présence indésirable", nil, {"Tuez 12 Péons redimensionnés puis faites votre rapport au traqueur des ombres Kaide."}},
[10001] = {"Le maître d'œuvre", nil, {"Altruis le Souffrant dans l'ouest de Nagrand vous a demandé de récupérer les Plans du maître d'œuvre."}},
[10002] = {"La messagère d'Aile-de-feu", nil, {"Trouvez et tuez Lisaile Tissefeu, puis retournez voir Theloria Capedombre."}},
[10003] = {"La messagère d'Aile-de-feu", nil, {"Trouvez et tuez Lisaile Tissefeu, puis retournez voir le traqueur des ombres Kaide."}},
[10004] = {"Patience et compréhension", nil, {"Altruis le Souffrant dans l'ouest de Nagrand veut que vous apportiez les Plans du maître d'œuvre à Sal'salabim à Shattrath et que vous le convainquiez de vous aider."}},
[10005] = {"Informer le lieur de terre Tavgren", nil, {"Parlez au lieur de terre Tavgren juste à l'extérieur du Fourré cénarien dans la forêt de Terokkar."}},
[10006] = {"Informer le lieur de terre Tavgren", nil, {"Parlez au lieur de terre Tavgren juste à l'extérieur du Fourré cénarien dans la forêt de Terokkar."}},
[10007] = {"Éclaircir les rangs", nil, {"Tuez 10 Dévastateurs Mâche-les-os et 6 Brise-dos Mâche-les-os puis retournez voir Theloria Capedombre."}},
[10008] = {"Ce qui se passe à Terokkar reste à Terokkar", nil, {"Le traqueur des ombres Kaide veut que vous alliez tuer 10 Dévastateurs Mâche-les-os et 6 Brise-dos Mâche-les-os."}},
[10009] = {"Casser des crânes", nil, {"Sal'salabim de Shattrath veut que vous récupériez la Dette de Raliq, la Dette de Coosh'coosh et la Dette de Floon."}},
[10010] = {"C'est si facile ?", nil, {"Sal'salabim de Shattrath veut que vous retourniez voir Altruis le Souffrant dans l'ouest de Nagrand."}},
[10011] = {"Le camp de forge Anéanti", nil, {"Altruis le Souffrant dans l'ouest de Nagrand vous a demandé de détruire le Camp de forge Haine et le Camp de forge Peur."}},
[10012] = {"Les plans des gangr'orcs", nil, {"Apportez les Plans des gangr'orcs à Bertelm au Bastion allérien, dans la Forêt de Terokkar."}},
[10013] = {"Une main invisible", nil, {"Apportez les Plans des gangr'orcs à Rokag au Fort des Brise-pierres, dans la Forêt de Terokkar."}},
[10016] = {"Les queues de worgs des bois", nil, {"Bertelm du Bastion allérien veut que vous lui apportiez 12 Queues de worgs des bois. Vous pouvez prendre ces queues soit sur des worgs des bois, soit sur des worgs Alpha des bois."}},
[10017] = {"Des réserves durement sollicitées", nil, {"Apportez 8 Glandes à venin de croc-d'effroi à Sha'nir à Shattrath.\n\nAccomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10018] = {"Les habits de l'esprit du loup", nil, {"Apportez 12 Fourrures de worg des bois à Malukaz au fort des Brise-pierres."}},
[10019] = {"Plus de glandes à venin", nil, {"Conditions :"}},
[10020] = {"Un remède pour Zahlia", nil, {"Apportez le Sang de Regard-de-pierre à Sha'nir à Shattrath.\n\nAccomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10021] = {"Rétablir la Lumière", nil, {"Ishanah veut que vous vous rendiez à l'avant-poste arakkoa à l'est de Shattrath et que vous utilisiez la Fiole bénite pour purifier l'Autel de l'est, l'Autel de l'ouest et l'Autel du nord.\n\nAccomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10022] = {"L'insaisissable Gueule-de-fer", nil, {"Apportez la Fourrure de Gueule-de-fer à Bertelm au Bastion allérien."}},
[10023] = {"Le patriarche Gueule-de-fer", nil, {"Apportez la Fourrure de Gueule-de-fer à Malukaz au fort des Brise-pierres."}},
[10024] = {"Les visions de Voren'thal", nil, {"Apportez 8 Yeux de basilic trempécaille à l'arcaniste Adyria à Shattrath.\n\nAccomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10025] = {"Plus d'yeux de basilic", nil, {"Conditions :"}},
[10026] = {"Perturbations magiques", nil, {"Andarl du Bastion allérien veut que vous alliez tuer 10 Traqueurs dimensionnels."}},
[10027] = {"Perturbations magiques", nil, {"Tuez 10 Traqueurs dimensionnels et retournez voir Kurgatok au fort des Brise-pierres."}},
[10028] = {"Calices de puissance", nil, {"Apportez 8 Calices draeneï à Ros'eleth au Bastion allérien."}},
[10030] = {"La récupération des os", nil, {"Récupérez 10 Ossements sans repos. Apportez-les à Ramdor le Fol, juste à côté du côté ouest du Cercle d'observance à Auchindoun, qui se trouve au milieu du désert des Ossements de la forêt de Terokkar."}},
[10031] = {"Aider les Perdus à trouver leur chemin", nil, {"Détruisez 10 Esprits perdus et 10 Squelettes roués, puis parlez à Soolaveen à la Caravane de réfugiés, dans le désert des Ossements de la forêt de Terokkar."}},
[10032] = {"Artéfacts de Tuurem", nil, {"Récupérez 6 Artéfacts tuurem sur les Charognards tuurem et les Chasseurs tuurem et apportez-les à Rakoria au fort des Brise-pierres."}},
[10033] = {"On recherche : des Fouette-radius morts !", nil, {"Tuez 20 Fouette-radius puis retournez voir Taela Vassanfin au Bastion allérien dans la forêt de Terokkar."}},
[10034] = {"On recherche : des Fouette-radius morts !", nil, {"Tuez 20 Fouette-radius puis retournez voir Mawg Trait-sinistre au fort des Brise-pierres dans la forêt de Terokkar."}},
[10035] = {"Torgos !", nil, {"Faites venir Torgos à l'aide de la Carcasse de Trachela. Procurez-vous une Plume de la queue de Torgos et rapportez-la à Taela Vassanfin au Bastion allérien dans la forêt de Terokkar."}},
[10036] = {"Torgos !", nil, {"Faites venir Torgos à l'aide de la Carcasse de Trachela. Procurez-vous une Plume de la queue de Torgos et apportez-la à Mawg Trait-sinistre au fort des Brise-pierres dans la forêt de Terokkar."}},
[10037] = {"J'préférerais aller à la pêche", nil, {"Apportez 8 Anguilles Scintécaille en parfait état à Seth à Shattrath."}},
[10038] = {"Parler au soldat Weeks", nil, {"Parlez au Soldat Weeks près de l'entrée nord de Grangol'var, dans la forêt de Terokkar."}},
[10039] = {"Parler à l'éclaireur Neftis", nil, {"Parlez à l'éclaireur Neftis près de l'entrée sud de Grangol'var, dans la forêt de Terokkar."}},
[10040] = {"Qui sont-ils ?", nil, {"Parlez avec l'Initié ténébreux, le Travailleur ténébreux et le Conseiller ténébreux en portant le déguisement. Puis retournez voir le soldat Weeks à Grangol'var dans la forêt de Terokkar."}},
[10041] = {"Qui sont-ils ?", nil, {"Parlez avec l'Initié ténébreux, le Travailleur ténébreux et le Conseiller ténébreux en portant le déguisement. Puis retournez voir l'éclaireur Neftis à Grangol'var dans la forêt de Terokkar."}},
[10042] = {"Tuer le Conseil des ombres !", nil, {"Tuez 10 Exécuteurs ténébreux, 10 Invocateurs ténébreux et le Maître d'ombre Dolent, puis retournez auprès du lieutenant Gravillemartel au Bastion allérien dans la forêt de Terokkar."}},
[10043] = {"Tuer le Conseil des ombres !", nil, {"Tuez 10 Exécuteurs ténébreux, 10 Invocateurs ténébreux et le Maître d'ombre Dolent, puis retournez auprès de la conseillère Faila au fort des Brise-pierres dans la forêt de Terokkar."}},
[10044] = {"Une visite à la grande-mère", nil, {"Parlez à la grande-mère Geyah et écoutez ce qu'elle a à vous dire."}},
[10045] = {"Composants matériels", nil, {"La grande-mère Geyah de Garadar en Nagrand vous a demandé de cueillir 1 Racine d'Olemba, 1 Canneberge, 1 Palme telaari et 1 Épine-de-dragon."}},
[10047] = {"Le sentier de la Gloire", nil, {"Purifiez 8 Squelettes piétinés sur le Sentier de la Gloire avec de l'Eau bénite draeneï, puis retournez voir le Scrute-dimensions Kryv au bastion de l'Honneur."}},
[10050] = {"Âmes Inflexibles", nil, {"Le garde d'honneur Wesilow veut que vous vainquiez 12 Fantassins inflexibles, 10 Ensorceleurs inflexibles et 5 Chevaliers inflexibles à l'Armurerie de l'expédition, au sud."}},
[10051] = {"L'évasion de la halte Aile-de-feu", nil, {"Escortez Isla Tressétoile jusqu'à un lieu sûr puis faites votre rapport au capitaine Auric Courresoleil au Bastion allérien dans la forêt de Terokkar."}},
[10052] = {"L'évasion de la halte Aile-de-feu", nil, {"Escortez Isla Tressétoile jusqu'à un lieu sûr puis faites votre rapport à la conseillère Faila au fort des Brise-pierres dans la forêt de Terokkar."}},
[10055] = {"Faut pas gâcher", nil, {"Apportez 8 morceaux de Métal récupéré et 8 morceaux de Bois récupéré à Dumphry au bastion de l'Honneur."}},
[10057] = {"Se tourner vers les chefs", nil, {"Triomphez des fantômes de l'archimage Xintor et du lieutenant-commandant Thalvos, puis retournez voir le garde d'honneur Wesilow à la tour au nord-ouest du bastion de l'Honneur."}},
[10058] = {"Un cadeau ancien", nil, {"Récupérez les \"Mystères de la Lumière\" et rapportez-le au père Malgor Devidicus au bastion de l'Honneur."}},
[10063] = {"La Ligue des explorateurs ? C'est pas un truc de gnomes ?", nil, {"Trouvez Clopper Crackboum, qui campe dans une vieille carapace de tortue sur la côte au nord du Guet du sang."}},
[10064] = {"Parle à la main", nil, {"Présentez-vous à l'éclaireur Jorli au Repos du redresseur de torts."}},
[10065] = {"Ouvrir un chemin", nil, {"Tuez 10 Ravageurs enragés puis retournez voir l'éclaireur Jorli au Repos du redresseur de torts."}},
[10066] = {"Oh, quelle toile embrouillée...", nil, {"Tuez 8 Noueurs mutés et faites votre rapport au redresseur de torts Corin au Repos du redresseur de torts."}},
[10067] = {"Les élémentaires d'eau souillée", nil, {"Tuez 6 Élémentaires d'eau souillés et retournez voir le redresseur de torts Corin au Repos du redresseur de torts."}},
[10068] = {"Les projectiles des Arcanes", nil, {"Apprenez Projectiles des Arcanes auprès de Julia Heurtesoleil. Ensuite, lancez Projectiles des Arcanes à 2 reprises sur un des Mannequins d'entraînement qui se trouvent devant la Flèche Solaire."}},
[10069] = {"La voie de la Lumière", nil, {"Apprenez le Jugement et le Sceau de piété auprès de Jesthenis Heurtesoleil. Lancez le Sceau de piété sur vous-même, et exercez-vous à utiliser le Jugement sur un Mannequin d'entraînement devant la flèche Solaire."}},
[10070] = {"Le tir assuré", nil, {"Apprenez le Tir assuré auprès du Forestier Sallina. Trouvez ensuite un Mannequin d'entraînement et utilisez Tir assuré contre lui à cinq reprises."}},
[10071] = {"L'éviscération", nil, {"Apprenez Éviscération auprès de l'Eclaireur Kariel. Trouvez un Mannequin d'entraînement devant la Flèche Solaire et utilisez Éviscération dessus à trois reprises."}},
[10072] = {"Soigner les blessés", nil, {"Apprenez les Soins rapides auprès de la Matrone Arène. Exercez-vous à lancer 5 fois des Soins rapides sur des Estafettes blessées."}},
[10073] = {"L'immolation", nil, {"Apprenez Immolation auprès de l'Invocateur Teli'Larien. Ensuite, lancez Immolation à 5 reprises sur un Mannequin d'entraînement, devant la Flèche Solaire."}},
[10074] = {"La poudre de cristal d'Oshu'gun", nil, {"La Directrice de recherche Amereldine de Halaa veut 10 Échantillons de poudre de cristal d'Oshu'gun."}},
[10075] = {"La poudre de cristal d'Oshu'gun", nil, {"Conditions :"}},
[10076] = {"La poudre de cristal d'Oshu'gun", nil, {"Le Directeur de recherche Kartos de Halaa veut 10 Échantillons de poudre de cristal d'Oshu'gun."}},
[10077] = {"La poudre de cristal d'Oshu'gun", nil, {"Conditions :"}},
[10078] = {"Anéantir les indésirables", nil, {"Utilisez la Torche enflammée pour brûler les Lance-lames de la Horde surplombant le sentier de la Gloire. Puis apportez la Torche enflammée à Dumphry au bastion de l'Honneur."}},
[10079] = {"Mineur, tu dors, ton wagon va trop vite", nil, {"Tuez 12 Sapeurs gan'arg puis retournez voir le contremaître Biggums."}},
[10081] = {"Rencontrer Mère Kashur", nil, {"La Grande-mère Geyah veut que vous parliez avec Mère Kashur."}},
[10082] = {"Les ancêtres agités", nil, {"Mère Kashur vous a demandé de donner le repos à 8 Esprits orcs agités."}},
[10085] = {"Une visite aux ancêtres", nil, {"Mère Kashur des Terres ancestrales en Nagrand vous a demandé de visiter le Poste de Berceau-de-l'été, les Ruines du Crâne ricanant, Garadar et les Ruines de l'Orbite-sanglante."}},
[10086] = {"Je travaille... Pour la Horde !", nil, {"Apportez 8 morceaux de Métal récupéré et 8 morceaux de Bois récupéré à Megzeg Phalanjette à Thrallmar."}},
[10087] = {"Brûlez-les... Pour la Horde !", nil, {"Utilisez la Torche enflammée pour brûler le Canon est de l'Alliance et le Canon ouest de l'Alliance, puis revenez voir Megzeg Phalanjette à Thrallmar."}},
[10091] = {"Les machines à âmes", nil, {"Volez 5 Machines à âmes puis apportez-les à la Maître-espionne Mehlisah Hautecouronne sur la Terrasse de la Lumière à Shattrath."}},
[10093] = {"Le temple de Telhamat", nil, {"Parlez à Amaan le Sage au Temple de Telhamat dans l'ouest de la Péninsule des Flammes infernales."}},
[10094] = {"Le codex de sang", nil, {"Lisez le Codex de sang dans le Labyrinthe des Ombres d'Auchindoun."}},
[10095] = {"Au cœur du labyrinthe", nil, {"Détruisez Marmon, puis allez à la terrasse de la Lumière à Shattrath pour faire un rapport de ce qui s'est passé dans le Labyrinthe des ombres à la Maître-espionne Mehlisah Hautecouronne."}},
[10096] = {"Sauver les sporeloks", nil, {"Tuez 10 Drague-marais et 10 Rôdeurs des marais, puis retournez voir Lauranna Thar'well au Refuge cénarien dans le marécage de Zangar."}},
[10097] = {"Frère contre frère", nil, {"Tuez le Tisseur d'ombre Syth dans les Salles des Sethekk, puis libérez Lakka. Retournez voir Isfar à l'extérieur des Salles des Sethekk lorsque vous aurez réussi à la sauver."}},
[10098] = {"L'héritage de Terokk", nil, {"Apportez le Masque de Terokk, la Rémige de Terokk et la Saga de Terokk à Isfar à l'extérieur des Salles des Sethekk."}},
[10099] = {"Le cerveau", nil, {"Trouvez et tuez Z'kral dans les mines sous le bastion de l'Honneur, puis faites votre rapport au contremaître Biggums."}},
[10101] = {"Lorsque les esprits parlent", nil, {"Mère Kashur des Terres ancestrales en Nagrand vous a demandé de partir à la recherche de la vision que vous avez eue. Trouvez K'ure à Oshu'gun."}},
[10102] = {"Un secret dévoilé", nil, {"Parlez à A'dal à Shattrath."}},
[10103] = {"Faire son rapport à Zurai", nil, {"Présentez-vous à Zurai au poste du Rat des marais dans le marécage de Zangar."}},
[10104] = {"Des soucis à Tuurem", nil, {"Parlez à Andarl au Bastion allérien dans la forêt de Terokkar."}},
[10105] = {"Des nouvelles pour Rakoria", nil, {"Parlez à Rakoria, au fort des Brise-pierres, dans la forêt de Terokkar."}},
[10106] = {"Les fortifications des Flammes infernales", nil, {"Prendre le Surplomb, le Stade et la Colline brisée, puis retourner voir l'adjudante Tracy Fièrepuits au bastion de l'Honneur dans la péninsule des Flammes infernales."}},
[10107] = {"Mesures diplomatiques", nil, {"Jorin Œil-mort de Garadar en Nagrand vous a demandé de trouver Lantresor de la Lame dans les ruines de la Lame ardente et de lui parler."}},
[10108] = {"Mesures diplomatiques", nil, {"Mo'mor le Briseur, de Telaar en Nagrand, vous a demandé de trouver Lantresor de la Lame dans les ruines de la Lame ardente et de lui parler."}},
[10109] = {"Il me les faut !", nil, {"Koissa, qui se trouve en face du Guet des clans dans le sud de Nagrand, veut que vous lui apportiez 3 Gaz d'élémentaires d'air."}},
[10110] = {"Les fortifications des Flammes infernales", nil, {"Prendre le Surplomb, le Stade et la Colline brisée, puis retourner voir le Crieur-de-guerre Coquard à Thrallmar."}},
[10111] = {"Rapportez-moi cet œuf !", nil, {"Koissa, de Nagrand, veut que vous utilisiez le Bond-o-tron 4000 pour parvenir jusqu'au nid de l'arbre tout proche et voler l'Œuf mystérieux."}},
[10112] = {"Une faveur personnelle", nil, {"Récupérez 5 des Marchandises volées de Lathrai. Rapportez-les au marchand des vents Lathrai près de la Taverne de la Fin du monde dans le quartier de la Ville basse de Shattrath."}},
[10113] = {"Le safari de Nesingwary", nil, {"Jones « Fusil » veut que vous parliez à Hemet Nesingwary au safari de Nesingwary, en périphérie du nord-est de Nagrand."}},
[10114] = {"Le safari de Nesingwary", nil, {"Ohlorn Pérégrin veut que vous parliez à Shado « Fitz » Pérégrin au safari de Nesingwary en Nagrand."}},
[10115] = {"La déviance des Tourbedague", nil, {"Tuez 3 Assassins Tourbedague et 15 Marche-boue Tourbedague."}},
[10116] = {"On recherche : chef Mummaki", nil, {"Apportez le Totem du chef Mummaki à Ikuti au Havre d'Orebor."}},
[10117] = {"On recherche : chef Mummaki", nil, {"Apportez le Totem du chef Mummaki au chasseur des ombres Denjai."}},
[10118] = {"Un message pour les Tourbedague", nil, {"Tuez 3 Assassins tourbedagues et 15 Marche-boue tourbedagues, puis retournez voir le Chasseur des ombres Denjai à Zabra'jin."}},
[10119] = {"À travers la Porte des ténèbres", nil, {"Traversez la Porte des ténèbres et allez trouver le commandant Duron. Il attend votre arrivée de l'autre côté - en Outreterre."}},
[10120] = {"Arrivée en Outreterre", nil, {"Le général de division Orion à la Porte des ténèbres dans la péninsule des Flammes infernales veut que vous apportiez le Rapport d'Orion à Vlagga Fileplume."}},
[10121] = {"Éradiquer la Légion ardente", nil, {"Parlez au sergent Fracasse-crâne à la Caravane de ravitaillement, juste à l'extérieur de Thrallmar, à l'est."}},
[10122] = {"À portée de la citadelle", nil, {"Fouillez le camp le plus au nord le long du Rempart du sud à la recherche de traces du soldat Imarion."}},
[10123] = {"Le Ravin de Gangrétincelle", nil, {"Le sergent Fracasse-crâne de la Caravane de ravitaillement à l'est de Thrallmar dans la péninsule des Flammes infernales veut que vous alliez tuer 4 Diablotins Attise-flammes, 6 Porteguerres infernaux et 1 Mande-effroi."}},
[10124] = {"Poste avancé : Trépas du saccageur", nil, {"Présentez-vous au commandant des lignes avancées To'arch au Trépas du saccageur."}},
[10125] = {"Mission : Brouiller les communications", nil, {"Le commandant des lignes avancées To'arch du Trépas du saccageur dans la péninsule des Flammes infernales vous a demandé d'utiliser les Explosifs de To'arch et de détruire l'Antenne de la Légion : Mépris, l'Antenne de la Légion : Haine, et l'Antenne de la Légion : Peur."}},
[10127] = {"Mission : Couper le cordon", nil, {"Le commandant des lignes avancées To'arch du Trépas du saccageur dans la péninsule des Flammes infernales vous a demandé de tuer 4 Dresseurs gangrenés, 8 Garde-courroux et le Dominateur Shi'aziv."}},
[10129] = {"Mission : Portes Murketh et Shaadraz", nil, {"Parlez au Chef d'escadrille Brack, bombardez la Porte Murketh et la Porte Shaadraz, puis retournez voir le Commandant des lignes avancées To'arch au Trépas du saccageur."}},
[10130] = {"Le flanc ouest", nil, {"Tuez 10 Gardes de la Main brisée, 10 Berserkers de la Main brisée et 5 Acolytes de la Main brisée, puis faites votre rapport au lieutenant Amadi dans la tour de garde ouest du bastion de l'Honneur."}},
[10132] = {"Menace colossale", nil, {"Tuez 5 Colosses déchaînés puis retournez voir Tola'thion au Poste cénarien dans la péninsule des Flammes infernales."}},
[10134] = {"Un indice de cristal cramoisi", nil, {"Apportez l'Éclat de cristal cramoisi à Tola'thion au Poste cénarien dans la péninsule des Flammes infernales."}},
[10136] = {"Les Liaisons cruelles", nil, {"Tuez Arazzius le Cruel, puis retournez voir Nazgrel à Thrallmar."}},
[10140] = {"En route pour le bastion de l'Honneur", nil, {"Volez jusqu'au bastion de l'Honneur et apportez le Rapport de Duron au maréchal Isildor."}},
[10141] = {"La renaissance de la Légion", nil, {"Parlez au sergent Altumus à la Caravane de ravitaillement de l'est, juste à l'extérieur du bastion de l'Honneur, à l'est."}},
[10142] = {"Le chemin de l'Angoisse", nil, {"Le sergent Altumus de la Caravane de ravitaillement de l'est, qui se trouve à l'est du bastion de l'Honneur dans la péninsule des Flammes infernales, vous a demandé de tuer 4 Diablotins attise-flammes, 6 Porteguerres infernaux et 1 Mande-effroi."}},
[10143] = {"La halte de l'expédition", nil, {"Présentez-vous au commandant des lignes avancées Kingston à la Halte de l'expédition."}},
[10144] = {"Couper l'ennemi de ses renforts", nil, {"Tuez des ennemis du Portail Grimh et du Portail Kaalez pour collecter des Pierres runiques démoniaques.\n\nUtilisez les Amorces de Kingston à l'intérieur du Portail Grimh et du Portail Kaalez.\n\nRetournez voir le commandant des lignes avancées Kingston avec les explosifs restants."}},
[10146] = {"Mission : Portes Murketh et Shaadraz", nil, {"Parlez au Chef d'escadrille Dabir'ee, bombardez la Porte Murketh et la Porte Shaadraz, puis retournez voir le Commandant des lignes avancées Kingston à la Halte de l'expédition."}},
[10159] = {"Nettoyer la colline de Roncecroc", nil, {"Tuez 8 Ravageurs Roncecroc et 8 Crache-venin Roncecroc, puis retournez voir Mahuram Sabot-robuste au Poste cénarien dans la péninsule des Flammes infernales."}},
[10160] = {"Connais ton adversaire", nil, {"Parlez au lieutenant Amadi à la vieille tour de garde juste à l'ouest du bastion de l'Honneur."}},
[10161] = {"En cas d'urgence...", nil, {"Récupérez 30 Débris de zeppelin et rapportez-les à Laïus « Brailleur » Airbuse à l'Épave du zeppelin."}},
[10162] = {"Mission : La saillie abyssale", nil, {"Le commandant des lignes avancées To'arch du Trépas du saccageur dans la péninsule des Flammes infernales vous demande de détruire 20 Péons gan'arg, 5 Surveillants mo'arg et 5 Gangrecanons."}},
[10163] = {"Mission : La saillie abyssale", nil, {"Le chef d'escadrille Griffongar de la Halte de l'expédition dans la péninsule des Flammes infernales vous a demandé de détruire 20 Péons gan'arg, 5 Surveillants mo'arg et 5 Gangrecanons."}},
[10164] = {"Tout va bien se passer", nil, {"Grandpère Aldrimus, à l'extérieur des Cryptes Auchenaï d'Auchindoun, vous a demandé d'entrer dans les Cryptes Auchenaï et de détruire l'Exarque Maladaar."}},
[10165] = {"Casser les prix", nil, {"Le prince-nexus Haramad, qui se trouve à l'extérieur des Tombes-mana, veut que vous alliez tuer le prince-nexus Shaffar et que vous lui rapportiez le Cafetan de Shaffar."}},
[10166] = {"La mémoire de Blanchécorce", nil, {"Larianna Ruissevent veut que vous placiez le Pendentif du vieux Blanchécorce près de la pierre runique au Bosquet incendié."}},
[10167] = {"Auchindoun...", nil, {"Voyagez jusqu'aux Cryptes Auchenaï dans le désert des Ossements de la forêt de Terokkar et tuez l’Exarque Maladaar pour libérer l'esprit de D'ore."}},
[10168] = {"Par les yeux de l'âme...", nil, {"Trouvez un Miroir des âmes quelque part dans les cryptes Auchenaï et utilisez-le pour invoquer un Esprit assombri issu des Ancêtres d'anciens orcs en Nagrand. Détruisez 15 Esprits assombris afin que les ancêtres puissent reposer en paix.\n\nRetournez voir Mère Kashur dans les Terres ancestrales lorsque vous aurez terminé cette tâche."}},
[10169] = {"Il faut savoir perdre", nil, {"Le magistère Falris de Shattrath veut que vous apportiez la Fiole de sérum sédatif sur la Terrasse de la Lumière et que vous la versiez dans la Marmite du régiment d'Haronem."}},
[10170] = {"Retourner voir la grande-mère", nil, {"Retournez à Garadar et parlez à la grande-mère Geyah."}},
[10171] = {"Le chef inconsolable", nil, {"La grande-mère Geyah à Garadar vous a demandé de parler à Garrosh."}},
[10172] = {"Il n'y a plus d'espoir", nil, {"Parlez à la grande-mère Geyah et rapportez-lui les propos de Garrosh."}},
[10173] = {"Le bâton de l'archimage", nil, {"Reprenez le Bâton de l'archimage Vargoth à Ekkorash l'Inquisitrice et apportez-le à Ravandwyr dans la Zone 52. Pour faire sortir Ekkorash de sa cachette, répandez la Poudre d'abjuration sur le brasero au centre des ruines."}},
[10174] = {"La malédiction de la Tour pourpre", nil, {"Utilisez le Bâton de l'archimage Vargoth pour entrer en contact avec ce dernier.\n\nSi vous perdez le Bâton de l'archimage Vargoth, allez voir Ravandwyr dans la Zone 52 pour obtenir un double."}},
[10176] = {"Ar'kelos le Gardien", nil, {"Entrez dans la Tour pourpre au centre de Kirin'Var et triomphez d'Ar'kelos, puis parlez à l'archimage Vargoth."}},
[10177] = {"Troubles à Auchindoun", nil, {"Présentez-vous au commandant Mahfuun à l'entrée du Labyrinthe des Ombres à Auchindoun, dans la forêt de Terokkar."}},
[10178] = {"Trouver l'espion To'gun", nil, {"Trouvez l'espion To'gun dans le Labyrinthe des ombres d'Auchindoun."}},
[10179] = {"Le protecteur de Kirin'Var", nil, {"Parlez au Protecteur Valemort à Kirin'Var, au Raz-de-Néant."}},
[10180] = {"Incapable de rester à l'écart", nil, {"Parlez à Isfar, qui se trouve à l’intérieur des salles des Sethekk à Auchindoun."}},
[10182] = {"Le mage de bataille Dathric", nil, {"Trouvez le domicile du mage de bataille Dathric dans l'Allée des sorciers et replacez sa Lame sur le râtelier d'armes. Puis parlez au protecteur Valemort à Kirin’Var."}},
[10183] = {"En route pour la Zone 52", nil, {"Apportez les Notes du néantologue au chef-de-fusée Karlingue dans la Zone 52 au Raz-de-Néant."}},
[10184] = {"Vestiges malveillants", nil, {"Le protecteur Valemort de Kirin'Var veut que vous vainquiez 20 Esprits disjoints de la Place centrale de Kirin'Var."}},
[10185] = {"Un sort pire que la mort", nil, {"Tuez 10 Pourfend-mage et 10 Chercheurs de mana puis retournez voir le protecteur Valemort à Kirin'Var."}},
[10186] = {"On vous engage !", nil, {"Récupérez 10 Cristaux de matrice d'etherlithium et apportez-les au chef-de-fusée Karlingue dans la Zone 52 au Raz-de-Néant."}},
[10187] = {"Un message pour l'archimage", nil, {"Parlez à l'archimage Vargoth en haut de la Tour pourpre, au Raz-de-Néant."}},
[10188] = {"Le cachet de Krasus", nil, {"Reprenez le Cachet de Krasus à la Lieuse de sort Maryana à la Manaforge Coruu, au nord-ouest de la Tour Pourpre."}},
[10189] = {"Manaforge B'naar", nil, {"Le maître-espion Thalodien de la Zone 52 veut que vous vous rendiez dans les environs de la manaforge B'naar et que vous récupériez le Tableau de service du personnel de B'naar sur le capitaine Arathyn.\n\nAccomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10190] = {"Recharger les batteries", nil, {"Utilisez la Pétoire charge-batterie sur assez de chasseurs phasiques pour faire monter le niveau de recharge de la batterie jusqu'à 10. Ensuite, rapportez-la à la robotologue Alley aux ruines d'Enkaat au Raz-de-Néant."}},
[10191] = {"Le Mod. V vit !", nil, {"Escortez le Maxx A. Million Mod. V dans les ruines d'Enkaat jusqu'à son retour en sécurité. Ensuite, allez voir la robotologue Alley à la sortie des ruines d'Enkaat au Raz-de-Néant."}},
[10192] = {"Le compendium de Krasus", nil, {"Récupérez le Compendium de Krasus - Chapitre 1, le Compendium de Krasus - Chapitre 2 et le Compendium de Krasus - Chapitre 3 sur la Place centrale de Kirin'Var."}},
[10193] = {"Cibles majeures", nil, {"Le maître-espion Thalodien de la Zone 52 veut que vous alliez à la manaforge B'naar pour y tuer 2 Maîtres-dimensionnels solfurie, 6 Ingénieurs dimensionnels solfurie et 8 Géologues solfurie.\n\nAccomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10194] = {"Vol furtif", nil, {"Parlez à Veronia à l'extérieur de la Zone 52 et volez jusqu'à la manaforge Coruu.\n\nAccomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10197] = {"Un déguisement convaincant", nil, {"Procurez-vous une Robe d'arcaniste solfurie, une paire de Gants de chercheur solfurie et un Médaillon de garde solfurie et apportez-les à Caledis Brillaube à la manaforge Coruu au Raz-de-Néant.\n\nAccomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10198] = {"Rassembler des informations", nil, {"Utilisez le déguisement de Solfurie pour vous rendre à la Manaforge Coruu et écouter la conversation entre le Commandant Forgelaube et l'Arcaniste Ardonis.\n\nAccomplir des tâches pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10199] = {"Un petit coup de fouet", nil, {"Procurez-vous 5 Aiguillons de raie du Néant pour Bottes dans la Zone 52 du Raz-de-Néant."}},
[10200] = {"Retourner voir Thalodien", nil, {"Retournez voir le maître-espion Thalodien dans la Zone 52 du Raz-de-Néant."}},
[10201] = {"Et maintenant, l'instant de vérité", nil, {"Trouvez Grek et demandez-lui d'essayer l'huile sur son arme. Une fois qu'il l'aura testée, retournez décrire le résultat à Rakoria."}},
[10202] = {"Un déserteur", nil, {"Allez à la manaforge Ara et parlez au magistère Theledorn.\n\nAccomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10203] = {"Zapper des atouts inestimables", nil, {"Utilisez le Zappeur d'ultra-déconsolidation pour renvoyer le Creus-O-Matic ultra-rotatif, la Griffe de drague servopneumatique, l'Analyseur de terrain multispectral et le Gros chariot rempli d'explosifs dans la Zone 52 au Raz-de-Néant. Présentez-vous ensuite au Sapeur en chef Failonfeu à l'extrémité est de la fissure qui parcourt la Zone 52 au Raz-de-Néant."}},
[10204] = {"Cristaux de gemmes de sang", nil, {"La magistrice Larynna veut que vous alliez à la manaforge B'naar pour vous y procurer un Éclat de gemme de sang sur un Magistère solfurie. Utilisez-le près des grands Cristaux de gemme de sang et retournez la voir dans la Zone 52.\n\nAccomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10205] = {"L'écumeur-dimensionnel Nesaad", nil, {"Tuez l'Écumeur-dimensionnel Nesaad, puis retournez voir le traqueur-du-Néant Khay'ji dans la Zone 52 de Raz-de-Néant."}},
[10206] = {"Prendre sa part", nil, {"Récupérez 10 modèles de Technologie éthérienne et rapportez-les à Papa Roulette, dans la Zone 52 au Raz-de-Néant."}},
[10208] = {"Perturber leurs renforts", nil, {"Terrassez les ennemis qui se trouvent autour des portails Xilus et Kruul pour rassembler des Pierres runiques démoniaques.\n\nUtilisez une Amorce de To'arch à l'intérieur des portails Xilus et Kruul.\n\nRapportez les Amorces restantes à To'arch au Trépas du saccageur dans la péninsule des Flammes infernales."}},
[10209] = {"Le trophée de l'invocateur Kanthin", nil, {"Triomphez de l'invocateur Kanthin à la manaforge Duro, sur l'île au nord du bastion des Solfurie, et reprenez-lui la Pierre de Glacius."}},
[10210] = {"A'dal", nil, {"Parlez à A'dal, le chef des Sha'tar, au centre de la Terrasse de la Lumière."}},
[10211] = {"Ville de Lumière", nil, {"Suivez le serviteur de Khadgar et écoutez son histoire. Retournez ensuite voir Khadgar."}},
[10212] = {"Héros des Mag'har", nil, {"Retournez voir la grande-mère Geyah à Garadar en Nagrand."}},
[10213] = {"Enquêter au sujet de l'accident", nil, {"Cherchez des survivants de l'accident."}},
[10216] = {"La sécurité avant tout", nil, {"L’Artificier Morphalius veut que vous tuiez 10 Écumeurs de cryptes éthériens, 5 Sorciers éthériens, 5 Traqueurs du Nexus et 5 Lieurs de sort éthériens.\n\nLorsque ce sera fait, trouvez le Transporteur éthérien dans la salle avant celle de Shaffar et activez-le."}},
[10218] = {"Le dur labeur des autres", nil, {"Escortez le cryo-ingénieur Sha'heen dans les tombes-mana pour qu'il puisse recueillir l'éther contenu dans les collecteurs d'éther de Shaffar.\n\nAllez voir le prince-nexus Haramad au campement du Consortium à l'extérieur des tombes-mana une fois votre mission accomplie."}},
[10220] = {"Les forcer à écouter", nil, {"Mordiba veut que vous vainquiez 12 Fantassins inflexibles, 8 Chevaliers inflexibles et 6 Ensorceleurs inflexibles à l'Armurerie de l'expédition à l'ouest du Poste de Brise-échine."}},
[10221] = {"Dr Boum !", nil, {"Tuez Dr Boum puis rapportez la bonne nouvelle au sapeur en chef Failonfeu dans la Zone 52 au Raz-de-Néant."}},
[10222] = {"La garnison Solfurie", nil, {"Le Protecteur Valemort à Kirin'Var veut que vous tuiez 10 Archers solfurie."}},
[10223] = {"Mort à Daellis", nil, {"Le protecteur Valemort de Kirin'Var veut que vous alliez tuer le maître Daellis Heurtelaube."}},
[10224] = {"De l'essence pour les moteurs", nil, {"Collectez 7 Essences d'âmes en peine de mana et rapportez-les à l'ingénieur en chef Trep à la Zone 52 au Raz-de-Néant."}},
[10225] = {"Rapport à l'ingénierie", nil, {"Apportez le Cristal de matrice d'etherlithium à l'Ingénieur en chef Trep dans la Zone 52 au Raz-du-Néant."}},
[10226] = {"Extraction de puissance élémentaire", nil, {"Extrayez 5 Puissances élémentaires et apportez-les à l'Ingénieur en chef Trep, dans la Zone 52 au Raz-de-Néant."}},
[10227] = {"Je vois des draeneï morts", nil, {"Parlez à Ramdor le Fol, qui se trouve sur le tas d'os à l'extérieur des Cryptes Auchenaï."}},
[10228] = {"Ezekiel", nil, {"Trouvez Ezekiel dans la ville de Shattrath, et parlez-lui."}},
[10229] = {"Déchiffrer le tome", nil, {"Apportez le Tome mystérieux à Althen l'Historien au Poste de Brise-échine."}},
[10230] = {"Le cor de bataille", nil, {"Prenez le Cor de bataille des Inflexibles au lieutenant-commandant Thalvos et apportez-le à Althen l'Historien au Poste de Brise-échine."}},
[10231] = {"Quel livre ? Je ne vois pas de livre.", nil, {"Ezekiel dit que le livre a été volé. Votre meilleure piste serait de trouver les ruffians et de voir s'ils ont des informations à ce sujet."}},
[10232] = {"Ferrailler contre la Légion", nil, {"Tuez 5 Forgerons funestes mo'arg et 15 Ingénieurs gan'arg. Puis retournez voir Papa Roulette dans la Zone 52 du Raz-de-Néant."}},
[10233] = {"Incendier le bastion des Solfurie", nil, {"À l'aide d'une Torche enflammée, détruisez 4 Balistes Solfurie et 4 Tentes Solfurie, puis retournez voir le lieutenant-ensorceleur Morran à Kirin'Var."}},
[10234] = {"Le malheur d'un démon...", nil, {"Rassemblez 10 Pièces de saccageur gangrené et apportez-les à Mama Roulette au comptoir des Terres-médianes à l'intérieur de l'écodôme Terres-médianes au Raz-de-Néant."}},
[10235] = {"Dégriffer Griffe-funeste", nil, {"Procurez-vous la Main de Griffe-funeste et apportez-la à Mama Roulette au comptoir des Terres-médianes à l'intérieur de l'écodôme Terres-médianes au Raz-de-Néant."}},
[10236] = {"L'Outreterre, c'est nul !", nil, {"Trouvez 6 Pièces de déchiqueteur et retournez voir le contremaître Razelcraz près de la mine au nord-ouest de Thrallmar."}},
[10237] = {"Avertir la Zone 52 !", nil, {"Annoncez la mauvaise nouvelle au chef-de-fusée Karlingue dans la Zone 52 au Raz-de-Néant."}},
[10238] = {"Comment servir le gobelin", nil, {"Sauvez Manni, Moh et Jakk des camps des gangr'orcs et retournez voir le contremaître Razelcraz près de la mine au nord-ouest de Thrallmar."}},
[10239] = {"Une source d'énergie potentielle", nil, {"Le lieutenant-ensorceleur Morran de Kirin'Var veut que vous vous empariez de 10 Cubes d'isolation à énergie dans les exploitations des elfes de sang de la manaforge Coruu."}},
[10240] = {"Établir un périmètre", nil, {"Utilisez le Mécanisme d'activation des runes près de la Rune est, la Rune nord-est et la Rune ouest. Ces runes se trouvent quelque part autour des quartiers de l'Allée des sorciers et de la Place centrale de Kirin'Var. Retournez voir le lieutenant-ensorceleur Morran une fois que vous aurez accompli cette tâche."}},
[10241] = {"Diversion à la manaforge B'naar", nil, {"Tuez 8 Magistères Solfurie et 8 Garde-sang Solfurie puis retournez voir l'exarque Orelis dans la Zone 52.\n\nAccomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10242] = {"Le poste de Brise-échine", nil, {"L'Apothicaire Zelana du Trépas du saccageur vous a demandé de parler au Chef d'escadrille Brack et de prendre une wyverne pour rallier le Poste de Brise-échine, puis de remettre l'Échantillon de sang de l'Orbite-sanglante à l'apothicaire Albreck au Poste de Brise-échine."}},
[10243] = {"Technologie naaru", nil, {"Entrez dans la manaforge B'naar et trouvez la Console de contrôle B'naar.\n\nAccomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10245] = {"La transcription de la console de B'naar", nil, {"Apportez la Transcription de la console de B'naar à l'anachorète Karja dans la Zone 52 au Raz-de-Néant.\n\nAccomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10246] = {"Attaquer la manaforge Coruu", nil, {"Partez pour la manaforge Coruu et tuez 8 Arcanistes solfurie et 5 Chercheurs solfurie. Retournez voir l'exarque Orelis lorsque vous aurez accompli cette tâche.\n\nAccomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10247] = {"Le docteur Vomisa, Ph.T.", nil, {"Allez voir le docteur Vomisa, Ph.T. au Terrain d'essai du Raz-de-Néant."}},
[10248] = {"Toi, le robot", nil, {"Activez les Commandes du saccageur ferrailleur X6000 et testez-en les fonctionnalités. Puis faites part de vos impressions au docteur Vomisa, Ph.T. sur le Terrain d'essai de Raz-de-Néant. Réglez tout problème qui se ferait jour."}},
[10249] = {"On retourne voir le chef !", nil, {"Retournez voir immédiatement le Chef-de-fusée Karlingue, dans la Zone 52 de Raz-de-Néant !"}},
[10250] = {"Vengeance sanglante", nil, {"Sonnez le Cor de bataille des Inflexibles près de la bannière de l'Alliance. Tuez Urtrak puis retournez voir Althen l'Historien au Poste de Brise-échine."}},
[10251] = {"Le Grand Dessein Du Maître ?", nil, {"Trouvez Nitrin l'Instruit à l'Armurerie abandonnée en Nagrand."}},
[10252] = {"Vision des morts", nil, {"Nitrin, à l'Armurerie abandonnée en Nagrand, veut que vous lui rameniez 1 Globe oculaire de Gronn des montagnes, 1 Bec de Venteroc supérieur sans défaut et 1 Poche de graisse de vieux sabot-fourchu."}},
[10253] = {"Levixus le Mandeur d'âme", nil, {"Tuez Levixus et prenez-lui le Livre des morts.\n\nApportez le Livre des morts à Ramdor le Fol à l'extérieur des Cryptes Auchenaï, à l'entrée d'Auchindoun.\n\nVous aviez passé un marché, vous vous souvenez ?"}},
[10254] = {"Le commandant Danath", nil, {"Le maréchal Isildor veut que vous vous présentiez au commandant de corps Danath Trollemort au bastion de l'Honneur."}},
[10255] = {"Tester l'antidote", nil, {"Administrez l'Antidote cénarien à un Sanglier infernal massif et observez le résultat. Une fois l'expérience terminée, retournez voir Thiah Crin-rouge au Poste cénarien dans la péninsule des Flammes infernales."}},
[10256] = {"Trouver le Maître des clés", nil, {"Trouvez Apex et procurez-vous un Focalisateur en cristal d'Apex. Retournez à la Tour pourpre et utilisez le Focalisateur à proximité de l'Orbe de l'archimage Vargoth pour découvrir qui détient la clé qui permettra de lever la malédiction pesant sur la tour."}},
[10257] = {"S'emparer de la Pierre cruciale", nil, {"Entrez dans la Botanica du donjon de la Tempête et prenez la Pierre cruciale au commandant Sarannis. Apportez-la à l'archimage Vargoth à la Tour pourpre."}},
[10258] = {"Honorer les morts", nil, {"Présentez-vous au commandant Hogarth à l'Armurerie de l'expédition."}},
[10259] = {"Dans la brèche", nil, {"Tuez 6 Gangregardes de l'invasion et retournez voir l'émissaire de l'Aube d'argent Fièrepuits."}},
[10261] = {"On recherche : servomoteur d'annihilateur !", nil, {"Procurez-vous un Servomoteur d'annihilateur et apportez-le à Papa Roulette dans la Zone 52 au Raz-de-Néant."}},
[10262] = {"Un monceau d'éthériens", nil, {"Collectez 10 Insignes de Zaxxis et apportez-les au Traqueur-du-Néant Khay'ji dans la Zone 52 de Raz-de-Néant."}},
[10263] = {"Aider le Consortium", nil, {"Parlez au traqueur-du-Néant Khay'ji dans la Zone 52 au Raz-de-Néant."}},
[10264] = {"Aider le Consortium", nil, {"Parlez au Traqueur-du-Néant Khay'ji dans la Zone 52 au Raz-de-Néant."}},
[10265] = {"La collection de cristaux du Consortium", nil, {"Procurez-vous un artéfact cristallin d'Arklon et rapportez-le au traqueur-du-Néant Khay'ji à la Zone 52 au Raz-de-Néant."}},
[10266] = {"Demande d'assistance", nil, {"Trouvez Gahruj et proposez-lui vos services. Il se trouve au comptoir des Terres-médianes dans l'Écodôme Terres-médianes au Raz-de-Néant."}},
[10267] = {"Récupérer ce qui nous revient de droit", nil, {"Récupérez 10 Boîtes d'équipement topographique et rapportez-les à Gahruj au comptoir des Terres-médianes dans l'Écodôme Terres-médianes au Raz-de-Néant."}},
[10268] = {"Une audience avec le prince", nil, {"Remettez l'Équipement topographique à l'image du prince-nexus Haramad à la Foudreflèche au Raz-de-Néant."}},
[10269] = {"Point de triangulation numéro un", nil, {"Utilisez l'Appareil de triangulation pour vous indiquer la direction du premier point de triangulation. Une fois que vous l'aurez trouvé, donnez-en l'emplacement au camelot Hazzin au poste de garde du Protectorat dans l'île de la manaforge Ultris au Raz-de-Néant."}},
[10270] = {"Une proposition pas si modeste", nil, {"Récupérez le Bloc d'alimentation du téléporteur et apportez-le à la Plateforme de téléportation éthérienne sur la Crête céleste à l'est de la manaforge Ultris. Activez la plateforme et donnez le Bloc d'alimentation du téléporteur à l'Image du marchand des vents Marid pour qu'il puisse l'installer."}},
[10271] = {"Passer aux choses sérieuses", nil, {"Chargez le Récolteur d’essence en l’utilisant sur des Drakes du Néant ou sur des Dragons du Néant de la Crête Céleste, puis retournez dans la zone de la Plateforme de téléportation éthérienne."}},
[10272] = {"Un départ prometteur", nil, {"Récoltez 15 Œufs de draconiens du Néant, puis retournez auprès de Tyri et Jorad sur la Crête céleste."}},
[10273] = {"Malencontreuses distractions", nil, {"Parlez au Marchand des vents Marid et attirez-le à l'écart de son commerce. Débarrassez-vous de lui puis retournez auprès de Tyri et Jorad sur la Crête céleste."}},
[10274] = {"S'emparer de la Crête céleste", nil, {"Utilisez le Défi du Vol bleu pour provoquer Veraku en duel sur le bord sud de la Crête céleste. Triomphez de lui et retournez voir Tyri et Jorad sur la Crête céleste."}},
[10275] = {"Point de triangulation numéro deux", nil, {"Utilisez l'Appareil de triangulation pour vous indiquer la direction du deuxième point de triangulation. Une fois que vous l'aurez trouvé, donnez-en l'emplacement au marchand des vents Tuluman au Point d'ancrage de Tuluman, juste de l'autre côté du pont vers l'île de la manaforge Ara au Raz-de-Néant."}},
[10276] = {"Le triangle est triangulé", nil, {"Récupérez le Cristal d'Ata'mal et rapportez-le à l'image du prince-nexus Haramad à la Foudreflèche au Raz-de-Néant."}},
[10277] = {"Les Grottes du temps", nil, {"Andormu vous demande de suivre la Protectrice du temps dans les Grottes du temps."}},
[10278] = {"Les failles dimensionnelles", nil, {"Utilisez le Générateur de faille dimensionnelle instable dans les Champs de distorsion. Récupérez 3 Néants dimensionnels sur des Marcheurs du Vide instables, puis retournez voir Ogath le Fol au poste de Brise-échine."}},
[10279] = {"Dans le repaire du maître", nil, {"Parlez à Andormu dans les Grottes du temps."}},
[10280] = {"Livraison spéciale à Shattrath", nil, {"Apportez le cristal d'Ata'mal à A'dal sur la Terrasse de la Lumière à Shattrath."}},
[10281] = {"Présentation formelle", nil, {"Retrouvez Tyri à la pointe nord de la Crête céleste et allez lui parler."}},
[10282] = {"Hautebrande d'antan", nil, {"Andormu des Grottes du temps vous demande de vous aventurer dans le Hautebrande d'antan et d'aller voir Erozion."}},
[10283] = {"La diversion de Taretha", nil, {"Rendez-vous au bastion de Fort-de-Durn et placez 5 charges incendiaires dans les tonneaux situés dans chacun des pavillons d'internement grâce au Paquet de bombes incendiaires qui vous a été donné par Erozion.\n\nParlez à Thrall dans les oubliettes du bastion de Fort-de-Durn une fois les Pavillons d'internement incendiés."}},
[10284] = {"Évasion de Fort-de-Durn", nil, {"Faites signe à Thrall lorsque vous serez prêt à continuer. Suivez-le dans son évasion du bastion de Fort-de-Durn et aidez-le à libérer Taretha et à accomplir son destin.\n\nAllez parler à Erozion au Hautebrande d'antan si vous parvenez à accomplir cette tâche."}},
[10285] = {"Retour vers Andormu", nil, {"Retournez voir le jeune dragon Andormu, aux Grottes du temps dans le désert de Tanaris."}},
[10286] = {"Le secret d'Arelion", nil, {"La magistrice Carinda veut que vous interrogiez le magistère Aledis, qui a été vu pour la dernière fois quittant le Guet de l'épervier pour se rendre dans le marécage de Zangar."}},
[10287] = {"La maîtresse dévoilée", nil, {"Retournez voir la magistrice Carinda au Guet de l'épervier, à la Péninsule des Flammes infernales."}},
[10288] = {"Arrivée en Outreterre", nil, {"Le commandant Duron à la Porte des ténèbres dans la péninsule des Flammes infernales veut que vous apportiez le Rapport de Duron à Amish Marteau-hardi."}},
[10289] = {"En route pour Thrallmar", nil, {"Voyagez sur le coursier du vent de Vlagga Fileplume jusqu'à Thrallmar. Apportez le Rapport d'Orion au général Krakork."}},
[10290] = {"En quête de farahlite", nil, {"Récoltez 4 morceaux de Farahlite brute et retournez voir Zuben Elgenubi à la Foudreflèche."}},
[10291] = {"Se présenter à Nazgrel", nil, {"Le général Krakork de Thrallmar veut que vous parliez à Nazgrel."}},
[10293] = {"Toucher le Gros-lot", nil, {"Zuben Elgenubi de la Foudreflèche veut que vous vous procuriez un Noyau de farahlite."}},
[10294] = {"La crête du Vide", nil, {"Allez sur la crête du Vide et tuez les créatures que vous y trouverez. Réunissez 40 Éclats d'âme de la crête du Vide et apportez-les à Ogath le Fol au Poste de Brise-échine."}},
[10295] = {"Du fond des abysses", nil, {"Tuez le baron du Vide Galaxis et récupérez l'Éclat de son âme. Apportez l'éclat à Ogath le Fol au Poste de Brise-échine."}},
[10296] = {"Le Noir Marécage", nil, {"Voyagez à travers les Grottes du temps jusqu'au Noir Marécage pendant l'ouverture de la Porte des ténèbres et parlez à Sa'at."}},
[10297] = {"L'ouverture de la Porte des ténèbres", nil, {"Sa'at, à l'intérieur du Noir Marécage des Grottes du temps, vous a demandé de défendre Medivh jusqu'à ce qu'il parvienne à ouvrir la Porte des ténèbres.\n\nRetournez voir Sa'at si vous parvenez à accomplir cette tâche."}},
[10298] = {"Héros de la progéniture", nil, {"Retournez voir l'enfant, Andormu, aux Grottes du temps dans le désert de Tanaris."}},
[10299] = {"Extinction de la manaforge B'naar", nil, {"Retournez à la manaforge B'naar et prenez le Cristal d'accès de B'naar au Surveillant Theredis. Utilisez-le à la console de contrôle de la manaforge B'naar pour l'éteindre et faites votre rapport à l'anachorète Karja.\n\nAccomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10300] = {"La reconstruction du bâton", nil, {"Obtenez 4 Éclats de cristal sans défaut sur les fantômes des ruines d'Enkaat et rapportez-les à Ravandwyr dans la Zone 52."}},
[10301] = {"Déverrouiller le compendium", nil, {"Prenez l'Oculus héliotrope à la Saccage-sort Marathelle au bastion des Solfurie."}},
[10302] = {"Mutations volatiles", nil, {"Tuez 8 Mutations volatiles, puis retournez voir la botaniste Taerix au Point d'impact dans le val d'Ammen."}},
[10303] = {"Les elfes de sang", nil, {"Tuez 10 Eclaireurs elfes de sang, puis retournez voir Tolaan sur l'Ombrecrête du val d'Ammen."}},
[10304] = {"Le redresseur de torts Aldar", nil, {"Parlez au redresseur de torts Aldar au point d'impact au val d'Ammen."}},
[10305] = {"L'abjuriste Belmara", nil, {"Trouvez la maison de l'abjuriste Belmara dans l'Allée des sorciers et replacez le livre sur l'Étagère de livres. Puis parlez au protecteur Valemort à Kirin'Var."}},
[10306] = {"L'adjurateur Luminrath", nil, {"Trouvez le domicile de l'adjurateur Luminrath dans l'allée des sorciers et replacez le mantelet dans sa Commode. Puis parlez au protecteur Valemort à Kirin'Var."}},
[10307] = {"Cohlien Tissegivre", nil, {"Trouvez le domicile de Cohlien Tissegivre dans l'Allée des sorciers et replacez le chapeau dans sa Cantine. Puis parlez au protecteur Valemort à Kirin'Var."}},
[10308] = {"Un autre monceau d'éthériens", nil, {"Conditions :"}},
[10309] = {"C'est un saccageur gangrené, mais avec un cœur", nil, {"Récupérez le Cœur du saccageur gangrené et apportez-le à N. D. Malacamp, au Monceau au Raz-de-Néant."}},
[10310] = {"Sabotez le portail dimensionnel !", nil, {"Escortez Drijya jusqu'au portail dimensionnel au Site d'invasion Destructeur, et faites en sorte qu'il reste en sécurité pendant qu'il tente de le saboter. Puis parlez à Gahruj au comptoir des Terres-médianes à l'intérieur de l'écodôme Terres-médianes au Raz-de-Néant."}},
[10311] = {"Drijya a besoin de votre aide", nil, {"Parlez à Drijya près du Site d'invasion Destructeur au Raz-de-Néant."}},
[10312] = {"Les annales de Kirin'Var", nil, {"Récupérez les Annales de Kirin'Var à l'hôtel de ville de Kirin'Var et rapportez-les au protecteur Valemort."}},
[10313] = {"Mesurer les énergies dimensionnelles", nil, {"L'exarque Orelis de la Zone 52 veut que vous emportiez l'Orbe d'harmonisation interdimensionnelle à la manaforge B'naar et que vous l'utilisiez à l'extrémité de ses quatre pipelines.\n\nAccomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10314] = {"Le doute subsiste...", nil, {"Récupérez 10 Restes répugnants sur les esprits retors de la Cour de la chapelle de Kirin'Var et apportez-les au protecteur Valemort."}},
[10315] = {"Neutraliser les néantomanciens", nil, {"Tuez 15 Néantomanciens solfurie puis retournez voir le traqueur-du-Néant Oazul au Point d'ancrage de Tuluman au Raz-de-Néant."}},
[10316] = {"À la recherche de preuves", nil, {"Fouillez la Cour de la chapelle de Kirin'Var à la recherche d'indices au sujet de l'identité de la personne responsable de la manipulation des esprits."}},
[10317] = {"S'occuper du contremaître", nil, {"Occupez-vous du contremaître Tombesoleil à l'intérieur de la mine de Trelleum, qui se trouve sous la manaforge Ara au Raz-de-Néant."}},
[10318] = {"S'occuper du contremaître", nil, {"Tuez le Grand seigneur Grincegarr. Retournez ensuite auprès du marchand des vents Tuluman au Point d'ancrage de Tuluman de Raz-de-Néant."}},
[10319] = {"La capture du phylactère", nil, {"Récupérez le Phylactère de Naberius dans le quartier de la Cour de la chapelle de Kirin'Var et apportez-le au protecteur Valemort."}},
[10320] = {"Détruisez Naberius !", nil, {"Le protecteur Valemort de Kirin'Var vous demande d'affronter Naberius et de le vaincre dans la Cour de la chapelle du village."}},
[10321] = {"Extinction de la manaforge Coruu", nil, {"Voyagez jusqu'à la manaforge Coruu, à l'est de la Zone 52, et prenez le Cristal d'accès de Coruu à la surveillante Seylanna. Utilisez-le à la console de la manaforge Coruu pour l'éteindre.\n\nAccomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10322] = {"Extinction de la manaforge Duro", nil, {"L'anachorète Karja vous demande de vous rendre à la manaforge Duro et de vous procurer le Cristal d'accès de Duro porté par le surveillant Athanel. Utilisez-le sur la Console de la manaforge Duro pour déclencher l'extinction.\n\nAccomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10323] = {"Extinction de la manaforge Ara", nil, {"Voyagez jusqu'à la manaforge Ara et prenez le Cristal d'accès d'Ara sur le contremaître Azarad. Utilisez-le à la console de la manaforge Ara pour l'éteindre.\n\nAccomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10324] = {"La grande chasse aux frôle-lune", nil, {"Apportez 6 Peaux de chevreuil frôle-lune à Acteon au Guet d'azur."}},
[10325] = {"Les marques de Kil'jaeden", nil, {"Remettre 10 Marques de Kil'jaeden à Adyen le Gardelumière, à Shattrath.\n\nAccomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10326] = {"De nouvelles marques de Kil'jaeden", nil, {"Conditions :"}},
[10327] = {"Une marque de Kil'jaeden", nil, {"Conditions :"}},
[10328] = {"Briefing des Solfurie", nil, {"Allez à la manaforge Duro et procurez-vous le Briefing militaire solfurie et le Briefing des arcanes solfurie auprès des soldats en poste. Retournez voir l'exarque Orelis une fois cette tâche accomplie.\n\nAccomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10329] = {"Extinction de la manaforge B'naar", nil, {"Retournez à la manaforge B'naar et prenez le Cristal d'accès de B'naar au Surveillant Theredis. Utilisez-le à la console de contrôle de la manaforge B'naar pour l'éteindre et faites votre rapport au maître-espion Thalodien.\n\nAccomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10330] = {"Extinction de la manaforge Coruu", nil, {"Procurez-vous le Cristal d'accès de Coruu sur la surveillante Seylanna. Utilisez-le à la console de contrôle de Coruu pour éteindre la manaforge et retournez voir Caledis Brillaube.\n\nAccomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès des Aldor."}},
[10331] = {"Des outils indispensables", nil, {"Reprenez le Marteau de forge aux apprentis de Kirin'Var et rapportez-le à l'apprenti Andrethan à Kirin'Var."}},
[10332] = {"Le maître forgeron Rhonsus", nil, {"L'apprenti Andrethan de Kirin'Var veut que vous alliez tuer le fantôme de Rhonsus, le maître forgeron de la ville."}},
[10333] = {"Aider Mama Roulette", nil, {"Parlez à Mama Roulette au comptoir des Terres-médianes à l'intérieur de l'écodôme Terres-médianes au Raz-de-Néant."}},
[10334] = {"Plus de clochettes", nil, {"Trouver Marguerite dans les champs près de la colline de la chapelle de Kirin'Var, au Raz-de-Néant."}},
[10335] = {"Cartographier les ruines", nil, {"Placez les trois Repères de géomètre puis revenez voir Zéphyrion à la Foudreflèche au Raz-de-Néant."}},
[10336] = {"Les serviteurs de Culuthas", nil, {"Tuez 10 Molosses de Culuthas et 5 Yeux de Culuthas."}},
[10337] = {"Marguerite rentre au pays", nil, {"Escortez Marguerite pour la ramener saine et sauve à son propriétaire, Thadell."}},
[10338] = {"Extinction de la manaforge Duro", nil, {"Retournez à la manaforge Duro et prenez le Cristal d'accès de Duro au surveillant Athanel. Utilisez-le à la console de contrôle de Duro pour l'éteindre et allez faire votre rapport au maître-espion Thalodien.\n\nAccomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10339] = {"L'Ethereum", nil, {"Le commandant Ameer veut que vous tuiez 5 Assassins de l'Ethereum, 5 Troupiers de choc de l'Ethereum, 2 Chercheurs de l'Ethereum et le capitaine Zovax puis que vous activiez le Transpondeur zéta de l'Ethereum."}},
[10340] = {"La halte du Fracas", nil, {"Le commandant des lignes avancées Kingston veut que vous parliez au chef d'escadrille Dabir'ee, que vous preniez un griffon pour la halte du Fracas et que vous vous présentiez à Runetog Marteau-hardi."}},
[10341] = {"Cognez-les maintenant qu'ils sont à terre", nil, {"Tuez 8 Adjurateurs solfurie, 6 Franc-archers solfurie et 4 Centurions solfurie. Retournez voir la magistrice Larynna dans la Zone 52 lorsque vous aurez achevé cette tâche.\n\nAccomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10342] = {"Dégoter de la lauze de peau-de-schiste", nil, {"Collectez 5 Lauzes de peau-de-schiste et apportez-les à Bottes dans la Zone 52 au Raz-de-Néant."}},
[10343] = {"L'invasion sans fin", nil, {"Fouillez la zone de Kirin'Var infestée de créatures de mana à la recherche d'un Fragment de bombe de mana et apportez-le au lieutenant-ensorceleur Morran."}},
[10344] = {"Le chef d'escadrille Griffongar", nil, {"Parlez au Chef d'escadrille Griffongar à la Halte du Fracas."}},
[10345] = {"La chair ment...", nil, {"Utilisez le Boute-feu du Protectorat sur 12 Cadavres flétris. Lorsque vous aurez fini, rendez le Boute-feu du Protectorat à l'agent Araxes à l'extérieur du Puits d'accès Zéon."}},
[10346] = {"Retour à la saillie abyssale", nil, {"Le griffonnier Soufflevent de la halte du Fracas dans la péninsule des Flammes infernales vous demande de détruire 20 Péons gan'arg, 5 Surveillants mo'arg et 5 Gangrecanons."}},
[10347] = {"Retour à la saillie abyssale", nil, {"Le chef d'escadrille Brack du Trépas du saccageur dans la péninsule des Flammes infernales vous demande de détruire 20 Péons gan'arg, 5 Surveillants mo'arg et 5 Gangrecanons."}},
[10348] = {"De nouvelles opportunités", nil, {"Cueillez 15 Clochettes d'ivoire et retournez voir Mihrdad au comptoir des Terres-médianes."}},
[10349] = {"La lieuse de terre", nil, {"Apportez l'Éclat de cristal cramoisi à la lieuse de terre Galandria Noctebrise au Poste cénarien dans la péninsule des Flammes infernales."}},
[10350] = {"Behomat", nil, {"Allez parler à Behomat, à l'Étage des commerces de l'Exodar."}},
[10351] = {"Remèdes naturels", nil, {"Utilisez la Graine de revitalisation au Cercle de la lieuse de terre pour guérir la terre autour du cristal. Ensuite, retournez voir la lieuse de terre Galandria Noctebrise au Poste cénarien dans la péninsule des Flammes infernales avec toutes les informations que vous aurez."}},
[10352] = {"Un don de laine", nil, {"Conditions :"}},
[10353] = {"Arconus l'Insatiable", nil, {"L'agent Ya-six du Puits d'accès Zéon du Raz-de-Néant vous a demandé de tuer Arconus l'Insatiable puis de retourner voir le Commandant Ameer au Poste de garde du Protectorat au Raz-de-Néant."}},
[10354] = {"Un don de soie", nil, {"Conditions :"}},
[10355] = {"Chair flétrie", nil, {"Réunissez 5 Échantillons d'hydre desséchée et 5 Échantillons de seigneur-tourbe flétri et apportez-les à Ruam à Telredor."}},
[10356] = {"Un don de tisse-mage", nil, {"Conditions :"}},
[10357] = {"Un don d'étoffe runique", nil, {"Conditions :"}},
[10358] = {"Encore de l'étoffe runique", nil, {"Conditions :"}},
[10359] = {"Un don de laine", nil, {"Conditions :"}},
[10360] = {"Un don de soie", nil, {"Conditions :"}},
[10361] = {"Un don de tisse-mage", nil, {"Conditions :"}},
[10362] = {"Un don d'étoffe runique", nil, {"Conditions :"}},
[10363] = {"Encore de l'étoffe runique", nil, {"Conditions :"}},
[10365] = {"Extinction de la manaforge Ara", nil, {"Voyagez jusqu'à la manaforge Ara et prenez le Cristal d'accès d'Ara sur le contremaître Azarad. Utilisez-le à la console de la manaforge Ara pour l'éteindre.\n\nAccomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10366] = {"Jol", nil, {"Allez parler à Jol, au Sanctum des anachorètes de l'Exodar."}},
[10367] = {"Un traître parmi nous", nil, {"Naladu, aux ruines de Sha'naar, veut que vous vous procuriez la Clé de Sha'naar dans une hutte des alentours."}},
[10368] = {"Les Anciens crapuches", nil, {"Libérez Morod le Touillevent, Akoru le Mandefeu et Aylaan le Troublelonde aux ruines de Sha'naar. Retournez voir Naladu lorsque ce sera fait."}},
[10369] = {"La fin d'Arzeth", nil, {"Utilisez le Bâton des anciens crapuches contre Arzeth le Sans-clémence et tuez-le lorsqu'il aura perdu ses pouvoirs. Retournez voir Naladu aux ruines de Sha'naar lorsque ce sera fait."}},
[10372] = {"Une demande discrète", nil, {"Parlez à Eralan à Tranquillien."}},
[10376] = {"Symbole d'espoir", nil, nil},
[10378] = {"Consumer la magie", nil, nil},
[10381] = {"L'Aldor c'est mort", nil, {"Retournez voir l'exarque Orelis dans la Zone 52.\n\nAccomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10382] = {"Partir pour le front", nil, {"Prenez le griffon pour la halte de l'Honneur et parlez au grand maréchal Brock."}},
[10384] = {"Les données de l'Ethereum", nil, {"L'Image du commandant Ameer du lieu de rassemblement de l'Ethereum - Transpondeur zéta de l'Ethereum - veut que vous récupériez la cellule de données de l'Ethereum."}},
[10385] = {"Risque de dommages cérébraux : élevé", nil, {"L'image du commandant Ameer au Transpondeur zéta du lieu de rassemblement de l'Ethereum vous demande de lui rapporter 15 recueils de Données de relais de l'Ethereum."}},
[10388] = {"Retour à Thrallmar", nil, {"Retournez à Thrallmar et allez parler à Nazgrel."}},
[10389] = {"Agonie et ténèbres", nil, {"Le magistère Faucon-de-sang veut que vous alliez tuer 10 Fiels de la terreur dans les bassins d'Aggonar."}},
[10390] = {"Le camp de forge Mageddon", nil, {"Nazgrel de Thrallmar vous demande d'attaquer le Camp de forge Mageddon, de tuer 10 Serviteurs gan'arg et de revenir avec la tête de Tranchescie."}},
[10391] = {"Les canons de la rage", nil, {"Détruisez 3 Gangrecanons Mod.I au Camp de forge Rage et retournez voir Nazgrel à Thrallmar."}},
[10392] = {"Une porte vers l'abîme", nil, {"Tuez Arix'Amal pour vous emparer de la Clé du portail de la Légion ardente. Utilisez cette clé sur la Rune de Mépris, puis retournez voir Nazgrel à Thrallmar."}},
[10393] = {"Plans abjects", nil, {"Apportez la Missive de la Légion ardente au Magistère Faucon-de-sang à Thrallmar."}},
[10394] = {"Une perturbation : le camp de forge Mageddon", nil, {"Tuez 10 Serviteurs gan'arg et Tranchescie au Camp de forge Mageddon puis retournez voir le Commandant des lignes avancées Brock à la halte de l'Honneur."}},
[10395] = {"La sombre missive", nil, {"Apportez la Missive au Scrute-dimensions Kryv au donjon principal du bastion de l'Honneur."}},
[10396] = {"Ennemi de mon ennemi...", nil, {"Détruisez 3 Gangrecanons Mod.I au Camp de forge Rage, puis retournez voir le Grand maréchal Brock à la halte de l'Honneur."}},
[10397] = {"Le site d'invasion Annihilateur", nil, {"Tuez Arix'Amal. Prenez la Clé du portail de la Légion ardente et utilisez-la sur la Rune de Mépris."}},
[10399] = {"Le cœur des ténèbres", nil, {"Tuez 10 Fiels de la terreur dans les Bassins d'Aggonar et retournez voir le Scrute-dimensions Kryv au donjon principal du Bastion de l'Honneur."}},
[10400] = {"Suzerain", nil, {"Tuez Arazzius le Cruel aux bassins d'Aggonar puis retournez voir le commandant de corps Danath Trollemort au donjon du Bastion de l'Honneur."}},
[10403] = {"Naladu", nil, {"Allez trouver Naladu à l'intérieur des ruines de Sha'naar."}},
[10405] = {"S-A-B-O-T-A-G-E", nil, {"L'image du commandant Ameer au Transpondeur zéta du lieu de rassemblement de l'Ethereum vous demande de lui rapporter un Linceul de l'Ethereum préparé."}},
[10406] = {"Faire passer le message", nil, {"L'Image du commandant Ameer du lieu de rassemblement de l'Ethereum - Transpondeur zéta de l'Ethereum - veut que vous escortiez le démolisseur du Protectorat jusqu'à la canalisation de la manaforge Ultris et que vous le protégiez pendant qu'il sabote le pipeline."}},
[10407] = {"L'ombre de Socrethar", nil, {"L'anachorète Karja de la Zone 52 veut que vous vous procuriez la Première moitié de la pierre de Socrethar sur le maître-forge Morug à la base de forge Oubli et la Seconde moitié de la pierre de Socrethar sur Silroth à la base de forge Géhenne.\n\nAccomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10408] = {"Le roi-nexus Salhadaar", nil, {"L'Image du commandant Ameer du lieu de rassemblement de l'Ethereum - Transpondeur zéta de l'Ethereum - veut que vous utilisiez le Disrupteur du Protectorat pour désactiver les champs de force de Salhadaar puis détruire le roi-nexus Salhadaar."}},
[10409] = {"Un coup fatal à la Légion", nil, {"La grande prêtresse Ishanah veut que vous alliez au Téléporteur de la Légion dans le coin nord-ouest de Raz-de-Néant et que vous l'utilisiez pour vous téléporter au siège de Socrethar. Une fois là-bas, triomphez de Socrethar.\n\nAccomplir des quêtes pour les Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10410] = {"L'aide d'Ishanah", nil, {"Apportez la Pierre de téléportation de Socrethar à la grande prêtresse Ishanah au Sanctuaire de la Lumière perpétuelle sur l'Éminence de l'Aldor à Shattrath.\n\nAccomplir des quêtes pour les Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10411] = {"Le bien-fondé des électrochocs !", nil, {"Le chercheur Navuud du poste de garde du Protectorat au Raz-de-Néant vous a demandé de détruire 30 Globules de rebut du Vide et 30 Globules de vase suintante."}},
[10412] = {"Les chevalières Aile-de-feu", nil, {"La magistrice Fyalenn de Shattrath veut que vous lui apportiez 10 Chevalières Aile-de-feu.\n\nAccomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10413] = {"Les horreurs de la pollution", nil, {"Apportez le Flacon de limon d'horreur du Vide au chercheur Navuud du poste de garde du Protectorat de Raz-de-Néant."}},
[10414] = {"Une chevalière Aile-de-feu", nil, {"Conditions :"}},
[10415] = {"De nouvelles chevalières Aile-de-feu", nil, {"Conditions :"}},
[10416] = {"Synthèse de puissance", nil, {"Voren'thal le Voyant de Shattrath veut que vous vous procuriez un Tome des arcanes.\n\nAccomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10417] = {"Faire un diagnostic !", nil, {"Récupérez les Résultats du diagnostic et apportez-les à Mihrdad au comptoir des Terres-médianes."}},
[10418] = {"S'occuper des saboteurs", nil, {"Mihrdad du comptoir des Terres-médianes veut que vous alliez tuer 8 Crocilisques Ecailles-barbelées."}},
[10419] = {"Tomes des arcanes", nil, {"Conditions :"}},
[10420] = {"Une Lumière purificatrice", nil, {"Apportez une Arme gangrenée à Ishanah à Shattrath.\n\nAccomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10421] = {"Armes gangrenées", nil, {"Conditions :"}},
[10422] = {"Le capitaine Tyralius", nil, {"Le manieur de chair Viridius au poste de garde du Protectorat de Raz-de-Néant vous a demandé de trouver et libérer le Capitaine Tyralius."}},
[10423] = {"À la Foudreflèche", nil, {"Parlez à Ghabar à la Foudreflèche, au Raz-de-Néant."}},
[10424] = {"Diagnostic : critique", nil, {"Utilisez la Machine à diagnostic en vous tenant près du Générateur de l'écodôme Sudron et apportez les Résultats du diagnostic à Ghabar à la Foudreflèche."}},
[10425] = {"L'évasion du lieu de rassemblement", nil, {"Escortez l'Avant-garde du Protectorat capturé jusqu'au Poste de garde du Protectorat au Raz-de-Néant."}},
[10426] = {"La flore des écodômes", nil, {"Utilisez le Modulateur de champ d'énergie sur des Flagellants de Farahlon. Observez les résultats et allez voir Aurine Braselune à la Foudreflèche lorsque vous aurez conduit 10 tests."}},
[10427] = {"Créatures des écodômes", nil, {"Marquez 12 Talbuks pour Aurine Braselune à la Foudreflèche. Vous pouvez marquer soit des Talbuks étalons soit des Talbuks femelles à l'aide du Marqueur de talbuk."}},
[10428] = {"Le pêcheur disparu", nil, {"Trouvez Cowlen sur le quai, au sud-ouest de l'Île de Brume-argent, au large de la côte de Brume-azur."}},
[10429] = {"Quand la nature va trop loin", nil, {"Aurine Braselune de la Foudreflèche veut que vous lui apportiez un Cœur d'hydre massive."}},
[10430] = {"Tester le prototype", nil, {"Apportez l'Appareil de réparation à Tashar à l'écodôme Champlointain, au Raz-de-Néant."}},
[10431] = {"Une aide extérieure", nil, {"Allez au Point d'ancrage de Tuluman au nord de la Zone 52 et parlez à Kaylaan.\n\nAccomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10432] = {"Preuves accablantes", nil, {"Procurez-vous 8 Ordres de Kael'thas sur les démons de la manaforge Ara et apportez-les au Maître-espion Thalodien dans la Zone 52.\n\nAccomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10433] = {"Sauvez les apparences", nil, {"Apportez 10 Fourrures de lynx arrache-crocs à Shauly Pore au comptoir des Terres-médianes."}},
[10434] = {"Le duo dynamique", nil, {"Allez parler à Audi la Pointe à la Foudreflèche, au Raz-de-Néant."}},
[10435] = {"Récupérer les marchandises", nil, {"Audi la Pointe de Foudreflèche veut que vous lui apportiez 10 Segments du générateur du dôme."}},
[10436] = {"Tout est prêt !", nil, {"Tashar, en poste à l'écodôme Champlointain, vous demande de tuer 12 Raptors Fauche-dent."}},
[10437] = {"Une recette de destruction", nil, {"Le professeur Dabiri du poste de garde du Protectorat de Raz-de-Néant veut que vous récupériez 8 Fragments de Dimensius."}},
[10438] = {"Sur des ailes néantines", nil, {"Le professeur Dabiri, du Poste de garde du Protectorat de Raz-de-Néant, veut que vous utilisiez votre Disrupteur de phase lorsque vous serez à portée du Conduit du Vide du sommet d'Ultris. Larguez autant de charges du disrupteur de phase que possible pour vous assurer de sa destruction totale. Les charges du disrupteur de phase atteignent automatiquement le conducteur du Vide du moment que vous êtes assez près pour l'utiliser.\n\nRetournez voir le professeur Dabiri si vous réussissez !"}},
[10439] = {"Dimensius l'Omni-dévoreur", nil, {"Le professeur Dabiri du poste de garde du Protectorat au Raz-de-Néant vous demande d'aller parler au capitaine Saeed et de suivre son armée à la manaforge Ultris pour tuer Dimensius l'Omni-dévoreur !"}},
[10440] = {"Victoire !", nil, {"Retournez voir Ghabar à la Foudreflèche, au Raz-de-Néant."}},
[10441] = {"Colporter la marchandise", nil, {"Parlez à Aurine Braselune en bas de la Foudreflèche, au Raz-de-Néant."}},
[10442] = {"Venir en aide au Poste cénarien", nil, {"Le fauconnier Drenna Ruissevent vous demande de proposer vos services à Thiah Crin-rouge au Poste cénarien dans la Péninsule des Flammes infernales."}},
[10443] = {"Venir en aide au Poste cénarien", nil, {"Amaan le Sage voudrait que vous proposiez vos services à Thiah Crin-rouge au Poste cénarien dans la Péninsule des Flammes infernales."}},
[10444] = {"Rapport au Poste allérien", nil, {"Jenai Murmétoile veut que vous vous présentiez au lieutenant Meridian au Poste allérien dans la forêt de Terokkar."}},
[10445] = {"Les fioles d'éternité", nil, {"Soridormi aux Grottes du temps vous demande de récupérer le Reste de la fiole de Vashj auprès de Dame Vashj au réservoir de Glissecroc et le Reste de la fiole de Kael auprès de Kael'thas Haut-Soleil au donjon de la Tempête."}},
[10446] = {"Le code final", nil, {"Le lieutenant Meridian vous demande d'utiliser le Code final pour désamorcer la Bombe de mana. Faites ensuite votre rapport à Jenai Murmétoile au Bastion allérien dans la forêt de Terokkar."}},
[10447] = {"Le code final", nil, {"Le sergent Chawni vous demande d'utiliser le Code final pour désamorcer la Bombe de mana. Faites ensuite votre rapport à Tooki au fort des Brise-pierres dans la forêt de Terokkar."}},
[10448] = {"Rapport au camp des Brise-pierres", nil, {"Tooki veut que vous vous présentiez au sergent Chawni au camp des Brise-pierres dans la forêt de Terokkar."}},
[10449] = {"L'apothicaire Zelana", nil, {"Vurtok Brise-hache de Thrallmar vous demande d'apporter les Échantillons de sang de Mâche-les-os à l'apothicaire Zelana au Trépas du saccageur."}},
[10450] = {"Le sang de Mâche-les-os", nil, {"Apportez 12 fioles de Sang de Mâche-les-os à Vurtok Brise-hache à Thrallmar."}},
[10451] = {"L'évasion de la citerne de Glissentaille", nil, {"Escortez la Soigneterre Wilda hors de la Citerne de Glissentaille puis allez trouver le Soigneterre Torlok à l'Autel de la damnation, qui se trouve à l'ouest de la Main de Gul'dan."}},
[10452] = {"DON'T USE [PH] Fel Orc 1", nil, {"[PH Fel Orc 1."}},
[10453] = {"DON'T USE [PH] Fel Orc bread", nil, {"[PH Fel Orc bread"}},
[10455] = {"Une nature envahissante", nil, {"Tuez 12 Lynx traquebosquet."}},
[10456] = {"Les loups en maraude", nil, {"Rapportez 4 Queues de loup redoutable sire-tonnerre."}},
[10457] = {"Pour protéger les nôtres", nil, {"Utilisez le Rameau de Rina sur 5 Boutures du Bosquet pour faire apparaître des défenseurs du Bosquet vivant."}},
[10458] = {"Esprits de feu et de terre enragés", nil, {"Le soigneterre Torlok à l'Autel de la damnation dans la vallée d'Ombrelune veut que vous utilisiez le Totem des esprits pour capturer 8 Âmes terrestres et 8 Âmes flamboyantes."}},
[10459] = {"Révéré par l'Expédition cénarienne", nil, {"Ysiel Chantelevent du Refuge cénarien veut que vous deveniez Révérée par l'Expédition cénarienne."}},
