[10661] = {"Ex-rate-ordinaires !", nil, {"Gnomus du bastion des Marteaux-hardis dans la vallée d'Ombrelune veut que vous récupériez 8 Rates gangrefeu."}},
[10662] = {"L'ermite forgeron", nil, {"Apportez la Boîte de lingots à David Wayne au Refuge de Wayne dans la forêt de Terokkar."}},
