[10668] = {"Contre les Illidari", nil, {"Altruis le Souffrant veut que vous alliez tuer Lothros à la Halte Illidari dans la vallée d'Ombrelune."}},
[10669] = {"Contre toute attente", nil, {"Altruis le Souffrant veut que vous preniez la Lance en argent imprégné et que vous l'utilisiez dans la Clairière du portail près du Lac des furoles du marécage de Zangar pour réveiller Xeleth. Retournez voir Altruis lorsque vous aurez tué le démon."}},
[10670] = {"La Larme de la Terre-mère", nil, {"Reprenez la Larme de la Terre-mère au Porteguerre O'mrogg et apportez-la à David Wayne au Refuge de Wayne."}},
[10671] = {"Plus d'une livre de chair", nil, {"Collectez 5 morceaux de Chair éthernergisée."}},
[10672] = {"Franchement, ça n'a aucun sens...", nil, {"Le chercheur Tiorus du village d'Ombrelune dans la vallée d'Ombrelune vous demande de voyager jusqu'aux coulées de lave près des ruines de Baa'ri, d'utiliser l'Unité d'arcano-contrôle pour prendre le contrôle d'un Arcano-Scorp, de nager dans la lave à l'aide de l'Arcano-Scorp et de marquer 8 Grands Dimeurtrodons de gangrefeu."}},
[10673] = {"Gangréchine la Très-Grande", nil, {"Le chercheur Tiorus du village d'Ombrelune dans la vallée d'Ombrelune vous demande de tuer Gangréchine la Très-Grande et de récupérer la Peau de Gangréchine."}},
[10674] = {"Et la lumière fut... piégée", nil, {"Utilisez le Piège à lumière pour attraper 15 Orbes de lumière razaani."}},
[10675] = {"Dégnomez-les tous !", nil, {"Récupérez la Collection d’âmes."}},
[10676] = {"La plaie des Illidari", nil, {"Attendez que David Wayne ait terminé de forger une épée pour vous."}},
[10677] = {"Le deuxième service...", nil, {"Gnomus du bastion des Marteaux-hardis dans la vallée d'Ombrelune veut que vous récupériez 1 Gésier de gangrefeu surdéveloppé."}},
[10678] = {"Le plat principal !", nil, {"Gnomus du bastion des Marteaux-hardis dans la vallée d'Ombrelune veut que vous récupériez la Peau de Gangréchine."}},
[10679] = {"Tremper la lame", nil, {"Voyagez jusqu'aux Bassins Brûlants situés près du sommet de la Main de Gul'dan et utilisez la Lame plaie-des-Illidari forgée pour la tremper dans les bassins de lave. Une fois que vous aurez la Lame plaie-des-Illidari trempée, retournez auprès de David Wayne au Refuge de Wayne."}},
[10680] = {"La Main de Gul'dan", nil, {"Parlez au soigneterre Torlok à l'Autel de la damnation dans la vallée d'Ombrelune."}},
