[10683] = {"Tablettes de Baa'ri", nil, {"L'arcaniste Thelis du Sanctum des Étoiles veut que vous collectiez 12 Tablettes baa'ri aux Ruines de Baa'ri.\n\nAccomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10684] = {"Oronu l'Ancien", nil, {"L'arcaniste Thelis du Sanctum des Étoiles veut que vous preniez les Ordres d'Akama à Oronu l'Ancien aux Ruines de Baa'ri.\n\nAccomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10685] = {"Les corrupteurs cendrelangues", nil, {"Procurez-vous les quatre fragments du médaillon sur Haalum, Eykenen, Lakaan et Uylaru et rapportez-les à l'arcaniste Thelis au Sanctum des Étoiles dans la vallée d'Ombrelune.\n\nAccomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10686] = {"La Cage de la gardienne", nil, {"L'arcaniste Thelis veut que vous entriez dans la Cage de la gardienne, au sud des ruines de Baa'ri, et que vous interrogiez Sanoru pour apprendre où se trouve Akama.\n\nAccomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10687] = {"Les terrains d'entraînement de Karabor", nil, {"Larissa Heurtesoleil au Sanctum des Étoiles veut que vous vous procuriez 8 Glaives solfurie sur les Suppliants solfurie et les Initiés solfurie aux Ruines de Karabor, au nord-est du Sanctum des étoiles.\n\nAccomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10688] = {"Une diversion nécessaire", nil, {"Larissa Heurtesoleil veut que vous retourniez aux Ruines de Karabor et que vous trouviez un moyen de lâcher Azaloth sur ses geôliers.\n\nAccomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10689] = {"Altruis", nil, {"Voyagez jusqu'au Nagrand occidental et allez trouver Altruis le Souffrant.\n\nAccomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
