[10786] = {"Carnage à Roche'mok", nil, {"Massacrez 5 Brutes de Roche'mok et 3 Chamans de Roche'mok, puis retournez voir Tor'chunk Doublegriffes au bastion des Sire-tonnerre, dans les Tranchantes."}},
[10788] = {"Retourner voir Talionia", nil, {"Parlez à Talionia à Lune-d'argent."}},
[10789] = {"Retourner voir Carendin Halgar", nil, {"Parlez à Carendin Halgar à Fossoyeuse."}},
[10790] = {"Retourner voir Gan'rul Œil-sanglant", nil, {"Parlez à Gan'rul Œil-sanglant à Orgrimmar."}},
[10791] = {"Accueillir l'esprit du loup", nil, {"Utilisez l'Encens de cérémonie près des bougies dans la hutte de Malukaz et assistez à la cérémonie. Quand elle sera terminée, parlez à nouveau à Malukaz."}},
[10792] = {"Que Zeth'Gor brûle !", nil, {"Tuez les Péons de l'Orbite-sanglante pour prendre leurs torches, allumez celles-ci à la forge de Zeth'Gor, puis jetez les torches enflammées sur les bâtiments de Zeth'Gor."}},
[10793] = {"Le journal de Val'zareq : Des présages de guerre", nil, {"Trouvez Marcus Auralion, grand général des Sha'tar, au Guet cramoisi.\n\nCherchez-le à l'intérieur d'une prison cristalline."}},
[10794] = {"Les voleurs de la Main brisée", nil, {"Parlez à Shenthul à Orgrimmar."}},
[10795] = {"Un rendez-vous avec Dorgok", nil, {"Tuez Dorgok au camp de la Masse-sanglante."}},
[10796] = {"Anéantissez le camp Masse-sanglante !", nil, {"Tuez 10 Marteleurs de la Masse-sanglante et 5 Démonistes de la Masse-sanglante."}},
[10797] = {"Une faveur des gronns", nil, {"Apportez la Faveur de Gorgrom au commandant Haephus Lamuraille au Bosquet éternel, dans les Tranchantes."}},
[10798] = {"Rendre visite au baron", nil, {"Parlez au Baron Sablecrin au Cercle de sang, dans les Tranchantes."}},
[10799] = {"Dans le Goulet bouillonnant", nil, {"Procurez-vous 7 Glandes à venin de perceur de croûte."}},
[10800] = {"Bonne nuit, l'horrible !", nil, {"Trouvez le Sac de Grulloc."}},
[10801] = {"C'est un piège !", nil, {"Apportez le Piège de feu draconique au commandant Haephus Lamuraille au Bosquet éternel dans les Tranchantes."}},
[10802] = {"Gorgrom le Mange-dragon", nil, {"Procurez-vous 3 Totems macabres sur les ogres de Roche'mok. Utilisez le Piège de Sablecrin à l'Autel de Gorgrom pour invoquer et tuer Gorgrom le Mange-dragon.\n\nUne fois Gorgrom mort, placez les 3 Totems macabres près de son cadavre."}},
[10803] = {"Carnage à Roche'mok", nil, {"Massacrez 5 Brutes de Roche'mok et 3 Chamans de Roche'mok."}},
[10804] = {"Un peu de gentillesse", nil, {"Mordenai, aux champs de l'Aile-du-Néant, dans la vallée d'Ombrelune, veut que vous nourrissiez 8 Drakes Aile-du-Néant adultes."}},
[10805] = {"Massacre au repaire de Gruul", nil, {"Tuez 10 Massacreurs de Flèchelame, 5 Mages de bataille de Flèchelame, 5 Ravageurs de Flèchelame et leur chef, Fingrom."}},
[10806] = {"Cartes sur table", nil, {"Utilisez la Chevalière de Sablecrin à l’Autel de Goc, puis tuez Goc."}},
[10807] = {"Les roués cendrelangues", nil, {"Varen le Récupérateur veut que vous partiez pour les Ruines de Baa'ri afin d'y tuer 3 Dresseurs cendrelangues, 4 Guerriers cendrelangues et 6 Chamans cendrelangues.\n\nAccomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10808] = {"Contrecarrer le sombre Conclave", nil, {"Tuez les Ritualistes du sombre Conclave et utilisez le Bâton de Parshah pour mettre fin à leur rituel d'invocation. Retournez ensuite voir Parshah dans les Champs de magma."}},
[10809] = {"On recherche : maître-worg Kruush", nil, {"Tuez le Maître-worg Kruush et apportez sa tête au Capitaine Rugissombre dans le Poste de Brise-échine."}},
[10810] = {"Le masque endommagé", nil, {"Apportez le Masque endommagé à O'Mally Rossecoquin au Bosquet éternel, dans les Tranchantes."}},
[10811] = {"Trouvez Neltharaku", nil, {"Partez à la recherche de Neltharaku, le protecteur du Vol de l'Aile-du-Néant."}},
[10812] = {"Le masque mystère", nil, {"Apportez le Masque mystère au seigneur sauvage Antelarion au Bosquet éternel, dans les Tranchantes."}},
[10813] = {"Les yeux de Grillok", nil, {"Utilisez l'Éclat de Zezzak pour capturer un Oeil de Grillok, puis approchez-vous du Chaudron de Zezzak pour l'extraire. Une fois que ce sera fait, rendez son éclat à Zezzak."}},
[10814] = {"L'histoire de Neltharaku", nil, {"Parlez à Neltharaku et écoutez son histoire."}},
[10815] = {"Le journal de Val'zareq : Des présages de guerre", nil, {"Trouvez Marcus Auralion, grand général des Sha'tar, au Guet cramoisi.\n\nCherchez-le à l'intérieur d'une prison cristalline."}},
[10816] = {"La reconquête des terres sacrées", nil, {"Le redresseur de torts Aluumen veut que vous tuiez 8 Pourfendeurs ombrelune, 8 Élus ombrelune et 4 Tisseurs d'ombre ombrelune.\n\nAccomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10817] = {"La grande vindicte", nil, {"Varen le Récupérateur veut que vous tuiez 8 Pourfendeurs ombrelune, 8 Élus ombrelune et 4 Tisseurs d'ombre ombrelune.\n\nAccomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10818] = {"Le baron Sablecrin veut vous voir", nil, {"Parlez au Baron Sablecrin au Cercle de sang, dans les Tranchantes."}},
[10819] = {"Le masque à gaz de gangrelige", nil, {"Portez le Masque à gaz de gangrelige et utilisez le Communicateur de la Légion au Camp de Forge : Colère, dans les Tranchantes."}},
[10820] = {"Ton ennemi tu tromperas", nil, {"Tuez 4 Domestiques Forge-ruine et 4 Ingénieurs Forge-ruine."}},
[10821] = {"Vous êtes virée !", nil, {"Tuez Vocifuneste."}},
[10822] = {"Une chevalière Solfurie", nil, {"Conditions :"}},
[10823] = {"De nouvelles chevalières Solfurie", nil, {"Conditions :"}},
[10824] = {"Les chevalières Solfurie", nil, {"La Mage de bataille Vyara du Sanctum des Étoiles veut que vous lui apportiez 10 Chevalières Solfurie.\n\nAccomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de la faction Aldor."}},
[10825] = {"La vérité toute nue", nil, {"Apportez l'Orbe des Grishna au Gardien des arbres Chawn, au Bosquet éternel, dans les Tranchantes."}},
[10826] = {"Les marques de Sargeras", nil, {"Remettez 10 Marques de Sargeras au Messager Saronen, dans la Vallée d'Ombrelune.\n\nAccomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10827] = {"De nouvelles marques de Sargeras", nil, {"Conditions :"}},
[10828] = {"Une marque de Sargeras", nil, {"Conditions :"}},
[10829] = {"Arbrobole doit savoir", nil, {"Parlez à Arbrobole, un Ancien du savoir qui vit dans le Bois aux corbeaux, dans les Tranchantes."}},
[10830] = {"Exorciser les arbres", nil, {"Collectez 5 Orbes grishnath et 5 Plumules redoutables puis combinez-les pour en faire des Plumes d'exorcisme. Utilisez ces plumes pour procéder à l'exorcisme et tuer 5 Esprits Kui-kui sortis des Barbes-feuillues du Bois aux corbeaux."}},
[10831] = {"Devenir un tailleur d'étoffe lunaire", nil, {"Utilisez le Carré de tisse-néant imprégné en vous tenant près du puits de lune du Refuge cénarien pour créer un Échantillon d'étoffe lunaire primordiale. Apportez l'échantillon terminé à Nasmara Lunechant dans la Ville basse de Shattrath.\n\n*ATTENTION !* Vous ne pouvez choisir qu'une spécialisation en couture."}},
[10832] = {"Devenir un tailleur de feu-sorcier", nil, {"Apportez un échantillon d'Essence d'âme en peine du Néant à Gidge Tisse-sort dans la Ville basse de Shattrath.\n\n*ATTENTION !* Vous ne pouvez choisir qu'une spécialisation en couture."}},
[10833] = {"Devenir un tailleur de tisse-ombre", nil, {"Utilisez le Cristal des ombres profondes près de l'autel des Ombres pour renforcer votre harmonisation avec l'autel, puis retournez voir Andrion Sombrefileur dans la Ville basse de Shattrath.\n\n*ATTENTION !* Vous ne pouvez choisir qu'une spécialisation en couture."}},
[10834] = {"Grillok « Sombrœil »", nil, {"Tuez Grillok Sombrœil et rapportez le Couvre-œil de Grillok à Zezzak au Poste de Brise-échine."}},
[10835] = {"L'apothicaire Antonivitch", nil, {"Apportez les Découvertes d’Albreck à l’Apothicaire Antonivitch à Thrallmar, dans la Péninsule des Flammes infernales."}},
[10836] = {"Infiltrer la forteresse Gueule-de-dragon", nil, {"Neltharaku, qui vole au-dessus des Champs de l'Aile-du-Néant dans la Vallée d'Ombrelune, veut que vous tuiez 15 Orcs Gueule-de-dragon.\n\nN'importe quels orcs de la Forteresse Gueule-de-dragon feront l'affaire."}},
[10837] = {"Vers l'escarpement de l'Aile-du-Néant !", nil, {"Neltharaku, qui vole au-dessus des champs de l'Aile-du-Néant dans la vallée d'Ombrelune, veut que vous ramassiez 12 cristaux vignéants à l'escarpement de l'Aile-du-Néant."}},
[10838] = {"Le scrutateur démonien", nil, {"Utilisez le Scrutateur démonien dans le Bassin des Flammes infernales, attendez qu'il soit harmonisé, puis faites un relevé.\n\nRapportez le Scrutateur démonien et le Relevé du scrutateur démonien à l'Apothicaire Antonivitch à Thrallmar."}},
[10839] = {"Voile Skith : Sombrepierre de Terokk", nil, {"Utilisez le Bâtonnet de purification sur la Sombrepierre de Terokk au Voile Skith puis retournez auprès de Kirrik l'Eveillé à la Caravane des réfugiés, dans le désert des Ossements."}},
[10840] = {"Le tombeau des lumières", nil, {"Le Grand prêtre Orglum veut que vous vous rendiez au Tombeau des lumières et que vous tuiez 10 Néantomanciens éthériens et 10 Pilleurs éthériens avant de revenir le voir à la Caravane de réfugiés."}},
[10842] = {"Âmes vengeresses", nil, {"Le grand prêtre Orglum veut que vous vous rendiez au Tombeau des lumières et que vous vainquiez 5 Draeneï vengeurs."}},
[10843] = {"Depuis des temps immémoriaux...", nil, {"Tuez Gnosh Brognat, le chef des ogres de l'avant-poste Flèchelame."}},
[10845] = {"Tuer la mère des couvées", nil, {"Tuez Aile-de-peur."}},
[10846] = {"Comprendre les Mok'Nathal", nil, {"Tuez Vekh, chef des arakkoa vekh'nir."}},
[10847] = {"Les yeux de Skettis", nil, {"Apportez l'Œil de Reskk et l'Œil de Shienor à Rilak le Racheté dans le quartier de la Ville basse de Shattrath."}},
[10848] = {"Voile Rhaze : Un mal sans vie", nil, {"Tuez 4 Esprits Serres-mort, 4 Esprits hurlants et 2 Esprits corbeaux puis revenez voir Kirrik l'Eveillé à la Caravane des réfugiés."}},
[10849] = {"À la recherche de Kirrik", nil, {"Apportez la Missive de Rilak à Kirrik l'Eveillé à la Caravane des réfugiés, dans le désert des Ossements."}},
[10850] = {"De l'essence de Néant dans un moteur gangrefeu", nil, {"Conditions :"}},
[10851] = {"Les totems de mon ennemi", nil, {"Collectez 5 Totems flèchelame."}},
[10852] = {"Les amis perdus", nil, {"Ethan veut que vous trouviez 12 de ses amis disparus au Voile Skith et que vous reveniez ensuite le voir à la Caravane des réfugiés."}},
[10853] = {"L'appel des esprits", nil, {"Utilisez les Totems d'appel des esprits pour collecter 8 Esprits de drakes du Néant inférieurs."}},
[10854] = {"La force de Neltharaku", nil, {"Neltharaku, qui vole au-dessus des champs de l'Aile-du-Néant dans la vallée d'Ombrelune, veut que vous libériez 5 Drakes de l'Aile-du-Néant asservis."}},
[10855] = {"Les saccageurs gangrenés ne passeront pas !", nil, {"Récupérez 5 mesures de Gaz de Néant condensé auprès des Mekgénieurs gan'arg de la Base de forge : Oubli, au nord-ouest de la Foudreflèche, et chargez-les dans un Saccageur gangrené inactif proche. Revenez voir le Traqueur-du-Néant Nauthis quand vous aurez terminé."}},
[10856] = {"La meilleure défense", nil, {"Le Traqueur-du-Néant Nauthis veut que vous tuiez 12 Porte-courroux à la Base de forge Géhenne, au nord de la Foudreflèche."}},
[10857] = {"Téléportez-moi ça !", nil, {"Le Traqueur-du-Néant Nauthis veut que utilisiez le Bâtonnet d'interférence mentale sur les mo'arg du Camp de forge Géhenne, et utilisez leur capacité pour Faire exploser le téléporteur sur les trois téléporteurs du camp de forge."}},
[10858] = {"Karynaku", nil, {"Trouvez Karynaku à la Forteresse Gueule-de-dragon."}},
[10859] = {"Réunir les orbes", nil, {"Utilisez le Totem collecteur d'orbes pour collecter 15 Orbes de lumière razaani."}},
[10860] = {"Délices de Mok'Nathal", nil, {"Récupérez 3 Côtelettes de raptor et 3 Chairs de serpent."}},
[10861] = {"Voile Lithic : Frappe préventive", nil, {"Cassez les œufs maudits au Voile Lithic. Rachetez 3 Oisillons et tuez 3 Oisillons maléfiques avant de retourner auprès de Kirrik l'Eveillé à la Caravane des réfugiés."}},
[10862] = {"Se rendre à la Horde", nil, {"Rokag veut que vous trouviez Rilak le Racheté dans le quartier de la Ville basse à Shattrath et que vous exigiez que lui et son peuple se rendent à la Horde."}},
[10863] = {"Les secrets des arakkoa", nil, {"Dame Dena Kennedy veut que vous alliez trouver Rilak le Racheté dans le quartier de la Ville basse à Shattrath pour découvrir quels secrets des arakkoa il est prêt à partager."}},
[10864] = {"Le fardeau des âmes", nil, {"Tuez 20 Orcs de la Main brisée, puis retournez voir l'Apothicaire Antonivitch à Thrallmar."}},
[10865] = {"Informez Leoroxx !", nil, {"Parlez à Leoroxx de ce que préparent les éthériens razaani. Vous le trouverez au village de Mok'Nathal dans les Tranchantes."}},
[10866] = {"Zuluhed le Fourbu", nil, {"Tuez Zuluhed le Fourbu et récupérez la Clé de Zuluhed. Utilisez-la sur les Chaînes de Zuluhed pour libérer Karynaku."}},
[10867] = {"La seule réponse possible", nil, {"Récupérez la Collection d'âmes."}},
[10868] = {"Le sentier de la guerre arakkoa", nil, {"La Conseillère Faila vous demande de tuer 14 Serrénites shienor et 6 Ensorceleurs shienor avant de revenir la voir au Fort des Brise-pierres."}},
[10869] = {"Restreindre la nuée", nil, {"Le lieutenant Gravillemartel vous demande de tuer 14 Serrénites shienor et 6 Ensorceleurs shienor avant de revenir le voir au Bastion allérien."}},
[10870] = {"Allié du vol du Néant", nil, {"Permettez à Karynaku de vous ramener auprès de Mordenai dans les Champs de l'Aile-du-Néant."}},
[10871] = {"Allié du vol du Néant", nil, {"Permettez à Karynaku de vous ramener auprès de Mordenai dans les Champs de l'Aile-du-Néant."}},
[10872] = {"Zuluhed le Fourbu", nil, {"Tuez Zuluhed le Fourbu et récupérez la Clé de Zuluhed. Utilisez-la sur les Chaînes de Zuluhed pour libérer Karynaku."}},
[10873] = {"Pris dans la nuit", nil, {"L’Éclaireur Navrin veut que vous vous rendiez sur la Crête de Toile-néant et que vous libériez 6 Guerriers sha'tar entoilés. Revenez ensuite le voir au Camp de base sha'tari."}},
[10874] = {"Voile Shalas : Feux de signal", nil, {"Kirrik l'Eveillé de la Caravane des réfugiés vous demande d'éteindre les quatre feux de signal du Voile Shalas."}},
[10875] = {"Se présenter à Nazgrel", nil, {"Parlez avec Nazgrel à Thrallmar, dans la Péninsule des Flammes infernales."}},
[10876] = {"Le pied de la citadelle", nil, {"Tuez le Commandant de corps Gorax, placez le Défi de la Horde sur son cadavre, tuez la Main de Kargath, puis rapportez la Tête de la Main de Kargath à Nazgrel dans Thrallmar."}},
[10877] = {"La relique de l'effroi", nil, {"Chênuin veut que vous partiez vers l'est pour la Caravane abandonnée afin de récupérer la Relique de l'effroi. Revenez voir Chênuin quand cette tâche sera achevée."}},
[10878] = {"Avant que l'ombre...", nil, {"Mekeda de la Caravane de réfugiés veut que vous tuiez 8 Tirailleurs de la Cabale, 4 Tisse-sorts de la Cabale et 2 Initiés de la Cabale."}},
[10879] = {"L'offensive de Skettis", nil, {"Repoussez les attaquants et parlez à Rilak le Racheté."}},
[10880] = {"Les ordres de la Cabale", nil, {"Remettez les Ordres de la Cabale à Mekeda, à la Caravane de réfugiés, dans le Désert des Ossements."}},
[10881] = {"La tombe des ombres", nil, {"Rendez-vous dans la Tombe des ombres, à l'ouest de la Caravane de réfugiés. Récupérez-y le Drapé d'Arunen, le Marteau de K'alen et le Parchemin d'Atalor. Lorsque vous aurez terminé, retournez voir Mekeda à la Caravane de réfugiés."}},
[10882] = {"Messager funeste", nil, {"Vous avez été chargé d'aller sur le satellite de l'Arcatraz du donjon de la Tempête et de tuer le Messager Cieuriss. Revenez voir A'dal à la Terrasse de la Lumière de Shattrath quand ce sera fait."}},
[10883] = {"La clé de la Tempête", nil, {"Parlez à A'dal à Shattrath."}},
[10884] = {"L'épreuve des naaru : Miséricorde", nil, {"A'dal de Shattrath vous demande de prendre la Hache inutilisée du bourreau dans les Salles brisées de la citadelle des Flammes infernales.\n\nCette quête doit être accomplie en mode de difficulté du donjon Héroïque."}},
[10885] = {"L'épreuve des naaru : Force", nil, {"A'dal de Shattrath vous demande de récupérer le Trident de Kalithresh et l'Essence de Marmon.\n\nCette quête doit être accomplie en mode de difficulté du donjon Héroïque."}},
[10886] = {"L'épreuve des naaru : Ténacité", nil, {"A'dal de Shattrath vous demande de sauver Milhouse Tempête-de-mana de l'Arcatraz du donjon de la Tempête.\n\nCette quête doit être accomplie en mode de difficulté du donjon Héroïque."}},
[10887] = {"Sortir du tombeau", nil, {"Aidez Akuno a trouver le chemin de la Caravane de réfugiés. Lorsque vous aurez terminé cette quête, parlez à Mekeda."}},
[10888] = {"L'épreuve des naaru : Magtheridon", nil, {"A'dal de Shattrath vous demande de tuer Magtheridon."}},
[10889] = {"Retour à Shattrath", nil, {"Rendez-vous à Shattrath et parlez à Rilak le Racheté dans la Ville Basse."}},
[10890] = {"[UNUSED] <NYI> ", nil, nil},
[10891] = {"L'armure impériale en plaques", nil, {"Parlez à Derotain Siroteboue à Gadgetzan, en Tanaris."}},
[10892] = {"L'armure impériale en plaques", nil, {"Parlez à Derotain Siroteboue à Gadgetzan, en Tanaris."}},
[10893] = {"Longue-queue est le pivot", nil, {"Éliminez Draaca Longue-queue, le chef des sectateurs du Ver dans le Tunnel Crâne-du-ver."}},
[10894] = {"Le guetteur du Crâne-du-ver", nil, {"Apportez la Note de rendez-vous au Guetteur Lunombrage à l'entrée du tunnel Crâne-du-ver dans les Tranchantes."}},
[10895] = {"Que Zeth'Gor brûle !", nil, {"Utilisez les Balises fumigènes sur chacune des tours de Zeth'Gor, puis retournez voir le Chef d'escadrille Dabir'ee à la Halte de l'expédition."}},
[10896] = {"Les protecteurs infestés", nil, {"Lakotae veut que vous tuiez 25 Mites des bois puis que vous reveniez le voir à la Caravane de réfugiés. On peut trouver les mites à l'intérieur des corps des Enragés des forêts pourrissants et des Marche-racine infestés."}},
[10897] = {"Maître des potions", nil, {"Lauranna Thar'well veut que vous vous rendiez dans la Botanica du donjon de la Tempête et que vous récupériez le Guide pratique du botaniste sur le Grand botaniste Freywenn. De plus, elle veut que vous lui rameniez 5 Potions de super-soins, 5 Potions de super-mana et 5 Potions de sommeil sans rêve majeures.\n\n*ATTENTION !* Vous ne pouvez choisir qu'une seule spécialité d'alchimie."}},
[10898] = {"Aile-du-ciel", nil, {"Escortez Aile-du-ciel et allez voir Rilak le Racheté à Shattrath."}},
[10899] = {"Maître de la transmutation", nil, {"Zarevhi, de la Foudreflèche du Raz-de-Néant, veut que vous lui rameniez 4 Pouvoirs primordiaux.\n\n*ATTENTION !* Vous ne pouvez choisir qu'une seule spécialité d'alchimie."}},
[10900] = {"La marque de Vashj", nil, nil},
[10901] = {"Le casse-tête de Kar'desh", nil, {"Skar'this l'Hérétique, dans les Enclos aux esclaves héroïques du Réservoir de Glissecroc, veut que vous lui apportiez la Chevalière terrestre et la Chevalière flamboyante."}},
[10902] = {"Maître des élixirs", nil, {"Partez pour les Grottes du temps dans le Noir Marécage et procurez-vous 10 Essences d'infinité sur les Seigneurs des failles et Gardiens des failles. Apportez-les avec 5 Élixirs de défense majeure, 5 Élixirs de maîtrise et 5 Élixirs d'agilité majeure à Lorokeem dans la Ville basse de Shattrath.\n\n*ATTENTION !* Vous ne pouvez choisir qu'une spécialisation en alchimie."}},
[10903] = {"Retour au bastion de l'Honneur", nil, {"Parlez à Klatu, à l'auberge du Bastion de l'Honneur."}},
[10904] = {"La récolte des munitions gangrenées", nil, {"Récupérez 5 Boulets de gangrecanon."}},
[10905] = {"Maître des potions", nil, {"Parlez à Lauranna Thar'well au Refuge cénarien dans le Marécage de Zangar.\n\n*ATTENTION !* Vous ne pouvez choisir qu'une spécialisation en alchimie."}},
[10906] = {"Maître des élixirs", nil, {"Parlez à Lorokeem dans la Ville basse de Shattrath.\n\n*ATTENTION !* Vous ne pouvez choisir qu'une spécialisation en alchimie."}},
[10907] = {"Maître de la transmutation", nil, {"Parlez à Zarevhi à la Foudreflèche au Raz-de-Néant.\n\n*ATTENTION !* Vous ne pouvez choisir qu'une spécialisation en alchimie."}},
[10908] = {"Parler à Rilak le Racheté", nil, {"Kirrik l'Éveillé veut que vous parliez à Rilak le Racheté dans la Ville basse de Shattrath."}},
[10909] = {"Les esprits gangrenés", nil, {"Klatu au Bastion de l'Honneur vous a demandé d'utiliser la Relique d'anachorète à l'Avant-poste de Gor'gaz, au sud-ouest du Bastion. Vous devrez ensuite tuer des Orcs de la Main brisée près de la relique, puis détruire les Esprits gangrenés libérés pour venger le Colonel Jules."}},
[10910] = {"La Porte de la mort", nil, {"Utilisez le Signal druidique à la Porte de la mort pour appeler et parler à un Druide du Bosquet éternel."}},
[10911] = {"Feu à volonté !", nil, {"Utilisez les Munitions naturalisées pour prendre le contrôle des Gangrecanons de la Porte de la mort. Utilisez-les pour détruire le Portail dimensionnel sud et le Portail dimensionnel nord."}},
[10912] = {"Le maître-chien", nil, {"Tuez Baelmon le Maître-chien."}},
[10913] = {"Des funérailles inadéquates", nil, {"Le commandant Ra’vaj, du Camp de base shat’ari dans le désert des Ossements de la forêt de Terokkar, veut que vous brûliez 8 Guerriers auchenaï tués et 8 Redresseurs de torts sha'tar tués."}},
[10914] = {"Il nous faut un héros", nil, {"Le commandant Ra’vaj, du Camp de base shat’ari dans le désert des Ossements de la forêt de Terokkar, veut que vous tuiez 12 Initiés auchenaï et 5 Auspices-funestes auchenaï."}},
[10915] = {"L'exarque déchu", nil, {"Le commandant Ra’vaj, du Camp de base shat’ari dans le désert des Ossements de la forêt de Terokkar, veut que vous détruisiez le contenu du Cercueil auchenaï."}},
[10916] = {"À la recherche de chapelets", nil, {"Retrouvez le Chapelet draeneï et rapportez-le à l'Assistant Klatu dans le Bastion de l'Honneur."}},
[10917] = {"La souffrance du paria", nil, {"Apportez 30 Plumes d'arakkoa à Vekax dans la Ville basse de Shattrath."}},
[10918] = {"Plus de plumes", nil, {"Conditions :"}},
[10919] = {"Les friandises de Fei Fei", nil, {"Conditions :"}},
[10920] = {"Au nom des disparus", nil, {"Le redresseur de torts Haylen veut que vous tuiez 20 Veuves croc-d'effroi, puis que vous reveniez le voir juste à l'ouest de la Crête de Toile-néant."}},
[10921] = {"Terokkarantula", nil, {"Le redresseur de torts Haylen veut que vous alliez tuer Terokkarantula et que vous reveniez le voir à l'ouest de la crête de Toile-néant."}},
[10922] = {"En fouillant dans les os", nil, {"Escortez Leglas et son équipe et protégez-les pendant les fouilles. Si vous réussissez, retournez voir Nanowitz au Camp de base sha’tari."}},
[10923] = {"L'approche du mal", nil, {"Chênuin veut que vous emportiez la Relique de l'effroi au Monticule grouillant. Là-bas, tuez des nécrorateurs et des auspices-funestes auchenaï pour collecter 20 Crânes du destin. Trouvez ensuite le Cercle d'invocation du Monticule grouillant et utilisez la Relique de l'effroi pour invoquer et détruire Teribus le Maudit. Revenez voir Chênuin quand ce sera fait."}},
[10924] = {"La part du diabl-otin !", nil, {"Sab'aoth de la Cosmovrille veut 8 fioles de Sang de poursuivant dimensionnel."}},
[10926] = {"Retour au camp de base sha'tari", nil, {"Le redresseur de torts Haylen veut que vous retourniez au Camp de base sha'tari, dans le Désert des Ossements et que vous y parliez à l'Éclaireur Navrin."}},
[10927] = {"Tuer les rampantes", nil, {"Tuez 6 Rampantes des cavernes."}},
[10928] = {"Tuer les rampantes", nil, {"Tuez 6 Rampantes des cavernes."}},
[10929] = {"Martelheurt", nil, {"Nanowitz, de l'Avant-poste sha'tari du désert des Ossements, veut que vous lui rameniez 8 Carcasses de cribleurs des ossements adultes."}},
[10930] = {"Le gros ver des ossements", nil, {"Nanowitz, de l'Avant-poste sha'tari du désert des Ossements, veut que vous trouviez des Organes d'énorme ver des ossements.\n\nIl veut aussi qu'on lui rapporte son Martelheurt."}},
[10931] = {"Retour à l'Exodar", nil, {"Parlez à Caedmos à l'Exodar."}},
[10932] = {"Retour à l'Exodar", nil, {"Parlez à Caedmos à l'Exodar."}},
[10933] = {"Retour à Lune-d'Argent", nil, {"Parlez à Aldrae à Lune-d'argent."}},
[10934] = {"Retour à Lune-d'Argent", nil, {"Parlez à Aldrae à Lune-d'argent."}},
[10935] = {"L'exorcisme du colonel Jules", nil, {"Parlez à l'Anachorète Barada. Utilisez le chapelet pour l'aider pendant le rituel, puis parlez au Colonel Jules quand il sera sauvé. Pour finir, retournez voir l'Assistant Klatu."}},
[10936] = {"Trollemort vous cherche !", nil, {"L'Assistant Klatu vous a indiqué que le Commandant de corps Danath Trollemort vous cherchait. Allez lui parler à la caserne du Bastion de l'Honneur dans la Péninsule des Flammes infernales."}},
[10937] = {"Détruisez le maître de manœuvre", nil, {"Le Commandant de corps Danath Trollemort vous a ordonné de tuer au plus vite le Maître de manœuvre Zurok. Retournez voir le commandant de corps au Bastion de l'Honneur dans la Péninsule des Flammes infernales une fois que le maître de manœuvre sera mort."}},
[10938] = {"Suite de Bénédictions de Sombrelune", nil, {"Rapporter la suite de Bénédictions à la foire de Sombrelune quand elle reviendra en ville."}},
[10939] = {"Suite de Tempêtes de Sombrelune", nil, {"Rapporter la suite de Tempêtes à la foire de Sombrelune quand elle reviendra en ville."}},
[10940] = {"Suite de Furies de Sombrelune", nil, {"Rapporter la suite de Furies à la foire de Sombrelune quand elle reviendra en ville."}},
[10941] = {"Suite de Déraison de Sombrelune", nil, {"Rapporter la suite de Déraison à la foire de Sombrelune quand elle reviendra en ville."}},
[10942] = {"La Semaine des enfants", nil, {"Utiliser le Sifflet d'orphelin elfe de sang pour parler à l'enfant que vous surveillerez durant la Semaine des enfants."}},
[10943] = {"La Semaine des enfants", nil, {"Utiliser le Sifflet d'orphelin draeneï pour parler à l'enfant que vous surveillerez durant la Semaine des enfants."}},
[10944] = {"Un secret compromis", nil, {"Allez à la Cage de la gardienne dans la vallée d'Ombrelune et parlez avec Akama."}},
[10945] = {"Hch'uu et le peuple champignon", nil, {"Emmenez votre orpheline, Salandria, à Sporeggar dans le marécage de Zangar. N'oubliez pas de l'appeler si elle n'est pas présente lorsque vous arrivez. Puis aller parler à Hch'uu."}},
[10946] = {"La ruse des Cendrelangues", nil, {"Pénétrez dans le Donjon de la Tempête et tuez Al'ar en portant la Capuche de Cendrelangue. Retournez voir Akama dans la Vallée d'Ombrelune une fois que ce sera fait."}},
[10947] = {"Un artéfact du passé", nil, {"Allez aux Grottes du temps en Tanaris, et rendez-vous à la Bataille du mont Hyjal. Quand vous y serez, triomphez de Rage Froidhiver et rapportez le Phylactère chronophasé à Akama dans la vallée d'Ombrelune."}},
[10948] = {"L'âme otage", nil, {"Rendez-vous à Shattrath pour soumettre la requête d'Akama à A'dal."}},
[10949] = {"L'entrée dans le Temple noir", nil, {"Rendez-vous à l'entrée du Temple noir dans la vallée d'Ombrelune et allez parler à Xi'ri."}},
[10950] = {"Auchindoun et le Cercle d'observance", nil, {"Emmenez votre orpheline, Dornaa, à la Pierre de rencontre du Cercle d'observance, au centre d'Auchindoun. Auchindoun est situé au centre du désert des Ossements de la forêt de Terokkar. N'oubliez pas de l'appeler si elle n'est pas présente lorsque vous arrivez."}},
[10951] = {"Une visite à la Porte des ténèbres", nil, {"Emmenez votre orpheline, Salandria, en haut de l'escalier du Destin, pour qu'elle puisse passer devant la Porte des ténèbres dans la péninsule des Flammes infernales. N'oubliez pas de l'appeler si elle n'est pas présente lorsque vous arrivez."}},
[10952] = {"Une visite à la Porte des ténèbres", nil, {"Emmenez votre orpheline, Dornaa, en haut de l'escalier du Destin pour qu'elle passe devant la Porte des ténèbres dans la péninsule des Flammes infernales. N'oubliez pas de l'appeler si elle n'est pas présente lorsque vous arrivez."}},
[10953] = {"Une visite au Trône des éléments", nil, {"Emmenez votre orpheline, Salandria, au Trône des éléments à Nagrand. N'oubliez pas de l'appeler si elle n'est pas présente lorsque vous arrivez. Ensuite, allez parler à l'Élémentaliste Sharvak."}},
[10954] = {"Jheel est au point d'ancrage Aeris !", nil, {"Emmenez votre orpheline, Dornaa, au Point d'ancrage Aeris en Nagrand. N'oubliez pas de l'appeler si elle n'est pas présente lorsque vous arrivez. Ensuite, parlez avec Jheel."}},
[10955] = {"Morthis Murmedaile", nil, {"Allez parler à Morthis Murmedaile au Refuge cénarien dans le marécage de Zangar."}},
[10956] = {"Le Siège du naaru", nil, {"Emmenez votre orpheline, Dornaa, voir O'ros au pied du Siège du naaru dans l'Exodar, sur l'île de Brume-azur. N'oubliez pas de l'appeler si elle n'est pas présente lorsque vous arrivez.\n\nRappelez-vous que vous pouvez utiliser votre carte à l'intérieur de la ville."}},
[10957] = {"La rédemption du Cendrelangue", nil, {"Aidez Akama à reprendre le contrôle de son âme en vainquant l'Ombre d'Akama dans le Temple noir. Retournez voir le Voyant Kanai quand ce sera fait."}},
[10958] = {"À la recherche des Cendrelangues", nil, {"Trouvez les Ligemorts d'Akama à l'intérieur du Temple noir."}},
[10959] = {"La chute du Traître", nil, {"Le voyant Kanai veut que vous vainquiez Illidan à l'intérieur du Temple noir."}},
[10961] = {"La protection de l'éveil", nil, {"Apportez 10 Fleurs des tourbières, 10 Lichens anciens et 10 Glauriers à Morthis Murmedaile, au Refuge cénarien."}},
[10962] = {"Il est temps de visiter les Grottes", nil, {"Emmenez votre orpheline, Dornaa, devant Zaladormu, le dragon qui se trouve sur l'estrade au milieu des Grottes du temps. N'oubliez pas de l'appeler si elle n'est pas présente lorsque vous arrivez.\n\nEnsuite, achetez-lui une Figurine de dragon auprès de l'Intendant des Gardiens du temps, Alurmi, près du fond du tunnel d'entrée."}},
[10963] = {"Il est temps de visiter les Grottes", nil, {"Emmenez votre orpheline, Salandria, devant Zaladormu, le dragon qui se trouve sur l'estrade au milieu des Grottes du temps. N'oubliez pas de l'appeler si elle n'est pas présente lorsque vous arrivez.\n\nEnsuite, achetez-lui une Figurine de dragon auprès de l'Intendant des Gardiens du temps, Alurmi, près du fond du tunnel d'entrée."}},
[10964] = {"Le réveil du dormeur", nil, {"Utilisez le Gardien de l'éveil pour aider Clintar Marcherêve à sortir du Rêve d'émeraude. Une fois qu'il a repris connaissance, parlez-lui."}},
[10966] = {"Retour à l'orphelinat", nil, {"Ramenez votre protégée à l'orphelinat de Shattrath, en rendant le Sifflet d'orphelin draeneï à Miss Hérikorde, directrice de l'orphelinat, dans la Ville basse."}},
[10967] = {"Retour à l'orphelinat", nil, {"Ramenez votre protégée à l'orphelinat de Shattrath, en rendant le Sifflet d'orphelin elfe de sang à Miss Hérikorde, directrice de l'orphelinat, dans la Ville basse."}},
[10968] = {"En visite chez le long-voyant", nil, {"Emmenez votre orpheline, Dornaa, rendre visite au Long-voyant Nobundo dans le Hall de Cristal à l'intérieur de l'Exodar, qui se trouve sur l'Île de Brume-azur. N'oubliez pas de l'appeler si elle n'est pas présente lorsque vous arrivez.\n\nRappelez-vous que vous pouvez consulter votre carte même à l'intérieur de la ville."}},
[10969] = {"Rechercher Ameer", nil, {"Trouvez le Commandant Ameer au Poste de garde du Protectorat."}},
[10970] = {"Une mission miséricordieuse", nil, {"Procurez-vous la Clé de la prison de l'Ethereum récupérée pour le Commandant Ameer au Poste de garde du Protectorat."}},
[10971] = {"Secrets de l'Ethereum", nil, {"Le Commandant Ameer, au Poste de garde du Protectorat au Raz-de-Néant, veut que vous lui apportiez une Plaque d'identité de prisonnier de l'Ethereum."}},
[10972] = {"Répertorier les prisonniers de l'Ethereum", nil, {"Conditions :"}},
[10973] = {"Un millier de mondes", nil, {"Le Commandant Ameer, au Poste de garde du Protectorat au Raz-de-Néant, veut que vous lui apportiez 5 Clés de la prison de l'Ethereum."}},
[10974] = {"Les chambres de stase de Bash'ir", nil, {"Utilisez la Tablette-clé de l'Ethereum - Alpha pour ouvrir la Chambre de stase alpha au Point d'ancrage de Bash'ir dans les Tranchantes.\n\nRapportez les Indices de l'alpha à l'Image du commandant Ameer au Point d'ancrage de Bash'ir."}},
[10975] = {"La purge des chambres de Bash'ir", nil, {"Conditions :"}},
[10976] = {"La Marque du roi-nexus", nil, {"L'Image du commandant Ameer au Point d'ancrage de Bash'ir dans les Tranchantes vous demande de lui rapporter une Marque du roi-nexus."}},
[10977] = {"Les chambres de stase des Tombes-mana", nil, {"L'Image du commandant Ameer au Point d'ancrage de Bash'ir dans les Tranchantes vous demande d'utiliser la Clé de la chambre de stase des Tombes-mana dans les Tombes-mana d'Auchindoun.\n\nCette quête ne peut être accomplie qu'au niveau de difficulté Héroïque."}},
[10979] = {"En route pour le Bosquet éternel", nil, {"Allez parler à Arthorn Chant-du-vent au Bosquet éternel dans les Tranchantes."}},
[10980] = {"Le Livre du Corbeau", nil, {"Utilisez un Œil de raie de l'éther avec votre Pierre de prophète pour voir les fantômes du culte du corbeau. Cherchez l'esprit de Sai'kkal l'Ancien dans la Cime du vortex et interrogez-le au sujet du livre du corbeau, puis retournez auprès d'Arthorn Chant-du-vent."}},
[10981] = {"La chambre personnelle du prince-nexus Shaffar", nil, {"Conditions :"}},
[10982] = {"L'œil d'Haramad", nil, nil},
[10983] = {"Mog'dorg le Ratatiné", nil, {"Allez voir Mog'dorg le Ratatiné au sommet de l'une des tours qui se trouvent devant le Cercle de sang dans les Tranchantes."}},
[10984] = {"Parler avec l'ogre", nil, {"Allez voir l'Ogre, Grok, dans le quartier de la Ville basse de Shattrath."}},
[10985] = {"Une distraction pour Akama", nil, {"Assurez-vous qu'Akama et Maiev pénètrent bien dans le Temple noir de la Vallée d'Ombrelune une fois que les forces de Xi'ri auront fait diversion."}},
[10987] = {"La chasse à la crécerelle", nil, {"Utilisez le Filet à crécerelles pour capturer une Crécerelle sauvage, et ramenez-la à la Gardienne Elaira sur la Crête du Crépuscule."}},
[10988] = {"Les pierres du Corbeau", nil, {"Rassemblez 8 Pierres du Corbeau à Skettis et apportez-les à Arthorn Chant-du-vent au Bosquet éternel dans les Tranchantes."}},
[10989] = {"Mog'dorg le Ratatiné", nil, {"Parlez à Mog'dorg le Ratatiné au sommet de la tour devant laquelle se tient Chort, juste devant le Cercle de sang, dans les Tranchantes."}},
[10990] = {"L'essence de l'aigle", nil, {"Terrassez le Gardien de l'Aigle et emparez-vous de l'Essence de l'Aigle. Utilisez le Sifflet à crécerelle d'Arthorn pour appeler la Crécerelle d'Arthorn afin de transmettre la nouvelle de votre succès.\n\nSi vous perdez le Sifflet à crécerelle d'Arthorn, retournez voir Arthorn Chant-du-vent au Bosquet éternel pour le faire remplacer."}},
[10992] = {"L'essence du faucon", nil, {"Terrassez le Gardien du Faucon et apportez l'Essence du faucon à Arthorn Chant-du-vent au Bosquet éternel."}},
[10994] = {"La chasse à la pierre de lune", nil, {"Apportez une Pierre de lune de Furie-du-Sud à Morthis Murmedaile au Refuge cénarien."}},
[10995] = {"Grulloc a deux crânes", nil, {"Emparez-vous du Crâne de dragon de Grulloc."}},
[10996] = {"Le coffre au trésor de Maggoc", nil, {"Emparez-vous du Coffre au trésor de Maggoc."}},
[10997] = {"L'étendard et la manière", nil, {"Emparez-vous de l'Étendard de Skori."}},
[10998] = {"Le tome de sa voix", nil, {"Emparez-vous du Grimoire infâme de Vim'gol."}},
[10999] = {"Faire mouche", nil, {"Rapportez-moi 8 Dents de pierre"}},
[11000] = {"Face au Broyeur-d'âme", nil, {"Récupérez l’Âme de Crânoc."}},
[11001] = {"Triompher du dieu corbeau", nil, {"Tuez le Dieu corbeau et retournez voir Morthis Murmedaile au Refuge cénarien.\n\nCette quête doit être accomplie en mode de difficulté du donjon Héroïque."}},
[11002] = {"La chute de Magtheridon", nil, {"Rapportez la Tête de Magtheridon au Commandant de corps Danath Trollemort au bastion de l'Honneur dans la péninsule des Flammes infernales."}},
[11003] = {"La chute de Magtheridon", nil, {"Rapportez la Tête de Magtheridon à Nazgrel à Thrallmar dans la péninsule des Flammes infernales."}},
[11004] = {"Un monde d'ombres", nil, {"Séverin souhaite que vous vous aventuriez dans Skettis et que vous récupériez 6 Poussières ombreuses sur les arakkoa qui s'y trouvent."}},
[11005] = {"Les secrets des prêtres de la Serre", nil, {"Procurez-vous un Élixir des ombres auprès de Séverin et utilisez-le pour trouver et tuer le Prêtre de la serre Ishaal, le Prêtre de la serre Skizzik et le Prêtre de la serre Zellek à Skettis. Retournez voir le commandant Adaris lorsque vous aurez accompli cette tâche."}},
[11006] = {"Plus de poussière ombreuse", nil, {"Conditions :"}},
[11007] = {"Kael'thas et la sphère verdoyante", nil, {"Apportez la Sphère verdoyante à A'dal dans Shattrath."}},
[11008] = {"Un déluge de feu sur Skettis", nil, {"Cherchez les Œufs de kaliri monstrueux au sommet des habitations de Skettis, et utilisez les Détonateurs de la Garde-ciel pour les détruire. Revenez voir ensuite le Sergent Doryn de la Garde-ciel."}},
[11009] = {"Le paradis des ogres", nil, {"Mog'dorg le Ratatiné vous demande d'aller parler à Chu'a'lor à Ogri'la dans les Tranchantes."}},
[11010] = {"Mission de bombardement", nil, {"Utilisez les Bombes de la Garde-ciel pour détruire 15 Piles de boulets de gangrecanon."}},
[11012] = {"Serment de sang de l'Aile-du-Néant", nil, nil},
[11013] = {"Au service des Illidari", nil, {"Rendez-vous à l'Escarpement de l'Aile-du-Néant et remettez la Lettre de service illidari au Suzerain Mor'ghor au Campement des Gueules-de-dragon."}},
[11014] = {"Le sous-chef entre en scène", nil, {"Allez parler au Sous-chef Varkule Souffle-de-dragon au Campement des Gueules-de-dragon."}},
[11015] = {"Les cristaux de l'Aile-du-Néant", nil, {"Le Sous-chef Varkule Souffle-de-dragon du Campement des Gueules-de-dragon dans la vallée d'Ombrelune veut que vous lui rapportiez 30 Cristaux de l'Aile-du-Néant."}},
[11016] = {"Des peaux d'écorcheurs mine-néant", nil, {"Le Sous-chef Varkule Souffle-de-dragon du Campement des Gueules-de-dragon dans la vallée d'Ombrelune veut que vous lui rapportiez 35 Peaux d'écorcheurs mine-néant."}},
[11017] = {"Du pollen de pruinéante", nil, {"Le Sous-chef Varkule Souffle-de-dragon du Campement des Gueules-de-dragon dans la vallée d'Ombrelune veut que vous lui rapportiez 40 échantillons de Pollen de pruinéante."}},
[11018] = {"Du minerai de néanticite", nil, {"Le Sous-chef Varkule Souffle-de-dragon du Campement des Gueules-de-dragon dans la vallée d'Ombrelune veut que vous lui rapportiez 40 échantillons de Minerai de néanticite."}},
[11019] = {"Un ami à l'intérieur", nil, nil},
[11020] = {"Une mort lente", nil, {"Yarzill le Mercenaire du Campement des Gueules-de-dragon de la Vallée d'Ombrelune veut que vous utilisiez la Viande de mouton de Yarzill associée à des Gangreglandes pour empoisonner 12 Campements de péons Gueules-de-dragon."}},
[11021] = {"L'almanach d'Ishaal", nil, {"Apportez l'Almanach d'Ishaal au Commandant de la Garde-ciel Adaris, au nord de Skettis."}},
[11022] = {"Parler à Mog'dorg", nil, {"Allez parler à Mog'dorg le Ratatiné. Il se trouve au sommet de la tour située du côté est du Cercle de sang dans les Tranchantes."}},
[11023] = {"Bombardez-les encore !", nil, {"Utilisez les Bombes de la Garde-ciel pour détruire 15 Piles de boulets de gangrecanon."}},
[11024] = {"Un allié dans la Ville basse", nil, {"Apportez l'Almanach d'Ishaal à Rilak le Racheté dans la Ville basse, à Shattrath."}},
[11025] = {"Les cristaux", nil, {"Chu'a'lor vous a confié pour mission de collecter 5 Éclats apogides. Apportez-les-lui à Ogri'la au sommet des Tranchantes."}},
[11026] = {"Bannissez les démons", nil, {"Utilisez le Cristal de bannissement pour bannir 15 démons au camp de forge : Courroux ou au camp de forge : Terreur."}},
[11028] = {"Compte à rebours avant anéantissement", nil, {"Retournez voir le Commandant Adaris de la Garde-ciel au Raie'odrome de Noirvent, dans la Forêt de Terokkar, avec les nouvelles concernant le retour de Terokk."}},
[11029] = {"Un déguisement miteux", nil, {"Utilisez le Déguisement arakkoa miteux pour obtenir les Lignées antagonistes de Sahaak, puis retournez auprès de Hazzik."}},
[11030] = {"Notre fils veut devenir patrouilleur de la Garde-ciel", nil, {"Procurez-vous un Flacon instable de la bête."}},
[11031] = {"Quitter la voie de l'archimage", nil, {"Conditions :"}},
[11032] = {"Quitter la voie du protecteur", nil, {"Conditions :"}},
[11033] = {"Quitter la voie de l'assassin", nil, {"Conditions :"}},
[11034] = {"Quitter la voie du guérisseur", nil, {"Conditions :"}},
[11035] = {"Les cieux pas si cléments...", nil, {"Yarzill le Mercenaire du Campement des Gueules-de-dragon de la Vallée d'Ombrelune veut que vous récupériez 10 Reliques de l'Aile-du-Néant."}},
[11036] = {"Marchandise extraordinaire !", nil, {"Apportez les Fruits en conserve au Chef-de-fusée Karlingue dans la Zone 52, à Raz-de-Néant."}},
[11037] = {"Une vision étrange", nil, {"Parlez au Chef-de-fusée Karlingue dans la Zone 52."}},
[11038] = {"Aider l'exarque Orelis", nil, {"Parlez à l'Exarque Orelis dans la Zone 52."}},
[11039] = {"Se présenter au maître-espion Thalodien", nil, {"Présentez-vous au Maître-espion Thalodien dans la Zone 52."}},
[11040] = {"Des pièces détachées pour le chef-de-fusée", nil, {"Apportez les Pièces détachées de Bossi au Chef-de-fusée Karlingue dans la Zone 52, à Raz-de-Néant."}},
[11041] = {"Un travail à terminer...", nil, {"Tuez 10 Écorcheurs Supremine et Barash la Mère de la tanière. Revenez voir le Suzerain Mor'ghor au Campement des Gueules-de-dragon si vous réussissez."}},
[11042] = {"Une vision mystique", nil, {"Parlez au Chef-de-fusée Karlingue dans la Zone 52."}},
[11043] = {"Le griffon de l'avenir", nil, {"Parlez à Brunn Flammebarbe au Bastion des Marteaux-hardis, dans la Vallée d'Ombrelune."}},
[11044] = {"Visions d'anéantissement", nil, {"Parlez au Chef d'escadrille Nuainn au Bastion des Marteaux-hardis."}},
[11045] = {"Zorus le Judicateur", nil, {"Parlez à Zorus le Judicateur au Bastion des Marteaux-hardis."}},
[11046] = {"L'apothicaire en chef Hildagard", nil, {"Parlez à l'Apothicaire en chef Hildagard au village d'Ombrelune."}},
[11047] = {"La requête de l'apprenti", nil, {"Parlez à Dama Crin-sauvage au village d'Ombrelune, dans la Vallée d'Ombrelune."}},
[11048] = {"Le rapport de Kroghan", nil, {"Parlez au Garde de sang Gulmok au village d'Ombrelune."}},
[11049] = {"La ruée vers les œufs de l'Aile-du-Néant", nil, {"Yarzill le Mercenaire, du campement des Gueules-de-dragon dans la vallée d'Ombrelune, veut que vous vous procuriez un Œuf de l'Aile-du-Néant."}},
[11050] = {"Tous les œufs sont bons à prendre", nil, {"Conditions :"}},
[11051] = {"Bannissez plus de démons", nil, {"Utilisez le Cristal de bannissement pour bannir 15 démons au Camp de forge : Courroux ou au Camp de forge : Terreur."}},
[11052] = {"La promesse d'Akama", nil, {"Apportez le Médaillon de Karabor à A'dal, à Shattrath."}},
[11053] = {"Debout, surveillant !", nil, {"Parlez au Suzerain Mor'ghor au Campement des Gueules-de-dragon dans la vallée d'Ombrelune."}},
[11054] = {"Être surveillant : savoir faire les bons choix", nil, {"Le Surveillant-chef Tadboue du campement des Gueules-de-dragon dans la vallée d'Ombrelune veut que vous lui apportiez 10 morceaux de Cuir granuleux et 1 Peau de Tyrantus durcie."}},
[11055] = {"Le botterang : un traitement pour les péons bons à rien", nil, {"Le Surveillant-chef Tadboue du campement des Gueules-de-dragon dans la vallée d'Ombrelune veut que vous punissiez 20 Péons Gueules-de-dragon paresseux. Vous devrez aussi rapporter votre Botterang."}},
[11056] = {"Le marché d'Hazzik", nil, {"Récupérez le Paquet d'Hazzik dans sa demeure, à l'est de Skettis, et remettez-le lui."}},
[11057] = {"Des ennuis en contrebas", nil, {"Parlez à Mog'dorg le Ratatiné, en haut d'une des tours juste à l'extérieur du Cercle de sang dans les Tranchantes."}},
[11058] = {"Une relique apogide", nil, {"Obtenir des Vibrations apogides d’une Relique apogide."}},
[11059] = {"Le gardien du monument", nil, {"Procurez-vous la tête d’un Gardien apogide."}},
[11061] = {"Le devoir d'un père", nil, {"Procurez-vous un Flacon instable du sorcier pour lui."}},
[11062] = {"L'avant-poste de la Garde-ciel", nil, {"Parlez au Commandant Keller de la Garde-ciel à l'Avant-poste de la Garde-ciel, dans les Tranchantes."}},
[11063] = {"Mériter ses ailes...", nil, nil},
[11064] = {"Course Gueule-de-dragon : la ballade de Vieillot McVieux", nil, {"Suivez Murg « Vieillot » Bourbegueule en tentant de rester sur votre monture volante. Si vous parvenez à le battre, informez Ja'y Nosliw de votre victoire au Campement des Gueules-de-dragon dans la Vallée d'Ombrelune."}},
[11065] = {"Allez dompter quelques raies de l'éther !", nil, {"Utilisez le Lasso pour capturer 5 Raies de l’éther."}},
[11066] = {"Allez dompter d'autres raies de l'éther !", nil, {"Utilisez le Lasso pour capturer 5 Raies de l’éther."}},
[11067] = {"Course Gueule-de-dragon : Trope le Rote-crasse", nil, {"Suivez Trope le Rote-crasse en tentant de rester sur votre monture volante. Si vous parvenez à le battre, informez Ja'y Nosliw de votre victoire au Campement des Gueules-de-dragon dans la vallée d'Ombrelune."}},
[11068] = {"Course Gueule-de-dragon : Corlok le Briscard", nil, {"Suivez Corlok le Briscard en tentant de rester sur votre monture volante. Si vous parvenez à le battre, informez Ja'y Nosliw de votre victoire au Campement des Gueules-de-dragon dans la vallée d'Ombrelune."}},
[11069] = {"Course Gueule-de-dragon : Chef d'escadrille Ichman", nil, {"Suivez le Chef d'escadrille Ichman en tentant de rester sur votre monture volante. Si vous parvenez à le battre, informez Ja'y Nosliw de votre victoire au Campement des Gueules-de-dragon dans la vallée d'Ombrelune."}},
[11070] = {"Course Gueule-de-dragon : Chef d'escadrille Mulverick", nil, {"Suivez le Chef d'escadrille Mulverick en tentant de rester sur votre monture volante. Si vous parvenez à le battre, informez Ja'y Nosliw de votre victoire au Campement des Gueules-de-dragon dans la vallée d'Ombrelune."}},
[11071] = {"Course Gueule-de-dragon : Capitaine Fracasse-ciel", nil, {"Suivez le Capitaine Fracasse-ciel en tentant de rester sur votre monture volante. Si vous parvenez à le battre, informez Ja'y Nosliw de votre victoire au Campement des Gueules-de-dragon dans la vallée d'Ombrelune."}},
[11072] = {"Lignées antagonistes", nil, {"Trouvez les Piles de crânes au milieu des cercles d'invocation de Skettis. Invoquez et vainquez chacun des descendants des adversaires de Terokk en utilisant 10 Parchemins perdus dans le temps à la Pile de crâne. Retournez auprès de Hazzik au Raie'odrome de Noirvent avec un gage de chacun d'eux."}},
[11073] = {"La chute de Terokk", nil, {"Emportez l'Offrande perdue dans le temps préparée par Hazzik à la Pile de crânes qui se trouve au milieu de Skettis, puis invoquez et terrassez Terokk. Retournez voir le Commandant Adaris de la Garde-ciel quand vous aurez accompli votre mission."}},
[11074] = {"Les attributs des descendants", nil, {"Rassemblez des Parchemins perdus dans le temps détenus par les arakkoa de Skettis, et emportez-les jusqu'à une Pile de crânes au milieu d'un cercle d'invocation de Skettis. Invoquez et vainquez les descendants des adversaires de Terokk et rapportez les Serres d'Akkarai, l'Échine de Garrok, l'Écaille de Vekkaz et les Griffes de Gezzarak à Hakkiz."}},
[11075] = {"Les mines de l'Aile-du-Néant", nil, {"Allez voir la Maîtresse des mines sur l'Escarpement de l'Aile-du-Néant."}},
[11076] = {"Ramasser les morceaux...", nil, {"La Maîtresse des mines, des Mines de l'escarpement de l'Aile-du-Néant de la vallée d'Ombrelune, veut que vous récupériez 15 caisses de Cargaison de Mine-néant."}},
[11077] = {"Les dragons sont les derniers de nos soucis", nil, {"Le Contremaître Gueule-de-dragon des Mines de l'Aile-du-Néant de la vallée d'Ombrelune veut que vous tuiez 15 Ecorcheurs mine-néant et 5 Ravageurs mine-néant."}},
[11078] = {"Pour régner sur les cieux", nil, {"Récupérez des Dents de dragon."}},
[11079] = {"Un gangrefouet pour Gahk", nil, {"Procurez-vous un Gangrefouet."}},
[11080] = {"Les émanations des reliques", nil, {"Obtenir des Emanations apogides d’une Relique apogide."}},
[11081] = {"La grande révolte des Bourbesang", nil, {"Apportez les Plans d'évasion bourbesang à la Maîtresse des mines."}},
[11082] = {"À la recherche de la vérité", nil, {"Trouvez un Surveillant bourbesang et interrogez-le. Retournez voir la Maîtresse des mines avec les Informations des Bourbesang et la Main du surveillant."}},
[11083] = {"Affolés et perturbés", nil, {"Ronag l'Esclavagiste, des Mines de l'Aile-du-Néant dans la vallée d'Ombrelune, vous demande de tuer le Contremaître bourbesang affolé et 5 Mineurs bourbesang affolés."}},
[11084] = {"Haut les cœurs, capitaine !", nil, {"Parlez au Suzerain Mor'ghor au Campement des Gueules-de-dragon dans la vallée d'Ombrelune."}},
[11085] = {"L'évasion de Skettis", nil, {"Escorter le Prisonnier de la Garde-ciel à l'abri, puis se présenter au Sergent Doryn de la Garde-ciel."}},
[11086] = {"Perturber la porte du Crépuscule", nil, {"Le Suzerain Mor'ghor du campement des Gueules-de-dragon dans la vallée d'Ombrelune vous demande de tuer 20 Agents ombremort."}},
[11089] = {"Le canon à âmes de Reth'hedron", nil, {"Le Seigneur illidari Balthas du campement des Gueules-de-dragon dans la vallée d'Ombrelune veut que vous lui apportiez 2 Barres de gangracier, 1 Structure en adamantite, 1 Batterie en khorium et 1 Essence des arcanes sans défaut."}},
[11090] = {"Dominer le Dominateur", nil, {"Le Seigneur illidari Balthas du campement des Gueules-de-dragon dans la vallée d'Ombrelune vous a chargée d'utiliser le Canon à âmes sur Reth'hedron le Dominateur en Nagrand.\n\nVous devrez aussi rapporter le Canon à âmes."}},
[11091] = {"Remerciement spécial", nil, {"Parlez à Jho'nass, à Ogri'la, dans les Tranchantes."}},
[11092] = {"Salut, commandant !", nil, {"Parlez au Suzerain Mor'ghor au Campement des Gueules-de-dragon dans la vallée d'Ombrelune."}},
[11093] = {"Raies du Néant affamées", nil, {"Utilisez la Cage de raie du Néant dans les bois qui se trouvent au sud du Raie'odrome de Noirvent et tuez des Poursuivants dimensionnels près de la Raie du Néant affamée. Retournez voir le Dresseur Deesak de la Garde-ciel quand vous aurez terminé."}},
[11094] = {"Tuez-les tous !", nil, {"Avertissez l'Arcaniste Thelis du contenu de votre mission et apportez-lui le Lance-fusée gueule-de-dragon au Sanctum des Étoiles dans la vallée d'Ombrelune."}},
[11095] = {"Le commandant Hobb", nil, {"Donnez le Lance-fusée gueule-de-dragon au Commandant Hobb au Sanctum des Étoiles dans la vallée d'Ombrelune."}},
[11096] = {"La menace vient d'en haut", nil, {"Tuez 20 Ogres gordunni de n'importe quelle variété dans la Barrière, puis retournez voir Yuula à Shattrath."}},
[11097] = {"Le plus mortel des pièges", nil, {"Aidez le Commandant Hobb et les Clairvoyants à défendre le Sanctum des Étoiles. Une fois que l'ensemble des forces Gueule-de-dragon aura été vaincu, retournez voir le suzerain Mor'ghor au Campement des Gueules-de-dragon avec les Médailles de Clairvoyant."}},
[11098] = {"En route pour Skettis !", nil, {"Apportez le Paquet d'explosifs au Sergent Doryn de la Garde-ciel au Raie'odrome de Noirvent à l'extérieur de Skettis."}},
[11099] = {"Tuez-les tous !", nil, {"Avertissez l'Anachorète Ceyla du contenu de votre mission et apportez-lui le Lance-fusée gueule-de-dragon à l'Autel de Sha'tar."}},
[11100] = {"Le commandant Arcus", nil, {"Donnez le Lance-fusée gueule-de-dragon au Commandant Arcus à l'Autel de Sha'tar dans la vallée d'Ombrelune."}},
[11101] = {"Le plus mortel des pièges", nil, {"Aidez le Commandant Arcus et l'Aldor à défendre l'Autel de Sha'tar. Une fois que l'ensemble des forces Gueule-de-dragon aura été vaincu, retournez voir le suzerain Mor'ghor au Campement des Gueules-de-dragon avec les Médailles de l'Aldor."}},
[11103] = {"Quitter la voie du sage", nil, {"Conditions :"}},
[11104] = {"Quitter la voie du guérisseur", nil, {"Conditions :"}},
[11105] = {"Quitter la voie du champion", nil, {"Conditions :"}},
[11106] = {"Quitter la voie du défenseur", nil, {"Conditions :"}},
[11107] = {"À genoux devant le généralissime !", nil, {"Parlez au Suzerain Mor'ghor au Campement des Gueules-de-dragon dans la vallée d'Ombrelune."}},
[11108] = {"Le seigneur Illidan Hurlorage", nil, {"Rencontrez Illidan Hurlorage. Si jamais vous survivez, allez trouver Barthamus dans la Ville basse de Shattrath."}},
[11109] = {"Jorus le drake de l'Aile-du-Néant cobalt", nil, nil},
[11110] = {"Malfas le drake de l'Aile-du-Néant violet", nil, nil},
[11111] = {"Onyxian le drake de l'Aile-du-Néant onyx", nil, nil},
[11112] = {"Suraku le drake de l'Aile-du-Néant azur", nil, nil},
[11113] = {"Voranaku le drake de l'Aile-du-Néant pourpre", nil, nil},
[11114] = {"Zoya le drake de l'Aile-du-Néant viride", nil, nil},
[11117] = {"Attraper le Wolpertinger sauvage !", nil, {"Archedor Chassebécasse vous a demandé de capturer 5 Wolpertingers sonnés avec le Filet à wolpertinger. Il a indiqué qu'il fallait être soûl ou porter des Lunettes Synthébière pour les voir."}},
[11118] = {"Le défilé des elekk roses", nil, {"Rendez-vous aux guinguettes à l'extérieur de Hurlevent, Darnassus et l'Exodar. Supprimez trois elekk dans chacune d'elles, puis revenez voir Archedor Chassebécasse. Vous devez vous soûler ou bien porter des Lunettes Synthébière pour voir les Elekk roses."}},
[11119] = {"Haro sur le point d'ancrage de Bash'ir", nil, {"Parlez au Techno-éther apprenti à l’Avant-poste de la Garde-ciel, dans les Tranchantes."}},
[11120] = {"Le défilé des elekk roses", nil, {"Rendez-vous aux guinguettes à l'extérieur de Lune-d'argent, des Pitons-du-Tonnerre et de Fossoyeuse. Supprimez trois elekk dans chacune d'elles, puis revenez voir Charedor Cassebéchasse. Vous devez vous soûler ou porter des Lunettes Synthébière pour voir les Elekk roses."}},
[11121] = {"QUÊTE JOURNALIÈRE DE TEST (Raid)", nil, {"Parlez à <QUESTGIVER> dans <REGION>."}},
[11122] = {"Histoire d'un aller et retour", nil, {"Allez chercher un tonneau auprès de Flynn Brassefeu à Kharanos et rapportez-le à Pol Distillambre. Rapportez trois tonneaux avant que votre bélier ne s'en aille."}},
[11123] = {"[PÉRIMÉ]Inspection des ruines", nil, {"Parlez à l'Inspecteur Tarem à l'Auberge du Repos ombragé."}},
[11124] = {"[PÉRIMÉ]Inspection des ruines", nil, {"Enquêtez à l'Auberge du Repos ombragé à la frontière des Tarides, au Marécage d'äprefange."}},
[11126] = {"Des traîtres parmi nous", nil, {"Démasquez 5 Agitateurs des déserteurs puis retournez voir Calia Hastings près des quais de Theramore."}},
[11128] = {"Guerre de propagande", nil, {"Remettez la Propagande des déserteurs à Calia Hastings près des quais de Theramore."}},
[11129] = {"Kyle a disparu !", nil, {"Ahab Sabot-de-blé, à Sabot-de-Sang en Mulgore, veut que vous nourrissiez son précieux chiot, Kyle le Frénétique.\n\nDonnez de la Viande de trotteur tendre à Kyle puis retournez voir Ahab Sabot-de-Blé."}},
[11130] = {"Houuuu, ça brille !", nil, {"Eskrokar veut que vous remettiez la Note d'Eskrokar à Budd, dans les Terres fantômes, à l'extérieur de Zul'Aman."}},
[11131] = {"Éteignez les incendies !", nil, {"La Directrice d'orphelinat costumée vous demande d'aider à éteindre tous les foyers d'incendie du village. Une fois qu'ils seront éteints, retournez lui parler."}},
[11132] = {"Des promesses, toujours des promesses...", nil, {"Budd Truducou, dans les collines de la Cognée, veut que vous récupériez sa carte. Elle se trouve sur la terrasse du Grand prêtre Nalorakk à Zul'Aman."}},
[11133] = {"Discréditer les déserteurs", nil, {"Calia Hastings veut que vous distribuiez les Pamphlets trafiqués à 6 Gardes de Theramore. Ensuite, retournez la voir près des quais de Theramore."}},
[11134] = {"La fin des déserteurs", nil, {"Capturez Gavis Targegrise, puis allez faire votre rapport à Calia Hastings près des quais de Theramore."}},
[11137] = {"[PÉRIMÉ]Des défias à Âprefange ?", nil, {"Récupérez les Ordres des défias sur Garn Mathers, et remettez-les au Capitaine Wymor à la halte de la Vigie."}},
[11138] = {"[PÉRIMÉ]Renn McBranchie", nil, {"Apportez les Ordres des défias à Renn McBranchie dans les eaux au large du Rivage de Troubleffroi."}},
[11142] = {"[PÉRIMÉ]Survoler l'île d'Alcaz", nil, {"Parlez à Cassa Cramoissaile et utilisez l'un de ses griffons pour survoler l'île d'Alcaz. Lorsque vous aurez achevé vos observations, parlez à dame Jaina Portvaillant à Theramore."}},
[11143] = {"[PÉRIMÉ]Un lien Sinistre", nil, {"Présentez-vous au Capitaine Darill à la Halte nord."}},
[11146] = {"[PÉRIMÉ]Rapt de raptor", nil, {"Le Sergent Lukas, de la Tour de la halte nord, veut que vous capturiez 6 raptors. Vous pouvez capturer des Raptors Rougefange ou des Hurleurs Rougefange, au choix."}},
[11152] = {"[PÉRIMÉ]Enfin la paix", nil, {"Le Capitaine Garran Vimaire, de la citadelle de Theramore, veut que vous déposiez la Couronne au Monument de la famille Hyal."}},
[11153] = {"Forcer le blocus", nil, {"Prenez les Bombes à sous-munitions de Petrov et lâchez-les sur les pirates. Vous devez tuer 25 Pirates du blocus et détruire 10 Canons du blocus. Lorsque vous aurez terminé, retournez voir le Bombardier Petrov au Donjon de la Garde de l’ouest."}},
[11154] = {"Dans le guano jusqu'au cou !", nil, {"Récupérez 10 doses de Guano de sombregriffe et livrez-les à l’Ingénieur Fauboulon, à la Porte d’acier."}},
[11155] = {"Encore de la soupe de brochepelle ?", nil, {"Le chef Bouillenoire vous a demandé de récupérer 6 morceaux de Viande de brochepelle et de les lui remettre au donjon de la Garde de l'ouest."}},
[11156] = {"[PÉRIMÉ]Les écumeurs navrecorne", nil, {"Tuez 12 Totem-sinistre de Navrecorne puis revenez voir l'Apprenti Morlann à la Ferme de Tabetha."}},
[11157] = {"Les œufs maléfiques", nil, {"Détruisez 15 Œufs de proto-drake et 15 Proto-dragonnets, puis retournez voir le Capitaine Adams au Donjon de la Garde de l'ouest."}},
[11158] = {"[PÉRIMÉ]Les plumes de Rougefange", nil, {"Brogg de Bourbe-à-brac veut 5 Plumes de Rougefange."}},
[11159] = {"[PÉRIMÉ]Les esprits du bastion Cognepierre", nil, {"Brogg de Bourbe-à-brac veut que vous apportiez le repos à 10 Esprits Cognepierre."}},
[11160] = {"[PÉRIMÉ]La bannière des Cognepierres", nil, {"Récupérez la Bannière cognepierre et remettez-la à Brogg de Bourbe-à-brac."}},
[11161] = {"[PÉRIMÉ]L'essence de l'ennemi", nil, {"Apportez 10 Essences de draconien noir à Brogg à Bourbe-à-brac."}},
[11162] = {"[PÉRIMÉ]Un défi au Vol noir", nil, {"Plantez la Bannière cognepierre près de l'entrée du Repaire d'Onyxia, attendez la réponse d'Onyxia, puis rendez la bannière à Brogg de Bourbe-à-brac."}},
[11164] = {"Illégitimes défenses", nil, {"Dasnob veut que vous récupériez 10 Défenses de trolls des forêts sur les trolls de Zul'Aman. Apportez-les-lui au camp de Budd dans les Terres fantômes."}},
[11165] = {"Un troll parmi les trolls", nil, {"Dasnob vous a chargé de trouver son cousin Zungam pour lui venir en aide, quelque part à l'intérieur de Zul'Aman."}},
[11166] = {"X marque... votre mort !", nil, {"Entrez dans Zul'Aman et visitez la Chambre d'Halazzi, la Plate-forme de Jan'alai et la Plate-forme d'Akil'zon. Rendez compte des détails de ces zones à Budd, dans son camp des Terres fantômes."}},
[11167] = {"La Peste nouvelle", nil, {"L'Apothicaire Lysander, à l'Accostage de la Vengeance, vous demande de récupérer 10 Conteneurs de peste intacts dans le navire échoué au sud-est."}},
[11168] = {"Enrichir le mélange", nil, {"Remettez 3 Glandes à toxine géantes prélevées sur des Clampants des marées géants à l'Apothicaire Lysander, à l'Accostage de la Vengeance, dans le Fjord hurlant."}},
[11169] = {"[PÉRIMÉ]L'arme des Totems-sinistres", nil, {"L'Apprenti Garion, de la ferme de Tabetha, veut que vous testiez le Totem capturé sur 10 Crocilisques tachetés Sèchefange ou Crocs-lames Sèchefange."}},
[11170] = {"Essai en mer", nil, {"Parlez à l'Éleveuse de chauves-souris Camille et prenez une chauve-souris pour intercepter les renforts de l'Alliance. Lorsque vous serez au-dessus de leur flotte, utilisez les Fioles de peste pour contaminer 16 Réservistes de la flotte du nord."}},
[11171] = {"Seigneur des maléfices ? Ha !", nil, {"Budd Truducou, aux Collines de la Cognée, veut que vous alliez tuer le Seigneur des maléfices Malacrass à Zul'Aman."}},
[11172] = {"[PÉRIMÉ]L'épave du zeppelin", nil, {"Parler à Moxie Grilledacier à l'Épave de Beezil."}},
[11173] = {"[PÉRIMÉ]Le voleur de composants", nil, {"Apporter 6 fioles de Venin des marais à l'Apprenti Garion à la Ferme de Tabetha."}},
[11174] = {"[PÉRIMÉ]Prévention de la corrosion", nil, {"Moxie Grilledacier vous demande de dissoudre 10 limons avant de retourner la voir à l'Épave de Beezil."}},
[11175] = {"Ma fille", nil, {"Le Vieux Mantepierre vous a demandé de trouver sa fille, la Surveillante Irena Mantepierre, à la Porte d'acier du Fjord hurlant."}},
[11176] = {"Allez, au travail !", nil, {"L’exploratrice Irena Mantepierre vous a demandé de parler avec un Chef archéologue de la Porte d’acier, à la Porte d’acier."}},
[11177] = {"[PÉRIMÉ]L'ermite du manoir des Flammeroles", nil, {"Parlez à Jarl « Oeil-des-marais » au Manoir des Flammeroles."}},
[11178] = {"Le sang du seigneur de guerre", nil, {"Apportez le Sang de Zul'jin à Budd à son campement dans les Terres fantômes, à l'extérieur de Zul'Aman."}},
[11180] = {"Qui hante la Colline des sorcières ?", nil, {"Mordant Grimsby du manoir des Flammeroles vous demande de collecter des informations auprès de 10 Carcasses ressuscitées ou Esprits ressuscités."}},
[11182] = {"Aux racines du mal", nil, {"Tuez 5 Dresseurs vrykuls et Skeld Fils-de-drake ; Si vous y parvenez, retournez voir un Ancien de l'Étreinte de braise."}},
[11183] = {"Nettoyer la Colline des sorcières", nil, {"Mordant Grimsby au Manoir des Flammeroles veut que vous plantiez la torche au bout du quai et que vous abattiez tout ce qui pourrait apparaître."}},
[11184] = {"[PÉRIMÉ]ON RECHERCHE : Griffétripe le Vorace", nil, {"Tuer Griffétripe le Vorace et se présenter à Drazzit Suintevalve à Bourbe-à-brac."}},
[11187] = {"Le lieutenant-mage Malister", nil, {"Le Capitaine Adams vous a demandé de parler à son conseiller, le Lieutenant-mage Malister, au Donjon de la Garde de l'ouest."}},
[11188] = {"Deux torts…", nil, {"Le Lieutenant-mage Malister, du donjon de la Garde de l'ouest, vous a demandé de détruire 3 Proto-drakes."}},
[11190] = {"Ce modèle n'existe pas en taille unique !", nil, {"Le Canonnier Ely veut que vous récupériez 10 Boulets de canons de la Garde de l’ouest et que vous les lui remettiez, au donjon de la Garde de l’ouest."}},
[11191] = {"[PÉRIMÉ]Le vieux phare", nil, {"Parler à Babs Patranivelle au phare sur l'île à l'est de Theramore."}},
[11192] = {"[PÉRIMÉ]L'huile de batteur", nil, {"Apportez 4 récipients d'Huile de batteur à Babs Patranivelle au phare près de Theramore."}},
[11194] = {"[PÉRIMÉ]Il existe ?", nil, {"Parlez à Nat Pagle au Point d’accostage de Nat, au Marécage d'Âprefange."}},
[11195] = {"Jouer à la poupée", nil, {"Apportez la Poupée vaudou en lambeaux à Eskrokar à Shattrath."}},
[11198] = {"[PÉRIMÉ]Il faut tuer Tethyr !", nil, {"Le Major Mills aux Docks de Theramore veut que vous abattiez Tethyr."}},
[11199] = {"Se présenter à l'éclaireur Knowles", nil, {"Le capitaine Adams vous a demandé de vous présenter à l'Éclaireur Knowles, à la Tour de la Lance de braise."}},
[11202] = {"Mission : la flamme éternelle", nil, {"L’Éclaireur Knowles, de la Tour de la Lance de braise, souhaite que vous utilisiez les Torches semperardentes pour détruire les quatre Chars de peste qui entourent Halegrince."}},
[11205] = {"[PÉRIMÉ]Rasez le poste de Navrecorne !", nil, {"Brûlez la Tente nord, la Tente est et la Tente nord-est du Poste de Navrecorne, puis revenez voir Tabetha dans sa ferme."}},
[11206] = {"[PÉRIMÉ]Justice est faite !", nil, {"Retourner voir Krog au village de Mur-de-Fougères."}},
[11207] = {"[PÉRIMÉ]Sauvez la cargaison !", nil, {"Moxie Grilledacier à l'Épave de Beezil veut que vous retrouviez 8 morceaux de Cargaison de zeppelin."}},
[11208] = {"Une livraison pour Drazzit", nil, {"Remettre la Cargaison du zeppelin à Drazzit Suintevalve à Bourbe-à-brac."}},
[11209] = {"[PÉRIMÉ]Le marché de Nat", nil, {"Nat Pagle, au Point d'accostage de Nat, vous demande de tester son nouvel appât à poissons."}},
[11210] = {"[PÉRIMÉ]Il existe !", nil, {"Parler au Major Mills sur les quais de Theramore."}},
[11211] = {"De l'aide pour Bourbe-à-brac", nil, {"Parler à Drazzit Suintevalve à Bourbe-à-brac."}},
[11212] = {"La ferme de Tabetha", nil, {"Allez trouver Tabetha dans sa ferme, dans le Bourbier."}},
[11213] = {"Quelles nouvelles de Tabetha ?", nil, {"Parlez à Tabetha dans sa ferme du Bourbier."}},
[11214] = {"[PÉRIMÉ]En mission à Bourbe-à-brac", nil, {"Parler à Drazzit Suintevalve à Bourbe-à-brac."}},
[11215] = {"Aider Bourbe-à-brac", nil, {"Parler à Drazzit Suintevalve à Bourbe-à-brac."}},
[11216] = {"L'archimage Alturus", nil, {"Parlez à l'Archimage Alturus à l'extérieur de Karazhan au défilé de Deuillevent."}},
[11217] = {"[PÉRIMÉ]Attraper un dragon par la queue", nil, {"Gizzix Glougloutache de Bourbe-à-brac vous a demandé de lui apporter 8 tiges de Queue de wyrm."}},
[11218] = {"Danger ! Explosifs !", nil, {"Utilisez la Dynamite sécurisée d’Anneau-d’acier pour récupérer 6 Fragments de minerai du goulet des Murmures et 18 Gemmes du goulet des Murmures. Ensuite, allez remettre tout ça au Sapeur Anneau-d’acier, au donjon de la Garde de l’ouest."}},
[11219] = {"Éteignez les incendies !", nil, {"La Directrice d'orphelinat masquée vous demande d'aider à éteindre tous les foyers d'incendie du village. Une fois qu'ils seront éteints, retournez lui parler en ville."}},
[11220] = {"Le Cavalier sans tête", nil, {"Trouvez le Sanctuaire de la citrouille du Cavalier sans tête."}},
[11221] = {"Rapports de terrain", nil, {"Parlez au Forestier-sombre Lyana et au Nécrotraqueur Razael, sur le champ de bataille du Val Sanglant, au sud de l’Accostage de la Vengeance. Lorsque ce sera fait, retournez voir le Grand exécuteur Anselme."}},
[11223] = {"[PÉRIMÉ]Retourner voir Jaina", nil, {"Parler à dame Jaina Portvaillant à Theramore."}},
[11224] = {"Têtes de mule", nil, {"L’Exploratrice Abigail vous a demandé de faire /ohé devant 10 Mules de bât abandonnées, puis de retourner la voir au donjon de la Garde de l’ouest."}},
[11225] = {"L'ermite de la Colline des sorcières", nil, {"Parlez à Jarl « Oeil-des-marais » au Manoir des Flammeroles."}},
[11227] = {"Qu'ils mangent du corbeau", nil, {"Pontius, le maître du chenil de l’Accostage de la Vengeance, veut que vous utilisiez la Cage de chien pestiféré pour invoquer un Chien pestiféré affamé. Vous lui donnerez de la Viande de corbeau, prélevée sur les Corbeaux du Fjord, à l’ouest de l’Accostage de la Vengeance."}},
[11228] = {"L'enfer a gelé…", nil, {"Parlez au vice-amiral Keller au port de Valgarde."}},
[11229] = {"La flotte du Coursevent", nil, {"Parlez à l’Éleveuse de chauve-souris Camille à l’Accostage de la Vengeance, et demandez-lui de vous envoyer sur le Coursevent. Une fois à bord, présentez-vous au capitaine Harker."}},
[11230] = {"Embusqués !", nil, {"Le capitaine Harker, à bord du Coursevent, veut que vous tuiez 15 Soldats de marine de la flotte du nord."}},
[11231] = {"Des clés et des cages", nil, {"Le Père Levariol vous a demandé de récupérer assez de Clés de cage de Gjalerbron pour libérer 10 Prisonniers de Gjalerbron. Lorsque ce sera fait, retournez le voir au Donjon de la Garde de l’ouest."}},
[11232] = {"Guidez nos tirs", nil, {"Utilisez les Fumigènes aux emplacements des Canons de l’Alliance, sur la paroi nord de la Grève des Épaves. Lorsque ce sera fait, présentez-vous au Forestier-sombre Lyana, au Val Sanglant."}},
[11233] = {"Le coup de grâce", nil, {"Le forestier-sombre Lyana, au sud de l’Accostage de la Vengeance, veut que vous tuiez le Capitaine Olster, le Sergent Lorric et le Lieutenant Celeyne à la Grève des Épaves."}},
[11234] = {"Se présenter à Anselme", nil, {"Présentez-vous au Grand exécuteur Anselme à l'Accostage de la Vengeance, dans le Fjord hurlant."}},
[11235] = {"S'occuper de Gjalerbron", nil, {"Le Capitaine Adams vous a demandé de tuer 15 Guerriers de Gjalerbron, 8 Lanceurs de runes de Gjalerbron et 8 Gardiens du sommeil de Gjalerbron. Ensuite, revenez le voir au donjon de la Garde de l’ouest."}},
[11236] = {"Le nécro-suzerain Mezhen", nil, {"Tuez le Nécro-suzerain Mezhen, puis retournez voir le Capitaine Adams au Donjon de la Garde de l’ouest."}},
[11237] = {"Les plans d'attaque de Gjalerbron", nil, {"Remettez les Plans d'attaque de Gjalerbron au Lieutenant-mage Malister, au Donjon de la Garde de l'ouest."}},
[11238] = {"Le wyrm de givre et son maître", nil, {"Tuez le Mande-wyrm Vile et utilisez le Cor mande-wyrm pour appeler et tuer Glacion. Lorsque ce sera fait, retournez avec le cor voir le Lieutenant-mage Malister au Donjon de la Garde de l’ouest."}},
[11239] = {"Au service de la Lumière", nil, {"Tuez 10 Guetteurs impérissables, 4 Horreurs effroyables et 2 Nécrophages putrides. Lorsque ce sera fait, informez de votre réussite le Père Levariol au Donjon de la Garde de l’ouest."}},
[11240] = {"Le chef des dérangés", nil, {"Tuez Squeeg Chassidole, puis présentez-vous au Sapeur Anneau-d'acier, au Donjon de la Garde de l'ouest."}},
[11241] = {"Une piste enflammée", nil, {"Escortez l’Apothicaire Hanes jusqu’à la sortie de la Grève des Épaves. Lorsque ce sera fait, présentez-vous à l’Apothicaire Lysander à l’Accostage de la Vengeance."}},
[11242] = {"Enfin libre !", nil, {"Apportez le Tome de Thomas Thomson à la Directrice d'orphelinat costumée à Comté-de-l'Or, à Kharanos ou au Guet d'azur."}},
[11243] = {"Si Valgarde tombe…", nil, {"Le Vice-amiral Keller, du port de Valgarde dans le Fjord hurlant, veut que vous tuiez 12 Envahisseurs écorche-dragon ou Worgs écorche-dragon."}},
[11244] = {"Au secours des secours", nil, {"Le Vice-amiral Keller, du port de Valgarde dans le Fjord hurlant, veut que vous secouriez 8 Éclaireurs de Valgarde qui ont été empalés et laissés pour morts."}},
[11245] = {"Les tours de la fatalité", nil, {"Le Sergent de la Garde de l’ouest vous a expliqué qu’il serait souhaitable de marquer les quatre tours de Mörg en prévision d’une canonnade, à l'aide du Fumigène du sergent."}},
[11246] = {"Salissant, mais nécessaire", nil, {"Le Sergent de la Garde de l’ouest vous a demandé de démembrer 20 Vrykuls de la tribu Mörghiver, à Mörg, à l'aide de la Machette du sergent."}},
[11247] = {"Brûle, Mörg, brûle !", nil, {"Le Sergent de la Garde de l’ouest vous a ordonné de mettre le feu aux maisons longues du nord-est et du nord-ouest et à la caserne de Mörg, à l'aide de la Torche du sergent."}},
[11248] = {"Opération Croque-Mörg", nil, {"Le capitaine Adams vous a demandé d’utiliser l’Insigne de commandement de la Garde de l’ouest à Mörg, puis de parler avec le Sergent de la Garde de l’ouest."}},
[11249] = {"Arrêter l'ascension !", nil, {"Utilisez le Parchemin d’ascension vrykul près du Bûcher du thane pour faire venir ce dernier et le tuer. Lorsque ce sera fait, vous êtes sûr que le Père Levariol, au Donjon de la Garde de l’ouest, sera intéressé par le parchemin, en raison de la nature du rituel."}},
[11250] = {"Gloire au conquérant de Mörg !", nil, {"Allez rendre l’Insigne de commandement de la Garde de l’ouest au Capitaine Adams, au Donjon de la Garde de l’ouest."}},
[11251] = {"Un second souffle", nil, {"L’Éclaireur Valory veut que vous enquêtiez sur les bruits de combat qui viennent du nord-est de Crâne-du-ver."}},
[11252] = {"Dans Utgarde !", nil, {"Le Défenseur Mordun vous a demandé de tuer Ingvar le Pilleur, qui réside dans les profondeurs d’Utgarde.\n\nLorsque ce sera fait, vous devez présenter sa tête au Vice-amiral Keller."}},
[11253] = {"Renifler l'ennemi", nil, {"Utilisez la Laisse de chien pestiféré près des cages des chiens pestiférés à l’Accostage de la Vengeance, et cherchez les indices là où vous conduira le Chien pestiféré traqueur."}},
[11254] = {"La carte en peau de dragon", nil, {"Apportez la Carte en peau de dragon au Grand exécuteur Anselme à l’Accostage de la Vengeance."}},
[11255] = {"Les prisonniers de Crâne-du-ver", nil, {"Le Vice-amiral Keller, au port de Valgarde dans le Fjord hurlant, veut que vous sauviez 3 Prisonniers de Valgarde capturés."}},
[11256] = {"Mörg doit tomber !", nil, {"Le Chef Totem-de-frêne vous a demandé d’utiliser l’Emblème de Sabot-d’hiver à Mörg, puis de parler avec le Brave Sabot-d’hiver."}},
[11257] = {"Salissant, mais nécessaire", nil, {"Le Brave Sabot-d’hiver vous a demandé de démembrer 20 Vrykuls Mörghiver, à Mörg, à l'aide de la Machette du brave."}},
[11258] = {"Brûle, Mörg, brûle !", nil, {"Le Brave Sabot-d’hiver suggère que vous mettiez le feu aux deux maisons longues les plus basses et à la caserne de Mörg à l'aide de la Torche du brave."}},
[11259] = {"Les tours de la fatalité", nil, {"Le Brave Sabot-d’hiver vous a informé qu’il serait souhaitable de marquer les quatre tours de Mörg en prévision de frappes aériennes à l'aide du Fumigène du brave."}},
[11260] = {"Arrêter l'ascension !", nil, {"Utilisez le Parchemin d’ascension vrykul près du Bûcher du thane pour faire apparaître ce dernier et le tuer. Lorsque ce sera fait, vous êtes sûr que la Grande-mère Ankha, au Camp Sabot-d’hiver, sera intéressée par le parchemin, en raison de la nature du rituel."}},
[11261] = {"Le conquérant de Mörg", nil, {"Allez rendre l’Emblème Sabot-d’hiver au Chef Totem-de-frêne, au Camp Sabot-d’hiver."}},
[11262] = {"Ingvar doit mourir !", nil, {"Le Forestier-sombre Marrah vous a demandé de tuer Ingvar le Pilleur à l’intérieur du Donjon d'Utgarde."}},
[11263] = {"S'occuper de Gjalerbron", nil, {"Le Chef Totem-de-frêne vous a demandé de tuer 15 Guerriers de Gjalerbron, 8 Lanceurs de runes de Gjalerbron et 8 Gardiens du sommeil de Gjalerbron. Ensuite, revenez le voir au Camp Sabot-d’hiver."}},
[11264] = {"Le nécro-suzerain Mezhen", nil, {"Tuez le Nécro-suzerain Mezhen, puis retournez voir le Chef Totem-de-frêne au Camp Sabot-d’hiver."}},
[11265] = {"Des clés et des cages", nil, {"La Grande-mère Ankha vous a demandé de récupérer assez de Clés de cages de Gjalerbron pour libérer 10 Prisonniers de Gjalerbron. Si vous y parvenez, retournez la voir au Camp Sabot-d’hiver."}},
[11266] = {"Les plans d'attaque de Gjalerbron", nil, {"Remettez les Plans d’attaque de Gjalerbron à Celea Crin-gelé, au Camp Sabot-d’hiver."}},
[11267] = {"Le wyrm de givre et son maître", nil, {"Tuez le Mande-wyrm Vile, puis utilisez le Cor mande-wyrm pour appeler Glacion. Tuez-le, et retournez voir Celea Crin-gelé au Camp Sabot-d’hiver avec le cor."}},
[11268] = {"Les morts qui marchent", nil, {"Tuez 10 Guetteurs impérissables, 4 Horreurs effroyables et 2 Nécrophages putrides. Lorsque ce sera fait, allez informer de votre succès la Grande-mère Ankha, au Camp Sabot-d’hiver."}},
[11269] = {"Y laisser des plumes", nil, {"Gil Grisert veut que vous lui rapportiez 10 touffes de Duvet d’hippogriffe tacheté."}},
[11270] = {"La guerre, c'est l'enfer", nil, {"Le Grand exécuteur Anselme, de l’Accostage de la Vengeance, veut que vous preniez la Torche ardente et que vous l’utilisiez pour brûler 10 des cadavres qui se trouvent à l’ouest de la ville."}},
[11271] = {"Des préparatifs hâtifs", nil, {"Ahota Givre-blanc, du Camp Sabot-d’hiver, veut que vous lui rapportiez 10 touffes de Duvet d’hippogriffe tacheté."}},
[11272] = {"Un compte à régler", nil, {"Le Grand exécuteur Anselme vous demande de tuer le prince Keleseth à l’intérieur du Donjon d'Utgarde."}},
[11273] = {"La ligue humaine", nil, {"Fouillez Crâne-du-ver à la recherche de Pulroy."}},
[11274] = {"Zedd est probablement mort", nil, {"Fouiller le village de Crâne-du-ver à la recherche de Zedd."}},
[11275] = {"Fabriquer le cor", nil, {"Nokoma Œil-des-neiges, au camp Sabot-d’hiver, veut que vous lui rapportiez 6 Cornes de bélier intactes."}},
[11276] = {"Ils étaient deux", nil, {"Fouillez les Catacombes d'Utgarde à la recherche de Glorenfeld."}},
[11277] = {"Les profondeurs de la dépravation", nil, {"Glorenfeld, dans les Catacombes d'Utgarde, vous demande de réunir 10 Tablettes Crâne-du-ver."}},
[11278] = {"Retour à Valgarde", nil, {"Remettez le Paquet de Glorenfeld à Beltrand McSorf, au port de Valgarde."}},
[11279] = {"Ces proto-dragonnets sont trop verts…", nil, {"Le Porte-peste Tillinghast vous a demandé de récupérer 10 Spécimens de proto-dragonnets pestiférés, et de les lui remettre à la Nouvelle-Agamand."}},
[11280] = {"Draconis Gastritis", nil, {"Le Porte-peste Tillinghast, de la Nouvelle-Agamand, veut que vous utilisiez la Viande pestiférée de Tillinghast pour attirer un Proto-drake. Observez ses réactions lorsqu’il la mangera."}},
[11281] = {"Imiter l'appel de la nature", nil, {"Testez le Cor sculpté, puis allez faire part du résultat à Nokoma Œil-des-neiges, au Camp Sabot-d’hiver."}},
[11282] = {"Une leçon de peur", nil, {"Gorth veut que vous tuiez Ulf le Saigneur, Oric le Torve et Gunnar Thorvardsson, et que vous plantiez la Bannière des Réprouvés sur leurs corps. Tuez des vrykuls tout au long du blocus des Réprouvés jusqu’à ce qu’ils apparaissent."}},
[11283] = {"Le décompte des cadavres de Torvheim", nil, {"Le sergent Gorth veut que vous entriez dans Torvheim pour y tuer 16 Vrykuls de Mörghiver."}},
[11284] = {"Le yéti d'à-côté", nil, {"Le Contremaître Colbey de Fort Hardivar veut que vous battiez Brise-corne."}},
[11285] = {"Torvheim doit brûler !", nil, {"Le sergent Gorth veut que vous entriez dans Torvheim et que vous vous serviez de la Torche de Gorth pour brûler 1 Logis Mörghiver, 1 Tour de guet Mörghiver, 1 Pont Mörghiver et 1 Caserne Mörghiver."}},
[11286] = {"Les artéfacts de la Porte d'acier", nil, {"Le Sage Marche-brume veut que vous récupériez 10 Artéfacts de la Porte d’acier. Lorsque vous les aurez, remettez-les-lui à la Porte d’acier."}},
[11287] = {"Trouver le sage Marche-brume", nil, {"La Grande-mère Ankha vous a demandé d'aller vérifier si le Sage Marche-brume allait bien. Elle pense qu'il se trouve à la Porte d'acier ou bien à proximité."}},
[11288] = {"La Lumière rayonnante", nil, {"Arès le Féal des Catacombes d’Utgarde veut que vous récupériez l’Artéfact sacré.\n\nCette quête est en temps limité."}},
[11289] = {"Guidé par l'honneur", nil, {"Rapportez l'Artéfact sacré au Seigneur Irulon Lamevraie à Valgarde."}},
[11290] = {"Les plans de bataille des Écorche-dragon", nil, {"Le Vice-amiral Keller, au port de Valgarde dans le Fjord hurlant, vous a demandé de lui apporter les plans de bataille des Écorche-dragon."}},
[11291] = {"Au donjon de la Garde de l'ouest !", nil, {"Remettez les Plans de bataille des Écorche-dragon au Capitaine Adams, au donjon de la Garde de l’ouest, dans le Fjord hurlant."}},
[11292] = {"Faire des faibles sa proie", nil, {"Le Trappeur Jethan, au Fort Hardivar, veut que vous lui rameniez 8 Proies piégées."}},
[11295] = {"L'offensive commence", nil, {"Parlez au Sergent Gorth, qui dirige le blocus, non loin de l’ascenseur, à l’ouest de l’Accostage de la Vengeance, dans le Fjord hurlant."}},
[11296] = {"Les prisonniers de Clivebois", nil, {"Le Long-coureur Nuage-du-ciel vous a demandé de libérer 7 Longs-coureurs Sabot-d’hiver. Lorsque ce sera fait, retournez à la Porte d’acier."}},
[11297] = {"Garder un œil sur les intrus", nil, {"Parlez à l’Apothicaire Malthus, au Camp des Apothicaires."}},
[11298] = {"Mais qu'est-ce qu'il y a dans ce truc ?", nil, {"L’Apothicaire Malthus, du Camp des Apothicaires, veut que vous lui rapportiez 5 Tonneaux des nains."}},
[11299] = {"L'arène du Jugement", nil, {"Trouvez Daegarn dans l’Arène du jugement, à l’intérieur des Catacombes d’Utgarde."}},
[11300] = {"Formidable déconfiture dans l'arène", nil, {"Triomphez des gladiateurs de l’Arène du Jugement et affrontez Oluf le Violent. Battez-le et récupérez le Code antique.\n\nRemettez le Code antique à Glorenfeld à l’entrée des Catacombes d’Utgarde."}},
[11301] = {"Cerveaux ! Cerveaux ! Cerveaux !", nil, {"L’Apothicaire Grick, du Camp des Apothicaires, veut que vous lui rapportiez 12 Cerveaux d’explorateurs dérangés."}},
[11302] = {"Les énigmatiques nymphes de givre", nil, {"Allez parler à Lurielle dans la Clairière Gelée."}},
[11303] = {"L'embuscade", nil, {"Parlez à Lydell à l'Embuscade de Lydell."}},
[11304] = {"La Nouvelle-Agamand", nil, {"Remettez la Souche de Lysander au Porte-peste en chef Harris, à la Nouvelle-Agamand, à l’ouest du Fjord Hurlant."}},
[11305] = {"Une formule sur mesure", nil, {"Procurez-vous le Sang du patriarche écorche-dragon sur Thorvald au Camp de Thorvald, et apportez-le au Porte-peste en chef Harris à la Nouvelle-Agamand."}},
[11306] = {"Réchauffez et remuez", nil, {"Remplissez le Flacon d’apothicaire vide au Chaudron de sang de vrykul, puis mélangez le contenu du Flacon de sang de vrykul avec les Échantillons de peste de Harris jusqu’à ce que vous obteniez une Décoction équilibrée.\n\nLorsque ce sera fait, retournez voir le Porte-peste en chef Harris."}},
[11307] = {"Essai sur le terrain", nil, {"Pénétrez dans Halegrince et utilisez le Pestorisateur sur 10 Vrykuls Écorche-dragon pestiférés. Ensuite, retournez voir le Porte-peste en chef Harris pour lui présenter les résultats."}},
[11308] = {"On nettoie et on ferme", nil, {"Parlez à Jenny « Scie à métaux » à Nouvelle-Agamand."}},
[11309] = {"Des p'tits bouts partout", nil, {"Jenny « Scie à métaux », à la Nouvelle-Agamand, veut que vous récupériez 6 Ligaments de brochepelle et 1 Livre de chair fraîche."}},
[11310] = {"Avertissement : assemblage nécessaire", nil, {"Servez-vous de la Trousse de confection d’abomination à Halegrince. Attirez des Vrykuls pestiférés avec votre Abomination décérébrée. Tuez-en au moins 20, puis retournez voir Jenny « Scie à métaux » à la Nouvelle-Agamand."}},
[11311] = {"Supprimer les éléments", nil, {"Le Dompteur des vents Kagan, au Camp Sabot-d’hiver, veut que vous tuiez 8 élémentaires des montagnes. Il peut s’agir de Cyclones hurlants ou d’Élémentaires Glacéclat."}},
[11312] = {"La clairière Gelée", nil, {"Parlez à Lurielle à la Clairière Gelée."}},
[11313] = {"Les esprits de la glace", nil, {"Lurielle de la Clairière Gelée veut que vous lui rapportiez 15 Noyaux glacials."}},
[11314] = {"Les sœurs déchues", nil, {"Lurielle de la Clairière Gelée veut que vous utilisiez son pendentif sur 7 Nymphes algides."}},
[11315] = {"Le lierre sauvage", nil, {"Lurielle de la Clairière Gelée veut que vous tuiez 8 Lierres écarlates."}},
[11316] = {"L'engeance de la clairière Tordue", nil, {"Lurielle de la Clairière Gelée veut que vous tuiez 10 Rampants roncevigne."}},
[11317] = {"La purification", nil, {"Le Sage Marche-brume vous a conseillé de vous purifier de votre Trouble intérieur par la méditation au Sanctuaire de Givrelame."}},
[11318] = {"Ça c'est de la course de béliers... enfin, presque.", nil, {"Vous avez 4 minutes pour chevaucher le bélier et maintenir 3 différentes allures pendant 8 secondes chacune."}},
[11319] = {"Les graines de gardiens à l'âme noircie", nil, {"Lurielle de la Clairière Gelée veut que vous geliez 8 Spores."}},
[11320] = {"[NYI] Now this is Ram Racing... Almost.", nil, {"Texte à venir"}},
[11322] = {"La purification", nil, {"La guetteuse Feuillelune vous a conseillé de vous purifier de votre Trouble intérieur par la méditation au Sanctuaire de Givrelame."}},
[11323] = {"Déguisé en worg", nil, {"Le Sage Marche-brume vous a demandé de trouver Ulfang et de l’aider. Il doit se trouver dans Clivebois.\n\nSi vous perdez le déguisement de worg, demandez au Sage Marche-brume de vous le remplacer."}},
[11324] = {"L'alpha des worgs", nil, {"Ulfang vous a demandé de tuer Garwal. Ensuite, retournez voir le Sage Marchebrume à la Porte d’acier et rendez-lui le Déguisement de worg."}},
[11325] = {"Déguisé en worg", nil, {"La guetteuse Feuillelune vous a demandé de trouver Ulfang et de l’aider. Il doit se trouver dans Clivebois.\n\nSi vous perdez le Déguisement de worg, demandez à la guetteuse Feuillelune de vous le remplacer."}},
[11326] = {"L'alpha des worgs", nil, {"Ulfang vous a demandé de tuer Garwal. Ensuite, retournez voir la Guetteuse Feuillelune à la Porte d’acier et rendez-lui le Déguisement de worg."}},
[11327] = {"Mission : récupération de paquet", nil, {"L’Éclaireur Knowles vous a demandé de récupérer le Paquet de l’apothicaire, qui est quelque part dans Halegrince. Lorsque vous l’aurez, retournez le voir à la tour de la Lance de braise."}},
[11328] = {"Mission : des infos sur les Réprouvés", nil, {"L’Éclaireur Knowles vous a demandé de livrer le Paquet de l’apothicaire à Peppy Tuyèrronée, au donjon de la Garde de l’ouest."}},
[11329] = {"J'essayerai n'importe quoi !", nil, {"Christopher Sloan, au Fort Hardivar, veut que vous lui rapportiez 5 Rascasses nordiques."}},
[11330] = {"Farpaitement ! Cha va marcher !", nil, {"Peppy Tuyèrronée suggère que vous preniez son Mélange spécial de Peppy et que vous l’administriez au Vrykul écorche-dragon prisonnier, pour voir."}},
