[11412] = {"Histoire d'un aller et retour", nil, {"Obtenez un tonneau auprès du Gobelin coincé sur la route de la Baie de Lamepoing, et rapportez-le à l’assistant du Maître des béliers Ray. Faites-le 3 fois avant que votre bélier ne s’en aille."}},
[11414] = {"Les frères félons", nil, {"Ulfang vous a demandé de tuer les traîtres worgs, Bjomolf et Varg. Lorsque ce sera fait, retournez le voir, déguisé , à Clivebois.\n\nSi par hasard vous perdiez le Déguisement de worg, retournez voir la Guetteuse Feuillelune et demandez-lui un autre exemplaire."}},
[11415] = {"Les frères félons", nil, {"Ulfang vous a demandé de tuer les traîtres worgs, Bjomolf et Varg. Lorsque ce sera fait, retournez le voir, déguisé , à Clivebois.\n\nSi par hasard vous perdiez le Déguisement de worg, retournez voir le Sage Marche-brume et demandez-lui un autre exemplaire."}},
[11416] = {"Les yeux de l'aigle", nil, {"Ulfang vous a demandé de récupérer les Yeux de l’Aigle. Lorsque ce sera fait, retournez le voir déguisé à Clivebois.\n\nSi vous perdez votre Déguisement de worg, allez en demander un autre à la Guetteuse Feuillelune."}},
[11417] = {"Les yeux de l'aigle", nil, {"Ulfang vous a demandé de récupérer les Yeux de l’Aigle. Lorsque ce sera fait, retournez le voir déguisé à Clivebois.\n\nSi vous perdez votre Déguisement de worg, allez en demander un autre au Sage Marche-brume."}},
[11418] = {"On l'appelle Plume-d'acier", nil, {"Trouvez Plume-d’acier, qui tourne autour de Fort Hardivar, et utilisez le Charme orné de plumes pour découvrir son secret. Ensuite, allez faire part de vos découvertes à Gil Grisert."}},
[11419] = {"Les béliers de la fête des Brasseurs", nil, {"Apportez le Tampon de « Brasseur honoraire » à Driz Voltevive, près de l'entrée de la Fête des Brasseurs, en Durotar."}},
[11420] = {"La voie de la vengeance", nil, {"Le Capitaine de la garde Zorek, à Valgarde dans le Fjord Hurlant, vous demande de récupérer le Manuel d'utilisation du harpon."}},
[11421] = {"Ça va jusqu'à 11…", nil, {"Le Lieutenant Martel-de-glace, dans le Fjord Hurlant, veut que vous détruisiez la Maison longue écorche-dragon, la Maison des quais écorche-dragon, l’Entrepôt et 8 Défenseurs écorche-dragon.\n\nRapportez-lui le Mécanisme de commande de harpon quand vous aurez accompli votre mission."}},
[11422] = {"Le trident du fils", nil, {"Le Vieux Nageglace veut que vous récupériez le Trident de Branchie-pourrie. Lorsque ce sera fait, rapportez-le-lui sur sa petite île, sur la côte de Frissonde."}},
[11423] = {"L'héritage de l'ennemi", nil, {"Rendez-vous à Nifflevar, au nord du poste de la Lame-fantôme, et cherchez La saga de la malédiction de l’hiver, La saga des Val'kyrs et La saga des jumeaux."}},
