[11425] = {"Quête de test - Craig", nil, {"La vermine, faut la bousiller !"}},
[11426] = {"Localiser le mécanisme", nil, {"Le capitaine de la garde Zorek, à Valgarde dans le Fjord Hurlant, vous demande de lui apporter un Mécanisme de commande de harpon."}},
[11427] = {"Je vous présente le lieutenant Martel-de-glace…", nil, {"Le capitaine de la garde Zorek vous demande d'apporter le Mécanisme de commande de harpon au Lieutenant Martel-de-glace stationné à l'extérieur de Nifflevar."}},
[11428] = {"Le gardien Fanefeuille", nil, {"Lurielle de la clairière Gelée veut que vous tuiez le Gardien Fanefeuille."}},
