[11446] = {"La fête des Brasseurs !", nil, {"Parlez à l'Organisateur de la fête des Brasseurs."}},
[11447] = {"Bienvenue à la fête des Brasseurs !", nil, {"[PH] Speak to the Brewfest Organizer and receive a free beer."}},
[11448] = {"L'avant-poste de la Ligue des explorateurs", nil, {"Remettre le Paquet de McSorf à Stanwad, à l’Avant-poste de la Ligue des explorateurs, dans le Fjord Hurlant."}},
[11449] = {"L'entraînement au combat du feu", nil, {"Jetez des Seaux d’eau sur 5 feux, puis retournez voir la Directrice d'orphelinat masquée à Brill."}},
[11450] = {"L'entraînement au combat du feu", nil, {"Jetez des Seaux d’eau sur 5 feux, puis retournez voir la Directrice d'orphelinat masquée sur la Place de l'Épervier."}},
[11451] = {"Le poème d'Alicia", nil, {"Alicia vous a demandé d'apporter son poème à Caylee Dak à Shattrath en Outreterre."}},
[11452] = {"Le roi endormi", nil, {"Triomphez de la Reine Angrboda et arrêtez le rituel destiné à éveiller le Roi Ymiron. Si vous y parvenez, retournez au Donjon de la Garde de l’ouest et annoncez votre succès au Capitaine Adams."}},
[11453] = {"Le roi endormi", nil, {"Triomphez de la Reine Angrboda et arrêtez le rituel destiné à éveiller le Roi Ymiron. Si vous y parvenez, retournez au Camp Sabot-d’hiver et annoncez votre succès au Chef Totem-de-frêne."}},
[11454] = {"Trouver les saboteurs", nil, {"Trouver Coren Navrebière dans le Sinistre écluseur, à l'intérieur des Profondeurs de Rochenoire."}},
[11455] = {"L'odeur de l'argent", nil, {"Terry le beau gosse, du Cap du Forban, vous demande de lui rapporter 4 échantillons de Musc d'ours prélevés sur les Ours bruns enragés des environs."}},
[11456] = {"Nourrir les rescapés", nil, {"L’Ancien Atuik, à Kamagua dans le Fjord Hurlant, veut que vous récupériez 6 morceaux de Viande de brochepelle insulaire sur des Brochepelles insulaires de l’Île des Lances."}},
[11457] = {"Armer Kamagua", nil, {"L’Ancien Atuik veut que vous récupériez 3 Cornes de chimères."}},
[11458] = {"Venger Iskaal", nil, {"L’Ancien Atuik veut que vous alliez à Iskaal et que vous tuiez 8 Esclavagistes des mers du Nord. En cas de besoin, sonnez du Cor de Kamagua."}},
[11459] = {"Zeh'gehn li pale", nil, {"Retournez voir Terry le beau gosse pour comprendre ce que vous demande Zeh’gehn."}},
[11460] = {"La confiance, ça se mérite", nil, {"Hidalgo, de l’Avant-poste de la Ligue des explorateurs du Fjord Hurlant, veut que vous trouviez une Larve du fjord et que vous vous en serviez pour nourrir l’un des éperviers de la cage qui se trouve à ses pieds."}},
[11464] = {"Une dette de jeu", nil, {"Taruk, au Cap du Forban, veut que vous récupériez la Dette de Harry « Lune-d’argent »."}},
[11465] = {"La caravane saccagée", nil, {"Hidalgo, à l’Avant-poste de la Ligue des explorateurs du Fjord Hurlant, veut que vous vous serviez de votre Epervier des rochers apprivoisé pour capturer 5 Dindes du Fjord.\n\nLorsque ce sera fait, retournez voir Hidalgo avec la cage."}},
[11466] = {"Jack aime trop la bouteille", nil, {"Taruk, au Cap du Forban, veut que vous récupériez la Dette de Jack Adams."}},
[11467] = {"La dette du mort", nil, {"Taruk, au Cap du Forban, veut que vous retrouviez le Trésor de Conrad le Noir, sur le plateau à l'extrémité nord-est de l’Île des Lances, au nord d'Iskaal."}},
[11468] = {"Faucon contre épervier", nil, {"Hidalgo, de l’Avant-poste de la Ligue des explorateurs du Fjord Hurlant, veut que vous utilisiez votre Epervier des rochers apprivoisé pour capturer 10 Faucons du Fjord.\n\nLorsque ce sera fait, retournez voir Hidalgo avec la cage."}},
[11469] = {"Le savon à briquer", nil, {"Sabord Givreproue veut que vous récupériez la Graisse de Gros Roy, l’ingrédient principal de son savon à briquer magique. Lorsque vous l’aurez, retournez la voir au vaisseau pirate le plus à l’est du Cap du Forban."}},
[11470] = {"Les oiseaux n'ont pas d'honneur", nil, {"Hidalgo, de l’Avant-poste de la Ligue des explorateurs du Fjord Hurlant, veut que vous utilisiez votre Épervier des rochers apprivoisé pour vous emparer de 8 Œufs de faucons du fjord.\n\nLorsque ce sera fait, retournez voir Hidalgo avec la cage."}},
[11471] = {"Le rideau tombe", nil, {"Annie Bonn du Cap du Forban vous demande de lui apporter la Longue-vue de Jonas Sterling."}},
[11472] = {"Le chemin de son cœur…", nil, {"Lancez de Délicieux poissons des récifs à une Otarie des récifs mâle pour la conduire à une femelle. Lorsque vous aurez aidé ce couple à trouver l’amour, retournez voir Anuniaq à Kamagua."}},
[11473] = {"Il y a un traître parmi nous", nil, {"Parlez à Zeh’gehn au Cap du Forban."}},
[11474] = {"Des problèmes au sommet", nil, {"Parlez à Walt."}},
[11475] = {"Il nous faut des outils pour faire le boulot", nil, {"Walt, de l’Avant-poste de la Ligue des explorateurs du Fjord Hurlant, veut que vous récupériez ses Outils de construction."}},
[11476] = {"Un coupe-coupe et une coasseuse", nil, {"Procurez-vous un Couteau rutilant auprès de Harry « Lune-d'argent » et capturez une Grenouille des forbans. Apportez tout cela à Zeh'Gehn au Cap du Forban."}},
[11477] = {"Hors de mon élément ?", nil, {"Donny, aux Ruines d'Ivald dans le Fjord Hurlant, vous demande de tuer 10 Destructeurs Rune-de-fer, 5 Travailleurs Rune-de-fer et 2 Sages Rune-de-fer."}},
[11478] = {"L'avant-poste par là-bas…", nil, {"Remettez la Lettre de Donny à Stanwad à l'Avant-poste de la Ligue des explorateurs."}},
[11479] = {"Dan « Patte-de-corbeau »", nil, {"Démasquez Dan « Patte-de-Corbeau », qui se trouve à bord de l’un des navires au Cap du Forban. Lorsque ce sera fait, allez parler à Terry le beau gosse."}},
[11480] = {"Rencontrer le Numéro Deux", nil, {"Parlez à Annie Bonn, au Cap du Forban."}},
[11481] = {"Crise au Puits de soleil", nil, {"Adyen le Gardelumière, de l'Éminence de l'Aldor à Shattrath, veut que vous vous rendiez au Plateau du Puits de soleil pour parler à Larethor."}},
[11482] = {"L'appel du devoir", nil, {"Dathris Heurtesoleil, du Degré des Clairvoyants de Shattrath, insiste pour que vous retourniez immédiatement en Azeroth et que vous vous présentiez à Larethor au Puits de soleil."}},
[11483] = {"Nous pouvons le reconstruire", nil, {"Walt, à l’Avant-poste de la Ligue des explorateurs du Fjord Hurlant, veut que vous lui rapportiez un Grand tonneau, de la Corde à usage industriel et des Lingots de sombrefer."}},
[11484] = {"Nous avons la technologie", nil, {"Walt, à l’Avant-poste de la Ligue des explorateurs du Fjord Hurlant, veut que vous lui rapportiez une Peau de brochepelle en parfait état et une Armature en acier."}},
[11485] = {"Les assemblages Rune-de-fer et vous : les fusées de saut", nil, {"Walt, à l’Avant-poste de la Ligue des explorateurs du Fjord Hurlant, veut que vous utilisiez les Fusées de saut de l’Assemblage Rune-de-fer avec la Gravure de rune de fer de Walt.\n\nQuittez votre assemblage en le renvoyant dans la fenêtre du familier, quand vous aurez terminé la quête."}},
[11488] = {"La terrasse des Magistères", nil, {"L'exarque Larethor, de la Zone de rassemblement du Soleil brisé, vous demande d'explorer la Terrasse des magistères à la recherche de Tyrith, un espion elfe de sang."}},
[11489] = {"Les assemblages Rune-de-fer et vous : le recueil de données", nil, {"Walt, à l’Avant-poste de la Ligue des explorateurs du Fjord Hurlant, veut que vous utilisiez le système de Recueil de données de l’Assemblage Rune-de-fer en vous plaçant près de la Relique des nains de fer.\n\nQuittez votre assemblage en le renvoyant dans la fenêtre du familier, quand vous aurez terminé la quête."}},
[11490] = {"Clairvoyance pour les Clairvoyants", nil, {"Tyrith veut que vous utilisiez l'orbe sur le balcon de la Terrasse des Magistères."}},
[11491] = {"Les assemblages Rune-de-fer et vous : le grand bluff", nil, {"Walt, à l’Avant-poste de la Ligue des explorateurs du Fjord Hurlant, veut que vous utilisiez le système de Bluff de l’Assemblage Rune-de-fer sur Lebronski quand vous aurez marché sur son tapis.\n\nQuittez votre assemblage en le renvoyant dans la fenêtre du familier, quand vous aurez terminé la quête."}},
[11492] = {"L'increvable", nil, {"Kalecgos vous a demandé de vaincre Kael'thas sur la Terrasse des Magistères. Vous devrez ramasser la tête de Kael avant de faire votre rapport à Larethor à la Zone de rassemblement du Soleil brisé."}},
[11493] = {"UNUSED", nil, nil},
[11494] = {"La relique imprégnée d'éclair", nil, {"Walt, à l’Avant-poste de la Ligue des explorateurs du Fjord Hurlant, veut que vous utilisiez le système de Recueil de données de l’Assemblage Rune-de-fer pour récolter 15 séries de Données Rune-de-fer.\n\nQuittez votre assemblage en le renvoyant dans la fenêtre du familier, quand vous aurez terminé la quête."}},
[11495] = {"Le doux son du tonnerre", nil, {"Walt, à l’Avant-poste de la Ligue des explorateurs du Fjord Hurlant, veut que vous utilisiez l’Assemblage Rune-de-fer pour explorer la caverne tonnante, aux Excavations de Baelgun.\n\nQuittez votre assemblage en le renvoyant dans la fenêtre du familier, quand vous aurez terminé la quête."}},
[11496] = {"Les protections du sanctum", nil, {"Le Capitaine Theris Âtraurore, de la Zone de rassemblement du Soleil brisé, vous demande de vous procurer 4 Restes de mana sur les Fielleux déshérités et les Dévoreurs déshérités et de vous en servir pour alimenter un des Cristaux de protection qui entoure le Sanctum des Confins du soleil, à l'est."}},
[11497] = {"Apprendre à voler", nil, {"Parlez à Ilsa Fulminebière au Bastion des Marteaux-hardis dans la Vallée d'Ombrelune."}},
[11498] = {"Apprendre à voler", nil, {"Parlez à Olrokk au Village d'Ombrelune dans la Vallée d'Ombrelune."}},
[11499] = {"On recherche : La chevalière du prince Kael'thas", nil, {"Le Marchand des vents Zhareem vous a demandé de vous procurer la Chevalière du prince Kael'thas. Apportez-la-lui à la Ville basse de Shattrath pour recevoir votre récompense.\n\nCette quête ne peut être accomplie qu'à la Terrasse des Magistères en mode Héroïque."}},
[11500] = {"On recherche : Les sœurs du tourment", nil, {"Le Traqueur-du-Néant Mah'duun vous a chargé de tuer 4 Sœurs du tourment. Revenez le voir à la Ville basse de Shattrath lorsque ce sera fait pour recevoir votre prime."}},
[11501] = {"Des nouvelles de l'est", nil, {"Parler au Capitaine Adams au Donjon de la Garde de l'Ouest."}},
[11502] = {"La défense de Halaa", nil, {"Vaincre 10 joueurs ennemis à Halaa, puis retourner voir Lakoor à Telaar."}},
[11503] = {"Ennemis, anciens et nouveaux", nil, {"Abattez 10 joueurs ennemis à Halaa, puis retournez voir Karrtog à Garadar."}},
[11504] = {"Les morts se lèvent !", nil, {"Orfus de Kamagua, à l'Antique élévateur dans le Fjord Hurlant, veut que vous découvriez l’Indice de Fengir, l’Indice de Rodin, l’Indice d’Isuldof et l’Indice de Windan."}},
[11505] = {"Les esprits d'Auchindoun", nil, {"Aidez l'Alliance à prendre le contrôle d'une tour dans le désert des Ossements."}},
[11506] = {"Les esprits d'Auchindoun", nil, {"Aidez la Horde à prendre le contrôle d'une Tour des esprits dans le désert des Ossements."}},
[11507] = {"L'Ancien Atuik et Kamagua", nil, {"Orfus de Kamagua, dans le Fjord hurlant, vous demande d'apporter le Paquet d'Orfus à l'Ancien Atuik à Kamagua."}},
[11508] = {"Grezzix Claquefuseau", nil, {"Parlez à Grezzix Claquefuseau."}},
[11509] = {"Un gars populaire…", nil, {"Parlez à Harry « Lune-d’argent », au Cap du Forban, des artéfacts vrykuls manquants."}},
[11510] = {"« Scoodles »", nil, {"Harry « Lune-d’argent », au Cap du Forban, veut que vous repreniez son Cristal de divination sin’dorei à l’épaulard « Scoodles »."}},
[11511] = {"Le bâton de la fureur de l'orage", nil, {"Récupérez le Bâton de la fureur de l’orage et remettez-le à Orfus de Kamagua, dans le Fjord hurlant."}},
[11512] = {"Le cœur gelé d'Isuldof", nil, {"Récupérez le Cœur gelé d’Isuldof et remettez-le à Orfus de Kamagua dans le Fjord Hurlant."}},
[11513] = {"Intercepter des cellules de mana", nil, {"Procurez-vous 10 Cellules de mana de contrebande et apportez-les à l'Exarque Nasuun sur la Terrasse de la Lumière à Shattrath."}},
[11514] = {"Maintenir le portail du Puits de soleil", nil, {"Procurez-vous 10 Cellules de mana de contrebande et apportez-les à l'Exarque Nasuun sur la Terrasse de la Lumière à Shattrath."}},
[11515] = {"Sang pour sang", nil, {"La Magistrice Seyla, au Trône de Kil'jaeden, vous demande de tuer 4 Gangresangs émaciés en utilisant le gangresiphon sur eux. Vous aurez besoin de sang démoniaque, qui s'obtient sur les Hérauts de courroux des alentours, pour alimenter le siphon."}},
[11516] = {"Détruire la porte", nil, {"La Magistrice Seyla, au Trône de Kil'jaeden, vous demande d'utiliser les Braises grésillantes pour invoquer une Fusée vivante et de tuer les Gangrétincelles incandescentes près d'elle jusqu'à ce qu'elle devienne une Fusée vivante instable. Retournez à la Porte de la Légion avec la Fusée vivante instable pour la détruire."}},
[11517] = {"Se présenter à Nasuun", nil, {"Le Commerçant Portonuus veut que vous parliez à l'Exarque Nasuun, à la Terrasse de la Lumière de Shattrath."}},
[11519] = {"Le bouclier perdu des Asirites", nil, {"Parlez au Capitaine Ellis à bord du Sœur Miséricorde."}},
[11520] = {"À la découverte de vos racines", nil, {"Mar'nah, au Port des Confins du soleil, vous demande de vous rendre sur l'Éminence de Tranchépine en Outreterre et de réunir 5 Racines de Tranchépine avant de revenir la voir."}},
[11521] = {"À la redécouverte de vos racines", nil, {"Mar'nah, du port des Confins du soleil, vous demande de vous rendre sur l'Éminence de Tranchépine en Outreterre et de vous procurer 5 Racines de Tranchépine avant de retourner la voir."}},
[11523] = {"Armez les protections !", nil, {"Le Capitaine Theris Âtraurore, de la Zone de rassemblement du Soleil brisé, vous demande de vous procurer 4 Restes de mana sur les Fielleux déshérités et les Dévoreurs déshérités et de vous en servir pour alimenter un des Cristaux de protection qui entoure les bâtiments des Confins du soleil."}},
[11524] = {"Comportement erratique", nil, {"Le redresseur de torts Xayann, de la Zone de rassemblement du Soleil brisé, vous demande de placer les Noyaux cristallins en phase à l'intérieur de 5 Factionnaires erratiques vaincus afin d'en faire des unités alliées."}},
[11525] = {"Plus de conversions", nil, {"Le redresseur de torts Xayann, de la Zone de rassemblement du Soleil brisé, vous demande de placer les Noyaux cristallins en phase à l'intérieur de 5 Factionnaires erratiques vaincus afin d'en faire des unités alliées."}},
[11526] = {"La magistrice disparue", nil, {"Servez-vous du Parchemin de la Légion capturé près du portail de la place du Point-du-jour. Lorsque vous aurez franchi le portail, trouvez la magistrice Seyla."}},
[11527] = {"Les mutins de la Miséricorde", nil, {"Le Capitaine Ellis, à bord du Sœur Miséricorde dans le Fjord Hurlant, vous a demandé de récupérer 5 Tonneaux de poudre d'explosion dans la cale du bateau."}},
[11528] = {"Un cadeau pour la fête du Voile d'hiver", nil, nil},
[11529] = {"Le butin de Sorlof", nil, {"Tuez Sorlof à l'aide du Gros canon et récupérez le Butin de Sorlof. Rapportez le Butin de Sorlof au capitaine Ellis à bord du Sœur Miséricorde."}},
[11530] = {"Le bouclier des Asirites", nil, {"Rapportez le Bouclier des Asirites à Orfus de Kamagua à l'Antique élévateur dans le Fjord Hurlant."}},
[11531] = {"Pièce de moteur étrange", nil, {"Apportez la Pièce de moteur étrange à Manue Fretin à Telredor."}},
[11532] = {"Diversion à la Malebrèche", nil, {"La mage de bataille Arynna vous demande de parler à Ayren Brisenuée quand vous serez prête à survoler la Malebrèche. Une fois là-bas, utilisez les Charges arcaniques pour tuer 2 Suzerains des abîmes, 3 Ensorceleurs érédars et 12 Massacreurs de courroux."}},
[11533] = {"Les frappes aériennes doivent continuer", nil, {"La mage de bataille Arynna vous demande de parler à Ayren Brisenuée quand vous serez prêt à survoler la Malebrèche. Une fois là-bas, utilisez les Charges arcaniques pour tuer 2 Suzerains des abîmes, 3 Ensorceleurs érédars et 12 Massacreurs de courroux."}},
[11534] = {"Se présenter à Nasuun", nil, {"Le Commerçant Portonuus veut que vous parliez à l'Exarque Nasuun, à la Terrasse de la Lumière de Shattrath."}},
[11535] = {"Préparatifs", nil, {"Le forgeron Hauthaa, de l'armurerie des Confins du soleil, veut que vous tuiez des Myrmidons Sombréchine, à l'est, et que vous utilisiez leurs clés pour dérober trois morceaux de minerai dans leurs coffres."}},
[11536] = {"Ne vous arrêtez pas...", nil, {"Le forgeron Hauthaa, de l'armurerie des Confins du soleil, veut que vous tuiez des Myrmidons Sombréchine, à l'est, et que vous utilisiez leurs clés pour dérober trois morceaux de minerai dans leurs coffres."}},
[11537] = {"La bataille doit continuer", nil, {"Le Messager Inuuro vous demande de tuer 6 Démons de la Légion ardente et l'Emissaire de haine sur la place du Point-du-jour. Utilisez la Bannière du Soleil brisé pour empaler le cadavre de l'Emissaire de haine."}},
[11538] = {"La bataille pour l'Armurerie des Confins du soleil", nil, {"Le Messager Inuuro vous demande de tuer 6 Démons de la Légion ardente et l'Emissaire de haine sur la place du Point-du-jour ou à l'armurerie des Confins du soleil. Utilisez la Bannière du Soleil brisé pour empaler le cadavre de l'Emissaire de haine."}},
[11540] = {"Écraser la Lame de l'aube", nil, {"Le Magistère Ilastar, de l'Armurerie des Confins du soleil, vous demande de tuer 6 Invocateurs de la Lame de l'aube, 6 Chevaliers de sang de la Lame de l'aube et 3 Archers de la Lame de l'aube."}},
[11541] = {"Déstabilisation de la Côte de Verte-branchie", nil, {"Le Capitaine Valindria vous demande de libérer 10 Esclaves verte-branchie. Retournez la voir au bord de la Fierté de Lune-d'argent au Port des Confins du soleil quand ce sera fait."}},
[11542] = {"Intercepter les renforts", nil, {"Le Redresseur de torts Kaalan, de l'Armurerie des Confins du soleil, vous demande de parler à Ayren Brisenuée et de survoler la flotte de renforts de la Lame de l'aube. Utilisez l'Huile enflammée pendant votre vol pour mettre le feu aux voiles des navires, et après avoir atterri, tuez 6 Réservistes de la Lame de l'aube."}},
[11543] = {"Tenir l'ennemi à distance", nil, {"Le redresseur de torts Kaalan, de l'Armurerie des Confins du soleil, vous demande de parler à Ayren Brisenuée et de survoler la flotte de renforts de la Lame de l'aube. Utilisez l'Huile enflammée pendant votre vol pour mettre le feu aux voiles des navires, et après avoir atterri, tuez 6 Réservistes de la Lame de l'aube."}},
[11544] = {"Les armes d'Ata'mal", nil, {"Le Forgeron Hauthaa vous demande de produire 5 morceaux de Métal ata'mal purifié. Pour ce faire, cassez des Armes d'Ata'mal sur son enclume à l'Armurerie des Confins du soleil sur l'île de Quel'Danas."}},
[11546] = {"On est ouvert", nil, {"Mar'nah, du Port des Confins du soleil, vous demande de cueillir 5 Sangrelles sur les buissons de Quel'Danas."}},
[11547] = {"Les lignes telluriques et vous", nil, {"L'astromancien Darnarian du Sanctum des Confins du soleil vous demande d'utiliser le Cristal de l'astromancien sur le Cristal-sanglant, le portail de la place du Point-du-jour et le sanctuaire naga de la Côte de Verte-branchie."}},
[11548] = {"Votre soutien indéfectible", nil, {"L'Anachorète Ayuri, des Confins du soleil sur l'île du Puits de soleil, vous demande de faire un don de 10 pièces d'or."}},
[11549] = {"Un bienfaiteur magnanime", nil, {"L'Anachorète Kairthos vous demande de faire don de 1000 pièces d'or pour soutenir l'action de l'anachorète Ayuri. Vous serez connu sous le nom de Bomdiyada du Soleil brisé si vous accomplissez cette quête."}},
[11550] = {"Le Trompeur entre en scène", nil, {"Présentez-vous à l'Archimage Ne'thul sur l'Île de Quel'Danas, au nord de Quel'Thalas.\n\nLes maîtres de vol de Forgefer et de Lune-d'argent peuvent vous y envoyer."}},
[11554] = {"Un ami sur le front", nil, nil},
[11555] = {"Honoré par vos alliés", nil, nil},
[11556] = {"Révéré sur le champ de bataille", nil, nil},
[11557] = {"Exalté parmi tous les combattants", nil, nil},
[11558] = {"Les périls de l'amour", nil, {"Obtenez une Carte de garde moisie et rapportez-la à Fenstad Argyle, à Fossoyeuse."}},
[11559] = {"Commercer avec les Ailerons-d'hiver", nil, {"Ramassez 5 Palourdes aileron-d'hiver et apportez-les à Ahlurglgr à la Retraite des Ailerons-d'hiver."}},
[11560] = {"Jamais sans mes têtards", nil, {"Le Roi Mrgl-Mrgl vous a demandé de sauver 20 Têtards aileron-d'hiver. Retournez le voir à la Retraite des Ailerons-d'hiver quand ce sera fait."}},
[11561] = {"Eux !", nil, {"Brglmurgl, de la Retraite des Ailerons-d'hiver, vous a demandé de tuer 15 Murlocs aileron-d'hiver. N'importe quelle combinaison de guerriers, oracles et guerriers des rivages fera l'affaire."}},
[11562] = {"Mon teinturier est un maître-chanteur", nil, {"Le Roi Mrgl-Mrgl vous a demandé de parler à Mrmrglmr à l'extrémité nord-ouest de la Retraite des Ailerons-d'hiver."}},
[11563] = {"Grmmurggll Mrllggrl Glrggl !!!", nil, {"Mrmrglmr vous demande de revenir le voir à la Retraite des Ailerons-d'hiver avec la Tête de Glrggl."}},
[11564] = {"Un ragoût d'épaulard succulent", nil, {"Le Fendeur Bmurglbrm vous a demandé de récupérer 7 Succulentes graisses d'épaulard. Retournez ensuite le voir à la Retraite des Ailerons-d'hiver."}},
[11565] = {"Le costume de rechange", nil, {"Rapportez son Costume de rechange au Roi Mrgl-Mrgl à la Retraite des Ailerons-d'hiver."}},
[11566] = {"Reddition... ou pas !", nil, {"Le Roi Mrgl-Mrgl, de la Retraite des Ailerons-d’hiver, vous a demandé de lui rapporter une Pince de Claximus."}},
[11567] = {"L'antique armure des kvaldirs", nil, {"Récupérez l'Antique armure des kvaldirs et apportez-la à Orfus de Kamagua dans le Fjord Hurlant."}},
[11568] = {"Le retour à la terre", nil, {"Utilisez le Paquet d'artéfacts vrykuls aux tertres funéraires mis à sac de la Colline du Bouclier pour restituer le Cœur gelé d'Isuldof, le Bouclier des Asirites, le Bâton de la fureur de l'orage et l'Antique armure des kvaldirs.\n\nRetournez voir Orfus de Kamagua à l'Antique élévateur, si vous réussissez."}},
[11569] = {"Le maître des clés Urmgrgl", nil, {"Glrglrglr vous a demandé de récupérer la Clé d’Urmgrgl. Lorsque ce sera fait, retournez le voir dans les Cavernes des Ailerons-d’hiver."}},
[11570] = {"L'évasion des cavernes des Ailerons-d'hiver", nil, {"Escorter Lurgglbr jusqu’à ce qu'il soit en sécurité, puis aller annoncer la bonne nouvelle au Roi Mrgl-Mrgl à la Retraite des Ailerons-d'hiver."}},
[11571] = {"Apprendre à communiquer", nil, {"Le Roi Mrgl-Mrgl vous a donné une conque à utiliser sur les restes de Brûle-gueule. Rapportez-lui la Conque du roi remplie à la Retraite des Ailerons-d'hiver."}},
[11572] = {"Retourner voir Atuik", nil, {"Transmettez les mauvaises nouvelles à l’Ancien Atuik à Kamagua."}},
[11573] = {"Orfus de Kamagua", nil, {"Trouvez Orfus de Kamagua à l'Antique élévateur dans le Fjord Hurlant."}},
[11574] = {"Un peu trop près à mon goût…", nil, {"Endorah veut que vous alliez à l’Escarpement d’Ambre au nord du Bastion Chanteguerre, et que vous remerciiez ceux qui l’ont sauvée."}},
[11575] = {"Juste à temps", nil, {"Moucheronne, au Donjon de la Bravoure, veut que vous alliez à l’Escarpement d’Ambre au nord, et que vous remerciiez ceux qui l’ont sauvée."}},
[11576] = {"Surveiller la faille : l'anomalie de la falaise fendue", nil, {"Garren, de l'Escarpement d'Ambre, vous demande d'utiliser l'Arcanomètre à l'Anomalie de la falaise fendue."}},
[11578] = {"La quête « Chow » (123)aa", nil, {"Tuez au moins deux Vermines kobolds."}},
[11579] = {"La quête « Chow » (123)aa COPIE", nil, {"Tuez au moins deux Vermines kobolds."}},
[11580] = {"Désacralisez ce feu !", nil, nil},
[11581] = {"Désacralisez ce feu !", nil, nil},
[11582] = {"Surveiller la faille : l'anomalie du gouffre scindé", nil, {"Garren, de l'Escarpement d'Ambre, vous demande d'utiliser l'Arcanomètre à l'Anomalie du Gouffre scindé et de retourner le voir avec les relevés."}},
[11583] = {"Honorer la flamme", nil, nil},
[11585] = {"La veillée de Hurlenfer", nil, {"Parlez à Garrosh Hurlenfer au Bastion Chanteguerre."}},
[11587] = {"Faire le mur", nil, {"Le Bibliothécaire Donathan de l'Escarpement d'Ambre vous demande de récupérer la Clé de la prison de Béryl sur un Chasseur de mages de Béryl, à la Halte de Béryl, et de vous en servir pour libérer un mage retenu dans une Prison arcanique."}},
[11590] = {"Enlèvement", nil, {"Donathan veut que vous utilisiez le Lieur d’arcanes sur un Ensorceleur de Béryl blessé, à la Halte de Béryl, et que vous le rameniez à l’Escarpement d’Ambre."}},
[11591] = {"Se présenter à la caravane de Mâchoire-d'acier", nil, {"Parlez au Grunt Ragepoing à la Caravane de Mâchoire-d'acier."}},
[11592] = {"Frapper", nil, {"Aidez le Long-coureur Sabot-fier et ses écumeurs chanteguerre à survivre à l'assaut contre la Caravane de Mâchoire-d'acier et à en finir avec l'esprit du commandant de corps Mâchoire-d'acier.\n\nSi vous réussissez, allez faire votre rapport au Suzerain Bor'gorok à l'Avant-poste de Bor'gorok."}},
[11593] = {"Les morts honorés", nil, {"Le Grunt Ragepoing, à la Caravane de Mâchoire-d'acier, vous demande d'utiliser sa torche pour brûler un total de 10 Gardes ou Ouvriers morts de la caravane."}},
[11594] = {"Donnez-leur le repos", nil, {"Détruisez 20 esprits taunkas, qui peuvent être des Sages fantomatiques ou des Long-coureurs ressuscités, puis retournez voir le Grunt Ragepoing à la Caravane de Mâchoire-d'acier."}},
[11596] = {"La défense du bastion Chanteguerre", nil, {"Présentez-vous au suzerain Razgor, à la hutte du côté sud du Bastion Chanteguerre."}},
[11598] = {"Reprendre la carrière de pierres de pouvoir", nil, {"Le suzerain Razgor du Bastion Chanteguerre vous demande de tuer 15 Nérubiens nérub'ar."}},
[11599] = {"Thassarian, mon frère", nil, {"Partez à la recherche de William Allerton à l'intérieur de la Mine de Comté-lointaine."}},
[11600] = {"Feu William Allerton", nil, {"Apportez la Carte d'enrôlement à Leryssa au Donjon de la Bravoure, dans la Toundra Boréenne."}},
[11601] = {"Objets trouvés", nil, {"Parlez à James Lediacre à l’auberge du Donjon de la Bravoure."}},
[11602] = {"Assécher la source", nil, {"Le suzerain Razgor du Bastion Chanteguerre vous demande de détruire 10 Cocons d'œufs nérub'ar."}},
[11603] = {"In vino veritas", nil, {"Repêchez une bouteille de Vin de Kul Tiras dans l’épave, au nord-est du Donjon de la Bravoure, et apportez-la au Vieux Colburn à l'auberge du Donjon de la Bravoure."}},
[11604] = {"Un déserteur", nil, {"Trouvez la prison dans le Bastion intérieur du Donjon de la Bravoure et parlez au déserteur."}},
[11605] = {"Les ancêtres honorés", nil, {"L'Ancien Atkanok, à l'extérieur de la Carrière de Rochefroide, vous demande d'identifier les statues de l'Ancien Kesuk, l'Ancien Sagani et l'Ancien Takret."}},
[11606] = {"La patience, une vertu dont nous nous passerons bien", nil, {"L'intendant Holgoth, au Bastion Chanteguerre, vous demande de récupérer 15 caisses de Munitions chanteguerres."}},
[11607] = {"Les esprits égarés", nil, {"L'Ancien Atkanok, à la lisière de la Carrière de Rochefroide, vous demande de libérer les esprits de 3 Artisans de Kaskala et 3 Chamans de Kaskala."}},
[11608] = {"Enterrez-moi ces cafards !", nil, {"L'intendant Holgoth, au Bastion chanteguerre, vous demande d'utiliser le Paquet de charges de profondeur à l'hydroglycérine pour détruire les puisards nérub'ar au sud, à l'est, à l'ouest et au nord du Bastion Chanteguerre."}},
[11609] = {"Ramasser les morceaux", nil, {"Apportez 6 Objets rituels roharts à l'Ancien Atkanok, qui se trouve à l'extérieur de la Carrière de Rochefroide."}},
[11610] = {"Conduire les ancêtres chez eux", nil, {"L'Ancien Atkanok vous demande de guider les esprits des Anciens Kesuk, Sagani et Takret jusqu'à leurs monuments de pierre respectifs."}},
[11611] = {"Pris par le Fléau", nil, {"Le Contremaître Mortuus au Bastion Chanteguerre veut que vous libériez 5 Péons chanteguerre."}},
[11612] = {"Reprendre la carrière", nil, {"Etaruk, qui campe près de l'entrée de la Carrière de Rochefroide, vous demande de tuer 12 Chasseurs de trésor de Béryl."}},
[11613] = {"Le serment de Karuk", nil, {"Karuk vous demande de tuer 6 Ecumeurs skadir et 5 Skadirs d'équipage."}},
[11614] = {"Vérités tues", nil, {"Trouvez le Traqueur des ombres Luther dans la Carrière de pierres de pouvoir."}},
[11615] = {"Les secrets nérub'ar", nil, {"Apportez le Journal de Luther au Traqueur des ombres Barthus au Bastion Chanteguerre."}},
[11616] = {"Un message pour Hurlenfer", nil, {"Apportez la Note de Barthus à Garrosh Hurlenfer au Bastion Chanteguerre."}},
[11617] = {"Gênez leur départ", nil, {"Etaruk, à l'extérieur de la Carrière de Rochefroide, vous demande de détruire la Plateforme nord, la Plateforme est et la Plateforme ouest."}},
[11618] = {"Et voilà les renforts...", nil, {"Allez trouver le Traqueur des ombres Ickoris à l'Avant-poste des fermes chanteguerres, à l'ouest du Bastion Chanteguerre."}},
[11619] = {"Gamel le Cruel", nil, {"Karuk vous demande de triompher de Gamel le Cruel sur la Grève des Courcinglants."}},
[11620] = {"Les paroles d'un père", nil, {"Partez à la recherche de Veehja dans le Sanctuaire des Écailles de la Grève des Courcinglants."}},
[11621] = {"La tablette de Leviroth", nil, {"Retournez voir Karuk avec les informations que vous avez trouvées sur la tablette."}},
[11622] = {"Les secrets des Courcinglants", nil, {"Trouvez Veehja dans les Ruines des Courcinglants de la toundra Boréenne."}},
[11623] = {"En visite chez le conservateur", nil, {"Etaruk, à la lisière de la Carrière de Rochefroide, vous demande de tuer le Conservateur Insivius."}},
[11624] = {"Le ciel saura", nil, {"Allez voir Impéréen dans les Ruines d'Eldra'nath."}},
[11625] = {"Le trident de Naz'jan", nil, {"Veehja, aux Courcinglants, vous demande d'aller à l'extrémité nord-est des ruines des Courcinglants. Une fois là-bas, tuez Ragnar Drakkarlund et prenez-lui le Trident de Naz'jan."}},
[11626] = {"L'émissaire", nil, {"Trouvez Leviroth sous l'iceberg flottant, le long de l'extrémité nord des Ruines des Courcinglants. Utilisez le Trident de Naz'jan pour le tuer, puis retournez voir Karuk au nord de la Grève des Courcinglants."}},
[11627] = {"Le point d'ébullition", nil, {"Impéréen vous demande de faire entendre raison à Frémiss et Bouillonn. Retournez la voir aux Ruines d'Eldra'nath quand ils auront plié."}},
[11628] = {"Le voile du Fléau", nil, {"Le Chef Trombe-de-l'hiver à Taunka'le veut que vous lui apportiez 5 Fourrures de mammouths flaellés."}},
[11629] = {"Retourner voir la spirite", nil, {"Apportez la Prime-essence d'Impéréen à la Spirite Grognecroc, à l'Avant-poste de Bor'gorok."}},
[11630] = {"La mauvaise terre", nil, {"Rapportez 8 mottes de Terre flaellée au Chef Trombe-de-l'hiver à Taunka'le."}},
[11631] = {"Vision de l'Air", nil, {"Utilisez la Prime-essence d'Impéréen pour accomplir un rituel devant le Totem de Grognecroc, à l'Avant-poste de Bor'gorok, afin de découvrir des informations sur le Long-voyant Marche-sinistre.\n\nEnsuite, allez parler à la Spirite Grognecroc."}},
[11632] = {"Ce qu'apporte le vent froid…", nil, {"Apportez la Carapace durcie d'Ith'rix au Haut seigneur Saurcroc au Bastion Chanteguerre."}},
[11633] = {"Se fondre dans le paysage", nil, {"Le Chef Trombe-de-l'hiver à Taunka'le veut que vous portiez le Voile du Fléau imprégné dans la Ville-temple d’En’kilah, et que vous alliez explorer la Flèche du Sang, la Flèche de la Douleur et la Flèche de la Décomposition."}},
[11634] = {"Le maître du vent To'bor", nil, {"Présentez-vous au Maître du vent To'bor au Bastion Chanteguerre."}},
[11635] = {"L'esprit du long-voyant Marche-sinistre", nil, {"Parlez à l’Esprit du long-voyant Marche-sinistre, à l’intérieur de Magmoth."}},
[11636] = {"Un tour en tapis volant", nil, {"Parlez à Yanni au Bastion Chanteguerre pour organiser votre voyage jusqu’à la côte. Une fois au Point d’accostage de Garrosh, trouvez l’annihilateur Grek’lor."}},
[11637] = {"Kaganishu", nil, {"Retrouvez le Fétiche de Kaganishu et utilisez-le près des Restes du long-voyant Marche-sinistre.\n\nVous devrez donner le fétiche à l’Esprit du long-voyant Marche-sinistre dans Magmoth."}},
[11638] = {"Rapatrier mes restes", nil, {"Remettez les Restes du long-voyant Marche-sinistre, qui reposent en dessous de son esprit, à la Spirite Grognecroc, à l’Avant-poste de Bor’gorok."}},
[11639] = {"La vengeance s'abat sur Magmoth", nil, {"Tuez 10 Chamans de Magmoth, 5 Fourrageurs de Magmoth, 3 Écraseurs de Magmoth et 3 Compagnes de Magmothregar. Lorsque ce sera fait, retournez voir Ortrosh à l’Avant-poste de Bor’gorok."}},
[11640] = {"Les mots de puissance", nil, {"Apportez le Parchemin du grand prêtre Naferset, le Parchemin du grand prêtre Talet-Kha et le Parchemin du grand prêtre Andorath au Chef Trombe-de-l'hiver à Taunka'le."}},
[11641] = {"Une frappe courageuse", nil, {"Tuez 15 Goules d’En’kilah et 5 Nécromanciens d’En’kilah, puis allez faire votre rapport à Durm Cuir-de-glace à Taunka’le."}},
[11642] = {"Un char, ça s'répare pas tout seul", nil, {"Parlez à Mobu au Point d'accostage de Garrosh."}},
[11643] = {"Le transbidulotron pneumatique pour char de Mobu", nil, {"Mobu, sur la Côte des Échos dans la Toundra Boréenne veut que vous récupériez son Transbidulotron pneumatique pour char."}},
[11644] = {"Des plaques de métal ultra-résistantes !", nil, {"Mobu, sur la Côte des Échos dans la Toundra Boréenne veut que vous récupériez 10 Plaques de métal ultra-résistantes."}},
[11645] = {"Stupides frigbolds joufflus !", nil, {"Récupérez 10 boîtes de Matériel de Fufute et rapportez-les à Fufute Tremblerouage sur la Piste d'atterrissage de Spumelevier."}},
[11646] = {"L'inquisition boréenne", nil, {"Donathan vous a ordonné de proposer votre aide au Bibliothécaire Normantis dans la tour des mages de l'Escarpement d'Ambre."}},
[11647] = {"Neutraliser les chaudrons", nil, {"La Sage Aeire de Taunka'le vous demande d'utiliser son totem pour neutraliser le Chaudron de l'est, le Chaudron central et le Chaudron de l'ouest dans la Ville-temple d'En'kilah."}},
[11648] = {"L'art de la persuasion", nil, {"Le Bibliothécaire Normantis de l'Escarpement d'Ambre vous demande d'utiliser l'Aiguillonneur neural sur l'Ensorceleur de Béryl emprisonné jusqu'à ce qu'il révèle où se trouve dame Evanor."}},
[11649] = {"Granules des enragés", nil, {"Rassemblez 5 Granules de tempête et rapportez-les à Impéréen aux Ruines d'Eldra'nath."}},
[11650] = {"Encore quelques bricoles…", nil, {"Récupérez les Outils de Fufute, un Condensateur surchargé et une Poignée de sable de Rocnar. Apportez-les à Fufute Tremblerouage à la Piste d'atterrissage de Spumelevier."}},
[11651] = {"Le char l'attend", nil, {"Parlez à l’Annihilateur Grek’lor sur la Côte des Échos."}},
[11652] = {"Les plaines de Nasam", nil, {"Utilisez le Char de siège de la Horde pour détruire 100 Soldats du Fléau et secourir 3 Soldats chanteguerre blessés, sur les Plaines de Nasam. Vous devez aussi découvrir l’identité du chef de l’attaque du Fléau.\n\nAllez ensuite faire votre rapport à Garrosh Hurlenfer au Bastion Chanteguerre si vous réussissez."}},
[11653] = {"Ha ! Z'êtes plus si grands que ça maintenant !", nil, {"Testez la Pétoire Raccourcissante Proto-Typique Ultra-Avancée de Fufute sur 5 Écraseurs de Magmoth ou Compagnes de Magmothregar. Quand ce sera fait, retournez voir cette dernière à la Piste d'atterrissage de Spumelevier."}},
[11654] = {"La flèche du Sang", nil, {"Fouillez la Flèche du Sang à la recherche d'informations sur la Fiole de sang frais."}},
[11655] = {"Dans la brume", nil, {"Waltor, de Pal'ea sur la Côte des Échos dans la Toundra Boréenne, vous demande de tuer des kvaldirs pour récupérer 8 Reliques roharts."}},
[11656] = {"Brûler en effigie", nil, {"Waltor de Pal’ea, sur la Côte des Échos de la Toundra Boréenne, veut que vous utilisiez la Torche rohart pour brûler les vaisseaux kvaldirs à Pal’ea et au Point d'accostage de Garrosh. Vous devez détruire les vaisseaux suivants : La Gueule du serpent, Le Drakkar de Kur, Le Marteau de Bor et L’Enclume de Bor."}},
[11657] = {"Attraper des torches", nil, {"Attrapez 4 torches de suite, puis parlez au Maître mangeur de feu."}},
[11658] = {"Plan B", nil, {"Rapportez 10 Tenues chanteguerres et une Bannière chanteguerre à Fufute Tremblerouage à la Piste d'atterrissage de Spumelevier."}},
[11659] = {"Brisez les orbes !", nil, {"Détruisez 5 Globes de sang et allez avertir le Pisteur des neiges Junek à Taunka'le."}},
[11660] = {"Le cor du vieux marinier", nil, {"Waltor, de Pal'ea sur la Côte des Échos dans la Toundra Boréenne, vous demande de lui apporter le Cor du vieux marinier."}},
[11661] = {"Orabus le Timonier", nil, {"Waltor de Pal’ea, sur la Côte des échos, dans la Toundra Boréenne, veut que vous utilisiez le Cor du vieux marinier, pour appeler Orabus le Timonier. Quand vous l’aurez appelé, tuez-le."}},
[11662] = {"Allez trouver Karuk !", nil, {"Allez trouver Karuk sur la Grève des Courcinglants."}},
[11663] = {"Rapporter les informations", nil, {"Normantis vous a demandé de vous présenter au Bibliothécaire Donathan à l'Escarpement d'Ambre."}},
[11664] = {"S'échapper de la brume", nil, {"Suivez Meuhtou la Jeune hors de la brume. Parlez à l'Ancien Meuhtou si vous parvenez à sauver Meuhtou la Jeune."}},
[11665] = {"Les crocilisques sont lâchés", nil, {"Apportez un Bébé crocilisque au vieux Barlo. Vous le trouverez en train de pêcher au nord-est de Shattrath, au bord du lac Silmyr."}},
[11666] = {"Succomber à vos appâts", nil, {"Apportez un Dard aileron-noir au vieux Barlo. Vous le trouverez en train de pêcher au nord-est de Shattrath, au bord du lac Silmyr."}},
[11667] = {"Celui qui s'échappa", nil, {"Attrapez le Plus grand éperlan du monde et apportez-le au Vieux Barlo. Vous le trouverez en train de pêcher au nord-est de Shattrath au bord du lac Silmyr."}},
[11668] = {"Les crevettes, c'est pas pour les mauviettes", nil, {"Le Vieux Barlo veut 10 Crevettes géantes d'eau douce. Vous pouvez le trouver au nord-est de Shattrath, près du lac Silmyr."}},
[11669] = {"Filet de gangresang", nil, {"Apportez un Lutjan gangresang monstrueux au Vieux Barlo. Vous le trouverez en train de pêcher au nord-est de Shattrath, au bord du lac Silmyr."}},
[11670] = {"C'était les orcs, j'vous jure !", nil, {"Plantez la Bannière chanteguerre dans le cadavre de Magmothregar en portant le Déguisement d'orc chanteguerre. Puis rapportez le déguisement à Fufute Tremblerouage à la Piste d'atterrissage de Spumelevier."}},
[11671] = {"Une course contre la montre", nil, {"Donathan de l'Escarpement d'Ambre vous a chargé d'utiliser le Détonateur contre le bouclier de Béryl afin d'abattre le bouclier à la Halte de Béryl. Vous devez ensuite vaincre Salrand et prendre la clé qu'elle détient."}},
[11672] = {"Le jour de l'incorporation", nil, {"Rencontrez le Général Arlos à l'intérieur de la caserne du donjon de la Bravoure."}},
[11673] = {"Sortez-moi de là !", nil, {"Escortez Bonker Virevolt jusqu'en lieu sûr puis présentez-vous à Spumelevier Pleingaz à la Piste d'atterrissage de Spumelevier."}},
[11674] = {"La sage Haute-mesa a disparu", nil, {"Trouver la Sage Haute-mesa près des Tanières du Trépas."}},
[11675] = {"Une mort décente", nil, {"Détruisez 10 Magnataures pestiférés puis retournez voir la Sage Haute-mesa au sud des Tanières du Trépas."}},
[11676] = {"Le cadeau de la liberté", nil, {"Le traqueur des ombres Canarius, à l'Avant-poste des fermes chanteguerres, vous demande de libérer 5 Prisonniers du Fléau."}},
[11677] = {"Mettre fin à la peste", nil, {"La Sage Haute-mesa, qui se trouve juste au sud des tanières du Trépas, vous a demandé d'utiliser les Graines purificatrices de Haute-mesa à un Chaudron de peste des tanières du Trépas, afin de le neutraliser."}},
[11678] = {"Trouvez Corne-dressée", nil, {"Trouvez le Long-coureur Corne-dressée à Talramas."}},
[11679] = {"Reforger la clé", nil, {"Donathan veut que vous parliez à Surristrasz sur l'Escarpement d'Ambre."}},
[11680] = {"L'envol", nil, {"Lorsque vous serez prêt à libérer Evanor, donnez la Clé de Salrand au Mage de guerre Anzim."}},
[11681] = {"Au secours d'Evanor", nil, {"Anzim, de l'Escarpement d'Ambre, vous demande de vous envoler avec ses mages de guerre pour libérer dame Evanor à la Halte de Béryl. Vous devrez ensuite lui faire votre rapport dans la tour des mages de l'Escarpement d'Ambre."}},
[11682] = {"Jargon & dragons", nil, {"Dame Evanor vous demande de parler à Surristrasz sur l'Escarpement d'Ambre."}},
[11683] = {"La nécropole écrasée", nil, {"Détruisez 20 créatures du Fléau de n’importe quel type à Talramas et dans les Bassins Purulents. Lorsque ce sera fait, retournez voir la Sage Haute-mesa, juste au sud des Tanières du Trépas."}},
[11684] = {"Reconnaissance des puisards", nil, {"Grandpère Mahan, à Taunka'le, vous demande d'utiliser la Carte des champs de Geysers afin d'y écrire l'emplacement du Puisard sud, du Puisard nord-ouest et du Puisard nord-est."}},
[11685] = {"Le cœur des éléments", nil, {"Apportez 5 Cœurs élémentaires à la Dompteuse des vents Barah à Taunka'le."}},
[11686] = {"Les fermes chanteguerre", nil, {"Le traqueur des ombres Ickoris, à l'Avant-poste des fermes chanteguerres, vous demande d'explorer le Grenier chanteguerre, la Ferme de Torp et l'Abattoir chanteguerre."}},
[11687] = {"Le docteur et le seigneur-liche", nil, {"Détruisez le Docteur Trancherictus et le Seigneur-liche Frisshiver. Puis retournez voir la Sage Haute-mesa juste au sud des Tanières du Trépas."}},
[11688] = {"Saleté de cochonnerie de pourceaux", nil, {"Le fermier Torp, à l’Avant-poste des fermes chanteguerres, veut que vous tuiez 10 Pourceaux non-vivants."}},
[11689] = {"Transmettre les mauvaises nouvelles", nil, {"Parlez à la Grande-mère Taiga à Taunka'le."}},
[11690] = {"Ram'nez-les vivants", nil, {"Le fermier Torp, à l’Avant-poste des fermes chanteguerres, veut que vous sauviez 8 Kodos infectés."}},
[11691] = {"L'invocation d'Ahune", nil, {"Apportez le Totem de magma du Cercle terrestre à la Pierre de glace."}},
[11692] = {"Allez voir Bixie", nil, {"Mordle Tournepignon vous a demandé de trouver Bixie Tournechancre au sud des Tanières du Trépas."}},
[11693] = {"Super… des magnataures pestiférés !", nil, {"Tuez 10 Magnataures pestiférés puis retournez voir Bixie Tournechancre au sud des Tanières du Trépas."}},
[11694] = {"Il se passe quelque chose dans ces grottes", nil, {"Bixie Tournechancre, qui se planque au sud des Tanières du Trépas, veut que vous utilisiez la Poudre inhibitrice de Bixie sur un Chaudron de peste des Tanières du Trépas, afin de le neutraliser."}},
[11695] = {"Le cor de fureur élémentaire", nil, {"Reprenez la Moitié inférieure du cor et la Moitié supérieure du cor au Chef Brouillegargouille et au Chef Grouillebarbouille et apportez-les à la Dompteuse des vents Barah à Taunka'le."}},
[11696] = {"Ahune est là !", nil, {"Trouvez Luma Mère-des-cieux dans les Enclos aux esclaves."}},
[11697] = {"Zut, Bricole est entrée dans la nécropole !", nil, {"Trouvez Bricole Sifflemèche à Talramas."}},
[11698] = {"On pourrait aussi bien éradiquer le Fléau", nil, {"Détruisez 20 créatures du Fléau de n'importe quel type à Talramas et aux Bassins Purulents. Puis retournez voir Bixie Tournechancre juste au sud des Tanières du Trépas."}},
[11699] = {"Je suis coincée dans cette maudite cage… mais pas pour longtemps !", nil, {"Récupérez un Cristal du bloc-moteur, un Gyroscope magique et une Pierre du Fléau de pilotage, et remettez-les à Bricole Sifflemèche, à Talramas."}},
[11700] = {"Prévenez Bixie", nil, {"Parlez à Bixie Tournechancre juste au sud des Tanières du Trépas."}},
[11701] = {"Retour à la piste d'atterrissage", nil, {"Parlez à Spumelevier Pleingaz sur la Piste d'atterrissage de Spumelevier, dans la Toundra Boréenne."}},
[11702] = {"Le roi Mrgl-Mrgl", nil, {"Allez voir le Roi Mrgl-Mrgl à la Retraite des Ailerons-d'hiver."}},
[11703] = {"Rallier Getry", nil, {"Allez voir le Traqueur des ombres Getry aux Fermes chanteguerre."}},
[11704] = {"Le roi Mrgl-Mrgl", nil, {"Parlez au Roi Mrgl-Mrgl à la Retraite des Ailerons-d'hiver."}},
[11705] = {"Opération absurde", nil, {"Aidez Getry à vaincre Varidus le Dépiauteur aux Fermes chanteguerre.\n\nFaites votre rapport à Garrosh Hurlenfer si vous deviez réussir."}},
[11706] = {"L'effondrement", nil, {"La Dompteuse des vents Barah, de Taunka'le, vous demande de faire s'effondrer les puisards nérubiens à l'aide du Cor de fureur élémentaire et de vaincre le seigneur Kryxix."}},
[11707] = {"Appel de détresse", nil, {"Allez voir Spumelevier Pleingaz à la Piste d'atterrissage de Spumelevier."}},
[11708] = {"Les mécagnomes", nil, {"Écoutez l'histoire de Spumelevier Pleingaz puis allez voir Voltij Lazimuté à la Piste d'atterrissage de Spumelevier."}},
[11709] = {"La charge de Nork Frénésie-sanglante", nil, {"Parlez au Gardien Nork Frénésie-sanglante au Bastion Chanteguerre, dans la Toundra Boréenne."}},
[11710] = {"Transmatière à rire", nil, {"Ramassez 15 Pièces détachées de Spumelevier et rapportez-les à Mordle Tournepignon à la Piste d'atterrissage de Spumelevier."}},
[11711] = {"Livraison de lâche… En moins de 30 minutes, ou c'est gratuit", nil, {"Emmenez le Déserteur de l'Alliance à la croisée des chemins à l'est du Bastion Chanteguerre puis utilisez le Lance-fusée chanteguerre pour envoyer un signal à l'Alliance.\n\nAllez rendre compte à l'Éclaireur Tungok une fois que le Déserteur de l'Alliance sera bien arrivé."}},
[11712] = {"Le re-maudisseur", nil, {"Voltij Lazimuté, à la Piste d'atterrissage de Spumelevier, vous a demandé d'utiliser l'Injecteur transmatière re-maudisseur sur 6 Mécagnomes de Spumelevier."}},
[11713] = {"Reconnaissance des puisards", nil, {"Abner Patranivelle, à la Piste d’atterrissage de Spumelevier, vous demande d'utiliser la Carte des champs de Geysers afin d'y marquer l'emplacement du Puisard sud, du Puisard nord-ouest et du Puisard nord-est."}},
[11714] = {"Extermination de la vermine", nil, {"L'éclaireur Tungok, aux Plaines de Spore-sang dans la Toundra Boréenne, vous a demandé de tuer 8 Moissonneurs spore-sang, 5 Boute-flammes spore-sang et 2 Rôtisseurs spore-sang."}},
[11715] = {"Faire carburer le projet", nil, {"Abner Patranivelle, à la Piste d’atterrissage de Spumelevier, vous demande de récupérer 8 tonneaux de Naphte à l'aide du Collecteur de naphte portable."}},
[11716] = {"Spore-sang merveilleuse !", nil, {"La Mage de sang Laurith, des Plaines de Spore-sang dans la Toundra Boréenne, vous a demandé de récupérer 10 Carpelles spore-sang."}},
[11717] = {"Le pollen à sa source", nil, {"La Mage de sang Laurith, des Plaines de Spore-sang dans la Toundra Boréenne, vous a demandé de récupérer 5 échantillons de Pollen de phalène spore-sang."}},
[11718] = {"L'habit fera le mammouth", nil, {"Abner Patranivelle, à la Piste d’atterrissage de Spumelevier, veut que vous lui rapportiez 6 Peaux de mammouth épaisses."}},
[11719] = {"Un cobaye adéquat", nil, {"La Mage de sang Laurith, des Plaines de Spore-sang dans la Toundra Boréenne, vous a demandé d’utiliser la Fleur de spore-sang pollinisée et de la laisser examiner le résultat."}},
[11720] = {"L'invasion de Gammoth", nil, {"Parlez à l'Aîné Corne-de-pouvoir dans les Plaines de Spore-sang, dans la Toundra Boréenne"}},
[11721] = {"Gammothra le Tourmenteur", nil, {"Tuez Gammothra et rapportez la Tête de Gammothra à l'Aîné Corne-de-pouvoir dans les Plaines de Spore-sang, dans la Toundra Boréenne."}},
[11722] = {"Les trophées de Gammoth", nil, {"Apportez la Tête de Gammothra à Garrosh Hurlenfer au Bastion Chanteguerre."}},
[11723] = {"Déployez le Sismo-trembleur !", nil, {"Abner Patranivelle, à la Piste d’atterrissage de Spumelevier, vous demande d'utiliser le Sismo-trembleur 5000 pour faire s'effondrer les tunnels nérubiens et de vaincre le Seigneur Kryxix."}},
[11724] = {"Une gigantesque omelette de phalènes ?", nil, {"Apportez l’Œuf massif luminescent à la Mage de sang Laurith dans les Plaines de Spore-sang, dans la Toundra Boréenne."}},
[11725] = {"Trouver le pilote la Vrille", nil, {"Trouvez Iggy « la Vrille » Virouage dans les Champs de Geysers."}},
[11726] = {"Un peu de piquant", nil, {"Iggy « la Vrille » Virouage, dans les Bassins Brûlants, vous demande de ramasser 4 Bourses d'épices gorlocs."}},
[11727] = {"L'époque est aux héros", nil, {"Présentez-vous au Sergent Martèlemont au Donjon de la Bravoure."}},
[11728] = {"Lupus Pupus", nil, {"Iggy « la Vrille » Virouage, dans les Bassins Brûlants, vous demande de lui apporter 8 morceaux de Microfilm."}},
[11729] = {"Le Tournevis Ultrasonique", nil, {"Apportez le Tournevis Ultrasonique à Fufute Tremblerouage à la Piste d'atterrissage de Spumelevier."}},
[11730] = {"Maître et serviteur", nil, {"Utilisez le Tournevis ultrasonique pour reprogrammer 15 robots, puis retournez voir Fufute Tremblerouage à la Piste d'atterrissage de Spumelevier.\n\nN'oubliez pas de prendre votre butin sur le robot avant de le reprogrammer."}},
[11731] = {"Lancer des torches", nil, {"Gagnez le jeu de lancer de torches, puis parlez au Maître mangeur de feu."}},
[11732] = {"Désacralisez ce feu !", nil, nil},
[11733] = {"Traverser la faille", nil, {"Surristrasz vous demande de partir pour Frimarra. Une fois là-bas, parlez à l'Archimage Bérinand."}},
