[11788] = {"Savez-vous planter les clous ?", nil, {"Désactivez Twonky, ED-210, Max Blasto et Le Broyeur, puis retournez voir Voltij Lazimuté à la Piste d'atterrissage de Spumelevier."}},
[11789] = {"Un soldat dans le besoin", nil, {"Le docteur Aubépin, à l’extérieur du donjon de la Bravoure, veut que vous montiez dans le Brise-tempête et que vous récupériez l’Anti-venin d’Aubépin dans une caisse de Fournitures de secourisme."}},
[11790] = {"Des sectateurs parmi nous", nil, {"Parlez au Capitaine Lougres « le Gaucher » sur le Brise-tempête au Donjon de la Bravoure."}},
[11791] = {"Avertir Arlos", nil, {"Parlez au Général Arlos dans le Bastion intérieur au Donjon de la Bravoure."}},
[11792] = {"Les ennemis de la Lumière", nil, {"Le Messager Vurenn vous demande d'aller combattre les Sectateurs nécrolytes près de Comté-lointaine au nord du Donjon de la Bravoure et de récupérer le Communiqué des sectateurs."}},
[11793] = {"Approfondir l'enquête", nil, {"Apportez le Communiqué des sectateurs au Redresseur de torts Yaala à l'auberge du Donjon de la Bravoure."}},
[11794] = {"La chasse est ouverte", nil, {"Le Redresseur de torts Yaala vous demande d'utiliser l'Oculus de l'Exorciste dans trois endroits du Donjon de la Bravoure pour percer à jour et abattre des membres du Culte des damnés."}},
[11795] = {"Protocole d'urgence : section 8.2, paragraphe C", nil, {"Récupérez 6 Insignes de pilote de Spumelevier sur les corps des Pilotes de reconnaissance de Spumelevier et rapportez-les à Iggy « la Vrille » Virouage, dans les Bassins Brûlants."}},
[11796] = {"Protocole d'urgence : section 8.2, paragraphe D", nil, {"Iggy « la Vrille » Virouage, dans les Bassins Brûlants, vous demande de détruire l'Épave à l'est, l'Épave au sud et l'Épave au nord-ouest."}},
[11797] = {"Le siège", nil, {"Le sergent Martèlemont vous demande de tuer 6 Rampants des cryptes dans les Sables de Nasam à l'extérieur du donjon de la Bravoure."}},
[11798] = {"Le maître des rouages", nil, {"Trouvez le Manuel du Maître des rouages Mécazod puis rapportez sa tête à Spumelevier Pleingaz."}},
