[12411] = {"Le serment d'une sœur", nil, {"Cherchez Anya dans la Tanière des Hurlesoir près de Solstice."}},
[12412] = {"L'ami de mon ennemi", nil, {"La Conquérante Krenna du Bastion de la Conquête vous demande de tuer 8 Chasseurs de Ruissargent ainsi que leur chef, Vladek, au poste de la Lisière."}},
[12413] = {"L'attaque de Ruissargent", nil, {"La Conquérante Krenna du Bastion de la Conquête vous demande de tuer 8 Défenseurs de Ruissargent dans les Collines de Ruissargent."}},
[12414] = {"Monture guère solide", nil, {"Chevauchez 5 Mustangs des hautes-terres jusqu'à l'Écuyer Percy, au Campement de la brigade de la marche de l'Ouest."}},
[12415] = {"L'homme qui hurlait à l'oreille des chevaux", nil, {"Effrayez 15 Mustangs des hautes-terres puis retournez voir Soulok Furie-des-tempêtes au Camp Oneqwah."}},
[12416] = {"Une vive bataille", nil, {"Aidez le Capitaine Iskandar à tenir les canyons en participant à la mort de 12 Goules algides attaquantes, 8 Geists algides attaquants et 1 Abomination algide attaquante. Retournez voir Ceristrasz lorsque vous aurez terminé."}},
[12417] = {"Retourner à la terre", nil, {"Entrez dans le Sanctuaire draconique rubis par l'une des passes du nord ou du sud et cherchez des Glands de rubis. Utilisez les Glands de rubis sur les cadavres de dragons rouges pour rendre leur corps à la terre. Retournez auprès de Ceristrasz une fois votre tâche accomplie."}},
[12418] = {"À travers les champs de flammes", nil, {"Entrez dans le Sanctuaire draconique rubis par l'une des passes du nord ou du sud, tuez 6 Nécromanciens algides et éliminez la source de la corruption sous le sanctuaire. Retournez voir Ceristrasz une fois votre tâche accomplie."}},
[12419] = {"Le destin du sanctuaire draconique rubis", nil, {"Apportez la Broche de rubis de Dahlia Soletouche à Krasus, dans la Chambre du Conseil régnant au Repos du ver, et informez-le du destin de Dahlia."}},
[12420] = {"Le club de la Bière du mois", nil, {"Apporter le Bulletin d'adhésion du club de la « Bière du mois » à Larkin Tonnebière à la Taverne de Brûlepierre, à Forgefer."}},
[12422] = {"Clémence tactique", nil, {"Apportez la Lettre de grâce à la Trappeuse capturée dans les Collines de Ruissargent."}},
[12423] = {"Le journal de Mikhail", nil, {"Apportez le Journal de Mikhail à la Conquérante Krenna au Bastion de la Conquête."}},
[12424] = {"Gorgonna", nil, {"Apportez le Journal de Mikhail à Gorgonna au Bastion de la Conquête."}},
[12425] = {"Ruuna l'Aveugle", nil, {"Parlez à Ruuna l’Aveugle, au Camp de Ruuna, près de la Passe de Vordrassil."}},
[12427] = {"La fosse des Conquérants : face à un ours !", nil, {"Vainquez Cuiracier dans la fosse des Conquérants, puis parlez avec Vel'jen la Paperasse pour obtenir votre récompense."}},
[12428] = {"La fosse des Conquérants : le furbolg fou !", nil, {"Vainquez Torgg Totem-de-tonnerre dans la fosse des Conquérants, puis parlez avec Vel'jen la Paperasse pour obtenir votre récompense."}},
[12429] = {"La fosse des Conquérants : sang et métal", nil, {"Vainquez Rouillesang dans la fosse des Conquérants, puis parlez avec Vel'jen la Paperasse pour obtenir votre récompense."}},
[12430] = {"La fosse des Conquérants : mort probable", nil, {"Vainquez Horgrenn Tranchenfer dans la fosse des Conquérants, puis parlez avec Vel'jen la Paperasse pour obtenir votre récompense."}},
[12431] = {"La fosse des Conquérants : l'affrontement final", nil, {"Vainquez Krenna dans la fosse des Conquérants."}},
[12432] = {"À cheval sur la fusée…", nil, {"Le Général Gorlok, du phare de la Baie de la KapitalRisk, veut que vous détruisiez le Transport de bois de l’Alliance.\n\nLe général Gorlok n'est présent au phare que si la Horde contrôle la baie."}},
[12433] = {"La chasse au dissolvant", nil, {"Gurtor, de la base de la Horde de la Baie de la KapitalRisk, veut que vous lui rapportiez une boîte d’Élément 115."}},
[12434] = {"Le retour de la chasse au dissolvant", nil, {"Conditions :"}},
[12435] = {"Rapport au seigneur Devrestrasz", nil, {"Parlez avec le seigneur Devrestrasz au Temple du Repos du ver."}},
[12436] = {"Un revenu supplémentaire", nil, {"L’Approvisionneur Lorkran, du Bastion de la Conquête, veut que vous récupériez 5 morceaux de Venaison succulente sur les Cerfs haute-ramure des alentours."}},
[12437] = {"À cheval sur la fusée…", nil, {"Le Commandant Howser, du phare de la Baie de la KapitalRisk, veut que vous couliez le chargement de bois de la Horde.\n\nLe Commandant Howser n'est présent au phare que lorsque celui-ci est aux mains de l’Alliance."}},
[12438] = {"On recherche : Kreug le Parjure", nil, {"Tuez Kreug le Parjure, puis allez voir le Généralissime Leoric von Zeldig au Donjon de Garde-hiver dans la Désolation des dragons pour toucher votre récompense."}},
[12439] = {"Une perturbation à l'ouest", nil, {"Parlez à Rodney Wells au Donjon de Garde-hiver pour prendre un vol à destination du Repos des étoiles, dans l’ouest de la Désolation des dragons."}},
[12440] = {"En route pour le Repos des étoiles !", nil, {"Parlez à l’Image de l’archimage Modera, au Repos des étoiles, dans l’ouest de la Désolation des dragons."}},
[12441] = {"On recherche : le Grand chaman Sangrepatte", nil, {"Tuez le Grand chaman Sangrepatte, puis allez voir le Généralissime Leoric von Zeldig au Donjon de Garde-hiver dans la Désolation des dragons pour toucher votre récompense."}},
[12442] = {"On recherche : le commandant de l'Assaut Iustus", nil, {"Tuez le Commandant de l'Assaut Iustus, puis allez voir le Généralissime Leoric von Zeldig au Donjon de Garde-hiver dans la Désolation des dragons pour toucher votre récompense."}},
[12443] = {"La chasse au dissolvant", nil, {"Barbillonneur, de la base de l'Alliance de la Baie de la KapitalRisk, veut que vous lui rapportiez une boîte d’Élément 115."}},
[12444] = {"Escarmouches sur la rivière Noire", nil, {"Le Capitaine des éclaireurs Carter veut que vous tuiez 10 unités ou personnages-joueurs de la Horde au Camp de bûcherons de la rivière Noire."}},
[12446] = {"Le retour de la chasse au dissolvant", nil, {"Conditions :"}},
[12447] = {"Le Sanctuaire draconique obsidien", nil, {"Allez au Sanctuaire draconique obsidien au nord-ouest du Repos du ver et juste à l'ouest du Portail du Courroux. Retrouvez-y Sérinar et proposez-lui votre aide."}},
[12448] = {"Bataille ardente", nil, {"Aidez le Capitaine Drayzen à tenir les canyons en participant à la mort de 12 Goules algides attaquantes, 8 Geists algides attaquants et 1 Abomination algide attaquante. Retournez auprès de Vargastrasz une fois cette tâche terminée."}},
[12449] = {"Retourner à la terre", nil, {"Pénétrez dans le Sanctuaire draconique rubis par le passage sud et cherchez les Glands de rubis. Utilisez les Glands de rubis sur les dragons rouges décédés pour ramener leur corps à la vie. Retournez auprès de Vargastrasz une fois la tâche accomplie."}},
[12450] = {"À travers les champs de flammes", nil, {"Entrez dans le Sanctuaire draconique rubis par la passe du sud, tuez 6 Nécromanciens algides et éliminez la source de la corruption sous le sanctuaire. Retournez voir Vargastrasz une fois votre tâche accomplie."}},
[12451] = {"En route pour le camp Oneqwah", nil, {"Parlez à l'Éclaireur Vor'takh au Camp Oneqwah."}},
[12452] = {"zzOLD The Fate of the Ruby Dragonshrine", nil, {"Apportez la Broche de rubis de Dahlia à Ceristrasz, et racontez-lui son destin."}},
[12453] = {"Vu d'en haut", nil, {"La Voyante des vents Corne-grise veut que vous utilisiez la Plume argentée sur 6 Aigles impériaux dans les Grisonnes."}},
[12454] = {"Le cycle de la vie", nil, {"Nishera la Gardienne du jardin au Sanctuaire draconique émeraude vous a demandé de tuer 5 Serres-du-ciel émeraudes."}},
[12455] = {"Dispersées aux quatre vents", nil, {"Ambo Cash, du Mausolée de Garde-hiver dans la Désolation des dragons, veut que vous récupériez 8 Caisses de munitions de Garde-hiver."}},
[12456] = {"La plume d'Alystros", nil, {"Nishera la Gardienne du jardin au Sanctuaire draconique émeraude vous a demandé d'obtenir La plume d'Alystros."}},
[12457] = {"Votre amie, la mitrailleuse", nil, {"Ambo Cash, du Mausolée de Garde-hiver dans la Désolation des dragons, veut que vous sauviez 8 Soldats blessés de la 7e Légion."}},
[12458] = {"Des graines de flagellant", nil, {"Le Seigneur Itharius, au sommet du Temple du Repos du ver, vous a demandé de lui rapporter 3 Graines de flagellant."}},
[12459] = {"Ce qui peut donner la vie peut également la reprendre", nil, {"Le Seigneur Itharius, au sommet du Temple du Repos du ver, vous a demandé de détruire un Wyrm de givre réanimé affaibli, Turgid l'Infâme affaibli et le Surveillant Miremort affaibli."}},
[12460] = {"Rapport au sanctuaire draconique rubis", nil, {"Allez faire votre rapport à Ceristrasz, sur la crête à l'est du Sanctuaire draconique rubis, à l'ouest du Repos du ver."}},
[12461] = {"Rapport au sanctuaire draconique rubis", nil, {"Allez faire votre rapport à Vargastrasz, sur une crête à l'extérieur du Sanctuaire draconique rubis, juste à l'est du Marteau d'Agmar."}},
[12462] = {"Éclatons-nous un peu !", nil, {"Yord « Calamité » Barbeglace, au Mausolée de Garde-hiver dans la Désolation des dragons, veut que vous tuiez le Nécro-seigneur X'avius, le Nécro-seigneur Horus et 15 membres du Fléau de Naxxramas."}},
[12463] = {"Il faut trouver Barbapille !", nil, {"Ambo Cash, au Mausolée de Garde-hiver dans la Désolation des dragons, vous a demandé de retrouver Barbapille."}},
[12464] = {"Ma vieille ennemie", nil, {"Rapportez la tête du Grand général au Commandant Eligor Portelaube au Donjon de Garde-hiver."}},
[12465] = {"Le journal de Barbapille", nil, {"Récupérez les pages 4, 5, 6 et 7 du journal de Barbapille et donnez-les à Ambo Cash au Mausolée de Garde-hiver dans la Désolation des dragons."}},
[12466] = {"À la poursuite de Foudreglace : le front de la 7e Légion", nil, {"Présentez-vous au Commandant de la Légion Tyralion au Front de la 7e Légion au centre de la Désolation des dragons."}},
[12467] = {"À la poursuite de Foudreglace : le phylactère de Thel'zan", nil, {"Reprenez le Phylactère de Thel'zan à Foudreglace et rapportez-le au Haut-commandant Halford Verroctone au Donjon de Garde-hiver."}},
[12468] = {"Les exigences de la conquérante", nil, {"Parlez au Sergent Nazgrim au Bastion de la Conquête."}},
[12469] = {"Retour à l'envoyeur", nil, {"Suivez la crête vers l'est et livrez le paquet d'explosifs à Xink au Poste de Rouilletuyère dans le nord de la Désolation des dragons."}},
[12470] = {"Mystère de l'infini", nil, {"Chromie, au sommet du Temple du Repos du ver, vous a demandé de vous rendre au Sanctuaire draconique bronze. Une fois à l'intérieur, posez le Sablier de l'Éternité au sol.\n\nProtégez le sablier le temps qu'il se vide, puis retournez la voir avec toute information que vous aurez obtenue."}},
[12471] = {"La cruauté des kvaldirs", nil, {"Parlez à Karuk du Prisonnier rohart capturé."}},
[12472] = {"Finalité", nil, {"Apportez le Phylactère de Thel'zan au commandant de la Légion Yorik à l'intérieur du Mausolée de Garde-hiver de la Désolation des dragons."}},
[12473] = {"Une fin et un début", nil, {"Vainquez Thel'zan le Portebrune et faites votre rapport au Haut-commandant Halford Verroctone au Donjon de Garde-hiver dans la Désolation des dragons."}},
[12474] = {"En route pour le bastion Fordragon !", nil, {"Rendez-vous au bastion Fordragon dans la Désolation des dragons et parlez au généralissime Bolvar Fordragon."}},
[12475] = {"Les secrets que cachent les hommes", nil, {"Zelig le Visionnaire au Donjon de Garde-hiver dans la Désolation des dragons veut que vous trouviez la Carte de l’Assaut et que vous la lui rapportiez."}},
[12476] = {"Le retour de la Croisade ?", nil, {"Le Commandant Lynore Frappevent, du Donjon de Garde-hiver dans la Désolation des dragons, veut que vous tuiez 20 membres de l’Assaut écarlate au Camp de base de l’Assaut écarlate."}},
[12477] = {"Le Chemin de la rédemption", nil, {"Remettez un exemplaire du « Chemin de la rédemption » au Haut-commandant Halford Verroctone, au Donjon de Garde-hiver dans la Désolation des dragons."}},
[12478] = {"La caverne de Deuillegivre", nil, {"Zelig le Visionnaire, du donjon de Garde-hiver dans la Désolation des dragons, veut que vous utilisiez l’Orbe de divination de Zelig au fond de la Caverne de Deuillegivre. Découvrez les secrets du passé !"}},
[12479] = {"En route pour le Norfendre !", nil, {"Parlez au Recruteur de l’Alliance au Donjon de la Bravoure, dans la toundra Boréenne ; ou à celui de Valgarde, dans le Fjord Hurlant."}},
[12480] = {"En route pour le Norfendre !", nil, {"Parlez au Recruteur de la Horde du Bastion Chanteguerre, dans la toundra Boréenne, ou à celui de l’Accostage de la Vengeance, dans le Fjord Hurlant."}},
[12481] = {"Retourner le couteau dans la plaie", nil, {"Lisez l’Insulte vrykule à Bjorn Halgurdsson à Torvheim, puis conduisez-le à l’embuscade de Lydell et participez à sa mise à mort."}},
[12482] = {"Contre Nifflevar", nil, {"Le capitaine des forestiers Areiel vous demande d'aller à Nifflevar et d'y tuer 5 Guerriers écorche-dragon, 4 Voyants des runes écorche-dragon et 4 Chiens de chasse écorche-dragon."}},
[12483] = {"Le ragoût de chapeneiges", nil, {"Dasnob, aux Sources de granit, veut que vous récoltiez 5 Chapeneiges chatoyants et 5 Yeux de serpents des glaces dans les Ruines de Drak'Zin, et 5 brins de Réglisse à Zeb'Halak."}},
[12484] = {"Kébab de Fléau", nil, {"Dasnob, des Sources de granit, veut que vous remettiez une Carcasse de troll momifié à Mack Froussen et que vous la jetiez au feu."}},
[12486] = {"À l'avant-poste de Bor'gorok, vite !", nil, {"Allez voir la spirite Grognecroc à l'Avant-poste de Bor'gorok."}},
[12487] = {"Au bastion de la Conquête, mais avec prudence !", nil, {"Présentez-vous à la Conquérante Krenna au Bastion de la Conquête dans les Grisonnes."}},
[12488] = {"Le grand exécuteur a besoin de vous", nil, {"Parlez au Grand exécuteur Ulcéré à Vexevenin, dans la Désolation des dragons."}},
[12489] = {"Bienvenue dans le bassin de Sholazar !", nil, {"Suivez le chemin au nord-ouest pour rejoindre le camp que Total a mentionné."}},
[12490] = {"La vengeance de Veehja", nil, {"Veehja, au sanctuaire des Écailles, vous demande de tuer y 6 Mariniers skadir, 3 Tisse-brume skadir et 2 Lanceurs de runes skadir."}},
[12491] = {"La navre-bière de Navrebière", nil, {"Donnez la Navre-bière de Navrebière à Ipfelkofer Baril-de-fer au camp de la fête des Brasseurs près de Forgefer."}},
[12492] = {"La navre-bière de Navrebière", nil, {"Donnez la navre-bière de Navrebière à Perce Fauchebaril au camp de la fête des Brasseurs près d'Orgrimmar."}},
[12493] = {"Test PvP", nil, {"Tuez 10 méchants."}},
[12495] = {"Une audience auprès de la reine dragon", nil, {"Trouvez Alexstrasza la Lieuse-de-vie au Temple du Repos du ver à la Désolation des dragons."}},
[12496] = {"Une audience auprès de la reine dragon", nil, {"Trouvez Alexstrasza la Lieuse-de-vie au Temple du Repos du ver, à la Désolation des dragons."}},
[12497] = {"Galakrond et le Fléau", nil, {"Parlez à Torastrasza au Temple du Repos du ver de la Désolation des dragons."}},
[12498] = {"Sur des ailes de rubis", nil, {"Détruisez 30 Charognards du désert et récupérez la faux d'Antiok. Retournez auprès d'Alexstrasza la Lieuse-de-vie au Temple du Repos du ver si vous parvenez à accomplir cette tâche."}},
[12499] = {"Retour à Angrathar", nil, {"Parlez au Généralissime Bolvar Fordragon au Bastion Fordragon et rapportez-lui votre victoire contre le Fléau."}},
[12500] = {"Retour à Angrathar", nil, {"Parlez à Saurcroc le Jeune à l'Avant-garde kor'kronne et rapportez-lui votre victoire contre le Fléau."}},
[12501] = {"Patrouille anti-trolls", nil, {"Le Commandant Kunz du Séjour d'Argent vous a demandé de vous rendre aux quatre postes de la Croisade d'argent, de suivre les ordres de leurs capitaines et de revenir ensuite lui faire un rapport.\n\nLe capitaine Brandon et le capitaine Rupert se trouvent à Drak'Sotra, le capitaine Grondel à Drak'Agal, et l'alchimiste Finklestein à Heb'Valok."}},
[12502] = {"Patrouille anti-trolls : hissez les étendards !", nil, {"Le Capitaine Brandon vous a demandé de placer la Bannière de la Croisade d'argent sur les Piédestaux drakkari à Drak'Sotra"}},
[12503] = {"Séjour de paix", nil, {"Le Commandant Falstaav vous a demandé de détruire 10 créatures du Fléau dans la cour du Séjour d'Argent."}},
[12504] = {"Croisade d'argent, parés au départ !", nil, {"Le Sergent Stockemarteau, à l'Autel de Sseratus, vous demande de parler avec 10 de ses Soldats d'argent et de leur dire de revenir au rapport."}},
[12505] = {"De nouveaux ordres pour le sergent Stockemarteau", nil, {"Le Commandant Kunz vous a demandé de remettre les Nouveaux ordres de déploiement au Sergent Stockemarteau à l'Autel de Sseratus."}},
[12506] = {"Ennuis d'autel", nil, {"Le Maléficieur Ubungo, du Séjour d'Argent, vous a demandé d'explorer le bâtiment principal de l'autel à l'Autel de Sseratus."}},
[12507] = {"Étrange mojo", nil, {"Apportez le Mojo étrange au Maléficieur Ubungo au Séjour d'Argent."}},
[12508] = {"Passer la serpillère", nil, {"Le Caporal Maga, à l'Autel de Sseratus, veut que vous tuiez 10 fidèles de Sseratus parmi les Champions de Sseratus, les Prêtres de Sseratus et les Charmeurs de serpents drakkari."}},
[12509] = {"Patrouille anti-trolls : un peu de courage !", nil, {"Le Capitaine Rupert, à Drak'Sotra, vous demande de redonner du courage aux Recrues de la Croisade."}},
[12510] = {"Des fluides élémentaires précieux", nil, {"Le Maléficieur Ubungo, au Séjour d'Argent, veut que vous obteniez 3 Fluides élémentaires précieux."}},
[12511] = {"Les Grisonnes ont besoin de nous", nil, {"Présentez-vous au Lieutenant Dumont, au Gîte Ambrepin, dans l'ouest des Grisonnes."}},
[12512] = {"Ne laissez personne derrière vous", nil, {"Le Dr Rogers à Drak'Sotra veut que vous trouviez le Croisé Lamoof, le Croisé Jonathan et la Croisée Joséphine. Utilisez le Bandage de croisé sur eux, puis mettez-les en sécurité auprès du Dr Rogers."}},
[12513] = {"Un joli chapeau...", nil, {"Don Carlos vous a, par inadvertance, chargé de défier et vaincre son jeune alter ego de la Hautebrande d'antan. Lorsque ce sera fait, apportez le Chapeau de don Carlos à Don Carlos, à Tanaris, comme preuve de votre victoire."}},
[12514] = {"Un cocktail aux champignons", nil, {"Récupérez 10 Champignons chapebrouilles et rapportez-les au Maléficieur Ubungo au Séjour d'Argent."}},
[12515] = {"Un joli chapeau...", nil, {"Don Carlos vous a, par inadvertance, chargé de défier et vaincre son jeune alter ego de la Hautebrande d'antan. Lorsque ce sera fait, apportez le Chapeau de don Carlos à Don Carlos, à Tanaris, comme preuve de votre victoire."}},
[12516] = {"Il ne faut pas abuser des bonnes choses", nil, {"Tuez le Prophète troublé de Sseratus et retournez auprès du Maléficieur Ubungo au Séjour d'Argent."}},
[12517] = {"Suite de Voleurs", nil, {"Utilisez la Suite de Voleurs pour invoquer une Diseuse de bonne aventure de la foire de Sombrelune et donnez-lui la suite."}},
[12518] = {"Suite de Mages", nil, {"Utilisez la Suite de Mages pour invoquer une Diseuse de bonne aventure de la foire de Sombrelune et donnez-lui la suite."}},
[12519] = {"Patrouille anti-trolls : vous voulez quoi ? Une médaille ?", nil, {"Le Capitaine Grondel à l'extérieur du Colisée vous demande de récupérer 7 Médailles drakkari sur les Cadavres drakkari des alentours."}},
[12520] = {"La maîtrise des rhinos : l'épreuve", nil, {"Tuez 15 Rhinocéros cornéclats et retournez voir Hemet Nesingwary au Camp de base de Nesingwary."}},
[12521] = {"Où est passé Hemet Nesingwary ?", nil, {"Parlez à l'Archimage Pentarus quand vous serez prêt à partir pour le Bassin de Sholazar. Une fois sur place, parlez à Total Tirocanon à la Mangrove de la Croissance sauvage, dans le Bassin de Sholazar."}},
[12522] = {"Besoin d'un moteur ? Servez-vous !", nil, {"Weslex Viveclef, au Camp de base de Nesingwary, veut que vous lui rapportiez un Moteur de machine volante."}},
[12523] = {"Pièces détachées à gogo", nil, {"Apportez 7 Pièces détachées de la KapitalRisk à Weslex Viveclef au Camp de base de Nesingwary."}},
[12524] = {"La mésaventure de la KapitalRisk", nil, {"Debaar, au Camp de base de Nesingwary, veut que vous tuiez 15 membres de la KapitalRisk. Vous pouvez tuer des Forbans de la KapitalRisk ou des Excavateurs de la KapitalRisk, au choix."}},
[12525] = {"Effacez-moi ce sourire de son visage", nil, {"Tuez le Contremaître Ricarnaque et Tourte-au-rôt, puis retournez auprès de Debaar au Camp de base de Nesingwary."}},
[12526] = {"La maîtrise des rhinos : la traque", nil, {"Parlez à l'Oracle Soo-fiett aux Os de Nozronn."}},
[12527] = {"Rôdeurs gloutons", nil, {"Apportez 5 Cristaux de basilic à l'Alchimiste Finklestein dans le sanctuaire de Heb'Valok."}},
[12528] = {"Jouer le jeu", nil, {"Présentez-vous au Haut-chaman Rakjak à la Colline de Frénécœur en tant que nouvel « esclave »."}},
[12529] = {"L'esclave du chasseur de singes", nil, {"Tuez 8 Fourrageurs poings-durs et 6 Fonceurs poings-durs pour le Haut-chaman Rakjak à la Colline de Frénécœur.\n\nSi vous perdez les Chaînes de Goregek, parlez à l'Ancien Harkek sur la Colline de Frénécœur."}},
[12530] = {"Asticoter les Poings-doux", nil, {"Goregek vous a donné un Asticoteur à poings-doux. Utilisez-le pour piquer les poings-doux jusqu'à ce que la Matriarche poing-dur sorte de sa cachette.\n\nTuez la Matriarche poing-dur, et retournez ensuite voir le Haut-chaman Rakjak à la Colline de Frénécœur.\n\nSi vous perdez les Chaînes de Goregek, parlez à l'Ancien Harkek à la Colline de Frénécœur."}},
[12531] = {"La menace souterraine", nil, {"Trouvez Serfex dans les sables de la rivière sud, tuez-le et rapportez sa Griffe au Haut-chaman Rakjak à la Colline de Frénécœur.\n\nSi vous perdez la Craie usée de Dajik, allez parler à l'Ancien Harkek à la Colline de Frénécœur."}},
[12532] = {"Il y a un couac !", nil, {"Capturez 12 Poulets évadés pour l'Ancien Harkek.\n\nSi vous perdez la Craie usée de Dajik, allez parler à l'Ancien Harkek à la Colline de Frénécœur."}},
[12533] = {"L'apprenti du Chasse-guêpe", nil, {"Tuez 6 Guêpes de la ruche Saphir et 9 Bourdons de la ruche Saphir pour le Haut-chaman Rakjak à la colline de Frénécœur.\n\nSi vous perdez la Craie usée de Dajik, allez parler à l'Ancien Harkek sur la Colline de Frénécœur."}},
[12534] = {"La reine de saphir", nil, {"Rapportez le Dard de la reine de saphir au Haut-chaman Rakjak à la Colline de Frénécœur.\n\nSi vous perdez la Craie usée de Dajik, allez parler à l'Ancien Harkek à la Colline de Frénécœur."}},
[12535] = {"Des bêtises", nil, {"Récupérez 8 Grappes de cristaux pour le Haut-chaman Rakjak à la Colline de Frénécœur."}},
[12536] = {"Une dure chevauchée", nil, {"Chevauchez le Crocilisque captif jusqu'au Refuge de Murmebrume et parlez ensuite à Zepik pour obtenir de nouvelles instructions.\n\nSi vous perdez le Cor de chasse de Zepik, allez parler à l'Ancien Harkek à la Colline de Frénécœur."}},
[12537] = {"La foudre frappe vraiment deux fois", nil, {"Détruisez les Grappes de cristaux des Confins-du-ciel, en face du Sanctuaire du temps Murmebrume, pour le compte de Zepik.\n\nSi vous perdez le Cor de chasse de Zepik, allez parler à l'Ancien Harkek à la Colline de Frénécœur."}},
[12538] = {"La brume n'écoute pas", nil, {"Tuez 12 Gorlocs murmebrumes pour Zepik.\n\nSi vous perdez le Cor de chasse de Zepik, allez parler à l'Ancien Harkek à la Colline de Frénécœur."}},
[12539] = {"À pattes", nil, {"Retrouvez votre chemin jusqu'à la Colline de Frénécœur, puis parlez au Haut-chaman Rakjak."}},
[12540] = {"Je n'ai fait que suivre les ordres…", nil, {"Trouvez un Oracle parlepluie blessé pour le Haut-chaman Rakjak à la Colline de Frénécœur."}},
[12541] = {"Patrouille anti-trolls : l'apprenti alchimiste", nil, {"L'Alchimiste Finklestein de Heb'Valok vous demande de l'aider dans la fabrication d'un sérum de vérité.\n\nQuand vous serez prêt à commencer, allez lui parler et suivez ses instructions."}},
[12542] = {"L'appel de la Croisade", nil, {"Faites votre rapport au Croisé Valus à la Confiance de Lumière, dans la Désolation des dragons."}},
[12543] = {"Une offrande à Soo-fiett", nil, {"Rapportez 5 Crocs de cobras intacts à l'Oracle Soo-fiett, près des Os de Nozronn."}},
[12544] = {"Les os de Nozronn", nil, {"Utilisez l'encens de Soo-fiett près des Os de Nozronn, puis retournez voir Hemet Nesingwary au camp de base de Nesingwary avec l'information obtenue."}},
[12545] = {"La purification de Jintha'kalar", nil, {"Le Croisé Valus de la Confiance de Lumière, à la Désolation des dragons, vous a demandé de tuer 15 membres du Fléau de Jintha'kalar."}},
[12546] = {"La reconquête", nil, {"Allez à l'Avalanche dans le Bassin de Sholazar et utilisez la Rune Oméga pour déployer l'Etymidian. Utilisez-le pour tuer 200 Serviteurs du Fléau, Bythius le Sculpte-chair, Urgreth des Mille Tombeaux et Morguegrêle.\n\nParlez à l'Avatar de Freya dans le Bassin de Sholazar quand vous aurez accompli cette tâche."}},
[12547] = {"La rune d'activation", nil, {"Obtenez la Rune Oméga à l'intérieur de la Terrasse du Façonneur et rapportez-la à Etymidian."}},
[12548] = {"L'Etymidian", nil, {"Traversez le Portail à l'extrémité sud du Bassin de Sholazar et cherchez l'arme connue sous le nom de l'Etymidian qui se trouve de l'autre côté."}},
[12549] = {"La maîtrise des sabres-d'effroi : devenir un prédateur", nil, {"Tuez 15 Sabres-d'effroi et retournez voir Buck Bellamble au Camp de base de Nesingwary."}},
[12550] = {"La maîtrise des sabres-d'effroi : traquer sa proie", nil, {"Buck Bellamble, au Camp de base de Nesingwary, vous a demandé d'identifier 3 Empreintes de Shango parmi les empreintes de sabres-d'effroi, près du lac de Flot-amer."}},
[12551] = {"La maîtrise des crocilisques : l'épreuve", nil, {"Tuez 15 Crocilisques de la mangrove et retournez voir Debaar au Camp de base de Nesingwary."}},
[12552] = {"Mort aux nécromagi", nil, {"Le Sergent Tesson-de-lune à Drak'Sotra vous a demandé de détruire 8 Nécromagi hath'ar."}},
[12553] = {"Filières de frôleurs", nil, {"La Spécialiste Roudenté à Drak'Sotra vous a demandé de récupérer 5 Filières de frôleurs intactes."}},
[12554] = {"Malas le Corrupteur", nil, {"Le Sergent Tesson-de-lune à Drak'Sotra vous a demandé de revenir avec la Tête du Corrupteur."}},
[12555] = {"Un écheveau enchevêtré", nil, {"La Spécialiste Roudenté à Drak'Sotra vous a demandé d'utiliser le Lance-écheveau enchevêtré pour détruire 5 Pulvérisateurs de peste."}},
[12556] = {"La maîtrise des rhinos : la mise à mort", nil, {"Rapportez la Corne de Farunn à Hemet Nesingwary au Camp de base de Nesingwary."}},
[12557] = {"Un travail de laboratoire", nil, {"L'Alchimiste Finklestein à Heb'Valok veut que vous lui rapportiez un Asticot fangeux, une Aile de chauve-souris desséchée, une Ambregraine et un peu de Mucus de serpent glacé dans son laboratoire."}},
[12558] = {"La maîtrise des sabres-d'effroi : prêt à bondir", nil, {"Rapportez la Peau de Shango à Buck Bellamble au Camp de base de Nesingwary."}},
[12559] = {"Activation du portail d'accès : le perchoir du Faiseur", nil, {"L'Avatar de Freya, aux Rapides Parlepluie, veut que vous alliez au Perchoir du Faiseur et que vous utilisiez l'Interrupteur d'activation Gamma."}},
[12560] = {"La maîtrise des crocilisques : le plan", nil, {"Rapportez 5 feuilles de Fougère des sables à Debaar, au Camp de base de Nesingwary."}},
[12561] = {"Un problème de confiance", nil, {"Obtenez la confiance de Freya en détruisant 6 Cadavres chancreux et 10 Ravageurs de la faucheuse d'os dans les Terres Perdues."}},
[12562] = {"Pas besoin que les Drakkari aient des élémentaires d'eau !", nil, {"Le Sous-lieutenant Jax au Séjour d'Argent vous a demandé de tuer 10 Lieurs d'eau drakkari."}},
[12563] = {"Patrouille anti-trolls", nil, {"Le Commandant Kunz du Séjour d'Argent vous a demandé de vous rendre aux quatre postes de la Croisade d'argent, de suivre les ordres de leurs capitaines puis de revenir lui faire votre rapport.\n\nLe Capitaine Brandon et le Capitaine Rupert sont en poste à Drak'Sotra, le Capitaine Grondel à Drak'Agal, et l'Alchimiste Finklestein à Heb'Valok."}},
[12564] = {"Patrouille anti-trolls : un remède contre la douleur", nil, {"Le Capitaine Brandon de Drak'Sotra vous a demandé de récolter 5 Pavots aquatiques à maturité dans les Champs de Drak'Sotra."}},
[12565] = {"La bénédiction de Zim'Abwa", nil, {"Le Maléficieur Ubungo au Séjour d'Argent veut que vous présentiez 10 Offrandes drakkari à la statue de Zim'Abwa."}},
[12566] = {"De l'aide pour le camp Sabot-d'hiver", nil, {"Parler au Chef Totem-de-frêne au Camp Sabot-d'hiver."}},
[12567] = {"Bénédiction de Zim'Abwa", nil, {"Conditions :"}},
[12568] = {"Patrouille anti-trolls : morts incinérés", nil, {"Le Capitaine Rupert vous a demandé d'utiliser l'Huile incinératrice sur 5 Fantassins d'argent défaits à Drak'Sotra."}},
[12569] = {"La maîtrise des crocilisques : l'embuscade", nil, {"Rapportez la Mâchoire de Broussard à Debaar, au Camp de base de Nesingwary."}},
[12570] = {"Un heureux malentendu", nil, {"Escortez l'Oracle parlepluie blessé jusqu'à la Canopée Parlepluie et parlez au Haut-oracle Soo-pape."}},
[12571] = {"Faire partir le méchant serpent", nil, {"Le Haut-oracle Soo-pape à la Canopée Parlepluie vous a demandé de tuer Pointevenin et 10 Cobras empereurs.\n\nSi vous perdez le Sac d'insectes de Lafoo, allez parler au Haut-oracle Soo-pape à la Canopée Parlepluie."}},
[12572] = {"Les dieux aiment le qui-brille", nil, {"Récupérez 6 des trésors déterrés par Lafoo et rapportez-les au Haut-oracle Soo-pape à la Canopée Parlepluie.\n\nSi vous perdez le Sac d'insectes de Lafoo, allez parler au Haut-oracle Soo-pape à la Canopée Parlepluie."}},
[12573] = {"Faire la paix", nil, {"Le Haut-oracle Soo-pape à la Canopée Parlepluie vous a demandé de donner l'Offrande de paix au Chaman Vekjik, puis de revenir lui donner sa réponse.\n\nSi vous perdez le Sac d'insectes de Lafoo, parlez au Haut-oracle Soo-pape à la Canopée Parlepluie."}},
[12574] = {"Déjà de retour ?", nil, {"Rendez-vous au Refuge de Murmebrume et parlez au Mandebrume Soo-flet.\n\nSi vous perdez le Cristal favori de Jaloot, allez parler au Haut-oracle Soo-pape à la Canopée Parlepluie."}},
[12575] = {"Le trésor perdu de Murmebrume", nil, {"Tuez le Seigneur de guerre Tartek, puis rapportez le Trésor de Murmebrume au Mandebrume Soo-flet au Refuge de Murmebrume.\n\nSi vous perdez le Cristal favori de Jaloot, parlez au Haut-oracle Soo-pape dans la Canopée Parlepluie."}},
[12576] = {"Nous veut pas…", nil, {"Tuez 8 Porte-épieux frénécœurs et 6 Charognards frénécœurs pour le Mandebrume Soo-flet au Refuge de Murmebrume.\n\nSi vous perdez le Cristal favori de Jaloot, parlez au Haut-oracle Soo-pape dans la Canopée Parlepluie."}},
[12577] = {"Il est temps de rentrer !", nil, {"Retournez à la Canopée Parlepluie."}},
[12578] = {"Le gorloc furieux", nil, {"Rendez-vous à Marchemousse et parlez à Moudel lorsque vous serez arrivé.\n\nSi vous perdez la Balle anti-stress de Moudel, parlez au Haut-oracle Soo-pape dans la Canopée Parlepluie."}},
[12579] = {"Le sang-de-vie du sanctuaire marchemousse", nil, {"Moudel vous a demandé de récolter 10 Éclats sang-de-vie, mais d'éviter de vous rendre dans la grotte à l'intérieur du pilier.\n\nSi vous perdez la Balle anti-stress de Moudel, parlez au Haut-oracle Soo-pape dans la Canopée Parlepluie."}},
[12580] = {"Le sauveur des Marchemousses", nil, {"Moudel vous a demandé de sauver 6 Victimes marchemousses à Marchemousse, même s'il sera sans doute trop tard pour certaines d'entre elles.\n\nSi vous perdez la Balle anti-stress de Moudel, parlez au Haut-oracle Soo-pape dans la Canopée Parlepluie."}},
[12581] = {"Le fardeau d'un héros", nil, {"Tuez Artruis le Sans-cœur.\n\nMoudel ne pourra pas vous accompagner pour cette tâche."}},
[12582] = {"Le champion frénécœur", nil, nil},
[12583] = {"Le pulvérisateur pulvérisé", nil, {"La Spécialiste Roudenté à Drak'Sotra vous a demandé de collecter des Pièces du pulvérisateur de peste."}},
[12584] = {"Le mal à l'état pur", nil, {"Le Capitaine Rupert vous a demandé de collecter 10 Morceaux de saronite et de les apporter à Eitrigg au Séjour d'Argent."}},
[12585] = {"Patrouille anti-trolls : une bonne flambée", nil, {"Le Capitaine Grondel de Drak'Agal vous a demandé de lui rapporter 20 morceaux de Bois d'épineux mort."}},
[12587] = {"Patrouille anti-trolls", nil, {"Le Commandant Kunz du Séjour d'Argent vous a demandé de vous rendre aux quatre postes de la Croisade d'argent, de suivre les ordres de leurs capitaines et de revenir ensuite lui faire votre rapport.\n\nLe Capitaine Brandon et le Capitaine Rupert sont en poste à Drak'Sotra, le Capitaine Grondel est en poste à Drak'Agal, et l'Alchimiste Finklestein est en poste à Heb'Valok."}},
[12588] = {"Patrouille anti-trolls : et que ça creuse !", nil, {"Le Capitaine Brandon, à Drak'Sotra, vous a demandé d'utiliser la Bêche en acier sur les Monticules de terre anciens alentours pour récupérer la Relique drakkari ancienne."}},
[12589] = {"Du recul ? Quel recul ?", nil, {"Avec la Carabine RJR, tirez sur la pomme placée sur le crâne de Wilhelm le Verni, puis parlez à Drostan."}},
[12590] = {"Blahblah [PH]", nil, nil},
[12591] = {"Patrouille anti-trolls : attention en dessous !", nil, {"Le Capitaine Rupert à Drak'Sotra vous a demandé d'utiliser des Grenades à percussion près de 5 Cratères néthurbiens."}},
[12592] = {"Le défi du grand chasseur", nil, {"Tuez 60 têtes de Gibier pour aider Hemet et ses compagnons pendant la chasse. Lorsque vous aurez tué vos 60 proies, retournez voir Drostan au Camp de base de Nesingwary pour comparer vos chiffres."}},
[12593] = {"Au service du roi-liche", nil, {"Présentez-vous à l’instructeur Razuvious au Cœur d'Achérus."}},
[12594] = {"Patrouille anti-trolls : je m'en fiche totalement !", nil, {"Le Capitaine Grondel vous a demandé de tuer 7 Chambardeurs moussus dans Drak'Agal."}},
[12595] = {"À la recherche d'un plus gros gibier", nil, {"Parlez à Dorian Traquedrake, à l'Avant-poste de Dorian, au Bassin de Sholazar."}},
[12596] = {"L'organisation des pays producteurs d'épais trolls", nil, {"Le Commandant Kunz au Séjour d'Argent vous a demandé de vous rendre aux quatre postes de la Croisade d'argent, de suivre les ordres de leurs chefs et de revenir lui faire votre rapport.\n\nLe Capitaine Brandon et le Capitaine Rupert sont en poste à Drak'Sotra, au sud-est.\n\nLe Capitaine Grondel est en poste à Drak'Agal, à l'est.\n\nL'Alchimiste Finklestein est en poste à Heb'Valok, au nord."}},
[12597] = {"Un remède contre la douleur", nil, {"Le Capitaine Brandon de Drak'Sotra vous a demandé de récolter 5 Pavots aquatiques à maturité dans les champs de Drak'Sotra."}},
[12598] = {"Attention en dessous !", nil, {"Le Capitaine Rupert à Drak'Sotra vous a demandé d'utiliser des Grenades à percussion près de 5 Cratères nérubiens."}},
[12599] = {"Une bonne flambée", nil, {"Le Capitaine Grondel de Drak'Agal vous a demandé de lui rapporter 20 morceaux de Bois d'épineux mort."}},
[12601] = {"L'apprenti alchimiste", nil, {"L'Alchimiste Finklestein de Heb'Valok vous demande de l'aider dans la fabrication d'un sérum de vérité.\n\nQuand vous serez prêt à commencer, allez lui parler et suivez ses instructions."}},
[12602] = {"L'apprenti alchimiste", nil, {"L'Alchimiste Finklestein de Heb'Valok vous demande de l'aider dans la fabrication d'un sérum de vérité.\n\nQuand vous serez prêt à commencer, allez lui parler et suivez ses instructions."}},
[12603] = {"Aiguisez vos griffes", nil, {"Tuez 6 Drakes primordiaux puis retournez voir Dorian Traquedrake, à l'Avant-poste de Dorian."}},
[12605] = {"Se procurer l'appât", nil, {"Apportez 6 Jeunes drakes primordiaux à Dorian Traquedrake, à l'Avant-poste de Dorian."}},
[12606] = {"Les cocons, c'est à ça qu'on les reconnaît.", nil, {"Le Capitaine Rupert vous a demandé d'ouvrir les Cocons nérubiens de Drak'Sotra et de revenir le voir quand vous aurez libéré 3 Fantassins emprisonnés."}},
[12607] = {"À pied, à cheval et en mammouth…", nil, {"Ramenez un Mammouth fracasse-défense à Zazoutille, à l'Avant-poste de Dorian."}},
[12608] = {"L'incursion des sectateurs", nil, {"Parlez à l'Avatar de Freya aux Rapides Parlepluie."}},
[12609] = {"Regarnir les étagères", nil, {"Le Capitaine Arnath à Heb'Valok vous a demandé de trouver 7 Ichors d'araignée frais sur les Rampantes des trappes à Zul'Drak."}},
[12610] = {"Leur couper les ailes", nil, {"Le Capitaine Arnath à Heb'Valok vous a demandé de lui trouver 7 Ailes de chauve-souris impeccables provenant de Chauve-souris de Zul'Drak."}},
[12611] = {"Rendu au centuple", nil, {"L'Avatar de Freya vous demande de vaincre Thalgran Portechancre en utilisant le Gardien de Freya pour lui renvoyer ses Traits de mort au centuple."}},
[12612] = {"Le pilier effondré", nil, {"Allez au Pilier Sang-de-vie et cherchez des indices sur les raisons de sa destruction."}},
[12613] = {"Activation du portail d'accès : le surplomb du Faiseur", nil, {"L'avatar de Freya vous demande de vous diriger vers le Surplomb du Faiseur, à l'est du Bassin de Sholazar, et d'utiliser l'Interrupteur d'activation Thêta."}},
[12614] = {"Agression post-partum", nil, {"Tuez la Mère des couvées Slivina et retournez voir Dorian Traquedrake, à l'Avant-poste de Dorian."}},
[12615] = {"La bénédiction de Zim'Torga", nil, {"Le Féticheur Khufu vous a demandé de présenter 10 Offrandes drakkari à la statue de Zim'Torga."}},
[12616] = {"La chambre des secrets", nil, {"L'Émissaire de l'Aube d'argent vous a demandé de fouiller la chambre de Tenris Bourbassang, dans les Quartiers des serviteurs de Karazhan."}},
[12617] = {"Exterminer les intrus", nil, {"L'Avatar de Freya aux Rapides Parlepluie veut que vous alliez au Campement de la Main de Mort, au nord-ouest, et que vous tuiez 8 Sectateurs infiltrés."}},
[12618] = {"Bénédiction de Zim'Torga", nil, {"Conditions :"}},
[12619] = {"La lame runique blasonnée", nil, {"Cherchez une Épée détériorée sur les râteliers d'armes du Fort d'Ébène et utilisez-la sur une Forgerune pour créer une Épée à lame runique.\n\nRemettez l'Épée à lame runique à l'Instructeur Razuvious au Fort d'Ébène."}},
[12620] = {"Le courroux de la Gardienne de la vie.", nil, {"L'Avatar de Freya, aux Rapides Parlepluie, veut que vous utilisiez le Cor de Freya au sommet du Pilier Scintillant."}},
[12621] = {"Le pacte de Freya", nil, {"Écoutez ce que l'Avatar de Freya veut vous dire."}},
[12622] = {"Les chefs de Jin'Alai", nil, {"Le Scalpeur Ahunae à Zim'Torga vous a demandé de récupérer les Trésors de Kutube'sa, de Gawanil et de Chulo le Fol."}},
[12623] = {"Rendez-vous chez le féticheur", nil, {"Parlez au Féticheur Khufu à Zim'Torga."}},
[12624] = {"Il pourrait être n'importe où !", nil, {"Recherchez l'Anneau de fiançailles en or sur les différentes créatures du Bassin de Sholazar."}},
[12625] = {"Dominer Achérus", nil, {"Présentez-vous au Maître poliorcète Corvus à l'Étage de la garnison du Fort d'Ébène."}},
[12626] = {"La compagnie des damnés", nil, {"Remettre le Rapport de Corvus au Commandant du Fléau Thalanor à l'Étage de la garnison du Fort d'Ébène."}},
[12627] = {"Interférences à Jin'Alai", nil, {"Le Féticheur Khufu à Zim'Torga vous a demandé de perturber le Chaudron bleu, le Chaudron vert, le Chaudron violet et le Chaudron rouge."}},
[12628] = {"Une discussion avec Har'koa", nil, {"Parlez à Har'koa à l'Autel de Har'koa."}},
[12629] = {"Oh, vous pouvez courir…", nil, {"Stefan au Guet d'Ébène vous a demandé d'aller au Reliquaire d'agonie et de récupérer 5 Entrailles d'abominations putrescentes sur des Abominations putrescentes et 5 Baves de goule collantes."}},
[12630] = {"L'engeance tous risques", nil, {"Stefan, du Guet d'Ébène, vous a demandé d'utiliser Nass pour recueillir 10 Échantillons de cheveux de Trolls flétris.\n\nSi vous perdez Nass, retournez voir Stefan pour le récupérer."}},
[12631] = {"Une invitation, en quelque sorte…", nil, {"Apportez le Sautoir non-vivant à Stefan au Guet d'Ébène."}},
[12632] = {"Ma progéniture d'abord", nil, {"Har'koa vous a demandé d'utiliser un poil de sa moustache sur les corps de ses enfants pour les libérer et les ramener à la vie. Retournez la voir à l'Autel de Har'koa une fois que ce sera fait."}},
[12633] = {"L'appel des ténèbres", nil, {"Apportez le Sautoir putride à Stefan au Guet d'Ébène."}},
[12634] = {"Certains font de la limonade, d'autres de la liqueur", nil, {"Rapportez 1 Orange, 2 Régimes de bananes et 1 Papaye à Tristevin Tonnebière au Camp de base de Nesingwary."}},
[12635] = {"Les reliques de la déesse léopard des neiges", nil, {"Le Chroniqueur To'kini à Zim'Torga vous demande de récupérer 10 Reliques har'koanes."}},
[12636] = {"L'œil d'Achérus", nil, {"Présentez-vous au Roi-liche dans le Fort d'Ébène."}},
[12637] = {"Manqué de justesse", nil, {"Stefan au Guet d'Ébène vous demande d'apporter le Sautoir non-vivant à Datura Rose-sang."}},
[12638] = {"Pas passé loin", nil, {"Stefan au Guet d'Ébène vous demande d'apporter le Sautoir putride à Datura Rose-sang."}},
[12639] = {"Les terres gelées", nil, {"La Dompteuse des éléments Dagoda à Zim'Torga veut que vous collectiez 7 Essences de terre gelée."}},
[12640] = {"Sceller les failles", nil, {"Le Scalpeur Ahunae à Zim'Torga vous a demandé de sceller 7 Failles élémentaires."}},
[12641] = {"La mort vient de l'œil", nil, {"Le Roi-liche vous ordonne d'utiliser l'Œil d'Achérus pour analyser le Fort écarlate, la Forge de la Nouvelle-Avalon, l'Hôtel de ville de la Nouvelle-Avalon et la Chapelle de la Flamme cramoisie.\n\nContrôlez l'Œil d'Achérus en utilisant le mécanisme de contrôle de l'Œil d'Achérus."}},
[12642] = {"L'esprit de Rhunok", nil, {"Parler avec l'Esprit de Rhunok à l'Autel de Rhunok."}},
[12643] = {"Le côté positif", nil, {"Stefan au Guet d'Ébène vous a demandé d'aller au Reliquaire d'agonie pour récupérer 5 Entrailles d'abominations putrescentes sur les Abominations putrescentes et 5 Baves de goules collantes."}},
[12644] = {"Une recette alambiquée", nil, {"Menez le procédé de distillation à son terme et rapportez le Punch de la jungle de Tonnebière à Tristevin Tonnebière, au Camp de base de Nesingwary."}},
[12645] = {"Le test de dégustation", nil, {"Proposez un Échantillon de punch de la jungle de Tonnebière à Hemet Nesingwary, Hadrius Harlowe et Tamara Tremblerouage, puis retournez voir Tristevin Tonnebière au Camp de base de Nesingwary."}},
[12646] = {"Mon prophète, mon ennemi", nil, {"L'Esprit de Rhunok à l'Autel de Rhunok vous a demandé de récupérer son Mojo du dieu ours arctique."}},
[12647] = {"La fin de la souffrance", nil, {"L'Esprit de Rhunok vous a demandé d'utiliser l'Encens du tourmenteur pour réveiller son corps. Vous devez ensuite tuer Rhunok en utilisant le pouvoir que cet esprit vous a donné.\n\nSi vous perdez le Mojo de Rhunok, retournez voir l'Esprit de Rhunok."}},
[12648] = {"Soirée déguisée", nil, {"Stefan au Guet d'Ébène veut que vous utilisiez le Sautoir ensorcelé pour appliquer le déguisement du Fléau.\n\nAinsi déguisé, vous devez acheter du Plasma amer à Carnentraille.\n\nSi vous perdez le Sautoir ensorcelé, retournez voir Stefan au Guet d'Ébène."}},
[12649] = {"En costume !", nil, {"Stefan au Guet d'Ébène veut que vous utilisiez le Sautoir ensorcelé pour appliquer le Déguisement du Fléau.\n\nAinsi déguisé, vous devez acheter du Plasma amer à Carnentraille.\n\nSi vous perdez le Sautoir ensorcelé, retournez voir Stefan au Guet d'Ébène."}},
[12650] = {"Se piller soi-même", nil, {"Le Chroniqueur To'kini à Zim'Torga vous demande de récupérer 7 Reliques rhunokiennes."}},
[12651] = {"Le terrain d'atterrissage de Rive-du-lac", nil, {"Parlez à Tamara Tremblerouage au Terrain d'atterrissage de Rive-du-lac."}},
[12652] = {"La goule au pot", nil, {"Carnentraille au Reliquaire d'agonie veut que vous utilisiez la Fracassée de boyaux à la cervelle près de 10 Goules putréfiées."}},
[12653] = {"Retour auprès de Har'koa", nil, {"Parlez à Har'koa à l'Autel de Har'koa."}},
[12654] = {"La chasseresse à temps partiel", nil, {"Rapportez les Restes de Poix à Tamara Tremblerouage au Terrain d'atterrissage de Rive-du-lac."}},
[12655] = {"La bénédiction de Zim'Rhuk", nil, {"Le Féticheur Khufu vous a demandé de présenter 10 Offrandes drakkari à la statue de Zim'Rhuk."}},
[12656] = {"Bénédiction de Zim'Rhuk", nil, {"Conditions :"}},
[12657] = {"La puissance du Fléau", nil, {"Le Roi-liche vous ordonne d'apporter ses ordres au Généralissime Darion Mograine au Fort d'Ébène.\n\nUtilisez le transporteur lumineux derrière le roi-liche pour vous rendre auprès du Généralissime Darion Mograine dans la Salle de commandement."}},
[12658] = {"Mon roc familier", nil, {"Rapportez 7 Œufs de roc à Zazoutille, à l'Avant-poste de Dorian."}},
[12659] = {"Des scalps !", nil, {"Le Scalpeur Ahunae à Zim'Torga vous demande d'utiliser le Couteau d'Ahunae pour scalper 10 Trolls de Heb'Drakkar.\n\nLe Scalpeur vous rappelle que vous devez d'abord récupérer le butin qu'ils ont sur eux."}},
[12660] = {"Les armes de destruction", nil, {"L'Avatar de Freya vous demande d'aller au Campement de la Main de Mort, au nord-ouest, pour y détruire 4 Explosifs instables."}},
[12661] = {"Infiltrer Voltarus", nil, {"Stefan veut que vous utilisiez le Sautoir ensorcelé pour pénétrer dans Voltarus et faire tout ce que le Commandant du Fléau sur place vous demande.\n\nSi vous perdez le Sautoir ensorcelé, retournez voir Stefan au Guet d'Ébène."}},
[12662] = {"Heb'Jin toxique", nil, {"La Dompteuse des éléments Dagoda à Zim'Torga vous a demandé de tuer Heb'Jin."}},
[12663] = {"Retrouvailles", nil, {"Drakuru, à Voltarus, veut que vous parliez à Sakassang et que vous visitiez Zul'Drak."}},
[12664] = {"Sombre horizon", nil, {"Drakuru, à Voltarus, veut que vous parliez à Sakassang et que vous visitiez Zul'Drak."}},
[12665] = {"Ça sent les problèmes", nil, {"Har'koa vous a demandé de découvrir ce qui est arrivé à Quetz'lun. Allez lui parler pour pouvoir chevaucher un de ses enfants et vous rendre discrètement à l'Autel de Quetz'lun.\n\nLorsque ce sera fait, retournez la voir à l'Autel de Har'koa."}},
[12666] = {"Préparation pour un voyage au cœur du monde souterrain", nil, {"Har'koa vous demande de récupérer 8 Parures sacrées et de les lui rapporter à l'Autel de Har'koa."}},
[12667] = {"À la recherche de la déesse serpent du vent", nil, {"Recherchez l'Esprit de Quetz'lun à l'Autel de Quetz'lun."}},
[12668] = {"Les bases de la vengeance", nil, {"L'esprit de Quetz'lun vous a demandé de tuer 12 trolls, soit des Adorateurs de Quetz'lun, soit des Berserkers touchés par le serpent, près de ses Fontaines d'âmes.\n\nCe n'est qu'une fois que vous aurez accompli cette tâche que vous pourrez retourner la voir à l'Autel de Quetz'lun."}},
[12669] = {"Pour l'instant, tout va mal", nil, {"Stefan au Guet d'Ébène veut que vous retourniez auprès de Drakuru et que vous accomplissiez les tâches qu'il vous donne.\n\nPendant que vous serez au Reliquaire de souffrance, versez le Diluant dans 5 Chaudrons de chancre.\n\nSi vous perdez le Sautoir ensorcelé, retournez voir Stefan au Guet d'Ébène."}},
[12670] = {"La moisson écarlate", nil, {"Présentez-vous au Prince Valanar à Brèche-de-Mort."}},
[12671] = {"Un vol de reconnaissance", nil, {"Aidez le Pilote Vic à effectuer un Vol de reconnaissance."}},
[12672] = {"Mise en place", nil, {"L'Esprit de Quetz'lun vous a demandé de récupérer 10 Fragments de pouvoir du monde souterrain. Retournez la voir à l'Autel de Quetz'lun une fois la tâche effectuée."}},
[12673] = {"Là-bas tout en bas", nil, {"Le Seigneur Drakuru à Voltarus veut que vous utilisiez le Sceptre de suggestion sur les Geists chancreux et que vous leur ordonniez ensuite de récolter 7 Cristaux chancreux."}},
[12674] = {"L'enfer a une furie", nil, {"L'Esprit de Quetz'lun à l'Autel de Quetz'lun vous a demandé d'utiliser la Badine maléficieuse de Quetz'lun sur le Grand prêtre Mu'funu, la Grande prêtresse Tua-Tua et le Grand prêtre Hawinni. Lorsque ce sera fait, tuez-les."}},
[12675] = {"Une dernière chose", nil, {"Apportez les Fragments de pouvoir du monde souterrain à Har'koa et suppliez-la de vous donner une portion de son esprit. Vous la trouverez à l'Autel de Har'koa."}},
[12676] = {"Sabotage", nil, {"Stefan au Guet d'Ébène vous a demandé d'effectuer une autre tâche pour Drakuru.\n\nDe plus, vous devez utiliser les Charges explosives sur 5 Chariots du Fléau au Reliquaire de souffrance.\n\nSi vous perdez le Sautoir ensorcelé, allez voir Stefan au Guet d'Ébène, il vous en donnera un autre."}},
[12677] = {"Matières dangereuses", nil, {"Stefan au Guet d'Ébène vous a demandé d'aller à Voltarus et d'exécuter les ordres de Drakuru. Une fois sur place, vous devez voler 5 Cristaux chancreux récoltés.\n\nSi vous perdez le Sautoir ensorcelé, retournez voir Stefan au Guet d'Ébène."}},
[12678] = {"Si le chaos règne, laissons la souffrance tenir les rênes", nil, {"Le Prince Valanar à Brèche-de-Mort vous ordonne de tuer 10 Croisés écarlates ou Paysans écarlates et 10 Citoyens de Havre-comté."}},
[12679] = {"Ce soir nous dînons à Havre-comté", nil, {"Orithos l'Assombrisseur des cieux à Brèche-de-Mort veut que vous récupériez 15 Flèches en saronite."}},
[12680] = {"Voleurs de chevaux", nil, {"Salanar le Cavalier vous a demandé de voler un cheval dans l'Écurie de Havre-comté et de le lui ramener à Brèche-de-Mort."}},
[12681] = {"Expert en composants", nil, {"Apportez 5 Griffes de roc déformées à Colvin Norrington, à l'Avant-poste de Dorian."}},
[12683] = {"Une envie brûlante d'aider", nil, {"Colvin Norrington, à l'Avant-poste de Dorian, vous a demandé de récupérer 5 Échantillons de glaires d'hydre et de tuer 5 Hydres Flot-amer."}},
[12684] = {"Le sang d'un dieu mort", nil, {"Har'koa vous a demandé de récupérer 7 doses de Sang de Mam'toth. Rapportez-les-lui à l'Autel de Har'koa."}},
[12685] = {"On récolte ce qu'on sème", nil, {"Tuez le Prophète de Quetz'lun épuisé, puis allez voir le Féticheur Khufu à Zim'Torga."}},
[12686] = {"Tolérance zéro", nil, {"Drakuru à Voltarus veut que vous alliez au Reliquaire de souffrance et que vous utilisiez le Sceptre de renforcement sur un Serviteur de Drakuru. Vous devez alors utiliser ses capacités pour battre Darmuk."}},
[12687] = {"Dans le royaume des ombres", nil, {"Salanar le Cavalier, à Brèche-de-Mort, vous a mis au défi de tuer le Cavalier noir d'Achérus et de lui prendre son Destrier de la mort d'Achérus.\n\nSi vous réussissez, ramenez le Destrier de la mort d'Achérus à Brèche-de Mort et utilisez l'Appel du cavalier pour invoquer Salanar."}},
[12688] = {"Orchestrer un désastre", nil, {"Aidez l'Ingénieur Hélice à s'enfuir du Site de fouilles de Ricarnaque, puis parlez avec Hemet Nesingwary au Camp de base de Nesingwary."}},
[12689] = {"La main des oracles", nil, nil},
[12690] = {"Alimenter le feu", nil, {"Drakuru à Voltarus veut que vous utilisiez le Sceptre de commandement sur des Abominations boursouflées et que vous utilisiez leurs capacités dans la Brèche algide pour tuer 60 Brise-tête drakkari et attirer 3 Chefs drakkari.\n\nLes chefs apparaîtront lorsque vous aurez tué suffisamment de Brise-têtes."}},
[12691] = {"Un coffre vétuste", nil, {"Obtenez une Énorme clé en pierre d'un des gardiens de Sholazar à proximité, et rapportez-la au Coffre vétuste au Surplomb du Faiseur."}},
[12692] = {"Le retour du chasseur de liches", nil, {"Retournez à la Colline de Frénécœur et parlez à l'ancien Harkek."}},
[12695] = {"Le retour du peau-sèche gentil", nil, {"Retournez à la Canopée Parlepluie et parlez au Haut-oracle Soo-pape."}},
[12696] = {"Surveillance aérienne", nil, {"Le Professeur Calvert veut que vous preniez le vol pour le Terrain d'atterrissage de Rive-du-lac, et que vous parliez au Pilote Vic une fois rendu là-bas."}},
[12697] = {"Gothik le Moissonneur", nil, {"Parler avec Gothik le Moissonneur à Brèche-de-Mort."}},
[12698] = {"Le don continu", nil, {"Gothik le Moissonneur à Brèche-de-Mort vous a remis le Don du moissonneur pour que vous l'utilisiez contre les Mineurs écarlates. Créez 5 Goules écarlates avec l'appareil et rapportez-les à Gothik."}},
[12699] = {"C'est un peu embarrassant…", nil, {"Cherchez les Clés de Vic dans le lac du Cœur du fleuve."}},
[12700] = {"Une occasion d'attaquer", nil, {"Parlez au Prince Valanar à Brèche-de-Mort."}},
[12701] = {"Massacre à la halte de la Lumière", nil, {"Le Prince Valanar à Brèche-de-Mort vous a ordonné de massacrer 100 Défenseurs écarlates."}},
[12702] = {"Des poulets et des varleus", nil, {"L'Ancien Harkek à la Colline de Frénécœur vous a demandé de capturer 12 Poulets évadés."}},
[12703] = {"Le massacre de Kartak", nil, {"Trouvez un échantillon pur de Sang d'oracle et apportez-le à l'autel de Kartak pour invoquer Kartak.\n\nAvec l'aide des pouvoirs de Kartak, massacrez 50 Gorlocs touchétincelles pour le Chaman Jakjek au Bastion de Kartak."}},
[12704] = {"Apaiser la pierre à Pluie toute-puissante", nil, {"Le Haut-oracle Soo-pape de la Canopée Parlepluie vous a demandé de récupérer 6 Trésors parmi ceux déterrés par votre compagnon.\n\nAllez parler au Haut-oracle Soo-pape à la Canopée Parlepluie pour obtenir l'aide d'un de vos anciens compagnons."}},
[12705] = {"La volonté des titans", nil, {"Récupérez un Cristal corrompu sur les varleus du Havre Touchétincelle. Rapportez-le à la Pierre à foudre toute-puissante pour demander l'aide de Soo-palonion. Grâce aux pouvoirs de Soo-palonion, tuez 50 Varleus frénécœurs. Une fois la tâche achevée, parlez au Mandefoudre Soo-briquet au Havre Touchétincelle."}},
[12706] = {"Victoire à Brèche-de-Mort !", nil, {"Livrez le Rapport du prince Valanar au Généralissime Mograine dans Achérus : le fort d'Ébène."}},
[12707] = {"Justice laineuse", nil, {"Le Scalpeur Ahunae à Zim'Torga vous a demandé d'utiliser le Médaillon de Mam'toth pour calmer et chevaucher un Mammouth enragé. Vous devez ensuite piétiner à mort 12 Disciples de Mam'toth."}},
[12708] = {"Guerriers tiki enchantés", nil, {"La Dompteuse des éléments Dagoda à Zim'Torga vous a demandé de détruire 12 Guerriers tiki enchantés."}},
[12709] = {"Les caches ensorcelées", nil, {"Le Chroniqueur To'kini à Zim'Torga vous a demandé d'obtenir 12 Caches du bastion de Zol'Maz."}},
[12710] = {"Révélation", nil, {"Drakuru à Voltarus veut que vous vous téléportiez jusqu'à la chambre supérieure."}},
[12711] = {"Du courrier abandonné", nil, nil},
[12712] = {"La clé du seigneur de guerre Zol'Maz", nil, {"Har'koa à Zim'Torga vous a demandé d'obtenir la Clé du Seigneur de guerre Zol'Maz."}},
[12713] = {"Trahison", nil, {"Stefan au Guet d'Ébène vous a demandé de vaincre Drakuru.\n\nSi vous perdez le Sautoir ensorcelé, retournez voir Stefan au Guet d'Ébène."}},
[12714] = {"La volonté du roi-liche", nil, {"Retournez à Brèche-de-Mort et faites votre rapport au Prince Valanar."}},
[12715] = {"La crypte du Souvenir", nil, {"Présentez-vous au Prince Keleseth dans la Crypte du Souvenir."}},
[12716] = {"La requête du porte-peste", nil, {"Noth le Porte-peste vous a demandé de lui rapporter 1 Chaudron vide, 1 Chaîne de fer et 10 Crânes de croisés."}},
[12717] = {"La cuvée spéciale de Noth", nil, {"Placez la Pile de crânes de croisés dans le Chaudron de peste."}},
[12718] = {"Plus de crânes pour la cuvée", nil, {"Conditions :"}},
[12719] = {"Nul lieu pour fuir ou se cacher", nil, {"Le Prince Keleseth, dans la Crypte du Souvenir, vous a ordonné de tuer le Maire Quimby et de rapporter le Registre de la Nouvelle-Avalon."}},
[12720] = {"Comment se faire des amis et influencer ses ennemis", nil, {"Le Prince Keleseth à la Crypte du Souvenir vous a ordonné de découvrir la vérité à propos de l'« Aube cramoisie ».\n\nEnlevez les Éloquentes de Keleseth de la Boîte à bijoux ouvragée et utilisez-les pour rendre la Croisade écarlate « éloquente »."}},
[12721] = {"Saccage", nil, {"Har'koa à Zim'Torga vous a demandé de libérer Akali et d'en informer ensuite le Féticheur Khufu à Zim'Torga."}},
[12722] = {"Comme des agneaux à l'abattoir", nil, {"Le Baron Vaillefendre à la Crypte du Souvenir vous a ordonné de tuer 10 Soldats de la Croisade écarlate et 15 Habitants de la Nouvelle-Avalon."}},
[12723] = {"Derrière les lignes écarlates", nil, {"Présentez-vous à Orbaz Sangreplaie à la Taverne écarlate de la Nouvelle-Avalon."}},
[12724] = {"Le chemin du croisé vertueux", nil, {"Orbaz Sangreplaie, à la Taverne écarlate de la Nouvelle-Avalon, vous a ordonné de voler le Plan de route des patrouilles de la Nouvelle-Avalon dans le Fort écarlate."}},
[12725] = {"Frères dans la mort", nil, {"Cherchez Koltira Tissemort dans le Fort écarlate."}},
[12726] = {"Le chant du Vent et de l'Eau", nil, {"Soo-premm veut que vous utilisiez les Tambours de la tempête à la Saillie de Forgetempête et que vous aidiez Haiphoon à dévorer 3 Revenants des tempêtes et 3 Esprits aqueux."}},
[12727] = {"Une issue sanglante", nil, {"Tuez le Grand inquisiteur Valroth et rapportez la Tête de Valroth à Thassarian à la Taverne écarlate à la Nouvelle-Avalon."}},
[12728] = {"Surveiller la faille : les cavernes des Ailerons-d'hiver", nil, {"Le Bibliothécaire Garren, à l'Escarpement d'Ambre, vous a demandé d'utiliser l'Arcanomètre dans les Cavernes des Ailerons-d'hiver."}},
[12729] = {"Les dieux ont parlé", nil, {"Livrez le Cœur du gardien impeccable et l'Essence venteuse du gardien à Har'koa, dans Zim'Torga."}},
[12730] = {"Convocation à Zol'Heb", nil, {"Har'koa à Zim'Torga vous a demandé de tuer le Prophète d'Akali."}},
[12732] = {"La force du sang du cœur", nil, {"Apportez une fiole de Sang du cœur Solegrâce à Rejek, à la Colline de Frénécœur."}},
[12733] = {"Le défi de la mort", nil, {"Olrun le Mande-guerre vous a ordonné de gagner 5 duels contre d'autres chevaliers de la mort.\n\nVous pouvez affronter des initiés chevaliers de la mort ou des joueurs chevaliers de la mort."}},
[12734] = {"Rejek : le premier sang", nil, {"Avec l'Épée de Rejek, tuez une Guêpe de la ruche Saphir, un Fonceur poing-dur, et 3 Membres de la tribu murmebrume. Vous pouvez tuer des Oracles murmebrumes ou des Guerriers murmebrumes, au choix. Retournez voir Rejek à la Colline de Frénécœur quand vous aurez effectué cette tâche."}},
[12735] = {"Un chant de purification", nil, {"Soo-premm, à la Canopée Parlepluie, vous demande de jouer du Carillon purificateur au lac Flot-amer, au fleuve le Joug-d'hiver et au Cœur du fleuve, et de tuer les esprits maléfiques qui polluent leurs eaux."}},
[12736] = {"Un chant de réflexion", nil, {"Soo-premm, à la Canopée Parlepluie, vous a demandé d'utiliser le Didjeridou de contemplation au sommet du Pilier Scintillant, du Pilier Solegrâce, du Pilier Mousselume et du Pilier Confins-du-ciel."}},
[12737] = {"Un chant de fécondité", nil, {"Soo-premm, à la Canopée Parlepluie, vous demande d'aller à la Saillie de Forgetempête, au nord-ouest du Perchoir du Faiseur, et d'utiliser la Corne de fécondité près de 8 Monticules de terre fertile trempés."}},
[12738] = {"Vengeance !", nil, {"Présentez-vous au Chevalier-commandant Poing-du-Fléau à la Chapelle de la Flamme cramoisie."}},
[12739] = {"Une surprise spéciale", nil, {"Le Chevalier-commandant Poing-du-Fléau à la Chapelle de la Flamme cramoisie vous a ordonné d'exécuter Malar Corne-brave, un prisonnier de l'Aube d'argent qui se trouve dans la prison voisine."}},
[12740] = {"Parachute argenté", nil, {"Le Commandant Falstaav au Séjour d'Argent vous a demandé d'équiper 10 Croisés d'argent ou Porte-boucliers d'argent qui se trouvent dans la cour de parachutes."}},
[12741] = {"La puissance de la tempête", nil, {"Rassemblez 3 Essences de la mousson et 3 Essences de la tempête, et apportez-les au Sanctuaire de la tempête. Retournez voir Rejek à la Colline de Frénécœur avec le Véritable pouvoir de la tempête."}},
[12742] = {"Une surprise spéciale", nil, {"Le Chevalier-commandant Poing-du-Fléau à la Chapelle de la Flamme cramoisie vous a ordonné d'exécuter Ellen Perrepont, une prisonnière de l'Aube d'argent qui se trouve dans la prison voisine."}},
[12743] = {"Une surprise spéciale", nil, {"Le Chevalier-commandant Poing-du-Fléau à la Chapelle de la Flamme cramoisie vous a ordonné d'exécuter Yazmina Roncechêne, une prisonnière de l'Aube d'argent qui se trouve dans la prison voisine."}},
[12746] = {"Une surprise spéciale", nil, {"Le Chevalier-commandant Poing-du-Fléau à la Chapelle de la Flamme cramoisie vous a ordonné d'exécuter Valok le Vertueux, un prisonnier de l'Aube d'argent qui se trouve dans la prison voisine."}},
[12747] = {"Une surprise spéciale", nil, {"Le Chevalier-commandant Poing-du-Fléau à la Chapelle de la Flamme cramoisie vous a ordonné d'exécuter Dame Eonys, une prisonnière de l'Aube d'argent qui se trouve dans la prison voisine."}},
[12748] = {"Une surprise spéciale", nil, {"Le Chevalier-commandant Poing-du-Fléau à la Chapelle de la Flamme cramoisie vous a ordonné d'exécuter Kug Gueule-de-fer, un prisonnier de l'Aube d'argent qui se trouve dans la prison voisine."}},
[12749] = {"Une surprise spéciale", nil, {"Le Chevalier-commandant Poing-du-Fléau à la Chapelle de la Flamme cramoisie vous a ordonné d'exécuter Iggy Sombrebroche, un prisonnier de l'Aube d'argent qui se trouve dans la prison voisine."}},
[12750] = {"Une surprise spéciale", nil, {"Le Chevalier-commandant Poing-du-Fléau à la Chapelle de la Flamme cramoisie vous a ordonné d'exécuter Antoine Brack, un prisonnier de l'Aube d'argent qui se trouve dans la prison voisine."}},
[12751] = {"Un retour au pays... en quelque sorte", nil, {"Retournez voir Thassarian dans la Taverne écarlate à la Nouvelle-Avalon et rapportez-lui la nouvelle de votre victoire."}},
[12752] = {"Recherche désespérée", nil, {"Parlez au Grand apothicaire Putrescin, au nouveau Camp des Apothicaires sur la Terrasse de la Lumière à Shattrath."}},
[12753] = {"Une alliance désespérée", nil, {"Parlez à l'Évêque Lazaril au Nouveau campement de l'Alliance à la Terrasse de la Lumière, à Shattrath."}},
[12754] = {"Embuscade au surplomb", nil, {"Orbaz Sangreplaie à la Taverne écarlate à la Nouvelle-Avalon vous a ordonné de tuer le Courrier écarlate et de récupérer les Possessions du courrier écarlate, ainsi que le Message du courrier écarlate."}},
[12755] = {"Une rencontre avec le destin", nil, {"Orbaz Sangreplaie vous a ordonné de livrer le Message du courrier écarlate au Grand général Abbendis à Port-royal."}},
[12756] = {"L'Assaut écarlate émerge", nil, {"Donnez le Chemin de la rédemption à Orbaz Sangreplaie dans la Taverne écarlate à la Nouvelle-Avalon."}},
[12757] = {"L'approche des armées écarlates", nil, {"Donnez le Chemin de la rédemption au Généralissime Darion Mograine à Achérus : le fort d'Ébène."}},
[12758] = {"Un couvre-chef de héros", nil, {"Récupérez des Explosifs de la KapitalRisk sur n'importe quelle unité de la KapitalRisk, au Site de fouilles de Ricarnaque, et utilisez-les pour obtenir la Tête d'un Guette-tempête. Apportez-la ensuite à Rejek à la Colline de Frénécœur."}},
[12759] = {"De drôle d'outils de guerre", nil, {"Récupérez certains des Pièges de Zepik dans son trésor au Bastion de Kartak. Utilisez-les pour tuer 50 Gorlocs touchétincelles, puis retournez voir le Chaman Jakjek au Bastion de Kartak."}},
[12760] = {"La puissance secrète des Frénécœurs", nil, {"Utilisez la boisson secrète frénécœur pour tuer 30 Gorlocs touchétincelles autour du Bastion de Kartak."}},
[12761] = {"La maîtrise des cristaux", nil, {"Récupérez les Défenses de cristal au Havre Touchétincelle et utilisez-les pour tuer 50 Varleus frénécœurs. Une fois la tâche effectuée, retournez voir le Mandefoudre Soo-briquet au Havre Touchétincelle."}},
[12762] = {"Le pouvoir des Tout-puissants", nil, {"Chargez le Cristal dormant poli au Pilier Sang-de-vie découvert. Rendez-vous au Havre Touchétincelle et utilisez le pouvoir contenu dans le cristal pour massacrer 30 Varleus frénécœurs, puis parlez au Mandefoudre Soo-briquet au Havre Touchétincelle."}},
[12763] = {"Changement de priorités", nil, {"L'Éclaireur Vor'takh veut que vous vous rendiez à Zul'Drak et que vous vous présentiez au Sergent Riannah à la Brèche de Lumière.\n\nMakki Trombe-de-l'hiver, au Camp Oneqwah, vous aidera à vous y rendre."}},
[12766] = {"Parler avec l'ambassadrice", nil, {"Parler avec Lauriel Lamevraie au Temple du Repos du ver."}},
[12767] = {"Parler avec l'ambassadeur", nil, {"Parler avec Golluck Poing-roc au Temple du Repos du ver."}},
[12768] = {"Le régisseur du temple du Repos du ver", nil, {"Parler à Tariolstrasz au Temple du Repos du ver."}},
[12769] = {"Le régisseur du temple du Repos du ver", nil, {"Parler à Tariolstrasz au Temple du Repos du ver."}},
[12770] = {"La répartition des ressources", nil, {"Gryan Roidemantel veut que vous vous présentiez au Sergent Riannah à la Brèche de Lumière, dans Zul'Drak.\n\nParlez à Samuel Livrouvert pour être transporté à l'Espoir de Lumière."}},
[12771] = {"Forgefer", nil, {"L'Evêque Lazaril vous demande d'apporter l'Artéfact imprégné de Lumière au Roi Magni Barbe-de-bronze à Forgefer."}},
[12772] = {"Une alliance désespérée", nil, {"Parlez à l'Évêque Lazaril au Nouveau campement de l'Alliance à la Terrasse de la Lumière, à Shattrath."}},
[12773] = {"Darnassus", nil, {"L'Evêque Lazaril vous demande d'apporter l'Artéfact imprégné de Lumière à Tyrande Murmevent à Darnassus."}},
[12774] = {"Hurlevent", nil, {"L'Evêque Lazaril vous demande d'apporter l'Artéfact imprégné de Lumière au Roi Varian Wrynn à Hurlevent."}},
[12775] = {"Une alliance désespérée", nil, {"Parlez à l'Évêque Lazaril au Nouveau campement de l'Alliance à la Terrasse de la Lumière, à Shattrath."}},
[12776] = {"L'Exodar", nil, {"L'Evêque Lazaril vous demande d'apporter l'Artéfact infusé de lumière à Velen, à l'Exodar."}},
[12777] = {"Une alliance désespérée", nil, {"Parlez à l'Évêque Lazaril au Nouveau campement de l'Alliance à la Terrasse de la Lumière, à Shattrath."}},
[12778] = {"L'apocalypse écarlate", nil, {"Cherchez le Roi-liche à Brèche-de-Mort."}},
[12779] = {"La fin de toutes choses…", nil, {"Le Roi-liche vous a ordonné d'éradiquer la Croisade écarlate.\n\nUtilisez le Cor des Couvegivres pour invoquer un Vainqueur couvegivre. Avec son aide, tuez 150 Soldats écarlates et détruisez 10 Balistes écarlates. Évitez les projectiles en sortant de leur ligne de tir !"}},
[12780] = {"PÉRIMÉ>>L'ennemi de notre ennemi", nil, {"Le Sergent Riannah à la Brèche de Lumière veut que vous parliez à Stefan au Guet d'Ébène et que vous lui offriez votre aide."}},
[12781] = {"Bienvenue !", nil, {"Apportez le Bon-cadeau du Fort d'Ébène à Siouxsie la banshee au Fort d'Ébène."}},
[12782] = {"Recherche désespérée", nil, {"Parlez au Grand apothicaire Putrescin, au nouveau Camp des Apothicaires sur la Terrasse de la Lumière à Shattrath."}},
[12783] = {"Recherche désespérée", nil, {"Parlez au Grand apothicaire Putrescin, au nouveau Camp des Apothicaires sur la Terrasse de la Lumière à Shattrath."}},
[12784] = {"Recherche désespérée", nil, {"Parlez au Grand apothicaire Putrescin, au nouveau Camp des Apothicaires sur la Terrasse de la Lumière à Shattrath."}},
[12785] = {"Orgrimmar", nil, {"Le Grand apothicaire Putrescin vous a demandé de donner le Cerveau doublement pestiféré à l'Apothicaire Karlov à Orgrimmar."}},
[12786] = {"Les Pitons-du-Tonnerre", nil, {"Le Grand apothicaire Putrescin vous a demandé d'apporter le Cerveau doublement pestiféré à l'Apothicaire Chaney aux Pitons-du-Tonnerre."}},
[12787] = {"Fossoyeuse", nil, {"Le Grand apothicaire Putrescin vous demande d'apporter le Cerveau doublement pestiféré au Maître apothicaire Faranell à Fossoyeuse."}},
[12788] = {"Lune-d'argent", nil, {"Le Grand apothicaire Putrescin vous a demandé de donner le Cerveau doublement pestiféré à l'Apothicaire Tepesh à Lune-d'argent."}},
[12789] = {"Dans la brèche !", nil, {"Le Croisé Valus de la Confiance de Lumière veut que vous vous présentiez auprès du Sergent Riannah à la Brèche de Lumière, dans Zul'Drak."}},
[12790] = {"Apprendre à partir et revenir : le chemin magique", nil, {"L'Archimage Célindra à la Porte Pourpre à Dalaran vous a demandé d'utiliser le Téléporteur vers le Cristal du Séjour pourpre et le Téléporteur vers le Cristal de Dalaran."}},
[12791] = {"Le royaume magique de Dalaran", nil, {"Donnez la Chevalière du Kirin Tor à l'Archimage Célindra à la Porte Pourpre à Dalaran."}},
[12792] = {"Chaque chose en son temps", nil, {"Le Commandant Kunz au Séjour d'Argent vous a demandé d'aller rejoindre le Sergent Riannah à la Brèche de Lumière."}},
[12793] = {"De la fumée à l'horizon", nil, {"Khufu, dans Zim'Torga, veut que vous vous rendiez à la Brèche de Lumière et que vous vous présentiez au Sergent Riannah.\n\nMaaka vous fournira le transport."}},
[12794] = {"Le royaume magique de Dalaran", nil, {"Donnez la Chevalière du Kirin Tor à l'Archimage Célindra à la Porte Pourpre à Dalaran."}},
[12795] = {"Passons au séjour", nil, {"Datura Rose-sang au Guet d'Ébène veut que vous alliez parler au Commandant Falstaav au Séjour d'Argent."}},
[12796] = {"Le royaume magique de Dalaran", nil, {"Donnez la Chevalière du Kirin Tor à l'Archimage Célindra à la Porte Pourpre à Dalaran."}},
[12797] = {"Retour par le portail d'accès", nil, {"Apportez la Rune Oméga à l'Avatar de Freya aux Rapides Parlepluie, dans le Bassin de Sholazar."}},
[12798] = {"Suite d'Épées", nil, {"Utilisez la Suite d'Épées pour invoquer une Diseuse de bonne aventure de la foire de Sombrelune et donnez-lui la suite."}},
[12799] = {"Récupération d'esprits", nil, {"Le Capitaine Arnath à Heb'Valok vous demande de récupérer 5 Ectoplasmes anciens d'Esprits drakkari égarés aux Bassins de Zha'Jin."}},
[12800] = {"Le dernier ordre du roi-liche", nil, {"Faites votre rapport au Commandant du Fléau Thalanor à la Scierie de Browman."}},
[12801] = {"La Lumière de l'Aube", nil, {"Battez les forces de l'Aube d'argent et découvrez la Lumière de l'Aube.\n\nSi vous survivez, faites votre rapport au Généralissime Darion Mograine."}},
[12802] = {"Mon cœur est entre tes mains", nil, {"Drakuru vous a demandé de lui rapporter le Cœur des anciens au Passage de Drak'atal.\n\nVous allez devoir récupérer 5 Mojos désespérés pour utiliser l'élixir sur place."}},
[12803] = {"Une force de la nature", nil, {"Rendez-vous à la Ligne de la désolation, entre les Rapides Parlepluie et les Terres Perdues du Bassin de Sholazar, et cherchez l'Avatar de Freya."}},
[12804] = {"Un steak digne d'un chasseur", nil, {"Apportez 5 Steaks de Longs-cous à Korg le Fendeur, au Camp de base de Nesingwary."}},
[12805] = {"La préservation de la force vitale", nil, {"L'Avatar de Freya veut que vous alliez à l'Éclat scindé au sud-est, et que vous utilisiez la Gemme de force vitale sur 8 Élémentaires sang-de-vie détruits."}},
[12806] = {"Se lever en toute hâte !", nil, {"Faites votre rapport au Seigneur-commandant Arete à la Cime de la Mort, dans la Couronne de glace."}},
[12807] = {"Résumé des épisodes précédents…", nil, {"Écoutez l'histoire du Seigneur-commandant Arete à la Cime de la Mort."}},
[12808] = {"Une alliance désespérée", nil, {"Parlez à l'Évêque Lazaril au Nouveau campement de l'Alliance à la Terrasse de la Lumière, à Shattrath."}},
[12809] = {"Forgefer", nil, {"L'Evêque Lazaril vous demande d'apporter l'Artéfact imprégné de Lumière au Grand Bricoleur Mekkanivelle à Forgefer."}},
[12810] = {"Du sang dans l'eau", nil, {"Le Seigneur-commandant Arete, à la Cime de la Mort,vous a demandé de remplir la Vessie de sang avec le sang de 10 Dents voraces de la mer."}},
[12811] = {"Recherche désespérée", nil, {"Parlez au Grand apothicaire Putrescin, au nouveau Camp des Apothicaires sur la Terrasse de la Lumière à Shattrath."}},
[12812] = {"Orgrimmar", nil, {"Le Grand apothicaire Putrescin vous a demandé de donner le Cerveau doublement pestiféré à l'Apothicaire Karlov à Orgrimmar."}},
[12813] = {"Réanimez leurs cadavres !", nil, {"Setaal Soignobscur à la Cime de la Mort vous a donné la Teinture du sombre réparateur. Appliquez-la sur les cadavres de 10 unités de l'Assaut écarlate trouvées au Port de l'Assaut."}},
[12814] = {"Vous aurez besoin d'un griffon", nil, {"Amenez un Griffon de l'Assaut à Uzo Mandemort à la Cime de la Mort."}},
[12815] = {"Zone d'exclusion aérienne", nil, {"Uzo Mandemort, à la Cime de la Mort, vous demande de tuer 10 Chevaucheurs de griffons de l'Assaut."}},
[12816] = {"Enquêter au sujet du Fléau à Lune-d'argent", nil, {"Récupérez 3 Pierres nécrotiques pâles du Fléau à l'extérieur de Lune-d'argent et enquêtez à propos des Cercles runiques brillants près de leur campement."}},
[12817] = {"Enquêter au sujet du Fléau à l'Exodar", nil, {"Récupérez 3 Pierres nécrotiques pâles sur les membres du Fléau à l'extérieur de l’Exodar et enquêtez sur les Cercles runiques brillants près de leur campement. Puis retournez voir le Lieutenant Kregor."}},
[12818] = {"Nettoyage", nil, {"Gouaille Grilledouille à K3 vous a demandé 10 morceaux de Débris calcinés."}},
[12819] = {"Juste à côté", nil, {"Gouaille Grilledouille, à K3, aimerait récupérer ses Outils de Grilledouille oubliés dans son champ de mines."}},
[12820] = {"Une délicate attention", nil, {"Ricket, à K3, veut que vous utilisiez les Mines améliorées à Plaie-de-Garm pour repousser 12 Attaquants de Garm. Les Envahisseurs de Garm et les Suivants Brûleglace comptent comme attaquants."}},
[12821] = {"Le tango du pénitencier", nil, {"Ricket à K3 voudrait que vous localisiez la Batterie de télétransporteur et que vous l'utilisiez pour activer la Plateforme de téléportation à la Cime de Garm."}},
[12822] = {"N'ayez pas peur", nil, {"Ricket à K3 vous demande de tuer 6 Guetteurs de Garm et 8 Fidèles brûleglace."}},
[12823] = {"Un plan sans accroc", nil, {"Gino à la Cime de Garm vous a demandé de placer le Paquet d'explosifs durcis près de l'Autel de Givrebide, de tuer Tormar Givrebide puis de vous échapper avant que la caverne ne s'effondre sur vous."}},
[12824] = {"Artificier extraordinaire", nil, {"Retournez voir Ricket à K3."}},
[12825] = {"Une preuve de gratitude", nil, {"[PLACEHOLDER] Utilisez les mini avions pour jouer les kamikazes sur le chef gnoll."}},
[12826] = {"Légèrement instable", nil, {"Apportez les Mines améliorées de Gouaille à Ricket à K3."}},
[12827] = {"Vol de rations", nil, {"Ricket à K3 vous demande de récupérer 16 Rations gnolles séchées sur les Gnolls de la colline Sauvage, qui se trouvent non loin."}},
[12828] = {"Une grande inspiration", nil, {"Ricket, à K3, veut que vous récupériez un T.E.M.O.R. Utilisez-le pour faire sauter un mammouth et récupérez 8 Viandes de mammouth copieuses."}},
[12829] = {"Installation", nil, {"Tore Grondelevier à l'extérieur de la Caverne de la Toile cristalline vous a demandé de tuer 12 Araignées de la Toile cristalline."}},
[12830] = {"Récupération de minerai", nil, {"Tore Grondelevier à l'extérieur de la Caverne de la Toile cristalline vous a demandé de récupérer 5 échantillons de Minerai de saronite impur."}},
[12831] = {"Seulement à moitié oublié", nil, {"Un Mineur gobelin blessé à la Caverne de la Toile cristalline vous a demandé de lui apporter une Glande à venin pointeglace trouvée sur une Rampante pointeglace."}},
[12832] = {"Un départ amer", nil, {"Escortez le Mineur gobelin blessé hors de la Caverne de la Toile cristalline, puis parlez à Ricket à K3."}},
[12833] = {"Surplus", nil, {"Ricket, à K3, veut que vous utilisiez les Mines améliorées à Plaie-de-Garm pour repousser les attaquants de Garm. Les Envahisseurs de Garm et les Suivants Brûleglace comptent comme attaquants."}},
[12834] = {"Ni la pluie, ni la neige, ni un avion qui explose", nil, {"[PLACEHOLDER] Test bombardement temps froid sur géant."}},
[12835] = {"Déjà grand", nil, {"[PLACEHOLDER] Test Jormungar adulte sur géant"}},
[12836] = {"Une preuve de gratitude", nil, {"Ricket à K3 veut que vous tuiez Peau-noueuse."}},
[12837] = {"Tore a les fournitures", nil, {"[PLACEHOLDER] Faites votre rapport à Tore"}},
[12838] = {"Trouver des informations", nil, {"Morneplaie des aurochs, à la Cime de la Mort, vous a demandé de récupérer 5 Documents de renseignement de l'Assaut."}},
[12839] = {"Le grand plan de l'amiral", nil, {"Remettez la Note du Grand Amiral au seigneur-commandant Arete à la Cime de la Mort."}},
[12840] = {"Que ça reste entre nous", nil, {"Le Seigneur-commandant Arete, à la Cime de la Mort, vous a demandé d'extirper l'information des capitaines Welsington et Hartford."}},
[12841] = {"La proposition de la mégère", nil, {"Lok'lira la Mégère à l'intérieur de la Mine Lugubre vous a demandé de récupérer les Runes des Yrkvinn que détient la Surveillante Syra."}},
[12842] = {"La forgerune : se préparer à la bataille", nil, {"L’Instructeur Razuvious du Fort d’Ébène, vous ordonne de vous rendre à une forgerune, et de vous servir de votre talent Forgerune pour graver une rune de votre choix sur votre Lame d’âme runique.\n\nVous pourrez la remplacer n’importe quand, tant que vous serez à proximité d’une forgerune."}},
[12843] = {"Elles ont pris nos hommes !", nil, {"Gretchen Pfutincelle vous demande d'aller à Sifreldar et de secourir 5 Prisonniers gobelins."}},
[12844] = {"Récupération de l'équipement", nil, {"Gretchen Pfutincelle vous a demandé d'obtenir 8 pièces d'Équipement de K3 à Sifreldar, au nord de K3."}},
[12846] = {"Le gobelin manquant", nil, {"Trouvez l'entrée de la Mine Lugubre au nord du Village Sifreldar et cherchez des indices sur l'endroit où se trouve Zeev Pfutincelle."}},
[12847] = {"Secondes chances", nil, {"Tuez le Grand abbé Langrain, puis invoquez la Porte d'Arete en présence de son cadavre. Parlez au Seigneur-commandant Arete à l'intérieur de la Cathédrale cramoisie une fois qu'il aura questionné l'esprit du grand abbé."}},
[12848] = {"La faim sans fin", nil, {"L'Instructeur Razuvious à Fort d'Ébène vous a demandé de libérer un Initié indigne puis de tuer l'Initié indigne de sang-froid !\n\nC'est votre vie ou la sienne !"}},
[12849] = {"Le pouvoir du sang, du givre et de l'impie", nil, nil},
[12850] = {"Rapport au commandant du Fléau Thalanor", nil, {"Faites votre rapport au Commandant du Fléau Thalanor sur le balcon au rez-de-chaussée du Fort d'Ébène."}},
[12851] = {"La pro de l’ours", nil, {"Brijana à Brunnhildar vous a demandé de chevaucher Croc-de-glace et d'abattre 7 Worgs de givre et 15 Géants du givre dans la Vallée des Anciens hivers."}},
[12852] = {"L'amiral à découvert", nil, {"Le Seigneur-commandant Arete vous a demandé de tuer le Grand amiral Ponevent. Retournez voir le Seigneur-commandant à la Cime de la Mort quand la tâche est accomplie."}},
[12853] = {"Une escapade luxueuse !", nil, {"Allez voir Gouaille Grilledouille à K3, aux Pics Foudroyés."}},
[12854] = {"Sur les traces de Brann", nil, {"Récupérez la Note enveloppée dans de la toile au bivouac de Brann et rapportez-la à l'Archéologue Andorin à Fort du Givre."}},
[12855] = {"Renifler le coupable", nil, {"Utilisez le Collier de molosse du givre au Camp abandonné pour suivre la piste des objets de Brann. Quand vous les aurez récupérés, utilisez l'appareil de communication et contactez qui vous pouvez."}},
[12856] = {"Brisons la glace", nil, {"Brijana, à l'est de Brunnhildar, veut que vous voliez jusqu'à Dun Niffelem, que vous libériez 3 Proto-drakes captifs et que vous sauviez 9 Prisonnières de Brunnhildar."}},
[12857] = {"Avis de recherche : Nageoire de Crin-de-rage", nil, {"Récupérez la Nageoire de Crin-de-rage et rapportez-la au Chef Grifferage à la Brèche de Lumière à Zul'Drak."}},
[12858] = {"Les pièces du puzzle", nil, {"Rassemblez les 6 Fragments du disque de l'inventeur et utilisez-les pour assembler le Disque de l'inventeur."}},
[12859] = {"Nouvelle neuve : le feu ça brûle !", nil, {"L'Ancien chaman Moky à la Brèche de Lumière à Zul'Drak vous demande d'utiliser l'Extincteur grifferage pour éteindre 15 Feux de hutte à la Tanière grifferage."}},
[12860] = {"À la recherche de données", nil, {"Utilisez le disque de l'inventeur pour récupérer 7 Fragments de données cachées dans les Banques de données."}},
[12861] = {"Les trolls ils ont tombé sur la tête !", nil, {"Le Chef Grifferage à la Brèche de Lumière à Zul'Drak vous demande d'utiliser les Ouvre-serrures de Drakuru trouvés sur différents trolls Drakuru pour libérer 8 Grifferages capturés."}},
[12862] = {"Quand tout le reste a échoué…", nil, {"Parlez à Ricket pour utiliser l'Engin de livraison à propulsion D16 de Ricket afin de pénétrer au cœur des Pics Foudroyés."}},
[12863] = {"Des remerciements", nil, {"Apportez la caisse de Fournitures médicales à Glorthal Barberêche à Fort du givre."}},
[12864] = {"Les éclaireurs manquants", nil, {"Glorthal, au Fort du Givre, vous a demandé d'explorer la vallée du Souffle de Bor et la Chute de Bor en quête d'une trace des Éclaireurs givre-nés disparus."}},
[12865] = {"Des compagnons loyaux", nil, {"Fjorlin Front-de-givre, au Fort du Givre, veut que vous récupériez 8 tranches de Viande fraîche de rhinocéros des glaces, et que vous les utilisiez pour nourrir les Aigles foudrecrête qui nichent au-dessus de la ville."}},
[12866] = {"Contenir les agresseurs", nil, {"Glorthal Barberêche au Fort du Givre veut que vous tuiez 8 Hurleuses givreplumes et 8 Sorcières givreplumes."}},
[12867] = {"Voleurs de bébés", nil, {"Fjorlin Front-de-givre au Fort du Givre vous a demandé de lui rapporter 15 Œufs d'aigles foudrecrêtes."}},
[12868] = {"Sirana Hurleglace", nil, {"Glorthal Barberêche au Fort du Givre veut que vous éliminiez Sirana Hurleglace."}},
[12869] = {"Trop loin", nil, {"Fjorlin Front-de-givre veut que vous chevauchiez un Aigle foudrecrête et que vous tuiez 16 Wyrms foudrepics dans le Pied-mont."}},
[12870] = {"Reliques anciennes", nil, {"Rapportez 10 Reliques d'Ulduar à Rork Menton-pointu au Fort du Givre."}},
[12871] = {"De l'aide de la Ligue des explorateurs", nil, {"Apportez la Coque de Norgannon à Lagnus, au Fort du Givre."}},
[12872] = {"La coque de Norgannon", nil, {"Récupérez la Coque de Norgannon auprès de l'Archiviste Mécaton."}},
[12873] = {"Le roi des givre-nés", nil, {"Parlez au Roi Yorg Foudrecœur au Fort du Givre."}},
[12874] = {"La ferveur du givre-né", nil, {"Yorg Foudrecœur, au Fort du givre, vous a demandé de parler à Fjorlin Front-de-givre pour récupérer une monture et passer votre épreuve."}},
[12875] = {"Un guide expérimenté", nil, {"Localiser Drom Poignegivre."}},
[12876] = {"Des invités indésirables", nil, {"Fjorlin Front-de-givre au Fort du givre vous a demandé de tuer 10 Envahisseurs forge-foudre."}},
[12877] = {"Le guetteur solitaire", nil, {"Reprenez la Chevalière de Poignegivre des mains d'un Observateur nain de fer et apportez-la à Creteus dans le Pied-mont."}},
[12878] = {"La relique cachée", nil, {"Creteus, dans le Pied-mont, vous a donné des informations qui vous permettent de localiser le Noyau de Norgannon."}},
[12879] = {"La fureur du roi givre-né", nil, {"Rapportez le noyau de Norgannon à Creteus au Pied-mont."}},
[12880] = {"L'explorateur en chef", nil, {"Rapportez le noyau de Norgannon à Brann Barbe-de-bronze au Moteur des Faiseurs."}},
[12881] = {"Les frères Barbe-de-bronze", nil, nil},
[12882] = {"Reliques anciennes", nil, {"Rapportez 10 Reliques d'Ulduar à Olut Alegut au Point d'impact de Grom'arsh."}},
[12883] = {"Ordres de Drakuru", nil, {"Rapportez les Ordres de Drakuru au Seigneur croisé Lantinga à la Brèche de Lumière à Zul'Drak."}},
[12884] = {"Le guet d'Ébène", nil, {"Portez les Ordres de Drakuru à Stefan Vadu au Guet d'Ébène à Zul'Drak."}},
[12885] = {"Les exilés d'Ulduar", nil, {"Parlez à Rochecombe le Sculpteroc au Refuge de Rochecombe."}},
[12886] = {"Le Drakkensryd", nil, {"Utilisez le Harpon hyldnir pour vaincre 10 Chevaucheuses de drake du Hyldsmeet au Temple des Tempêtes."}},
[12887] = {"Qu'est-ce qu'on se marre", nil, {"Détruisez l'Oculaire et faites votre rapport au Baron Lesquille au Caveau des Ombres."}},
[12888] = {"FERRAILL-E", nil, {"Apportez la Carte d'accès de FERRAILL-E, ainsi que 4 Poignées de boulons en cobalt et 5 Condensateurs surchargés à FERRAILL-E à la Bibliothèque de l'inventeur."}},
[12889] = {"La console prototype", nil, {"Utilisez la Console prototype à la Bibliothèque de l'inventeur."}},
[12890] = {"Si la taille était importante", nil, nil},
[12891] = {"J'ai une idée mais d'abord…", nil, {"Le Baron Lesquille, au Caveau des Ombres, vous a demandé de trouver un Bâtonnet de sectateur, un Crochet d'abomination, une Corde de Geist et 5 Essences du Fléau."}},
[12892] = {"Qu'est-ce qu'on se marre", nil, {"Détruisez l'Oculaire puis faites votre rapport au Baron Lesquille au Caveau des Ombres."}},
[12893] = {"Libérez votre esprit", nil, {"Le Baron Lesquille, au Caveau des Ombres, vous demande d'utiliser le Bâtonnet souverain sur les cadavres de Vil, Dame Bois-de-nuit et Le Bondisseur."}},
[12894] = {"Le camp avancé des croisés", nil, {"Allez trouver le Croisé MacKellar au Camp avancé des croisés à Zul'Drak."}},
[12895] = {"Le Barbe-de-bronze manquant", nil, {"Récupérez la Note enveloppée dans de la toile au bivouac de Brann et rapportez-la à Boktar Rougefurie au Point d'impact de Grom'arsh."}},
[12896] = {"S'il ne peut pas être détourné…", nil, {"Tuez le Général Plaie-de-lumière puis allez rapporter votre succès à Thassarian à bord de la canonnière Brise-ciel."}},
[12897] = {"S'il ne peut pas être détourné…", nil, {"Tuez le Général Plaie-de-lumière puis allez rapporter votre succès auprès de Koltira Tissemort à bord de la canonnière, le Marteau d'Orgrim."}},
[12898] = {"Le caveau des Ombres", nil, {"Présentez-vous au Baron Lesquille au Caveau des Ombres."}},
[12899] = {"Le caveau des Ombres", nil, {"Présentez-vous au Baron Lesquille au Caveau des Ombres."}},
[12900] = {"Fabriquer un harnais", nil, {"Astrid Bjornrittar à Brunnhildar veut que vous rapportiez 3 Peaux de yéti crins-de-glace."}},
[12901] = {"Système D", nil, {"L’Ingénieur Reed, du Camp avancé des croisés à Zul’Drak, vous a demandé de récupérer 10 Morceaux de ferraille du Fléau."}},
[12902] = {"En quête de réponses", nil, {"Fouillez la Tanière Grifferage à la recherche d’indices sur l’origine de l’attaque contre les varleus Grifferage."}},
[12903] = {"Les amis sont là pour ça…", nil, {"Le Croisé MacKellar, au Camp avancé des croisés, vous a demandé de trouver le Croisé Dargath, ainsi que Gerk et Burr."}},
[12904] = {"La Lumière ne m'accorde pas la vengeance", nil, {"Gerk, à la Fin de Thrym à Zul’Drak, veut que vous tuiez 15 Varguls."}},
[12905] = {"Mildred la Cruelle", nil, {"Parlez à Mildred la Cruelle à la Mine Lugubre."}},
[12906] = {"Discipline", nil, {"Mildred la Cruelle à la Mine Lugubre vous a demandé d'utiliser le Bâton de discipline sur 6 Vrykuls épuisés."}},
[12907] = {"Faire des exemples", nil, {"Mildred la Cruelle à la Mine Lugubre vous a demandé de tuer Garhal."}},
[12908] = {"La bravache et la prisonnière", nil, {"Apportez la Clé de Mildred à Lok'lira la Mégère à la Mine Lugubre."}},
[12909] = {"Le nez sait", nil, {"Parlez à Khaliisi au pied de la montagne au Point d'impact de Grom'arsh."}},
[12910] = {"Renifler le coupable", nil, {"Chevauchez Morsure-de-givre et amenez-la jusqu'à la source de l'odeur de la note de Brann. Puis, retrouvez le Communicateur de Brann et utilisez-le pour entrer en contact avec Brann Barbe-de-bronze."}},
[12911] = {"Test de crédit de mort", nil, {"/signe au test de crédit de mort"}},
[12912] = {"Une grande tempête approche", nil, {"Gymer, le roi des géants des tempêtes, vous a demandé de trouver un moyen de le libérer de sa cage. Allez voir l’Ingénieur Reed, au Camp avancé des croisés."}},
[12913] = {"Vous ne pouvez pas parler orc comme tout le monde ?", nil, {"Parlez à Moteha Né-du-vent, au Point d'impact de Grom'arsh."}},
[12914] = {"Le salut de Gymer", nil, {"L’Ingénieur Reed, du Camp avancé des croisés à Zul’Drak, a besoin de 6 Essences de banshees et de 6 Diatomites."}},
[12915] = {"Rentrer dans les bonnes grâces", nil, {"Thorim au Temple des Tempêtes vous a demandé de tuer Fjorn et 5 Géants de fer forge-foudre à l'Enclume de Fjorn, à l'est de Dun Niffelem.\n\nUtilisez le Charme de terre de Thorim pour invoquer ses Terrestres qui se battront à vos côtés. Vous aurez besoin d'un Rocher de granit près de l'Enclume de Fjorn pour que le charme fonctionne."}},
[12916] = {"Notre seul espoir", nil, {"Utilisez la Charge d’hydroglycérine éléphantesque pour détruire l’Enclos du Fléau qui retient Gymer prisonnier. Lorsque ce sera fait, parlez à Gymer, à la Fin de Thrym à Zul’Drak."}},
[12917] = {"Parler avec la voix du vent", nil, {"Apportez 5 Voix du vent à Moteha Né-du-vent, au Point d'impact de Grom'arsh."}},
[12918] = {"La perfection des gemmes", nil, {"Ounhulo, à Valgarde, a besoin de deux Énormes citrines, de deux Jades sombres et de deux Cristaux ombreux."}},
[12919] = {"La vengeance du roi des tempêtes", nil, {"Parlez à Gymer pour commencer.\n\nLorsque vous serez sur son dos, détruisez 100 créatures du Fléau, et tuez Algar l’Élu, Thrym et le Prince Navarius. Si vous réussissez, allez faire votre rapport au Croisé MacKellar, au Camp avancé des croisés."}},
[12920] = {"Retrouver Brann", nil, {"Utilisez le Communicateur de Brann pour contacter Brann Barbe-de-bronze et parlez-lui, puis parlez à Boktar Rougefurie au Point d'impact de Grom'arsh.\n\nSi vous perdez le Communicateur de Brann, allez parler à Moteha Né-du-vent au Point d'impact de Grom'arsh."}},
[12921] = {"Changer de décor", nil, {"Retrouvez Lok'lira la Mégère à Brunnhildar."}},
[12922] = {"Le feu du raffineur", nil, {"Récupérez 10 Étincelles furieuses sur des Revenants vengeurs au Lac du Champ-gelé, puis utilisez l'enclume à l'Enclume de Fjorn."}},
[12924] = {"Forger une alliance", nil, {"Rendez-vous à Dun Niffelem et demandez au Roi Jokkum de permettre que l'Armure de Thorim soit reforgée. Après avoir terminé la tâche de Jokkum, allez parler à Njormeld à Dun Niffelem."}},
[12925] = {"Aberrations", nil, {"Thyra Kvinnshal à Brunnhildar vous demande de vous rendre à Valkyrion et d'obtenir des Fioles d'huile glaciale des Aspirantes de Valkyrion. Utilisez les Fioles d'huile glaciale pour détruire 30 Œufs de Proto-drakes pestiférés."}},
[12926] = {"Les pièces du puzzle", nil, {"Utilisez le Communicateur de Brann pour contacter Brann Barbe-de-bronze afin de savoir que faire ensuite."}},
[12927] = {"Collecte de données", nil, {"Utilisez le Disque de l'inventeur pour retrouver 7 fragments de Données cachées dans les Banques de données."}},
[12928] = {"La coque de Norgannon", nil, {"Récupérez le Noyau de Norgannon auprès de l’Archiviste Mécaton."}},
[12929] = {"Les Terrestres d'Ulduar", nil, {"Parlez à Rochecombe le Sculpteroc au Refuge de Rochecombe."}},
[12930] = {"Une terre rare", nil, {"Utilisez la Pioche terrestre pour extraire 7 mottes de Terre enchantée. Apportez la Terre enchantée et 5 Etoffes de tisse-givre à Rochecombe le Sculpteroc au Refuge de Rochecombe."}},
[12931] = {"Contre-attaquer", nil, {"Rochecombe le Sculpteroc veut que vous tuiez 10 Attaquants forge-foudre et que vous retourniez le voir au Refuge de Rochecombe. Vous pouvez tuer soit des Ecumeurs forge-foudre soit des Saccageurs forge-foudre."}},
[12932] = {"L'Amphithéâtre de l'Angoisse : Yggdras !", nil, {"Entrez dans l’arène de l’Amphithéâtre de l’Angoisse et triomphez d’Yggdras ! Ensuite, allez voir Wodin le Serviteur-troll pour toucher votre récompense."}},
[12933] = {"L'Amphithéâtre de l'Angoisse : magnataure !", nil, {"Entrez dans l’arène de l’Amphithéâtre de l’Angoisse et triomphez de Barbapue ! Ensuite, allez voir Wodin le Serviteur-troll pour toucher votre récompense."}},
[12934] = {"L'Amphithéâtre de l'Angoisse : de l'au-delà !", nil, {"Entrez dans l’arène de l’Amphithéâtre de l’Angoisse et triomphez de l’un des seigneurs élémentaires ! Ensuite, allez voir Wodin le Serviteur-troll pour toucher votre récompense."}},
[12935] = {"L'Amphithéâtre de l'Angoisse : rohart-maggedon !", nil, {"Entrez dans l’arène de l’Amphithéâtre de l’Angoisse et triomphez d’Orinoko Brise-défense ! Ensuite, allez voir Wodin le Serviteur-troll pour toucher votre récompense."}},
[12936] = {"L'Amphithéâtre de l'Angoisse : Korrak le Ragesang !", nil, {"Entrez dans l’arène de l’Amphithéâtre de l’Angoisse et triomphez de Korrak le Ragesang ! Ensuite, allez voir Wodin le Serviteur-troll pour toucher votre récompense."}},
[12937] = {"Soulager les blessés", nil, {"Utilisez le Cataplasme tellurique pour soigner 8 Défenseurs terrestres tombés au combat, puis retournez voir Rochecombe le Sculpteroc au Refuge de Rochecombe."}},
[12938] = {"Le duc", nil, {"Parlez au Duc Lankral au Caveau des Ombres."}},
[12939] = {"Un défi pour l'honneur", nil, {"Le Duc Lankral, au Caveau des Ombres, vous a demandé d'utiliser le Drapeau de défi pour défier et vaincre 6 Combattants de Mjordin."}},
