[12951] = {"Informer le baron", nil, {"Parlez au Baron Lesquille au Caveau des Ombres."}},
[12952] = {"La perfection des gemmes", nil, {"Carter Tiffens, de l’Accostage de la Vengeance, a besoin de deux Énormes citrines, de deux Jades sombres et de deux Cristaux ombreux."}},
[12953] = {"Brûle, Valkyrion, brûle !", nil, {"Utilisez les Lance-harpons de Valkyrion pour brûler 6 Meules de foin sèches."}},
[12954] = {"L'Amphithéâtre de l'Angoisse : Yggdras !", nil, {"Entrez dans l’arène de l’Amphithéâtre de l’Angoisse et triomphez d’Yggdras ! Ensuite, allez voir Wodin le Serviteur-troll pour toucher votre récompense."}},
[12955] = {"Éliminez la concurrence", nil, {"Le Bondisseur, au Caveau des Ombres, vous demande d'affronter en duel et de vaincre Sigrid Née-de-glace, Efrem le Fidèle, Onu'zun et Bricole Sifflemèche."}},
[12956] = {"Une faible lueur d'espoir", nil, {"Vous devez apporter le Minerai resplendissant raffiné à Thorim au Temple des tempêtes."}},
[12957] = {"Les esclaves des Forge-foudre", nil, {"Rochecombe le Sculpteroc au Refuge de Rochecombe veut que vous libériez 6 Mécagnomes captifs et que vous tuiez 3 Sous-chefs forge-foudre."}},
[12958] = {"Livraison : Amulette en jade de sang", nil, {"Combinez une Amulette vrykule, un Jade sombre et une Pierre de sang pour créer une Amulette en jade de sang et l'apporter ensuite à Timothy Jones à Dalaran.\n\nVous trouverez une Amulette vrykule sur n'importe quel vrykul du Norfendre."}},
[12959] = {"Livraison : Figurine luminescente en ivoire", nil, {"Combinez de l’Ivoire nordique, une Calcédoine et un Cristal ombreux pour créer une Figurine luminescente en ivoire et apportez-la à Timothy Jones à Dalaran. Vous trouverez de l’Ivoire nordique sur n'importe quel Brochepelle ou Mammouth du Norfendre."}},
[12960] = {"Livraison : Broche du soleil pernicieux", nil, {"Combinez une Broche des nains de fer, une Énorme citrine et un Cristal soleil pour créer une Broche du soleil pernicieux et apportez-la ensuite à Timothy Jones à Dalaran.\n\nVous trouverez une Broche des nains de fer sur n'importe quel Nain de fer du Norfendre."}},
[12961] = {"Livraison : Figurine complexe en os", nil, {"Combinez un Os de proto-dragon, un Cristal soleil et un Jade sombre pour créer une Figurine complexe en os et l'apporter à Timothy Jones à Dalaran.\n\nVous trouverez un Os de proto-dragon sur n'importe quel Proto-dragon du Norfendre."}},
[12962] = {"Livraison : Relique de l'armure éclatante", nil, {"Combinez un Morceau d'armure des éléments, une Pierre de sang et une Enorme citrine pour créer une Relique de l'armure éclatante et l'apporter à Timothy Jones à Dalaran.\n\nVous trouverez un Morceau d'armure des éléments sur n'importe quel Revenant du Norfendre."}},
[12963] = {"Livraison : Bibelot du soleil changeant", nil, {"Combinez un Bibelot du Fléau, un Cristal soleil et un Cristal ombreux pour créer un Bibelot du soleil changeant et l'apporter à Timothy Jones à Dalaran.\n\nVous trouverez un Bibelot du Fléau sur n'importe quelle créature du Fléau en Norfendre."}},
[12964] = {"Le minerai sombre", nil, {"Apportez 5 Echantillons de minerai à Rochecombe le Sculpteroc au Refuge de Rochecombe."}},
[12965] = {"Les cadeaux de Loken", nil, {"Détruisez la Fureur de Loken, le Pouvoir de Loken et la Faveur de Loken, puis retournez voir Rochecombe le Sculpteroc au Refuge de Rochecombe."}},
[12966] = {"Vous ne pourrez pas le rater", nil, {"Le Roi Jokkum, à Dun Niffelem, veut que vous rejoigniez Njormeld à l'Enclume de Fjorn."}},
[12967] = {"Affronter les éléments", nil, {"Njormeld veut que vous accompagniez Snorri à l'Enclume de Fjorn et que vous l'aidiez à tuer 10 Revenants vengeurs."}},
[12968] = {"La folie de Yulda", nil, {"Iva la Vengeresse à Brunnhildar vous a demandé de tuer Yulda la Parlorage à Valkyrion."}},
[12969] = {"Il est à vous, ce gobelin ?", nil, {"Défiez Agnetta Tyrsdottar afin de sauver Zeev Pfutincelle. Retournez auprès de Lok'lira la Mégère à Brunnhildar une fois la tâche accomplie."}},
[12970] = {"Le Hyldsmeet", nil, {"Écoutez la proposition de Lok'lira la Mégère."}},
[12971] = {"Accepter tous les défis", nil, {"Lok'lira la Mégère à Brunnhildar vous a demandé de vaincre 6 Prétendantes victorieuses."}},
[12972] = {"Vous aurez besoin d'un ours", nil, {"Parlez à Brijana à l'extérieur de Brunnhildar."}},
[12973] = {"Les frères Barbe-de-bronze", nil, {"Accompagnez Brann Barbe-de-bronze et sa Machine volante au Fort du Givre."}},
[12974] = {"L'appel du champion !", nil, {"Cherchez Gurgthock à l'Amphithéâtre de l'Angoisse à Zul'Drak."}},
[12975] = {"In Memoriam", nil, {"Le Roi Jokkum à Dun Niffelem veut que vous récupériez 8 Fragments de cor à Chute-tonnerre."}},
[12976] = {"Un monument à ceux qui sont tombés", nil, {"Le Roi Jokkum veut que vous apportiez les Fragments du Cor de Hodir à Njormeld à Dun Niffelem."}},
[12977] = {"L’appel de Hodir", nil, {"Vous devez vaincre 5 Ancêtres niffelem et 5 Givre-nés sans repos à Chute-tonnerre. Vous devez ensuite souffler dans le Cor de Hodir au-dessus de leurs cadavres, puis rapportez le Cor de Hodir à Dun Niffelem."}},
[12978] = {"Face à la tempête", nil, {"Bruor Plaie-du-fer au Refuge de Rochecombe veut que vous assassiniez 10 Forge-foudre nidavelirs. Vous pouvez tuer des Champions forge-foudre, des Magi forge-foudre, ou des Golems de guerre forge-foudre."}},
[12979] = {"L'armure des ténèbres", nil, {"Apportez 4 Echantillons d'armure sombre à Bruor Plaie-du-fer au Refuge de Rochecombe."}},
[12980] = {"Les secrets de l'armure", nil, {"Emportez la Plaque d'armure sombre à l'Atelier de Mimir et demandez au Domestique Tock de l'examiner. Quand ce sera fait, retournez auprès de Bruor Plaie-du-fer au Refuge de Rochecombe."}},
[12981] = {"Chaud et froid", nil, {"Vous devez vous rendre au Lac du Champ-gelé pour y tuer les Revenants vermoulus et utiliser ensuite l'Essence de glace sur les Ferrailles fumantes à l'Enclume de Fjorn. Vous devrez alors récupérer 6 Morceaux de fer gelé et retourner à l'Enclume de Fjorn à Dun Niffelem."}},
[12982] = {"Les prisonniers de la Lame d'ébène", nil, {"Vaelen l'Écorché, au Caveau des Ombres, vous a demandé de libérer 8 Prisonniers de la Lame d'ébène. Ils se trouvent dans des cages partout dans la région de Jotunheim."}},
[12983] = {"La dernière de son espèce", nil, {"Astrid Bjornrittar à Brunnhildar vous a demandé de sauver une Matriarche glacegueule à la Caverne de l'Hibernation."}},
[12984] = {"Valduran Issu-des-tempêtes", nil, {"Utilisez le Cor de guerre de Rochecombe quand vous aurez trouvé Valduran Issu-des-tempêtes. Battez Valduran Issu-des-tempêtes et retournez voir Rochecombe le Sculpteroc au Refuge de Rochecombe."}},
[12985] = {"Forger une tête", nil, {"Njormeld à Dun Niffelem a besoin de vous pour utiliser la Pioche à pointe de diamant sur les Géants de fer morts au lac du Champ-gelé, puis retournez le voir quand vous aurez récupéré 8 Yeux de Forge-foudre."}},
[12986] = {"Le destin des titans", nil, {"Creteus dans le Pied-mont vous a demandé de vous rendre au Temple de l'Innovation, au Temple de l'Hiver, au Temple de la Vie et au Temple de l'Ordre, et d'utiliser la Banque de données pour examiner chaque zone."}},
[12987] = {"Placer le heaume de Hodir", nil, {"Njormeld vous demande de voler vers le sommet des Pointes de glace du Nord à Dun Niffelem et d'utiliser les Tablettes de déclaration."}},
[12988] = {"Détruisez les forges !", nil, {"Détruisez la Forge de foudre du nord, la Forge de foudre centrale et la Forge de foudre du sud avec les Bombes de Rochecombe, puis retournez voir Rochecombe le Sculpteroc au Refuge de Rochecombe."}},
[12989] = {"Les ténèbres rampantes", nil, {"Astrid Bjornrittar vous a demandé de tuer 8 Jormungars voraces à la Caverne de l'Hibernation à l'est de Brunnhildar."}},
[12991] = {"Tapez là où ça fait mal", nil, {"Tuez 10 Artificiers forge-foudre, puis retournez voir Bruor Plaie-du-fer au Refuge de Rochecombe."}},
[12992] = {"Écrasez les vrykuls !", nil, {"Vil, au Caveau des Ombres, veut que vous assassiniez 15 Vrykuls dans la région de Jotunheim."}},
[12993] = {"Une menace colossale", nil, {"Récupérez le Plan des caractéristiques offensives du colosse et le Plan des caractéristiques défensives du colosse, puis rapportez-les à Rochecombe le Sculpteroc au Refuge de Rochecombe."}},
[12994] = {"À la chasse aux espions", nil, {"Vous devez vaincre 3 Espions forge-foudre dans la Vallée des Anciens hivers.\n\nUtilisez le Croc de worg éthérien sur place pour invoquer un Chasseur éthérien qui débusquera les ennemis cachés. Vous devez retourner auprès de la Mère de la tanière worg de givre à Dun Niffelem une fois la vallée nettoyée de ces espions."}},
[12995] = {"Laisser notre marque", nil, {"Le Baron Lesquille, au Caveau des Ombres, veut que vous plantiez 15 Bannières de la Lame d'ébène près des cadavres de Vrykuls trouvés dans la région de Jotunheim."}},
[12996] = {"Tour de chauffe", nil, {"Astrid Bjornrittar à Brunnhildar veut que vous utilisiez les Rênes de matriarche ours de guerre pour vaincre Kirgaraak."}},
[12997] = {"Dans la fosse", nil, {"Astrid Bjornrittar à Brunnhildar veut que vous utilisiez les Rênes de matriarche ours de guerre dans la Fosse du croc et que vous battiez 6 Ours de guerre du Hyldsmeet."}},
[12998] = {"Le Cœur de la tempête", nil, {"Rochecombe le Sculpteroc au Refuge de Rochecombe veut que vous alliez à Uldis pour essayer d'empêcher que le Surveillant Narvir n'obtienne le Cœur de la tempête."}},
[12999] = {"La sorcière des ossements", nil, {"Parlez à la Sorcière des ossements dans la caverne, à Njorndar."}},
[13000] = {"Mesures d'urgence", nil, {"La Garde de sang Lorga vous demande d'aller à la tanière de Gimorak à l'est et de récupérer 6 Fourrures de worg."}},
[13001] = {"Forger la lance de Hodir", nil, {"Le Gardien du savoir Randvir à Dun Niffelem vous a demandé de récupérer 3 Éclats permagivre des profondeurs de la Caverne de l'hibernation et 3 Peaux de mammouth stoïque dans la Vallée des Anciens hivers."}},
[13002] = {"La perfection des gemmes", nil, {"Geba'li, du Bastion Chanteguerre, a besoin de deux Enormes citrines, de deux Jades sombres et de deux Cristaux ombreux."}},
[13003] = {"L’art de bien tuer son dragon", nil, {"Vous devez utiliser la Lance de Hodir pour défier un Wyrm sauvage dans la Vallée des Anciens hivers. Une fois que c'est fait, vous devez vous battre à mort."}},
[13004] = {"La perfection des gemmes", nil, {"Alestros, du Donjon de la Bravoure, a besoin de deux Énormes citrines, de deux Jades sombres et de deux Cristaux ombreux."}},
[13005] = {"Le serment des Terrestres", nil, {"Thorim, à la Terrasse des Faiseurs, vous demande d'utiliser le Cor des pics pour appeler les Terrestres, et vaincre 20 Nains de fer et 7 Sentinelles de fer."}},
[13006] = {"Nettoyage à suc", nil, {"Vous devez aller à la Caverne de l'hibernation à l'ouest de Dun Niffelem pour récupérer 5 unités d'Huile visqueuse sur les créatures du même nom sur place.\n\nVous devez ensuite retourner à Dun Niffelem pour appliquer l'huile sur le Heaume de Hodir."}},
[13007] = {"Le colosse de fer", nil, {"Utilisez un Jormungar apprivoisé pour vaincre le Colosse de fer, puis retournez voir Rochecombe le Sculpteroc au Refuge de Rochecombe."}},
[13008] = {"Les tactiques du Fléau", nil, {"Le Commandant de la croisade Entari, à l'Avant-garde d'Argent dans la Couronne de glace, vous a demandé de libérer 8 Croisés entoilés."}},
[13009] = {"Un nouveau départ", nil, {"Njormeld veut que vous apportiez l'Armure reforgée à Thorim au Temple des tempêtes."}},
[13010] = {"Krolmir, le marteau des tempêtes", nil, {"Thorim vous demande d'aller parler au Roi Jokkum à Dun Niffelem et de découvrir ce qu'il sait à propos de Krolmir."}},
[13011] = {"Éliminer Jorcuttar", nil, {"Le Roi Jokkum à Dun Niffelem vous demande de tuer Jorcuttar à la Caverne de l’hibernation."}},
