[13034] = {"Le témoin et le héros", nil, {"Xarantaur, au Camp Tunka'lo, veut que vous lisiez le Premier parchemin de l'histoire, le Second parchemin de l'histoire et le Troisième parchemin de l'histoire."}},
[13035] = {"Les laquais de Loken", nil, {"Thorim vous demande de tuer Eisenfaust dans le Hall du Façonneur, et Halefnir l'Enfant du vent et Duronn le Formerune à la Terrasse des Faiseurs."}},
[13036] = {"L'honneur avant tout", nil, {"Présentez-vous au Commandant de la croisade Entari à l'Avant-garde d'Argent, dans la Couronne de glace."}},
[13037] = {"Souvenirs de Sabot-tempête", nil, {"Parlez au Chef Vivelance au Gouffre du Sérac, puis retournez voir Xarantaur au Camp Tunka'lo."}},
[13038] = {"Distortions temporelles", nil, {"Utilisez le Totem du chef pour fermer 4 Failles du Sérac, puis rapportez le Totem à Xarantaur au Camp Tunka'lo."}},
[13039] = {"Défendre l'avant-garde", nil, {"Le Seigneur croisé Dalfors, à l'Avant-garde d'Argent dans la Couronne de glace, vous a demandé de tuer 15 Nérubiens des profondeurs oubliées."}},
[13040] = {"Soigner l'insoignable", nil, {"Le Père Gustav, à l'Avant-garde d'Argent dans la Couronne de glace, vous a demandé de lui apporter 10 Glandes à venin des profondeurs oubliées."}},
[13041] = {"Terminer la livraison", nil, {"Apportez une Calcédoine à Timothy Jones à Dalaran."}},
[13042] = {"Dans les profondeurs des Basses-salles", nil, {"La Sorcière des ossements à Njorndar vous a demandé de battre l'Apprenti Osterkilgr afin d'obtenir des informations concernant ce que prépare le Dr Terrible.\n\nVous devez ensuite tuer l'Apprenti."}},
[13043] = {"Le tout est plus grand que la somme des parties", nil, {"Montez Nergeld et prenez-en le contrôle. Défendez-le contre les habitants de la Salle des Horreurs jusqu'à ce que le Dr Terrible se montre.\n\nUtilisez Nergeld pour tuer le docteur, puis retournez voir la Sorcière des ossements à Njorndar."}},
[13044] = {"S'il reste des survivants...", nil, {"Parlez à Penumbrius à l'Avant-garde d'Argent dans la Couronne de glace."}},
[13045] = {"Ça va tanguer dans la verdure !", nil, {"Sauvez 3 Croisés d'argent capturés à Fléaulme et amenez-les à l'Infirmerie de l'avant-garde au centre de l'Avant-garde d'Argent. Allez voir le Généralissime Tirion Fordring si vous réussissez."}},
[13046] = {"Caler la dent creuse d'Arngrim", nil, {"Vous devez donner à manger à Arngrim 5 Jormungars désincarnés puis retourner auprès de l'image gelée d'Arngrim à Dun Niffelem.\n\nRendez-vous dans la Vallée des Anciens hivers et utilisez la Dent d'Arngrim sur les Jormungars mobiles qui s'y trouvent. Une fois qu'ils sont désincarnés, vous devez les combattre jusqu'à ce qu'ils soient affaiblis."}},
[13047] = {"La rétribution", nil, {"Rencontrez Thorim près du Temple de la Sagesse. Racontez l'issue du combat au Roi Jokkum à Dun Niffelem."}},
[13048] = {"Lorsque le temps tourne court", nil, {"Utilisez le Marteau de la connaissance sur les Reliques taunkas pour le mettre en phase avec l'époque de Sabot-tempête. Une fois que vous l'aurez mis en phase avec 6 Reliques de l'époque de Sabot-tempête, retournez voir Xarantaur au Camp Tunka'lo."}},
[13049] = {"Les armes du héros", nil, {"Retrouvez la Lance de Sabot-tempête et la Cotte de maille de Sabot-tempête, et rapportez-les à Xarantaur au Camp Tunka'lo."}},
[13050] = {"Veranus", nil, {"Thorim, au Temple des tempêtes, veut que vous obteniez 5 Petits œufs de proto-drake sur les pics proches de Brunnhildar."}},
[13051] = {"Violation de territoire", nil, {"Placez les Œufs de proto-dragon volés au sommet du Nid de la mère des couvées, puis retournez voir Thorim au Temple des tempêtes."}},
[13054] = {"Le pisteur disparu", nil, {"Cherchez le Traqueur Val'zij dans la tanière de Gimorak, au sud-est du Point d'impact de Grom'arsh."}},
[13055] = {"Remède de caverne", nil, {"Le Traqueur Val'zij, à la tanière de Gimorak, vous a demandé de récupérer 1 Glande à toxine jormungar et 8 Champignons de la caverne."}},
[13056] = {"La vengeance est un plat qui se mange à toute heure", nil, {"Le Traqueur Val'zij vous demande de tuer Gimorak dans la Tanière de Gimorak."}},
[13057] = {"La terrasse des Faiseurs", nil, {"Trouvez Thorim à la limite sud-est de la Terrasse des Faiseurs, au nord-ouest de Dun Niffelem."}},
[13058] = {"Changer le sens du vent", nil, {"Utilisez le Marteau de la connaissance pour remonter le temps et affronter le Vent du nord sous les traits de Sabot-tempête. Quand Sabot-tempête perd, récupérez le Cor de fureur élémentaire et achevez le Vent du nord, puis retournez voir Xarantaur au Camp Tunka'lo."}},
[13059] = {"Vengeance pour les varguls", nil, {"Bethod Feigr, aux Basses-salles, vous demande de défier le Thane Illskar en jetant son épée, puis de le tuer au Cercle de Souffrance."}},
[13060] = {"Quand tout le reste a échoué…", nil, {"Parlez à Ricket pour utiliser l'Engin de livraison à propulsion D16 de Ricket afin de pénétrer au cœur des Pics Foudroyés."}},
[13061] = {"Préparation pour la gloire", nil, {"Parlez à Lok'lira la Mégère à Brunnhildar."}},
[13062] = {"Cadeau d'adieu de Lok'lira", nil, {"Parlez à Gretta la Médiatrice à Brunnhildar."}},
[13063] = {"Digne de valeur", nil, {"Brijana veut que vous alliez à Brunnhildar pour parler avec Astrid Bjornrittar."}},
[13064] = {"Rivalité fraternelle", nil, {"Thorim veut que vous écoutiez son histoire."}},
[13065] = {"L'ancien Ohanzee", nil, nil},
[13066] = {"L'ancien Yurauk", nil, nil},
[13067] = {"L'ancien Chogan'gada", nil, nil},
[13068] = {"Une histoire de bravoure", nil, {"Le Généralissime Tirion Fordring, à la Cime des Croisés, vous a demandé de trouver un héros, le Croisé Bridenbrad, à la Veille silencieuse, au nord-est de la Couronne de glace. Son feu de camp devrait être le signe le plus évident de sa position depuis les cieux."}},
[13069] = {"Descendez-les tous !", nil, {"Le Bondisseur, au Caveau des Ombres, veut que vous utilisiez un Harpon à tir rapide jotunheim pour abattre 15 Proto-Drakes de Jotunheim et leurs Chevaucheurs."}},
[13070] = {"Un front froid se rapproche", nil, {"Présentez-vous au Maître de siège Fezzik à l'Avant-garde d'Argent, dans la Couronne de glace."}},
[13071] = {"Vil aime feu !", nil, {"Vil, au Caveau des Ombres, vous a demandé de voler un Proto-drake njorndar et de l'utiliser pour mettre le feu à 8 Bâtiments vrykuls dans la région de Jotunheim.\n\nLa forteresse de Balargarde, en raison de sa nature, ne peut pas être enflammée."}},
[13072] = {"Un héros reste", nil, {"Apportez des nouvelles du destin du Croisé Bridenbrad au Généralissime Tirion Fordring à la Cime des Croisés."}},
[13073] = {"La faveur du gardien", nil, {"Le Généralissime Tirion Fordring, à la Cime des Croisés, vous a demandé d'obtenir de l'Archidruide Lilliandra un passage vers Reflet-de-Lune afin de parler au Gardien Remulos."}},
[13074] = {"Un espoir dans le cauchemar d'émeraude", nil, {"Le Gardien Remulos, à Reflet-de-lune, vous a demandé de pénétrer dans le Rêve d'émeraude et de récupérer 3 Glands d'émeraude.\n\nPassez les limites extérieures du Sanctuaire de Remulos pour retourner en Azeroth."}},
[13075] = {"La faveur de Remulos", nil, {"Retournez voir le Croisé Bridenbrad à la Veille Silencieuse, dans la Couronne de glace, avec le Gland d'émeraude."}},
[13076] = {"Encore un peu de temps", nil, {"Retournez voir le Généralissime Tirion Fordring à la Cime des Croisés et rapportez-lui les effets du Gland d'émeraude."}},
[13077] = {"Le toucher d'un Aspect", nil, {"Demandez l'aide d'Alexstrasza la Lieuse-de-vie au Temple du Repos du ver."}},
[13078] = {"Les larmes de Dahlia", nil, {"Alexstrasza la Lieuse-de-vie, au Temple du Repos du ver, vous a demandé de récupérer les Larmes de Dahlia sur une terre récemment purifiée par les flammes d'un Gardien rubis."}},
[13079] = {"La faveur d'Alexstrasza", nil, {"Apportez le Souffle d'Alexstrasza au Croisé Bridenbrad à la Veille Silencieuse, dans la Couronne de glace."}},
[13080] = {"L'espoir fait vivre", nil, {"Retournez voir le Généralissime Tirion Fordring à la Cime des Croisés et rapportez-lui les effets du Souffle d'Alexstrasza."}},
[13081] = {"La volonté des naaru", nil, {"Demandez l'aide d'A'dal à Shattrath, dans la Forêt de Terokkar."}},
[13082] = {"La faveur d'A'dal", nil, {"Retournez auprès du Croisé Bridenbrad à la Veille Silencieuse, dans la Couronne de glace."}},
[13083] = {"La Lumière dans les ténèbres", nil, {"Apportez le Tabard du Croisé Bridenbrad au Généralissime Tirion Fordring à la Cime des Croisés."}},
[13084] = {"Jouer les vandales à Jotunheim", nil, {"Le Duc Lankral, au Caveau des Ombres, vous demande de brûler 10 Bannières vrykules."}},
[13085] = {"Vaelen est de retour", nil, {"Parlez à Vaelen l'Écorché au Caveau des Ombres."}},
[13086] = {"La dernière ligne de défense", nil, {"Utilisez un Canon d'Argent pour détruire 100 Attaquants du Fléau. Vous devez également tuer 3 Destructeurs couvegivres.\n\nRetournez voir le Maître de siège Fezzik si vous deviez réussir."}},
[13087] = {"La cuisine du Grand Nord", nil, {"Apportez 4 morceaux de Viande glacée à Brom Mouillebière à Valgarde. La viande glacée se trouve sur n’importe quelle bête du Norfendre."}},
[13088] = {"La cuisine du Grand Nord", nil, {"Apportez 4 Viandes glacées à Rollick Mackreel au Donjon de la Bravoure. Vous pouvez récupérer de la Viande glacée sur n'importe quelle bête du Norfendre."}},
[13089] = {"La cuisine du Grand Nord", nil, {"Apportez 4 morceaux de Viande glacée à Thomas Kolichio, à l'Accostage de la Vengeance. La viande glacée se trouve sur n’importe quelle bête du Norfendre."}},
[13090] = {"La cuisine du Grand Nord", nil, {"Apportez 4 Viandes glacées à Orn Tendre-sabot au Bastion Chanteguerre. Vous pouvez récupérer de la Viande glacée sur n'importe quelle bête du Norfendre."}},
[13091] = {"L'art d'être une terreur d'eau", nil, {"La Sorcière des ossements, à Njorndar, vous demande de posséder une Terreur d'eau et de l'utiliser pour tuer 10 Vrykuls dans la région de Jotunheim.\n\nSont considérés comme des Vrykuls les Gardiens du sommeil jotunheim, les Guerriers jotunheim, les Magi d'eau mjordin, les Njorndars sœur-de-lance et les Varguls du Valhalas."}},
[13092] = {"Lecture des ossements", nil, {"La Sorcière des ossements, à Njorndar, vous a demandé de récupérer 15 Os vrykuls."}},
[13093] = {"Lecture des ossements", nil, {"Conditions :"}},
[13094] = {"Ils n'ont vraiment honte de rien, alors ?", nil, {"L’Image du mage de guerre Kaitlyn veut que vous récupériez les Recherches de Bérinand."}},
[13095] = {"Ils n'ont vraiment honte de rien, alors ?", nil, {"L’Image du mage de guerre Kaitlyn veut que vous récupériez les Recherches de Bérinand."}},
[13096] = {"Gal'darah doit payer", nil, {"Tol'mar à Dubra'Jin veut que vous assassiniez Gal'darah à Gundrak."}},
[13097] = {"Terminer le travail", nil, {"Har'koa vous demande de parler à Tol'mar à Dubra'jin."}},
[13098] = {"Pour la postérité", nil, {"La chroniqueuse Bah'Kini à Dubra'Jin vous demande d'aller à Gundrak et de récupérer 6 Tablettes de l'histoire drakkari."}},
[13099] = {"Petite vérification", nil, {"To'kini vous a demandé de parler à Bah'kini à Dubra'Jin."}},
[13100] = {"Pain de viande aux champignons infusés", nil, {"Cuisinez 4 Champignons infusés des égouts de Dalaran avec 2 Viandes glacées dans votre Plat à pain de viande et apportez le Pain de viande à Orton Bennet aux Objets de la Moore, situé sur la Place Tisserune à Dalaran."}},
[13101] = {"Convention à l'Abracadabar", nil, {"Apportez 4 Ragoûts nordiques préparés avec votre compétence en cuisine et une Cruche de vin de la Fromagerie de Dalaran à Arille Mirazur, à l'Abracadabar qui se trouve sur la Place Tisserune de Dalaran."}},
[13102] = {"Le ragoût des égouts", nil, {"Cuisinez 4 Carottes du Chant de cristal de la Forêt du Chant de cristal avec 4 Viandes glacées dans la Marmite.\n\nUne fois qu'il est prêt, apportez le Ragoût de légumes à Ajay Green, l'Aubergiste des égouts de Dalaran."}},
[13103] = {"Du fromage pour Froncelor", nil, {"Combinez le Plateau à fromages avec un Vieux limbourg de Dalaran provenant de la fromagerie « Un dernier pour la route » et avec 6 Verres de vin à moitié pleins trouvés sur des tables à Dalaran.\n\nApportez le Plateau de vin et de fromages à Ranid Froncelor chez les Tanneurs de peaux à la Halle des mages."}},
[13104] = {"Encore une fois sur la Brèche, héros", nil, {"Présentez-vous au Gardien d'ébène dans la Vallée des Échos, à l'extérieur de la Brèche."}},
[13105] = {"Encore une fois sur la Brèche, héros", nil, {"Présentez-vous au Gardien d'ébène dans la Vallée des Échos, à l'extérieur de la Brèche."}},
[13106] = {"Le Guet noir", nil, {"Parlez à la Cavalière noire Arly au Guet noir, dans la Couronne de glace."}},
[13107] = {"Hot-dogs à la moutarde !", nil, {"Remplissez le Panier à pique-nique vide avec 4 Saucisses de rhinocéros que vous aurez cuisinées avec votre compétence en cuisine et 4 Moutardes sauvages que vous trouverez dans les zones herbeuses de Dalaran.\n\nApportez le Panier à pique-nique à l'Archimage Pentarus à l'Aire de Krasus à Dalaran."}},
[13108] = {"Quoi qu'il en coûte !", nil, {"Le Roi Jokkum à Dun Niffelem vous demande de pénétrer dans les Salles de Foudre et de battre Loken. Vous devez ensuite retournez voir le Roi Jokkum avec la Langue de Loken."}},
[13109] = {"Diamétralement opposé", nil, {"Le Roi Jokkum à Dun Niffelem vous demande de pénétrer dans les Salles de Foudre et de battre Volkhan."}},
[13110] = {"Les morts sans repos", nil, {"Le Père Gustav, de la Vallée des Échos dans la Couronne de glace, vous demande d'utiliser de l'Eau bénite sur les cadavres des Croisés réanimés afin de libérer 10 Âmes sans repos."}},
[13111] = {"Unique en son genre", nil, {"La Chroniqueuse Bah'Kini à Dubra'Jin vous demande d'entrer à Gundrak et de récupérer un morceau de Colosse drakkari."}},
[13112] = {"Pain de viande aux champignons infusés", nil, {"Cuisinez 4 Champignons infusés des égouts de Dalaran avec 2 Viandes glacées dans votre Plat à pain de viande et apportez le Pain de viande aux champignons à Orton Bennet, aux Objets de la Moore situé sur la Place Tisserune à Dalaran."}},
[13113] = {"Convention à l'Abracadabar", nil, {"Apportez 4 Ragoûts nordiques et une Cruche de vin de la Fromagerie de Dalaran à Arille Mirazur à l'Abracadabar sur la Place Tisserune à Dalaran."}},
[13114] = {"Le ragoût des égouts", nil, {"Cuisinez 4 Carottes du Chant de cristal de la Forêt du Chant de cristal avec 4 Viandes glacées dans la Marmite.\n\nPuis apportez le Ragoût de légumes à Ajay Green, l'Aubergiste des égouts de Dalaran."}},
[13115] = {"Du fromage pour Froncelor", nil, {"Combinez le Plateau à fromages avec un Vieux limbourg de Dalaran provenant de la fromagerie « Un dernier pour la route » et avec 6 Verres de vin à moitié pleins trouvés sur des tables à Dalaran.\n\nApportez le Plateau de vin et de fromages à Ranid Froncelor chez les Tanneurs de peaux à la Halle des mages."}},
[13116] = {"Hot-dogs à la moutarde !", nil, {"Remplissez le Panier à pique-nique vide avec 4 Saucisses de rhinocéros que vous avez cuisinées avec votre compétence et 4 Moutardes sauvages que vous trouverez dans les zones herbeuses de Dalaran.\n\nApportez le Panier à pique-nique à l'Archimage Pentarus à l'Aire de Krasus à Dalaran."}},
[13117] = {"D'où viennent-ils ?", nil, {"Enquêtez sur un Autel d'invocation dans la zone est de la Charognerie, puis retournez faire votre rapport à la Cavalière noire Arly au Guet noir."}},
[13118] = {"La purification de Fléaulme", nil, {"Le Gardien d'ébène, de la Vallée des Échos dans la Couronne de glace, vous demande de tuer 3 Grands prêtres des profondeurs oubliées, 3 Sous-rois des profondeurs oubliées et 8 Croisés réanimés."}},
[13119] = {"Détruire les autels", nil, {"Tuez le Maître invocateur Zarod et utilisez son Bâton d'invocation pour détruire le Premier autel d'invocation, le Deuxième autel d'invocation, le Troisième autel d'invocation et le Quatrième autel d'invocation. Une fois la tâche terminée, retournez voir la Cavalière noire Arly au Guet noir."}},
[13120] = {"Le regard de la mort", nil, {"Utilisez l'Orbe du regard de la mort pour placer des orbes sur le Socle de l'orbe du laboratoire des abominations, sur le Socle de l'orbe du laboratoire des géants de chair et sur le Socle de l'orbe de la zone du chaudron, puis retournez voir la Cavalière noire Arly au Guet noir."}},
[13121] = {"À travers l'Œil", nil, {"La Sorcière des ossements, à Njorndar, vous a demandé d'utiliser l'Œil du roi-liche pour obtenir des informations."}},
[13122] = {"La pierre du Fléau", nil, {"Le Gardien d'ébène, de la Vallée des Échos dans la Couronne de glace, vous a demandé de lui apporter 15 Pierres du Fléau."}},
[13124] = {"La lutte continue", nil, {"Raelorasz veut que vous sauviez Belgaristrasz et ses compagnons dans l’Oculus."}},
[13125] = {"L'air est immobile", nil, {"Le Gardien d'ébène, de la Vallée des Échos dans la Couronne de glace, vous a demandé de tuer Salranax le Tranche-chair, le Sous-roi Talonox et le Grand prêtre Yath'amon."}},
[13126] = {"Un front uni", nil, {"Belgaristrasz veut que vous tuiez 10 Assemblages centrifuges pour détruire le Bouclier de Varos. Vous devez ensuite détruire Varos Arpentenuée."}},
[13127] = {"Le seigneur-mage Urom", nil, {"Belgaristrasz veut que vous triomphiez du Seigneur-mage Urom dans l’Oculus."}},
[13128] = {"Une petite chance de réussir", nil, {"Belgaristrasz veut que vous tuiez Eregos dans l'Oculus."}},
[13129] = {"Manipulation", nil, {"Kurzel veut que vous utilisiez le Morceau de chemise de Kurzel sur le cadavre de Novos l’Invocateur, puis que vous lui remettiez le Tissu souillé d’ichor à l’intérieur du donjon de Drak’Tharon."}},
[13130] = {"La pierre qui lança une révolution", nil, {"L'Architecte de la croisade Silas, de la Vallée des Échos dans la Couronne de glace, vous demande de récupérer 10 Troncs de Cœur-du-bois cristallins et 10 morceaux de Pierres de construction elfique."}},
[13131] = {"Des rebuts dans ma malle", nil, {"Brigg, à la Cime d'Utgarde, vous a demandé de lui apporter 5 Barres d'argent immaculées, 3 Verroteries rutilantes, 2 Coupes dorées et une Statue en jade."}},
[13132] = {"La vengeance sera mienne !", nil, {"Brigg, à la Cime d'Utgarde, vous a demandé de tuer le Roi Ymiron."}},
[13133] = {"Trouver l'ancien héros", nil, {"La Sorcière des ossements, à Njorndar, veut que vous lui rameniez Iskalder."}},
[13134] = {"Répandre leur sang", nil, {"La Cavalière noire Arly, au Guet noir, vous demande de détruire 5 cuves de Fluide d'embaumement et 5 Orbes de sang."}},
[13135] = {"Elle pourrait nous tuer tous", nil, {"L'Ingénieur de la croisade Spitzpatrick, de la Vallée des Échos dans la Couronne de glace, vous a demandé de récupérer 8 morceaux d'Énergie cristallisée."}},
[13136] = {"Éclats dentelés", nil, {"Apportez 10 Éclats dentelés au Croisé Olakin Sainrith au Guet noir."}},
[13137] = {"Combat pas-si-honorable-que-ça", nil, {"La Sorcière des ossements, à Njorndar, vous demande de vaincre Iskalder sur la Flèche de la Balafre."}},
[13138] = {"Je fonds… Je fonds !", nil, {"Utilisez le Sac d'éclats dentelés près de l'une des Fournaises de Malykriss du niveau inférieur. Retournez voir le Croisé Olakin Sainrith au Guet noir avec la Barre de métal fondue."}},
[13139] = {"Dans le cœur gelé du Norfendre", nil, {"Apportez le rapport du Père Gustav au Généralissime Tirion Fordring à l'Avant-garde d'Argent, dans la Couronne de glace."}},
[13140] = {"Les forgerunes de Malykriss", nil, {"Apportez 5 Plaques runiques en saronite au Croisé Olakin Sainrith au Guet noir."}},
[13141] = {"La bataille pour la cime des Croisés", nil, {"Rendez-vous à la Cime des Croisés et utilisez la Bannière bénie de la Croisade au sommet de la Pile de crânes de croisés. Défendez la bannière contre toute attaque du Fléau jusqu'à ce que la terre au sommet de la Cime soit sanctifiée, puis retournez voir le Père Gustav dans la Vallée des Échos, dans la Couronne de glace."}},
[13142] = {"La revanche de la banshee", nil, {"La Sorcière des ossements, à Njorndar, vous a demandé d'exercer sa revanche en défiant et en battant le Thane suprême Balargarde."}},
[13143] = {"Nouvelle recrue", nil, {"Vereth le Fourbe, à la Cime de Souffrance, vous a demandé de lui amener un Traqueur agile soumis au sommet des rochers au nord-est de la Cime de Souffrance."}},
[13144] = {"D'un squelette, deux membres du Fléau", nil, {"La Cavalière noire Arly, au Guet noir, vous a demandé de détruire 3 Abominations enchaînées avec des Squelettes ardents."}},
[13145] = {"Le Fort infâme", nil, {"Vereth le Fourbe, à la Cime de Souffrance, vous a demandé d'utiliser un Traqueur agile pour reconnaître Malykriss."}},
[13146] = {"Une abondance de générosité", nil, {"Vereth le Fourbe veut que vous utilisiez la Chaîne de fer du Traqueur agile pour tirer des Bombes du Fléau vers 4 Atrocités chancelantes dans Malykriss."}},
[13147] = {"Affrontements en vue", nil, {"Vereth le Fourbe, à la Cime de Souffrance, vous demande d'utiliser un Traqueur agile pour lancer des rochers sur des Brutes umbrales quand celles-ci s'approchent de morts-vivants inférieurs à Malykriss. Revenez quand vous aurez déclenché 5 bagarres."}},
[13148] = {"Réparation du collier", nil, {"Réparez le Collier avec une simple Calcédoine et rapportez-le à Timothy Jones à la joaillerie à la Halle des mages à Dalaran."}},
[13149] = {"Dissiper les illusions", nil, {"Chromie veut que vous utilisiez le Disrupteur arcanique sur les Caisses de céréales suspectes dans le Stratholme d'antan, puis parlez-lui près de l'entrée de Stratholme."}},
[13151] = {"Une escorte royale", nil, {"Chromie vous a demandé d'accompagner Arthas au cours de l'Épuration de Stratholme. Retournez lui parler une fois Mal'Ganis vaincu."}},
[13152] = {"Une visite chez le docteur", nil, {"La Cavalière noire Arly, au Guet noir, vous demande de détruire le Pieu en métal qui retient Sutures dans le Sanctum de réanimation, puis de l'aider à prendre sa revanche sur le Docteur Sabnok."}},
[13153] = {"Protéger les guerriers", nil, {"La Sorcière Kaylana, à la Forteresse de Joug-d'hiver, veut que vous récupériez 10 Armures imprégnées de la Horde, aux Chutes glaciales."}},
[13154] = {"Des os et des flèches", nil, {"Le Fabricant d'arcs Randolph, à la Forteresse de Joug-d'hiver, vous a demandé de récupérer 10 Carquois de flèches en os de dragon à la Forêt des Ombres."}},
[13155] = {"Vereth le Fourbe", nil, {"Présentez-vous à Vereth le Fourbe à la Cime de Souffrance, dans la Couronne de glace."}},
[13156] = {"Une herbe rare", nil, {"L'Anachorète Tessa, à la Forteresse de Joug-d'hiver, vous a demandé de récupérer 10 Bourses d'herbes de la Horde sur des personnages-joueurs de la Horde, dans les steppes de la Vie."}},
[13157] = {"La cime des Croisés", nil, {"Allez trouver le Généralissime Tirion Fordring à la Cime des Croisés dans la Couronne de glace."}},
[13158] = {"La discrétion avant tout", nil, {"Rhonin vous demande d'aller au Fort Pourpre à Dalaran et de parler au Gardien Alturas."}},
[13159] = {"Confinement", nil, {"Le Gardien Alturas vous demande de pénétrer dans le Fort Pourpre et d'éliminer la force d'invasion des Dragons bleus. Retournez le voir une fois Cyanigosa tuée."}},
[13160] = {"Une vision étonnante", nil, {"Vereth le Fourbe, à la Cime de Souffrance, vous demande d'utiliser la compétence en saut d'un Traqueur agile pour détruire 12 Factionnaires peau-de-glace endormis."}},
[13161] = {"Le cavalier de l'impie", nil, {"Vereth le Fourbe, à la Cime de Souffrance, vous demande de tuer Rokir, le Cavalier de l'Impie."}},
[13162] = {"La cavalière du Givre", nil, {"Vereth le Fourbe, à la Cime de Souffrance, vous demande de tuer Saph, la Cavalière du Givre."}},
[13163] = {"Le cavalier du Sang", nil, {"Vereth le Fourbe, à la Cime de Souffrance, vous demande de tuer Baelok, le Cavalier du Sang."}},
[13164] = {"Le destin de Sangreplaie", nil, {"Vereth, à la Cime de Souffrance, vous a envoyé tuer Orbaz Sangreplaie."}},
[13165] = {"Reprendre Achérus", nil, {"Servez-vous du sort Porte de la mort pour retourner à Achérus : le fort d’Ébène. À votre arrivée, présentez-vous au Généralissime Darion Mograine."}},
[13166] = {"La bataille pour le fort d'Ébène", nil, {"Tuez 10 créatures du Fléau et aidez à tuer Recousu. Si vous y parvenez, retournez auprès du Généralissime Darion Mograine."}},
[13167] = {"Mort au roi traître", nil, {"Kilix le Démêleur, à la Fosse de Narjun, veut que vous triomphiez d’Anub’arak à Azjol-Nérub. Vous devez lui rapporter la Carcasse brisée d’Anub’arak."}},
[13168] = {"Cadeaux de départ", nil, {"Keritose Lame-sanglante, du Caveau des Ombres, vous a demandé d'utiliser un Œil d'assujettissement pour prendre le contrôle d'un Gardien de l'Eidolon capturé."}},
[13169] = {"Le meilleur ami du mort-vivant", nil, {"Keritose Lame-sanglante, du Caveau des Ombres, vous demande d'utiliser le Gardien de l'Eidolon pour nourrir 18 Chiens pestiférés efflanqués."}},
[13170] = {"L'honneur, c'est pour les faibles", nil, {"Keritose Lame-sanglante, du Caveau des Ombres, voudrait que vous assassiniez 20 Guetteurs sans repos."}},
[13171] = {"D'où ils sont venus", nil, {"Keritose Lame-sanglante, du Caveau des Ombres, vous demande de détruire 10 Cristaux de confinement du Fléau."}},
[13172] = {"Les graines du chaos", nil, {"Keritose Lame-sanglante, du Caveau des Ombres, vous a demandé de tuer 80 morts-vivants dans la Carrière des Larmes. Parlez-lui pour obtenir un Griffon squelette pour le combat."}},
[13173] = {"Profiter de la confusion", nil, nil},
[13174] = {"Dans la confusion", nil, {"Keritose Lame-sanglante, du Caveau des Ombres, vous a demandé un Document de la carrière des Larmes, un Plan de la carrière des Larmes, une Carte de la carrière des Larmes et un Emploi du temps de la carrière des Larmes."}},
