[13182] = {"N'oubliez pas les œufs !", nil, {"Kilix le Démêleur, à la Fosse de Narjun, veut que vous pénétriez dans Azjol-Nérub pour y détruire 6 Œufs de nérubiens du Fléau."}},
[13183] = {"Victoire à Joug-d'hiver", nil, {"Gagnez une bataille sur le champ de bataille de Joug-d'hiver, et retournez voir l'Officier tactique Kilrath, à Joug-d'hiver."}},
[13184] = {"Survivre à l'utilité", nil, nil},
[13185] = {"L'arrêt du siège", nil, {"Le Lieutenant Murp, à la Forteresse de Joug-d'hiver, vous a demandé de détruire 3 Engins de siège de l'Alliance."}},
[13186] = {"L'arrêt du siège", nil, {"Le Démolisseur en chef Legoso, à la Forteresse de Joug-d'hiver, vous a demandé de détruire 3 Engins de siège de la Horde."}},
[13187] = {"Les Sans-visages", nil, {"Le Voyant Ixit, dans Ahn’Kahet, veut que vous tuiez le Héraut Volazj et les trois Oubliés qui l’accompagnent."}},
[13188] = {"Là où marche le roi", nil, {"Remettez la lettre au roi Varian Wrynn de l’Alliance, au Donjon de Hurlevent, dans la ville de Hurlevent."}},
[13189] = {"La bénédiction du chef de guerre", nil, {"Remettez la lettre au chef de guerre de la Horde, à Orgrimmar."}},
[13190] = {"Tout vient à point…", nil, {"Kilix le Démêleur, à la Fosse de Narjun, vous a demandé d'obtenir un Cadavre de guetteur d'Ahn'kahar et de le placer sur le brasero d'Ahn'kahet à Ahn'kahet.\n\nCette quête ne peut être accomplie qu'en mode héroïque."}},
[13191] = {"Alimenter les démolisseurs", nil, {"Le Poliorcète Sabot-puissant, à la Forteresse de Joug-d'hiver, vous a demandé de récupérer 10 Braises éternelles sur des personnages-joueurs de l'Alliance, au Chaudron des flammes."}},
[13192] = {"Surveiller les murs", nil, {"Le Maître vodoun Fu'jin, à la Forteresse de Joug-d'hiver, vous a demandé de récupérer 10 Cuirasses enchantées de l'Alliance aux Chutes glaciales."}},
[13193] = {"Des os et des flèches", nil, {"Le Lieutenant Vieron Plumembrase, à la Forteresse de Joug-d'hiver, vous a demandé de récupérer 10 Carquois de flèches en os de dragon, dans la Forêt des Ombres."}},
[13194] = {"La guérison par les plantes", nil, {"Le Primaliste Mulfort vous a demandé de récupérer 10 Bourses d'herbes de l'Alliance sur des personnages-joueurs de l'Alliance, dans les Steppes de la Vie."}},
[13195] = {"Une herbe rare", nil, {"L'Anachorète Tessa, à Joug-d'hiver, vous a demandé de récupérer 10 Bourses d'herbes de la Horde sur des personnages-joueurs de la Horde, dans les Steppes de la Vie."}},
