[13202] = {"Ensorceler les murs", nil, {"Le Maître vodoun Fu'jin, à Joug-d'hiver, vous a demandé de récupérer 10 Cuirasses enchantées de l'Alliance sur des personnages-joueurs de l'Alliance, aux Chutes Glaciales."}},
[13203] = {"Un cadeau pour Voile d'hiver", nil, nil},
[13204] = {"Des champignons fétides", nil, {"Vous devez récupérer 6 Champignons ignobles sur des Bêtes sauvages de la caverne, à Ahn'kahet, et les apporter à Kilix le Démêleur à la Fosse de Narjun."}},
[13205] = {"Désarmement", nil, {"Le Défenseur Mordun veut que vous entriez dans le Donjon d’Utgarde pour y voler 5 Armes vrykules."}},
[13206] = {"Désarmement", nil, {"Le Forestier-sombre Marrah vous a demandé de voler 5 Armes vrykules au Donjon d'Utgarde et de les lui apporter à l’intérieur du Donjon d'Utgarde."}},
[13207] = {"Les salles de Pierre", nil, {"Brann Barbe-de-bronze veut que vous l'accompagniez à la découverte des secrets des Salles de Pierre."}},
[13211] = {"Purifions-les par le feu", nil, {"Le Croisé Olakin Sainrith, au Guet noir, vous a demandé de brûler 7 Cadavres purulents en utilisant la Torche d'Olakin."}},
[13212] = {"Il est tombé en pièces", nil, {"Récupérez le Torse d'Olakin, les Jambes d'Olakin, le Bras gauche d'Olakin et le Bras droit d'Olakin, puis apportez-les à la Cavalière noire Arly au Guet noir."}},
[13213] = {"Du rififi au Valhalas", nil, {"Parlez à Geirrvif au Valhalas."}},
[13214] = {"Du rififi au Valhalas : héros défunts", nil, {"Geirrvif, au Valhalas, vous a mis au défi de vaincre, au cours d'un même combat 6 Héros défunts : Eldreth, Geness Demi-Âme, Jhadras, Masud, Rith et Talla.\n\nSi vous réussissez, parlez à Gjonner le Sans-merci surveillant le rebord sud du Valhalas."}},
[13215] = {"Du rififi au Valhalas : Khit'rix le Maître ténébreux", nil, {"Geirrvif, au Valhalas, vous a mis au défi de vaincre Khit'rix le Maître ténébreux.\n\nSi vous réussissez, parlez à Gjonner le Sans-merci surveillant le rebord sud du Valhalas."}},
[13216] = {"Du rififi au Valhalas : le retour de Sigrid Née-de-glace", nil, {"Geirrvif, au Valhalas, vous a mis au défi de vaincre Sigrid Née-de-glace.\n\nSi vous réussissez, parlez à Gjonner le Sans-merci surveillant le rebord sud du Valhalas."}},
[13217] = {"Du rififi au Valhalas : Carnage !", nil, {"Geirrvif, au Valhalas, vous a mis au défi de vaincre Carnage.\n\nSi vous réussissez, parlez à Gjonner le Sans-merci surveillant le rebord sud du Valhalas."}},
[13218] = {"Du rififi au Valhalas : Thane « le Coup de grâce »", nil, {"Geirrvif, au Valhalas, vous a mis au défi de vaincre le Thane Banahogg, le Coup de grâce.\n\nSi vous réussissez, parlez à Gjonner le Sans-merci, surveillant le rebord sud du Valhalas."}},
[13219] = {"Du rififi au Valhalas : l'ultime défi", nil, {"Geirrvif, au Valhalas, vous a mis au défi de vaincre le Prince Sandoval.\n\nSi vous réussissez, parlez à Gjonner le Sans-merci, qui surveille le rebord sud du Valhalas."}},
[13220] = {"Remettre Olakin sur pied", nil, {"Obtenez une Bobine de fil et le Fendoir du docteur, puis apportez-les au Bloc du Sanctum de réanimation et utilisez les Restes du croisé Olakin pour essayer de le réanimer. Retournez voir la Cavalière noire Arly au Guet noir une fois la tâche terminée."}},
[13221] = {"Je ne suis pas encore mort !", nil, {"Escortez le Père Kamaros jusqu'à ce qu'il soit en sécurité et présentez-vous à Absalan le Pieux, à bord du Brise-ciel."}},
[13222] = {"La défense du siège", nil, {"Le Démolisseur en chef Legoso, à la Forteresse de Joug-d'hiver, vous demande de piloter ou de protéger un engin de siège pendant qu'il détruit trois structures.\n\nLes joueurs ayant un grade de soldat ou supérieur peuvent construire des engins de siège dans les Ateliers gobelins."}},
[13223] = {"La défense du siège", nil, {"Le Lieutenant Murp, à Joug-d'hiver, vous demande de défendre ou de piloter un véhicule de siège tandis que ce dernier détruit trois structures de l'Alliance.\n\nLes machines de siège peuvent être construites par des personnages-joueurs de la Horde gradés, dans les Ateliers gobelins."}},
[13224] = {"Le Marteau d'Orgrim", nil, {"Retrouvez le Saccageur-des-cieux Korm Noire-balafre à bord du Marteau d'Orgrim, qui survole la Couronne de glace."}},
