[14086] = {"Un cavalier débutant à Orgrimmar", nil, {"Rapportez ce fascicule à Kildar, dans la Vallée de l'Honneur d'Orgrimmar."}},
[14087] = {"Un cavalier débutant à Mulgore", nil, {"Rapportez ce fascicule à Kar Chante-l’Orage, à Sabot-de-Sang en Mulgore."}},
[14088] = {"Un cavalier débutant en Durotar", nil, {"Rapportez ce fascicule à Xar'Ti, à Sen'jin en Durotar."}},
[14089] = {"Un cavalier débutant dans les clairières de Tirisfal", nil, {"Rapportez ce fascicule à Velma Warnam à Brill dans les Clairières de Tirisfal."}},
[14090] = {"Gormok veut ses frigbolds !", nil, {"Servez-vous du Filet lesté pour capturer 8 Suivants Brûleglace, et remettez-les à Savinia Chanterite, dans l’Enceinte du tournoi d’Argent."}},
[14091] = {"Quelque chose ne va pas", nil, {"Retournez voir le Prince Liam Grisetête sur la Place des Marchands."}},
[14092] = {"Le petit-déjeuner des champions", nil, {"Procurez-vous 4 Cocons d’œufs de jormungar et remettez-les à Tylos Coursaurore dans l’Enceinte du tournoi d’argent."}},
[14093] = {"Une pagaille monstrueuse", nil, {"Le Prince Liam Grisetête vous demande de tuer 6 Worgens déchaînés."}},
[14094] = {"Préserver le ravitaillement", nil, {"Récupérez 4 Caisses de fournitures."}},
[14095] = {"L'identification des restes", nil, {"Apportez la Dent de kraken au Croisé Aubargent comme preuve de votre victoire."}},
[14096] = {"Oh bravo, Kul !!!", nil, {"Sauvez Kul le Téméraire et 4 Aspirants captifs du Guet du nécrorateur. Lorsque ce sera fait, retournez voir Narasi Aubeneige, dans l'Enceinte du tournoi d'Argent."}},
[14098] = {"L'évacuation de la place des Marchands", nil, {"Évacuez 3 Maisons de civils."}},
[14099] = {"Des ordres royaux", nil, {"Parlez à Gwen Armstead dans le Secteur Militaire, au sud-est."}},
[14100] = {"La relique du Cercle terrestre", nil, {"Apportez vos quatre totems à Gotura Quatre-vents à Orgrimmar."}},
[14101] = {"Le drottinn Hrothgar", nil, {"Le Haut croisé Adélard, dans l’Enceinte du tournoi d’Argent, veut que vous vainquiez le Drottinn Hrothgar."}},
[14102] = {"Le mandebrume Yngvar", nil, {"Le Haut croisé Adélard, dans l’Enceinte du tournoi d’Argent, veut que vous tuiez le Mandebrume Yngvar."}},
[14103] = {"La poudre de titane", nil, {"Conditions :"}},
[14104] = {"Ornolf le Balafré", nil, {"Le Haut croisé Adélard, dans l’Enceinte du tournoi d’Argent, veut que vous tuiez Ornolf le Balafré."}},
[14105] = {"Le nécrorateur Kharos", nil, {"Le Haut croisé Adélard, dans l’Enceinte du tournoi d’Argent, veut que vous tuiez le Nécrorateur Kharos."}},
[14106] = {"Quête de test de Na Kada", nil, {"Conditions :"}},
[14107] = {"Le destin des défunts", nil, {"Le Croisé Aubargent veut que vous apportiez la paix à 6 Esprits de héros défunts."}},
[14108] = {"Sus au kraken !", nil, {"Le Croisé Aubargent veut que vous chevauchiez un Hippogriffe d’Argent, que vous lanciez 8 Lances enflammées sur le Kraken de la mer Boréale, et que vous abattiez 6 Mande-fonds kvaldirs.\n\nVous trouverez des Hippogriffes d’Argent devant la tente de la Croisade d’argent, dans l’Enceinte du tournoi d’argent."}},
[14109] = {"Un nouveau vous", nil, {"Achetez un Bling-bling rutilant à Diastème Dent-d'argent, une Nouvelle tenue branchée auprès de Szabo et des Lunettes de soleil cool chez Missa Labinocle, dans la Rue de la Fauche."}},
[14110] = {"Une nouvelle vous", nil, {"Achetez un Bling-bling rutilant à Diastème Dent-d'argent, une Nouvelle tenue branchée auprès de Szabo et des Lunettes de soleil cool chez Missa Labinocle, dans la Rue de la Fauche."}},
[14111] = {"La relique du Cercle terrestre", nil, {"Apportez vos quatre totems au Long-voyant Eannu à Forgefer."}},
[14112] = {"Ça mange quoi, un yéti, d'abord ?", nil, {"Savinia Chanterite, dans l'Enceinte du tournoi d'Argent, veut que vous lui apportiez 3 morceaux de Chair de requin de la mer Boréale."}},
[14113] = {"Membre d'honneur", nil, {"Revêtez votre Tenue de fête sensationnelle (Lunettes de soleil cool, Nouvelle tenue branchée et Bling-bling rutilant), puis dirigez-vous vers la fête à la piscine du Quartier général de la SCK et divertissez 10 Bambocheurs de Kezan."}},
[14115] = {"Les trouble-fête pirates", nil, {"Tuez 12 Trouble-fête pirates pour sauver votre carrière."}},
[14116] = {"Un invité indésirable", nil, {"Parlez au Prince marchand Gallywix à l'étage du bâtiment principal du Quartier général de la SCK, sur Kezan."}},
[14117] = {"Les yeux d'Orneval", nil, {"Tuez 8 Éclaireurs de Talrendis."}},
[14118] = {"Une livraison de venaison", nil, {"Récupérez 15 Tranches de venaison sur des Cerfs sabot-de-mousse affaiblis."}},
[14120] = {"Mille milliards de radis ?!", nil, {"Parlez à Chipie Serrelavis au Quartier général de la SCK, sur Kezan."}},
[14121] = {"Robin des bois moderne", nil, {"Utilisez votre Voiture de sport pour renverser 12 Pillards embauchés et récolter 12 Butins volés par la même occasion."}},
[14122] = {"Le casse du jour", nil, {"Faites sauter le coffre de la Banque Centrale de Kezan pour récupérer vos Biens personnels."}},
[14123] = {"On entre et on ressort", nil, {"Obtenez l'Épervier de Maldy, La gobeline Lisa et la Bombe ultime."}},
[14124] = {"L'extraction de la kaja'mite", nil, {"Collectez 12 Morceaux de kaja'mite."}},
[14125] = {"447", nil, {"Surchargez le Générateur défectueux, activez le Poêle qui fuit, laissez tomber un cigare sur le Lit inflammable et utilisez le Gazbot pour mettre le feu au Quartier général de la SCK !"}},
[14126] = {"Les économies de toute une vie", nil, {"Cédez vos Économies d'une vie au Prince marchand Gallywix sur le Voilier du prince marchand, sur Kezan."}},
[14127] = {"Le retour des Bien-nés ?", nil, {"Apportez les Ordres de l’éclaireur à Ag’tor Poing-de-sang, à la Porte d’Azshara."}},
[14128] = {"Le retour des Bien-nés ?", nil, {"Apportez les Ordres de l’éclaireur à Malynéa Saccagecieux, à la Station d’Orgrimmar du fusorail."}},
[14129] = {"Déchiqueteur emballé !", nil, {"Attaquez l’un des Déchiqueteurs emballés près de la Porte d’Azshara et sautez dedans une fois qu’il aura subi des dégâts importants."}},
[14130] = {"Un ami haut en couleur", nil, {"Trouvez Ergll sur la côte des Ruines d’Arkkoran."}},
[14131] = {"Un petit remontant", nil, {"Récupérez 10 Fèves de kaphay sur les kaphayers dans les Ruines d’Arkkoran."}},
[14132] = {"C’est vraiment mal élevé !", nil, {"Tuez 10 Makrinnis dans les Ruines d’Arkkoran."}},
[14134] = {"Il faut bûcher", nil, {"Utilisez le Déchiqueteur reprogrammé pour récupérer 6 piles de Bois d’Azshara. Si vous perdez votre déchiqueteur, vous pouvez en attaquer un autre et le reprogrammer."}},
[14135] = {"Une coupe sombre", nil, {"Utilisez la scie circulaire du déchiqueteur pour abattre des Arbrisseaux d’Azshara, et tuez 9 Snipers de Talrendis dès que vous les apercevez."}},
[14136] = {"Secours en mer", nil, {"Girana Premier-sang, de l’Enceinte du tournoi d’Argent, veut que vous tuiez 8 Berserkers kvaldirs et 3 Harponneurs kvaldirs à bord du Faucon-de-feu."}},
[14138] = {"La main à la pâte", nil, {"Apportez l'Encouragement de Chipie au Contremaître Mèchumide au Quartier général de la SCK, sur Kezan."}},
[14140] = {"Arrêter les agresseurs", nil, {"Girana Premier-sang, de l’Enceinte du tournoi d’Argent, veut que vous tuiez 10 Kvaldirs."}},
[14141] = {"Gormok veut ses frigbolds !", nil, {"Servez-vous du Filet lesté pour capturer 8 Suivants Brûleglace, et remettez-les à Tylos Coursaurore, dans l’Enceinte du tournoi d’Argent."}},
