[14143] = {"Comment retomber sur ses pattes…", nil, {"Récupérer 10 Pilons de haut-trotteur volés pour Girana Premier-sang, dans l'Enceinte du tournoi d'Argent."}},
[14144] = {"La miséricorde de la Lumière", nil, {"Utilisez le Livre de prière du confesseur pour célébrer les derniers sacrements sur 8 Villageois tualiq morts, puis retournez voir Girana Premier-sang dans l'Enceinte du tournoi d'Argent."}},
[14145] = {"Ça mange quoi, un yéti, d'abord ?", nil, {"Tylos Coursaurore, dans l'Enceinte du tournoi d'Argent, veut que vous lui apportiez 3 morceaux de Chair de requin de la mer Boréale."}},
[14146] = {"Défendez les portes !", nil, {"Le Capitaine d’ouvrage Grailletout veut que vous retourniez sans tarder à la Porte d’Azshara pour massacrer 20 Écumeurs elfes de la nuit."}},
[14151] = {"Le rubis cardinal", nil, {"Linzy Noirboulon, à Dalaran, veut que vous effectuiez 5 transmutations de gemmes épiques grâce à vos compétences d'alchimiste. Les transmutations acceptées sont les suivantes : Amétrine, Œil de Zul, Pierre d'effroi, Ambre du roi et Zircon majestueux."}},
[14152] = {"Secours en mer", nil, {"Partez pour la Mer Boréale et tuez 8 Berserkers kvaldirs et 3 Harponneurs kvaldirs parmi les assaillants du Hautevague."}},
[14153] = {"Membre d'honneur", nil, {"Revêtez votre Tenue de fête sensationnelle (Lunettes de soleil cool, Nouvelle tenue branchée et Bling-bling rutilant), puis dirigez-vous vers la fête à la piscine du Quartier général de la SCK et divertissez 10 Bambocheurs de Kezan."}},
[14154] = {"Pas passé loin", nil, {"Repoussez les worgens au sommet de la prison de Gilnéas pendant 2 minutes."}},
[14155] = {"Arbricide", nil, {"Utilisez le Déchiqueteur pour détruire l’Ancien de Talrendis. Si vous perdez votre déchiqueteur, des machines de secours sont disponibles à la Porte d’Azshara."}},
[14157] = {"D'anciennes rancœurs", nil, {"Parlez au Capitaine Broderick dans la Prison de Gardepierre, à Gilnéas."}},
[14159] = {"L'arsenal du seigneur séditieux", nil, {"Parlez à Josiah Avery dans le Secteur Militaire, à Gilnéas."}},
[14160] = {"Commission de mérite", nil, {"Présentez la Commission de mérite à Timothy Jones, à Dalaran."}},
[14161] = {"Un parfum de basilic", nil, {"Tuez 10 Basilics grispierre dans la Mine à ciel ouvert du Puy-du-Pied."}},
[14162] = {"Présentez-vous à Horzak", nil, {"Trouvez Horzak Zigrignote à la Station d’Orgrimmar du fusorail, en Azshara."}},
[14163] = {"Appel aux armes : île des Conquérants", nil, {"Gagnez sur l'Île des Conquérants, puis présentez-vous à un Général de brigade de l'Alliance, à Joug-d'hiver, à Dalaran, à Shattrath ou dans l'une des capitales de l'Alliance."}},
[14164] = {"Appel aux armes : île des Conquérants", nil, {"Gagnez sur l'Île des Conquérants, puis présentez-vous à un Porteguerre de la Horde, à Joug-d'hiver, à Dalaran, à Shattrath ou dans l'une des capitales de la Horde."}},
[14165] = {"Pétrifiés", nil, {"Allez chercher un Mineur du Puy-du-Pied pétrifié dans la Mine à ciel ouvert du Puy-du-Pied, et ramenez-le à Horzak Zigrignote, à la Station d’Orgrimmar du fusorail."}},
