[14179] = {"Appel aux armes : Œil du cyclone", nil, {"Gagnez dans l'Œil du cyclone, puis présentez-vous à un Général de brigade de l'Alliance, à Joug-d'hiver, à Dalaran, à Shattrath ou dans l'une des capitales de l'Alliance."}},
[14180] = {"Appel aux armes : goulet des Chanteguerres", nil, {"Remportez la bataille dans le Goulet des Chanteguerres, puis présentez-vous à un Général de brigade de l'Alliance, à Joug-d'hiver, à Dalaran, à Shattrath ou dans l'une des capitales de l'Alliance."}},
[14181] = {"Appel aux armes : bassin Arathi", nil, {"Remportez la bataille dans le Bassin Arathi, puis présentez-vous à un Porteguerre de la Horde, à Joug-d'hiver, à Dalaran, à Shattrath ou dans l'une des capitales de la Horde."}},
[14182] = {"Appel aux armes : Œil du cyclone", nil, {"Remportez la bataille dans l'Œil du cyclone, puis présentez-vous à un Porteguerre de la Horde, à Joug-d'hiver, à Dalaran, à Shattrath ou dans l'une des capitales de la Horde."}},
[14183] = {"Appel aux armes : goulet des Chanteguerres", nil, {"Remportez la bataille dans le Goulet des Chanteguerres, puis présentez-vous à un Porteguerre de la Horde, à Joug-d'hiver, à Dalaran, à Shattrath ou dans l'une des capitales de la Horde."}},
[14184] = {"Mon heure est venue", nil, {"Furien de Tethris Aran veut que vous utilisiez son Faucon-dragon pour vous rendre au Poste de Furien et prévenir sa sœur, Cérélia, de sa mort."}},
[14188] = {"Vengez Furien !", nil, {"Cérélia vous a demandé de parler à Sorrem au Poste de Furien."}},
[14189] = {"La traduction", nil, {"Sorrem veut que vous trouviez les Tablettes antiques.\n\nAllez parler à Narimar au Poste de Furien où il vous enverra à l’endroit où Furien faisait ses recherches."}},
[14190] = {"Le prisme parfait", nil, {"Récupérez un Pendentif en cristal sur un Saboteur de Talrendis dans la Mine à ciel ouvert du Puy-du-Pied."}},
[14191] = {"Sur les traces de Furien", nil, {"Les tablettes antiques du sanctuaire d’Élune vous ont conduit jusqu'aux trois plus grandes ruines de Sargeron."}},
[14192] = {"Un cristal à tout prisme !", nil, {"Apportez le Pendentif en cristal au Râtelier d’armes du quartier général de la Mine à ciel ouvert du Puy-du-Pied."}},
[14193] = {"Choper le melon", nil, {"Récoltez 8 Melons."}},
[14194] = {"Reviandication", nil, {"Utilisez le Reviandicateur pour dépétrifier 8 Mineurs du Puy-du-Pied, dans la Mine à ciel ouvert du Puy-du-Pied."}},
[14195] = {"Tout devient plus clair", nil, {"Le Journal de Furien vous met sur la piste des ruines du Palais d’Élune, au nord de Sargeron."}},
[14196] = {"La guerre du feu", nil, {"Vous devez créer un Vortex déchaîné en tuant 7 Disciples d’Élune en présence de la tempête de feu.\n\nSi vous perdez votre Gemme du vortex, vous pouvez en récupérer une autre sur l’Ancienne pierre runique du vortex."}},
[14197] = {"Un quota à respecter", nil, {"Récupérez 20 lingots de Fer du Puy-du-pied dans la Mine à ciel ouvert du Puy-du-pied, en Azshara."}},
[14198] = {"Le cavalier de l'orage", nil, {"Utilisez la Gemme du vortex déchaîné aux ruines de la combe de Nijel. À l’aide de votre Tempête-de-feu, tuez 40 Défenseurs de la combe de Nijel et brûlez 15 Marchands de la combe de Nijel.\n\nSi vous perdez votre Gemme du vortex déchaîné, vous pouvez en récupérer une autre sur l’Ancienne pierre runique du vortex."}},
[14201] = {"Un millier d'histoires dans le sable", nil, {"Rassemblez 5 Artéfacts récupérés dans les ruines qui entourent le Lac Mennar, en Azshara."}},
[14202] = {"La surveillance des rives du lac", nil, {"Escortez une Géomètre gobeline jusqu’aux trois appareils de l’équipement de géomètre qui entourent le Lac Mennar, et défendez-la le temps qu’elle relève les mesures. Vous pouvez toujours invoquer une autre Géomètre avec votre Balise de géomètre."}},
[14203] = {"Une recette détrempée", nil, {"Remettez la Recette détrempée à Christi Stockton à Dalaran."}},
[14204] = {"Dans les ombres", nil, {"Tuez 6 Rôdeurs croc-de-sang."}},
[14209] = {"Viscosité", nil, {"Inspectez le Buldo de Clubnik et récupérez un échantillon de l’ectoplaf qui encrasse le moteur."}},
[14212] = {"Sacrifices", nil, {"Montez derrière le Seigneur Darius Crowley et rassemblez 30 Traqueurs croc-de-sang."}},
[14213] = {"Dix livres de chair", nil, {"Vous devez rassembler 10 lambeaux de Chair de satyre sur les satyres de Sargeron, et les déposer sur le Brasero d’Élune."}},
[14214] = {"Un message pour Grisetête", nil, {"Parlez au Roi Genn Grisetête dans le Secteur Militaire."}},
[14215] = {"Souvenirs des morts", nil, {"Apportez l’Anneau de Kalytha sur les rives du Lac Mennar. Touchez l’Esprit de Kalytha pour revivre ses souvenirs. Parlez aux autres esprits pour découvrir le secret du Lac Mennar."}},
[14216] = {"Le mystère de la pierre de Sarcen", nil, {"Récupérez la Pierre de Sarcen au fond du Lac Mennar."}},
[14217] = {"Offrandes satyriques", nil, {"Vous devez rassembler 10 lambeaux de Chair de satyre sur les satyres de Sargeron, et les déposer sur le Brasero d’Élune."}},
[14218] = {"Par le sang et les cendres", nil, {"Massacrez 80 Traqueurs croc-de-sang en utilisant les Canons rebelles."}},
[14219] = {"Les sabres mouvants", nil, {"Vous devez récupérer 20 Sabres de satyre dans les râteliers d’armes et sur les satyres de la région, puis les placer sur la statue de la Suivante d’Élune."}},
[14220] = {"C'est la fin", nil, nil},
[14221] = {"Ne jamais abandonner, mais se replier si nécessaire", nil, {"Parlez au Seigneur Darius Crowley dans la Cathédrale de l'Aube de Lumière."}},
[14222] = {"Dans les derniers retranchements", nil, {"Tuer 8 Traqueurs frénétiques."}},
[14223] = {"La paix de l'âme", nil, {"Sorrem, du Poste de Furien, vous a demandé de tuer Kohor."}},
[14225] = {"Vous le saurez quand vous le verrez", nil, {"Sorrem, du Poste de Furien, veut que vous trouviez l’Ancien rituel d’invocation dans la Forteresse de Hache-Tonnerre."}},
[14226] = {"Un bon coup de savate", nil, {"Utilisez votre Assistant arcanique personnel pour transformer 12 Démons legashi en insectes, puis écrabouillez-les."}},
[14227] = {"Le casse-tête", nil, {"Rassemblez 100 Crânes blanchis de la Forteresse de Hache-tonnerre, déposez-les dans le Cercle d’invocation de la Larme ardente, puis tuez Nazargen."}},
[14230] = {"Du travail manuel", nil, {"Récupérez le Manuel d’abjurateur dans le Camp Legash."}},
[14231] = {"Archive de quête inutile", nil, {"Conditions :"}},
[14232] = {"Les oreilles qui chauffent", nil, {"Vous devez rapporter 25 Oreilles de la Lame ardente à Sorrem, au Poste de Furien."}},
[14233] = {"Les orcs savent écrire ?", nil, {"Apportez le Journal de campagne à Chipie Serrelavis sur le Rivage de l’Épave, sur les Îles Perdues."}},
[14234] = {"L'ennemi de mon ennemi", nil, {"Remettez le Journal de campagne à Aggra au Camp de base de la Horde, sur les Îles Perdues."}},
[14235] = {"Le val Vicieux", nil, {"Trouvez Kilag Sangrecroc dans le Val Vicieux, sur les Îles Perdues."}},
[14236] = {"Un désherbant radical", nil, {"Utilisez le Massacr'herbes de votre gadgetoceinture gobeline pour faucher 100 plantes dans le Val Vicieux."}},
[14237] = {"Déplacement offensif", nil, {"Trouvez Kilag Sangrecroc et ses éclaireurs au Surplomb Sauvage, sur les Îles Perdues."}},
[14238] = {"Infrarouge = Inframort", nil, {"Tuez 10 Assassins du SI:7.\n\nS'il arrive quoi que ce soit à votre Éclaireur orc, retournez voir Kilag qui pourra peut-être vous trouver un autre équipier."}},
[14239] = {"N'allez pas vers la Lumière !", nil, nil},
[14240] = {"Vers les falaises", nil, {"Parlez à l'Éclaireur Brax sur le chemin en direction des falaises au nord du Vallon Sauvage, sur les Îles Perdues."}},
[14241] = {"Au gyrocoptère !", nil, {"Récupérez les Clés du gyrocoptère."}},
