[24511] = {"Les échos des âmes torturées", nil, {"Tuez Bronjahm et le Dévoreur d'âmes pour sécuriser l'accès à la Fosse de Saron."}},
[24512] = {"La rédemption du guerrier", nil, {"Récupérez 5 caisses d'Armes confisquées au Séjour de l'Honneur et équipez-en 5 Prisonniers des Serres-Rocheuses. Utilisez les cordes à proximité pour pénétrer et sortir discrètement du camp de l'Alliance."}},
[24513] = {"Œil pour œil", nil, {"Tuez le Capitaine Peake et remettez ses yeux à Oltarg."}},
[24514] = {"Faire la peau aux raptors", nil, {"Écorchez des Coureurs ou des Faucheurs dent-d’effroi et récoltez 8 de leurs peaux."}},
[24515] = {"Des signaux dans le ciel", nil, {"Atteignez le Camp Una’fe, dans le Lacis des Tarides du Sud et parlez à Makaba Sabot-plat."}},
[24517] = {"Insufflez-leur la peur", nil, {"Tuez 8 Fantassins du Séjour de l’honneur et 1 Officier du Séjour de l’honneur."}},
[24518] = {"La route basse", nil, {"Remettez le Plan de route à Kilrok Marteltripe à la Colline du Chasseur, dans les Tarides du Sud."}},
[24519] = {"Mettre des bâtons dans les roues au cartographe", nil, {"Trouvez et tuez le cartographe Henry Zykes."}},
[24520] = {"Une bonne nouvelle pour Chipie", nil, {"Parlez à Chipie Serrelavis au Quartier général de la SCK, sur Kezan."}},
[24522] = {"Un voyage au Puits de soleil", nil, {"Parlez à Halduron Luisaile à l'entrée du Plateau du Puits de soleil, sur l'île de Quel'Danas."}},
[24523] = {"Le totem des Crin-sauvage", nil, {"Récupérez 4 Dents de loup alpha des prairies sur des Loups alpha des prairies."}},
[24524] = {"La purification de Crin-sauvage", nil, {"Utiliser le Totem de purification de Crin-sauvage au puits Crin-sauvage."}},
[24525] = {"Tenir les chiens à distance", nil, {"Tuez 6 Charognards Jappecroc."}},
[24526] = {"Remplir le grimoire", nil, {"Apprenez Projectiles des Arcanes auprès de Teegli Merrowith. Réussissez à lancer Projectile des Arcanes 2 fois sur les Mannequins d’entraînement, à l’entrée de Courbenclume."}},
[24527] = {"Votre voie commence ici", nil, {"Apprenez Frappe primordiale auprès de Teo Martel-tempête, puis utilisez 2 fois cette technique sur un Mannequin d’entraînement à l’entrée de Courbenclume."}},
[24528] = {"Le pouvoir de la Lumière", nil, {"Apprenez Sceau de piété et Jugement auprès de Bromos Grummneur. Après avoir lancé Sceau de piété sur vous-même, utilisez le Jugement sur l’un des Mannequins d’entraînement, à l’entrée de Courbenclume."}},
[24529] = {"De mal en pis", nil, {"Libérez 5 Réfugiés de Taurajo incarcérés et tuez n’importe quelle combinaison de 8 Tisseurs d’épines et de Garde-lames dos-hirsutes."}},
[24530] = {"La vie de chasseur", nil, {"Apprenez le Tir assuré auprès de Thorgas Grimson, puis utilisez-le 5 fois sur des Mannequins d’entraînement, à l’entrée de Courbenclume."}},
[24531] = {"Être prêt pour la bataille", nil, {"Apprenez la Charge auprès de Thran Khorman, puis utilisez-la sur l’un des Mannequins d’entraînement, à l’entrée de Courbenclume."}},
[24532] = {"Éviscérer l’ennemi", nil, {"Apprenez l’Éviscération auprès de Solm Hargrin, puis utilisez 3 fois cette technique sur un Mannequin d’entraînement à l’entrée de Courbenclume."}},
[24533] = {"Des mots de pouvoir", nil, {"Apprenez à prodiguer des Soins rapides auprès de Branstock Khalder, puis lancez Soins rapides 5 fois sur des Milices blessées, à Courbenclume."}},
[24534] = {"Jouer leur jeu avec nos cartes", nil, {"Sonnez le Cor du défi au Cercle de défi dos-hirsute et battez Hurecroc en combat singulier."}},
[24535] = {"Thalorien Cherchelaube", nil, {"Rendez-vous près des Restes de Thalorien Cherchelaube et obtenez sa bénédiction pour le port de Quel'Delar."}},
[24536] = {"Il y a quelque chose de pourri…", nil, {"Utilisez l'Analyseur d'air de Tordboulon pour tester l'air autour de 6 Gardes lourdement parfumés, puis retournez voir l'Inspecteur Lapince Tordboulon à Orgrimmar."}},
[24539] = {"L'attaque des plantes", nil, {"Secourez 5 Réfugiés de Taurajo en tuant les Lacis avides qui les ligotent."}},
[24540] = {"La danse de guerre", nil, {"Triomphez d’Orno Totem-sinistre à la Passe des Serres-Rocheuses."}},
[24541] = {"Dérober du parfum", nil, {"Mettez le Déguisement de coursier, rendez-vous à la Réserve de la Compagnie de Chimie La Royale à l'extérieur des portes d'Orgrimmar, puis rapportez le paquet qu'on vous aura jeté à l'Inspecteur Lapince Tordboulon à Orgimmar."}},
[24542] = {"Une curieuse floraison", nil, {"Cueillez 10 Boutons de fleur insolites sur les Fleurs en bourgeon, dans le Lacis."}},
[24543] = {"Une famille divisée", nil, {"Trouvez Kirge Corne-sévère à la Halte de la Vendetta, dans les Tarides du Sud."}},
[24545] = {"Le sacré et le corrompu", nil, {"Placez Vengeance de la Lumière, 25 Saronites primordiales, le Sang acide de Trognepus et le Sang acide de Pulentraille sur la Forgerune du Généralissime Mograine, à la Citadelle de la Couronne de glace.\n\nLe Sang acide de Trognepus et le Sang acide de Pulentraille peuvent uniquement être obtenus en mode 25 joueurs à la Citadelle de la Couronne de glace."}},
[24546] = {"Délimiter notre territoire", nil, {"Prenez une colline neutre dans les Champs du Sang et défendez-la contre une contre-attaque de l'Alliance."}},
[24547] = {"Un festin d'âmes", nil, {"Le Généralissime Darion Mograine veut que vous utilisiez Tranchant de l'ombre pour tuer 50 séides du roi-liche à la Citadelle de la Couronne de glace. Les âmes peuvent être obtenues en mode 10 ou 25 joueurs."}},
[24548] = {"Le trône brisé", nil, {"Le Généralissime Darion Mograine veut que vous trouviez 50 Eclats ombregivres.\n\nLes Eclats ombregivres peuvent uniquement être obtenus en mode 25 joueurs."}},
[24549] = {"Deuillelombre…", nil, {"Le Généralissime Darion Mograine veut que vous lui rapportiez Tranchant de l'ombre."}},
[24550] = {"Un voyage aux Pitons-du-Tonnerre", nil, {"Parlez à Baine Sabot-de-Sang aux Pitons-du-Tonnerre."}},
[24551] = {"Une pomme pour la nouvelle maîtresse", nil, {"Tuez des Patrouilles de reconnaissance de Guet-du-Nord et récupérez 10 Plaques de mérite de Theramore."}},
[24552] = {"La fierté du lion", nil, {"Tuez n’importe quelle combinaison de 5 Rôdeurs des plaines ou de Fiercrins des plaines."}},
[24553] = {"La purification de Quel'Delar", nil, {"Plongez votre Quel'Delar trempée dans le Puits de soleil. Parlez à un Gardien du Puits de soleil quand vous serez prêt à aller sur le Plateau du Puits de soleil."}},
[24554] = {"La poignée endommagée", nil, {"Apporter la Poignée endommagée à Myralion Solembrase au Repos de Quel'Delar, au sud de l'Enceinte du tournoi d'Argent."}},
[24555] = {"Ce que savent les dragons", nil, {"Parlez à Krasus, au sommet du Temple du Repos du ver."}},
[24556] = {"Nettoyage à secte", nil, {"Procurez-vous un Tabard emprunté auprès de Shandy Cireluisante."}},
