[5305] = {"Doce serenidade", {"Vá para Stratholme e mate o Forjador de Martelos Carmesim. Recolha o Avental do Forjador de Martelos Carmesim e leve-o para Lilith.\r\n"}},
[5306] = {"A litofídia da caçadora sombria", {"Vá para o Pico da Rocha Negra e mate a Caçadora Sombria Vosh'gajin. Recolha a Litofídia de Vosh'gajin e fale novamente com Kilram."}},
[5307] = {"Corrupção", {"Encontre o Cuteleiro da Guarda Negra em Stratholme e destrua-o. Recolha a Insígnia da Guarda Negra e fale novamente com Seril Flagelicida."}},
[5321] = {"A adormecida despertou", {"Escolte Kerlonian Perenumbra até Liladris Luneflúvia no Entreposto de Maestra, situado no Vale Gris."}},
[5341] = {"A fortuna da família Barov", {"Desbrave Scolomântia e recupere a fortuna da família Barov. A fortuna é composta por quatro escrituras: A Escritura de Castro das Flechas, A Escritura de Montalvo, A Escritura da Serraria Tarren e A Escritura de Costa Sul. Fale novamente com Alexei Barov quando tiver terminado a tarefa."}},
[5342] = {"O último Barov", {"Vá para o Acampamento Ventogelante, no território da Aliança, e assassine Weldon Barov. Recolha a cabeça dele e fale novamente com Alexei Barov.","Esteja avisada: Weldon provavelmente estará muito bem protegido pela escória da Aliança."}},
[5343] = {"A fortuna da família Barov", {"Desbrave Scolomântia e recupere a fortuna da família Barov. A fortuna é composta por quatro escrituras: A Escritura de Castro das Flechas, a Escritura de Montalvo, a Escritura da Serraria Tarren e a Escritura de Costa Sul. Fale novamente com Weldon Barov quando tiver terminado a tarefa."}},
[5344] = {"O último Barov", {"Vá para o Baluarte, no território da Horda, e assassine Alexei Barov. Recolha a cabeça dele e fale novamente com Weldon Barov.","Esteja avisada: Alexei provavelmente estará muito bem protegido pela escória da Horda."}},
[5361] = {"Árvore genealógica", {"Leve o Relatório de Vigia do Rochedo Chifre Longo para Nataoka Chifre Longo no Entreposto do Espírito que Anda, situado em Desolação."}},
[5381] = {"Mão da Iruxos", {"Recolha a Caixa Demoníaca na Fortaleza Machado do Trovão e leve-a, junto com a Picareta do Diabo, para Taiga Crina Sábia na Aldeia Pescassombra.\r\n"}},
[5382] = {"Doutor Theolen Krastinov, o Carniceiro", {"Encontre o Doutor Theolen Krastinov em Scolomântia. Destrua-o, depois queime os Restos Mortais de Eva Sarkhoff e os Restos Mortais de Lucien Sarkhoff. Fale novamente com Eva Sarkhoff depois que tiver terminado a tarefa.\r\n"}},
[5384] = {"Kirtonos, o Arauto", {"Volte a Scolomântia com o Sangue dos Inocentes em mãos. Encontre a varanda e derrame o Sangue dos Inocentes no braseiro. Kirtonos virá na intenção de devorar sua alma.","Lute com bravura, não dê nenhuma brecha! Destrua Kirtonos e fale novamente com Eva Sarkhoff.\r\n"}},
[5386] = {"Peixe do dia", {"Converse com Jinar'Zillen, na Aldeia Pescassombra, e depois leve 2 peixes pança-sangrenta para Nataoka Chifre Longo."}},
[5421] = {"Peixe no balde", {"Jinar'Zillen quer que você pegue 5 Lagostins nas armadilhas próximas à Aldeia Pescassombra."}},
[5441] = {"Peões preguiçosos", {"Use o Cassetete do Encarregado nos Peões Preguiçosos enquanto estiverem dormindo. Acorde 5 peões, depois devolva o Cassetete do Encarregado ao Encarregado Thazz'ril, no Vale das Provações."}},
[5461] = {"Ras Friomúrmuro, o humano", {"Viaje para o Planalto Arathi, até as ruínas de Stromgarde. Vasculhe Stromgarde em busca de um Livro de Recordações. Se encontrar o item, retorne com ele até o Magistrado Marduque."}},
[5463] = {"Dádiva de Menethil", {"Vá até Stratholme e encontre a Dádiva de Menethil. Ponha o Livro de Recordações no solo profanado. "}},
[5464] = {"Dádiva de Menethil", {"Leve a Lembrança Vinculada para Leonid Bartolomeu, nas Terras Pestilentas Orientais."}},
[5465] = {"Lembrança Vinculada", {"Fale novamente com o Magistrado Marduque, no Castro das Flechas. Mostre-lhe a Lembrança Vinculada."}},
[5466] = {"Ras Friomúrmuro, o lich", {"Procure por Ras Friomúrmuro em Scolomântia. Quando o encontrar, use a Lembrança Vinculada em sua versão morto-vivo. Quando conseguir transformá-lo novamente em mortal, mate-o e recupere a Cabeça Humana de Ras Friomúrmuro. Leve a cabeça de volta ao Magistrado Marduque."}},
[5481] = {"Beijo do Gordo", {"Colha 3 Ervas-do-emo e entregue para o Boticário-júnior Hollanda, no cemitério de Montalvo."}},
[5482] = {"Erva-do-demo", {"Colha 10 Ervas-do-demo e entregue para o Boticário-júnior Hollanda.\r\n"}},
[5501] = {"Ossos do ofício", {"Entregue 10 Ossos de Kodo do Cemitério dos Kodos para Bibbly Ferrafivela no Barraco do Kormek."}},
[5502] = {"Um protegido da Horda", {"Compre um Cavalete para Espada de Espuma de Blax Bumfoguete e leve seu protegido de volta ao orfanato de Orgrimmar. Basta falar com a Matriarca Uivadora no Bazar."}},
[5504] = {"Dragonas da Aurora", {"Leve 10 Símbolos de Bravura da Aurora Argêntea à Intendente Hasana, no Baluarte, que fica nas Terras Pestilentas Ocidentais."}},
