[6122] = {"A fonte principal", {"Use o Coletor Vazio das Quedas de Fontescarpa para coletar uma amostra de água da entrada da caverna da cachoeira.","Entregue o Coletor Cheio das Quedas de Fontescarpa para Alanndária Noturcanto em Auberdine, na Costa Negra."}},
[6123] = {"Colhendo a cura", {"Leve 5 Raízes-telúricas e 12 Fungos Lunares para Alanndária Noturcanto em Auberdine para que ela possa preparar o bálsamo curativo."}},
[6124] = {"Curando os doentes", {"Use o Bálsamo Curativo para Animais em 10 Cervos Adoentados espalhados pela Costa Negra. Isso deve curá-los. Cervos Adoentados foram avistados da região ao sul do Rio Fontescarpa, seguindo até a área ao norte de Auberdine, até a Costa Negra meridional, na fronteira do Vale Gris."}},
[6125] = {"O poder sobre os venenos", {"Fale novamente com Mathrengyl Ursivagus no Enclave Cenariano, em Darnassus."}},
[6126] = {"Novas lições", {"Vá à Clareira da Lua e fale com Dendrite Stellardor no vilarejo de Refúgio Noturno."}},
[6127] = {"A fonte principal", {"Use o Coletor Vazio do Morro de Brumedo para obter uma amostra de água de um lago no topo do morro.","Entregue o Coletor Cheio do Morro de Brumedo a Tonga Runa Totem na Encruzilhada, nos Sertões."}},
[6128] = {"Colhendo a cura", {"Leve 5 Raízes-telúricas e 5 Chifres de Kodo para Tonga Runa Totem na Encruzilhada para que ele possa preparar um bálsamo curativo."}},
[6129] = {"Curando os doentes", {"Use o Bálsamo Curativo para Animais em 10 Gazelas Doentes espalhadas pelo norte dos Sertões. Isso deve curá-las. Gazelas Doentes foram avistadas por toda parte a norte da estrada leste-oeste, que atravessa a Encruzilhada."}},
[6130] = {"O poder sobre os venenos", {"Fale novamente com Turak Runa Totem no Platô dos Anciãos, no Penhasco do Trovão."}},
[6132] = {"Me tira daqui!", {"Acompanhe Melizza Zumbidorla."}},
[6133] = {"A mando do lorde patrulheiro", {"Vá à fronteira norte das Terras Pestilentas Orientais e recupere os Arquivos de Quel'Thalas em algum lugar do Abrigo Quel'Lithien.","Você também precisa matar 8 Patrulheiros, 8 Trilheiros e 8 Mateiros."}},
[6134] = {"Vá catar fantasmoplasma!", {"Colete 8 Fantasmoplasmas."}},
[6135] = {"Ah, Quiroscuro, como vos odeio...", {"Encontre Quiroscuro e mate-o. Do cadáver, extraia um Tufo de Pele de Quiroscuro e leve para Nathanos Arauto da Praga.","Dizem que Quiroscuro vaga pelo campo ao norte do Sítio dos Marris."}},
[6136] = {"O corpulento", {"Encontre Brocassangre nos ermos do norte das Terras Pestilentas e destrua-o. Fale novamente com Nathanos Arauto da Praga quando tiver cumprido a missão."}},
[6141] = {"Irmão Anton", {"Speak with Brother Anton in Desolace."}},
[6142] = {"Boa isca", {"Encontre 10 nacos de Carne de Marisco de Casca Mole e entregue-os para Mai'Lahii na Aldeia Pescassombra."}},
[6143] = {"Camarão que dorme a onda leva", {"Mate 7 Mirmidões Sibilâmina, 7 Nagas Sibilâmina e 5 Feiticeiras Sibilâmina. Em seguida, fale com Drulzeger, na Aldeia Pescassombra, em Desolação."}},
[6145] = {"O mensageiro carmesim", {"Volte às Terras Pestilentas Orientais e rastreie o Mensageiro Carmesim. Mate o mensageiro e obtenha as Ordens do Grão-cruzado.","Leve-as para Nathanos Arauto da Praga para obter suas próximas instruções."}},
[6148] = {"Demétria, o Oráculo Escarlate", {"A Cruzada Escarlate está em marcha. Em algum ponto da estrada da Manopla de Tyr para Stratholme estará o Oráculo, Demétria, e a comitiva dela. Encontre-a e mate-a. Fale novamente com Nathanos Arauto da Praga se tiver sucesso."}},
[6161] = {"O tesouro de Rodovalho!", {"Encontre a Chave Prateada de Rodovalho e a Chave Dourada de Rodovalho. Encontre e abra o Baú de Rodovalho."}},
[6162] = {"[DEPRECATED]A Husband's Last Battle", {"Mate o Suserano Ror e leve a garra dele para Dreka'Sur no Posto Peçonha, na Selva Maleva."}},
[6163] = {"Ramstein", {"Vá a Stratholme e mate Ramstein, o Devorador. Leve a cabeça dele como souvenir para Nathanos."}},
[6181] = {"Mensagem rápida", {"Mostre o Bilhete do Ludovico a Thor, o mestre de grifos."}},
[6182] = {"O primeiro e último", {"Fale com Mathias Shaw na Cidade Velha de Ventobravo. Ele mora no Quartel."}},
[6184] = {"Flint Umbramais", {"Vá até o Acampamento Ventogelante, nas Terras Pestilentas Ocidentais, e encontre seu contato, Flint Umbramais.\r\n"}},
[6185] = {"As Pestes do Leste", {"Vasculhe as Terras Pestilentas Orientais em busca de pistas sobre o \"Arauto da Praga\" e os agentes da AVIN desaparecidos. Ser encontrar alguma Insígnia da AVIN, leve de volta para Flint Umbramais, no Acampamento Ventogelante."}},
[6186] = {"A ascensão do Arauto da Praga", {"Return to Stormwind and inform King Varian Wrynn of the fate of Nathanos Marris."}},
[6187] = {"A restauração da ordem", {"Reúna um exército e siga até as Terras Pestilentas Orientais. Lance um ataque maciço contra Nathanos Arauto da Praga e qualquer lixo da Horda que tente protegê-lo.","Mantenha-se sempre alerta, Bomdiyada. Os membros da Horda vão defender o lorde patrulheiro com as próprias vidas."}},
[6261] = {"Dungar Tragolongo", {"Leve o Caixote de Larso para Dungar Tragolongo, o mestre de grifos."}},
[6281] = {"Siga para Ventobravo", {"Compre uma passagem de grifo do mestre de grifos Thor, e leve o Bilhete do Ludovico para Larso Norde na loja Imunidade Limitada, que fica na Cidade Velha, em Ventobravo."}},
[6282] = {"Harpias ameaçadoras", {"Maggran Ata Terra, no Retiro Rocha do Sol, quer que você mate 7 Hapias Furissague, 7 Emboscadoras Furissangue, 7 Matadoras Furissangue e 7 Plumerrantes Furissangue."}},
[6283] = {"O sangue das Furissangue", {"Maggram, no Retiro Rocha do Sol, quer que você mate a Estripadora Furissangue e traga os restos mortais dela como prova de seu feito."}},
[6284] = {"Aracnofobia", {"Mate Besseleth e leve a Presa de Besseleth a Maggran no Retiro Rocha do Sol."}},
[6285] = {"Entrega para Ludovico", {"Compre uma passagem para o Morro da Sentinela do mestre de grifos Dungar Tragolongo. Em seguida, leve o Caixote de Larso para Ludovico, no Morro da Sentinela."}},
[6301] = {"Ciclo de renascimento", {"Tammra Campo de Vento, no Retiro Rocha do Sol, quer que você colete 10 Sementes de Gaia."}},
[6321] = {"Suprimentos para Montalvo", {"Entregue o Pedido de Morres para a Tratadora de Morcegos Anésia."}},
[6322] = {"Miguel Garreta", {"Leve o Caixote do Antônio até Miguel Garreta."}},
[6323] = {"Carona para a Cidade Baixa", {"Peça uma carona de morcego até a Cidade Baixa à tratadora de morcegos Anésia Garrido. Em seguida, leve o Pedido do Morres para Antônio Nunes na Cidade Baixa."}},
[6324] = {"De volta ao Morres", {"Compre um voo de morcego do tratador de morcegos Miguel Garreta para Montalvo. Chegando lá, entregue o Caixote do Antônio para o Necroguarda Morres."}},
[6341] = {"Para Darnassus", {"Fale com Fidélio para alugar um voo até Darnassus, depois leve o Livro de Receitas de Dolanaar para Irmã Aquinne em Darnassus."}},
[6342] = {"Um presente inesperado", {"Leve o Bilhete da Irmã Aquinne para Leora, a mestre de hipogrifos em Darnassus."}},
[6343] = {"Rever Nyoma", {"Fale com Leora para alugar um hipogrifo para levar você de volta para Dolanaar. Quando chegar, leve o Bilhete da Irmã Aquinne para Nyoma."}},
[6344] = {"Lembranças do lar", {"Leve o Livro de Receitas de Dolanaar para Fidélio, em Dolanaar."}},
[6361] = {"Um pacote de peles", {"Leve o Pacote de Peles para Tak, o mestre de mantícoras, na Aldeia Casco Sangrento."}},
[6362] = {"Voo para o Penhasco do Trovão", {"Pegue uma mantícora com o mestre de mantícoras Tak e leve o Pacote de Peles para Ahanu no Penhasco do Trovão."}},
[6363] = {"Tal, o Mestre de Mantícoras", {"Leve os Artigos em Couro do Ahanu para Tal no Penhasco do Trovão."}},
[6364] = {"Fale novamente com Varg", {"Pegue uma mantícora para a Aldeia Casco Sangrento com o mestre de mantícoras Tal e leve os Artigos em Couro do Ahanu para Varg Vento Sussurrante."}},
[6365] = {"Encomenda para Gryshka", {"Leve as Carnes do Grimtak para Burok em Monte Navalha."}},
[6381] = {"Vida nova", {"Plante 10 Sementes de Gaia em Montículos de Terra de Gaia e, então, devolva as Sementes Encantadas de Gaia restantes a Tammra, no Retiro Rocha do Sol."}},
[6382] = {"A caçada do Vale Gris", {"Fale com Senani Coração Trovejante no Posto Machadada, que fica no Vale Gris."}},
[6384] = {"Carona para Orgrimmar", {"Pegue uma mantícora para Orgrimmar do mestre de mantícoras Burok. Chegando lá, entregue as Carnes do Grimtak para Gryshka."}},
[6385] = {"Doraso, o mestre de mantícoras", {"Entregue a Carta de Gryshka para Doraso em Orgrimmar."}},
[6386] = {"De volta ao Monte Navalha", {"Pegue uma mantícora do mestre de mantícoras Doraso para o Monte Navalha. Chegando lá, entregue a Carta de Gryshka para Grimtak."}},
[6387] = {"Alunos brilhantes", {"Entregue a Lista de Gremlock para Brolan Barbavento em Kharanos."}},
[6388] = {"Grif Trovino", {"Entregue os Cutelos de Honra para Grif Trovino em Altaforja."}},
[6391] = {"Carona para Altaforja", {"Compre um voo de grifo para Altaforja do mestre de grifos Brolan Barbavento. Chegando lá, entregue a Lista de Gremlock para Golnir Topadão."}},
[6392] = {"Fale novamente com Gremlock", {"Compre um voo de grifo para Kharanos do mestre de grifos Grif Trovino. Chegando lá, entregue os Cutelos de Honra para Gremlock Pilsnor."}},
[6393] = {"Guerra elemental", {"Leve 10 Incenderitas a Tsunomem no Retiro Rocha do Sol."}},
[6394] = {"A picareta de Thazz'ril", {"Leve a Picareta de Thazz'ril para o Encarregado Thazz'ril."}},
[6395] = {"O último desejo de Marla", {"Leve os Restos de Saulo Mitos para o Túmulo de Marla, depois fale novamente com a Noviça Elvira."}},
[6401] = {"Kaya está viva", {"Conte a boa notícia a Tammra Campo de Vento no Retiro Rocha do Sol.\r\n"}},
[6421] = {"Ravina da Avalanche", {"Explore as profundezas da caverna na Ravina da Avalanche e colete 10 Cristais de Ressonita para que Mor'rogal, no Retiro Rocha do Sol, os analise."}},
[6441] = {"Chifres de Sátiro", {"Consiga 16 Chifres de Sátiro para Pixel no Posto Machadada."}},
[6442] = {"Nagas na Praia de Zoram", {"Entregue 20 Cabeças de Iracauda a Marukai, na Praia de Zoram."}},
[6461] = {"Fome sangrenta", {"Xen'Zilla, em Malaka'Jin, precisa que você mate 10 Rastejadoras de Fundolimo e 7 Cospeçonhas de Fundolimo."}},
[6462] = {"Patuá troll", {"Entregue 8 Patuás Troll para Mitsuwa no Posto de Vigia Grito Infernal."}},
[6481] = {"O terrano se ergue", {"Abra o esquife de ressonita com o Cristal de Ressonita Encantado e, então, mate Goggeroc. Fale novamente com Mor'rogal levando as notícias e o Cristal de Ressonita Encantado."}},
[6482] = {"Liberdade para Ruul!", {"Escolte Ruul e, em seguida, fale com Yama Casco de Neve no Posto Machadada."}},
[6501] = {"O Olho do Dragão", {"Você deve vasculhar o mundo em busca de uma criatura capaz de restaurar o poder do Fragmento do Olho do dragão. A única informação que você tem sobre essa criatura é que ela existe."}},
[6502] = {"Amuleto da Chama Dracônica", {"Confronte o General Drakkisath e consiga o Sangue do Campeão dos Dragões Negros. Drakkisath está na sala do trono atrás dos Salões da Ascensão, no Pico da Rocha Negra."}},
[6503] = {"Vanguardeiros do Vale Gris", {"Mate 9 Vanguardeiros do Vale Gris."}},
[6504] = {"As páginas perdidas", {"Encontre as 12 páginas que voaram do Manual de Operações do Retalhador e organize-as nos capítulos 1, 2 e 3. Devolva o manual a Gurda Marcafúria, que está perto do Posto Machadada."}},
[6521] = {"Uma aliança profana", {"Bring Ambassador Malcin's Head to Bragor Bloodfist in the Undercity."}},
[6522] = {"Uma aliança profana", {"Take the Small Scroll to Bragor Bloodfist at the Royal Quarter in the Undercity."}},
[6523] = {"Proteja Kaya", {"Escolte Kaya Casco Chato até a Aldeia Apareje e, então, fale novamente com Makaba Casco Chato perto da extremidade sudeste das Torres de Pedra."}},
[6541] = {"Apresente-se a Kadrak", {"Report to Kadrak at the watch tower in Northern Barrens."}},
[6542] = {"Apresente-se a Kadrak", {"Apresente-se a Kadrak na torre de vigia dos Sertões Setentrionais."}},
[6543] = {"Os relatórios dos Brado Guerreiro", {"Abra o Monte de Relatórios e entregue os Relatórios dos Brado Guerreiro ao Batedor do Brado Guerreiro, o Mensageiro do Brado Guerreiro e o Pioneiro do Brado Guerreiro. Em seguida, entregue o informe sobre a situação atual que você receberá de cada um deles para Kadrak, na torre de vigia do norte dos Sertões."}},
[6544] = {"Junte-se aos bons", {"Acompanhe Torek no ataque ao Posto Avançado Asa de Prata e sua comandante, Duriel Flameluna. Em seguida, apresente-se a Ertog Raivatusco no Posto Machadada."}},
