[7201] = {"O último elemento", {"Vá até o Abismo Rocha Negra e obtenha 10 Essências dos Elementos. Seu primeiro impulso é procurar nos golens e criadores de golens. Você lembra que Vivian Latumba também mencionou elementais.\r\n"}},
[7202] = {"Korrak, o Sanguinário", {"De acordo com a lenda, o líder dos poderosos trolls Invernacha aparece quando bem entende para trazer destruição e caos aos moradores do Vale Alterac.","Se Korrak aparecer, mate-o e fale novamente com o Tenente Haggerdin nas Montanhas de Alterac."}},
[7223] = {"Sobras de Armadura", {"Leve 20 Sobras de Armadura para Murgot Baixaforja em Dun Baldar."}},
[7224] = {"Espólios dos inimigos", {"Leve 20 Sobras de Armadura para o Ferreiro Regzar na Aldeia Lobo do Gelo."}},
[7241] = {"Em defesa dos Lobo do Gelo", {"Explore o Vale Alterac, localizado nas Montanhas de Alterac. Encontre o Mestre Guerreiro Laggrond, que fica diante da entrada do túnel, e converse com ele para começar a sua carreira de soldado Lobo do Gelo. O Vale Alterac fica ao norte da Serraria Tarren, no sopé das Montanhas de Alterac."}},
[7261] = {"Imperativo do soberano", {"Vá ao Vale Alterac no Contraforte de Eira dos Montes. Encontre e fale com o Tenente Haggerdin em frente ao túnel de entrada.","Pela glória de Barbabronze!"}},
[7281] = {"Amor fraternal", {"Siga para o território dos Lançatroz e mate o Comandante Carlos Filipe. Em seguida, fale novamente com o Comandante Luis Filipe."}},
[7282] = {"Amor fraternal", {"Siga para o território dos Lobo do Gelo e mate o Comandante Luis Filipe. Fale novamente com o Comandante Carlos Filipe quando a missão for completada."}},
[7301] = {"Senhores do céu caídos", {"Siga para o território dos Lobo do Gelo e procure pelo Comandante de Ala Slidore, Comandante de Ala Vipore e Comandante de Ala Ichman. Fale novamente com o Comandante Duffy ao completar a missão."}},
[7302] = {"Senhores do céu caídos", {"Descubra o que houve com o Comandante de Ala Guze, o Comandante de Ala Jeztor e o Comandante de Ala Mulverick. Fale novamente com o Comandante Mulfort ao completar a missão."}},
[7321] = {"[DEPRECATED] Soothing Turtle Bisque", {"Bring 10 pieces of Turtle Meat to Christoph Jeffcoat in Tarren Mill."}},
[7383] = {"Teldrassil: O dever dos Kaldorei", {"Encha o Frasco de Ametista."}},
[7385] = {"Um galão de sangue", {"Requisitos:"}},
[7386] = {"Drusa de Cristal", {"Requisitos:"}},
[7441] = {"Pusillin e o Ancião Azj'Tordin", {"Vá ao Gládio Cruel e encontre o Diabrete Pusillin. Convença-o usando qualquer meio que for necessário a lhe dar o Livro de Encantamentos de Azj'Tordin.","Fale novamente com Azj'Tordin no Pavilhão Lariss, em Feralas, caso você recupere o Livro de Encantamentos."}},
[7461] = {"A loucura intrínseca", {"Mate os guardiões que cercam os cinco pilares de força da Prisão de Immol'thar. Quando os pilares se desligarem, o campo de força que cerca Immol'thar se dissipará.","Destrua o terrível demônio que fica no coração da Prisão de Immol'thar. Por fim, enfrente o Príncipe Tortheldrin, no Ateneu.","Fale novamente com a Anciã Shen'dralar, no pátio, após cumprir a missão."}},
[7481] = {"Lendas élficas", {"Procure Telmius Romanesca no Gládio Cruel. Reporte-se, então, ao Sábio Korolusk na Aldeia Mojache com qualquer informação que você encontrar."}},
[7482] = {"Lendas élficas", {"Procure Telmius Romanesca no Gládio Cruel. Reporte-se, então, à Erudita Runacardos em Plumaluna com qualquer informação que você encontrar."}},
[7483] = {"Incunábulo de Rapidez", {"Leve 1 Incunábulo de Rapidez, 1 Diamante Preto Límpido, 2 Estilhaços Brilhantes Grandes, e 2 Sangues dos Heróis ao Erudito Lydros no Gládio Cruel para receber um Arcano de Rapidez."}},
[7484] = {"Incunábulo de Concentração", {"Leve 1 Incunábulo de Concentração, 1 Diamante Preto Límpido, 4 Estilhaços Brilhantes Grandes, e 2 Peles de Sombra ao Erudito Lydros no Gládio Cruel para receber um Arcano de Concentração."}},
[7485] = {"Incunábulo de Proteção", {"Leve 1 Incunábulo de Proteção, 1 Diamante Preto Límpido, 2 Estilhaços Brilhantes Grandes e 1 Sutura de Abominação Puída ao Erudito Lydros no Gládio Cruel para receber um Arcano de Proteção."}},
