[7489] = {"A teia de Letendris", {"Leve a Teia de Letendris para Talo Casco de Espinhos na Aldeia Mojache, em Feralas."}},
[7490] = {"Vitória da Horda", {"Leve a Cabeça da Onyxia para Garrosh em Orgrimmar."}},
