[8302] = {"A mão do íntegro", {"Colete 200 Fragmentos de Carapaça Silitídea e fale novamente com Baristolth."}},
[8303] = {"Anacronos", {"Encontre Anacronos nas Cavernas do Tempo, em Tanaris."}},
[8304] = {"Querida Natália", {"Questione Frankal e Rutgar no Acampamento do Barbabronze sobre o paradeiro de Natália Mar'alith."}},
[8305] = {"Relembranças longínquas", {"Localize a Lágrima Cristalina em Silithus, e contemple suas profundezas."}},
[8306] = {"No ventre da loucura", {"O Comandante Mar'alith do Forte Cenariano em Silithus quer que você encontre Natália. A informação que conseguiu aponta para Colme'Régia, ao sul, como sendo a área em que você encontrará a Senhora Natália Mar'alith.","Não se esqueça de falar com os anões no acampamento do Barbabronze antes de se aventurar na colmeia. Eles podem ter algum trabalho e conselhos para você.","E, Bomdiyada, lembre-se das palavras do Comandante: \"Faça o que for necessário...\""}},
[8307] = {"Receita do deserto", {"Procure informações sobre como cozinhar Carne de Verme da Areia no acampamento do Martelo do Crepúsculo, a oeste do Forte Cenariano."}},
[8308] = {"A carta perdida de Brann Barbabronze", {"Leve a Carta de Brann Barbabronze para o Acampamento do Barbabronze na região sul de Silithus."}},
[8309] = {"À procura de glifos", {"Use o Kit de Transcrição do Geólogo para obter um Decalque de Colme'Régia do Cristal Entalhado de Glifos de Colme'Régia."}},
[8310] = {"Quebrando o código", {"Obtenha um Cérebro de Silitídeo de Colme'Régia intacto de qualquer silitídeo em Colme'Régia."}},
[8311] = {"Gostosuras da Noturnália para Jacinto!", {"Fale com os estalajadeiros de Ventobravo, Altaforja e Darnassus, e também com Didiê Du Mocó, em Altaforja. Pague as prendas que eles pedirem em troca de gostosuras.","Leve um Marzipan de Darnassus, uma Jujuba de Gnomeregan, um Nougat de Ventobravo e uma Balinha de Altaforja para Jacinto, que está em frente a Ventobravo."}},
[8312] = {"Gostosuras de Noturnália para Assustosa!", {"Fale com os estalajadeiros de Orgrimmar, do Bairro Inferior e do Penhasco do Trovão, e também com Kali Remik, no Vale dos Espíritos de Orgrimmar. Pague as prendas que eles pedirem e receba doces como recompensa.","Leve um Marzipã do Penhasco do Trovão, uma Jujuba Lançanegra, um Nougat de Orgrimmar e uma Balinha do Bairro Inferior para Assustosa, nas Ruínas de Lordaeron."}},
[8313] = {"Compartilhando o conhecimento", {"Leve a Página Rasgada de Receita para Calandrath na estalagem do Forte Cenariano."}},
[8314] = {"Desvendando o mistério", {"Entregue o Prisma de Cristal com Glifos para a Geóloga Larksbane no Forte Cenariano, que fica em Silithus."}},
[8317] = {"Assistência culinária", {"Calandrath, no Forte Cenariano, precisa de uma porção de 10 Bolinhos Defumados do Deserto."}},
[8318] = {"Comunicação secreta", {"Traga 10 Textos Cifrados do Crepúsculo para Bor Juba Agreste no Forte Cenariano."}},
