[8325] = {"Retomada da Ilha Andassol", {"Mate 6 Moreias de Mana. Elas ficam perto dos Cristais Flamejantes junto à torre e ao mirante."}},
[8326] = {"Medidas drásticas", {"Recolha 8 Coleiras de Linces Garrataque e de Filhotes de Garrataque. Fale novamente com a Magistra Erona na Ilha Andassol quando terminar."}},
[8327] = {"Apresente-se a Lanthan Perilon", {"Fale com Lanthan Perilon na Ilha Andassol; ele encontra-se perto do caminho para oeste além da Torre do Sol."}},
[8328] = {"Treinamento de mago", {"Fale com Julia Golpessol, a instrutora de Magos, na Torre do Sol, que fica na Ilha Andassol."}},
[8329] = {"Treinamento de guerreiro", {"Fale com o instrutor de guerreiros Delios na Torre do Sol, que fica na Ilha Andassol."}},
[8330] = {"Os pertences de Solanian", {"Recolha o Orbe de Vidência de Solanian, o Pergaminho de Magia do Flagelo e o Diário. Eles estão na Ilha Andassol ao lado do poço, da fonte e de um dos Cristais Flamejantes. Leve-os ao Vigia da Nascente na Torre do Sol, que fica na Ilha Andassol, quando estiver de posse de todos eles."}},
