[8460] = {"[DEPRECATED]Timbermaw Ally", {"Grazle quer que você prove seu valor matando 6 Guerreiros de Lenha Morta, 6 Desbravadores de Lenha Morta e 6 Jardineiros de Lenha Morta. Quando você terminar, fale novamente com ele na parte sul da Selva Maleva, perto do Santuário Esmeralda."}},
[8461] = {"[DEPRECATED]Deadwood of the North", {"Nafien quer que você mate 6 Vigias do Covil de Lenha Morta, 6 Vingadores de Lenha Morta e 6 Xamãs de Lenha Morta. Fale novamente com ele na parte norte da Selva Maleva, perto da entrada do Domínio dos Presamatos."}},
[8462] = {"[DEPRECATED]Speak to Nafien", {"Viaje para o norte ao longo da estrada principal em Selva Maleva e fale com o pelurso chamado Nafien. Ele monta guarda diante da entrada do Domínio dos Presamatos."}},
[8463] = {"Cristais de mana instáveis", {"Leve 6 Cristais de Mana Instáveis para Aledon Marcassol na Praça Asa do Falcão."}},
[8464] = {"[DEPRECATED]Winterfall Activity", {"Salfa quer que você mate 8 Xamãs Invernosos, 8 Vigias do Covil dos Invernosos e 8 Ursinos Invernosos. Salfa está perto da entrada do Domínio dos Presamatos, em Hibérnia.\r\n"}},
[8465] = {"[DEPRECATED]Speak to Salfa", {"Viaje pelo Domínio dos Presamatos até chegar a Hibérnia. Fale com Salfa, que está de guarda em frente à entrada do Domínio dos Presamatos."}},
[8468] = {"Procura-se: Thaelis, o Famélico", {"Entregue a Cabeça do Thaelis ao Sargento Kan'ren na Praça Asa do Falcão, que fica na Floresta do Canto Eterno."}},
[8470] = {"Totem ritual de Lenha Morta", {"Leve o Totem Ritual de Lenha Morta para dentro do Domínio dos Presamatos e veja se um dos pelursos dali tem alguma utilidade para o artefato. Os Presamatos não lhe dirigirão a palavra a não ser que você tenha alcançado, no mínimo, a reputação de tolerado entre eles."}},
[8471] = {"Totem Ritual Invernoso", {"Leve o Totem Ritual Invernoso para dentro do Domínio dos Presamatos e veja se um dos pelursos dali tem alguma utilidade para o artefato. Os Presamatos não lhe dirigirão a palavra a não ser que você tenha alcançado, no mínimo, a reputação de tolerado entre eles."}},
[8472] = {"Defeito grave", {"Consiga 6 Núcleos Arcanos das Patrulhas Arcanas que perambulam pelas ruínas de Luaprata e entregue-os ao Magíster Jaronis na Praça Asa do Falcão, que fica na Floresta do Canto Eterno."}},
[8473] = {"Trabalho sujo", {"Mate 10 Jardineiros Murchos na Mata Queimada. Em seguida apresente-se a Larianna Fluminéola na torre a noroeste da Mata Queimada, que fica na Floresta do Canto Eterno."}},
[8474] = {"Pingente do Velho Cascabranca", {"Encontre a pessoa que talvez tenha confeccionado o Pingente do Velho Cascabranca. Ela pode ter informações sobre o item."}},
[8475] = {"A Trilha da Morte", {"Mate 8 Pilhadores Pragossos e apresente-se à Patrulheira Jaela na Trilha da Morte, que fica na Floresta do Canto Eterno."}},
[8476] = {"Defendendo o terreno", {"A Tenente Correalba, do Retiro dos Andarilhos, que fica na Floresta do Canto Eterno, quer que você mate 5 Berserkers Amani e 5 Arremessadores de Machado Amani."}},
[8477] = {"O martelo do lanceiro", {"Procure o Armeiro de Lanças Otembe em Tor'Watha, a sudeste do Retiro dos Andarilhos, e pegue o Martelo de Otembe. Em seguida, entregue o Martelo para Arathel Forjassol no Retiro dos Andarilhos, que fica na Floresta do Canto Eterno."}},
[8479] = {"Zul'Marosh", {"Ven'jashi, o prisioneiro troll em Tor'Watha, quer que você entregue a Cabeça do Chefe Zul'Marosh para ele. O Chefe Zul'Marosh está em Zeb'Watha, do outro lado do Lago Elrendar."}},
[8480] = {"Armamentos perdidos", {"Entregue 8 Armamentos Sin'dorei para Velendris Matinalba na Floresta do Canto Eterno."}},
[8481] = {"A raiz de todo o mal", {"Bote a Tocha de Evocação Demoníaca na entrada da caverna do Grolnar, na aldeia dos pelursos Invernosos. Em seguida, derrote o demônio, recolha a Essência de Shandivious e entregue-a para Gorn Caolho, no Domínio dos Presamatos."}},
[8482] = {"Documentos incriminadores", {"Leve os Documentos Incriminadores para Aledon Marcassol na Praça Asa do Falcão, que fica na Floresta do Canto Eterno."}},
[8483] = {"O espião enânico", {"Entregue a Cabeça do Minerador Bigorneiro para Aledon Marcassol na Praça Asa do Falcão, que fica na Floresta do Canto Eterno."}},
[8484] = {"Promovendo a paz", {"Leve a Oferenda de Paz dos Presamatos ao Rei Magni Barbabronze em Altaforja."}},
[8486] = {"Instabilidade arcana", {"A Protetora de Meridianos Velania quer que você mate 5 Espectros de Mana e 5 Espreitadores de Mana."}},
[8487] = {"Solo conspurcado", {"A Aprendiz Mirveda, do Sacrário do Leste, que fica na Floresta do Canto Eterno, quer que você colete 8 Amostras de Solo Infecto."}},
[8488] = {"Resultados inesperados", {"Proteja a Aprendiz Mirveda da emboscada do Flagelo."}},
[8490] = {"Fortalecendo as defesas!", {"Insira o Cristal Infuso na Pedra Rúnica do Leste e proteja-a do Flagelo por 1 minuto. Entregue o Cristal Infuso para o Guarda-runas Deryan na Floresta do Canto Eterno para receber a recompensa."}},
[8491] = {"Caça às peles", {"Velan Roburclaro, da Vila de Brisabela, que fica na Floresta do Canto Eterno, quer que você leve 6 Pelagens de Garrataque para ele."}},
[8492] = {"A Aliança precisa de barras de cobre!", {"Leve 20 Barras de Cobre ao Sargento Pedracenho na Ala Militar de Altaforja."}},
