[8885] = {"O anel de Mmmrrrggglll", {"Retome o Anel de Mmmrrrggglll dos restos mortais do chefe dos Escamatroz. Entregue-o a Hathvelion Mirassol na Floresta do Canto Eterno, no penhasco próximo à Costa Plácida."}},
[8886] = {"Piratas Escamatroz!", {"Recupere 6 itens do Carregamento da Capitã Kelisendra e devolva-os a ela no campo protegido a leste do Ancoradouro Velaclara, que fica na Floresta do Canto Eterno."}},
[8887] = {"As cartas náuticas perdidas da capitã Kelisendra", {"Devolva as Cartas Náuticas Perdidas da Capitã Kelisendra para ela no acampamento fortificado a leste do Ancoradouro Velaclara, na Floresta do Canto Eterno."}},
[8888] = {"A aprendiz do magíster", {"Fale com a Aprendiz Loralthalis perto da Torre do Ocaso na Floresta do Canto Eterno."}},
[8889] = {"Desativando a torre", {"Desative 3 Fontes de Energia da Torre do Ocaso. Em seguida, entregue a Joia Desativadora para a Aprendiz Loralthalis na Floresta do Canto Eterno."}},
[8890] = {"Notícias da torre", {"Fale novamente com o Magíster Ocaso no Retiro dos Andarilhos na Floresta do Canto Eterno."}},
