[10366] = {"Jol", {"Fale com Jol no Sacrário dos Vindicantes, na Exodar."}},
[10367] = {"Um traidor entre nós", {"Naladu, nas Ruínas de Sha'naar, quer que você obtenha a Chave de Sha'naar de uma cabana próxima."}},
[10368] = {"Os anciãos Capuzcória", {"Liberte Morod, o Acorda-ventos, Akoru, o Chama-fogo e Aylaan, o Conclama-águas. Fale novamente com Naladu quando cumprir a missão."}},
[10369] = {"O fim de Aezeth", {"Use o Cajado dos Anciãos Capuzcória em Aezeth, o Impiedoso, e mate-o quando ele perder os poderes. Fale novamente com Naladu nas Ruínas de Sha'naar quando cumprir esta missão."}},
[10372] = {"Uma pergunta discreta", {"Fale com Eralan em Tranquillien."}},
[10378] = {"Consumir magia", nil},
[10381] = {"Abaixo com Aldor", {"Apresente-se ao Exarca Orelis, na Área 52.","Cumprir missões para os Aldor fará com que sua reputação entre os Áugures diminua."}},
[10382] = {"Siga para o front", {"Pegue um grifo até o Ponto da Honra e fale com o Marechal-de-campo Barbosão."}},
[10384] = {"Dados do Etéreum", {"A Imagem do Comandante Ameer nos Bivaques do Etéreum, no rádio-sinalizador Zeta do Etéreum, quer que você recupere a Unidade de Dados de Etéreum."}},
[10385] = {"Risco de dano cerebral: Alto", {"A Imagem do Comandante Ameer nos Bivaques do Etéreum, no rádio-sinalizador Zeta do Etéreum, quer que você retorne com 15 peças de dados do interruptor Etéreum."}},
[10388] = {"Retorno a Thrallmar", {"Volte a Thrallmar e fale com Nazgrel."}},
[10389] = {"A agonia e a escuridão", {"O Magíster Falcossangre quer que você mate 10 Terríferos nos Poços de Aggonar."}},
[10390] = {"Campo-forja: Megido", {"Nazgrel, em Thrallmar, quer que você ataque o Campo-forja: Megido, mate 10 Serviçais Gan'arg e retorne com a cabeça de Navalhasserra."}},
[10391] = {"Canhões da raiva", {"Destrua 3 Canhões Vis MKI no Campo-forja: Raiva e fale novamente com Nazgrel em Thrallmar."}},
[10392] = {"À beira do abismo", {"Mate Arix'Amal para conseguir a Chave do Portal da Legião Ardente. Use a Chave do Portal da Legião Ardente na Runa do Rancor, e fale novamente com Nazgrel em Thrallmar."}},
[10393] = {"Planos vis", {"Leve a Missiva da Legião Ardente para o Magíster Falcossangre em Thrallmar."}},
[10394] = {"Sabotagem - Campo-forja: Megido", {"Mate 10 Serviçais Gan'args e o Navalhasserra no Campo-forja: Megido e fale novamente com o Marechal-de-campo Barbosão no Ponto da Honra."}},
[10395] = {"Correspondência sombria", {"Leve a Missiva para o Áugure Dimensional Kryv no prédio principal da Fortaleza da Honra."}},
[10396] = {"Inimigo de meu inimigo...", {"Destrua 3 Canhões Vis MKI no Campo-forja: Raiva e fale novamente com o Marechal-de-campo Barbosão no Ponto da Honra."}},
[10397] = {"Ponto de Invasão: Extirpador", {"Mate o Armipotente Arix'Amal. Pegue a Chave do Portal da Legião Ardente. Use a Chave do Portal da Legião Ardente na Runa do Rancor."}},
[10399] = {"O coração das trevas", {"Mate 10 Terríferos e fale novamente com o Áugure Dimensional Kryv no prédio principal da Fortaleza da Honra."}},
[10400] = {"Lorde supremo", {"Mate Arrázius, o Cruel, nos Poços de Aggonar e fale novamente com o Comandante-chefe Danath Matatroll na Fortaleza da Honra."}},
[10403] = {"Naladu", {"Encontre Naladu nas Ruínas de Sha'naar."}},
[10405] = {"S-A-B-O-T-A-G-E-M", {"A Imagem do Comandante Ameer, nos Bivaques do Etéreum, no rádio-sinalizador Zeta do Etéreum, quer que você retorne com uma Fita de Etéreum Preparado."}},
[10406] = {"A entrega da mensagem", {"A Imagem do Comandante Ameer, nos Bivaques do Etéreum, no rádio-sinalizador Zeta do Etéreum, quer que você escolte o Demolidor do Protetorado ao condutor de Manaforja Ultris e que o mantenha protegido enquanto ele sabota a tubulação."}},
[10408] = {"Rei do Nexus Salhadaar", {"A Imagem do Comandante Ameer nos Bivaques do Etéreum, no rádio-sinalizador Zeta dos Etéreum, quer que você use o Disruptor do Protetorado para dissipar os campos de força de Salhadaar e que, em seguida, você destrua Salhadaar."}},
[10409] = {"O golpe de misericórdia à Legião", {"A Alta-sacerdotisa Ishanah quer que você vá ao Teleportador da Legião, no canto noroeste de Eternévoa, e que o use para teleportar-se para o Trono de Socrethar. Alcançado o destino, derrote Socrethar.","Cumprir missões para os Aldor fará com que sua reputação com os Áugures diminua."}},
[10411] = {"Santo eletrochoque!", {"O Pesquisador Navuud, no Posto de Guarda do Protetorado, pediu que você destruísse 30 Glóbulos de Refugos do Caos e 30 Glóbulos de Lodo Infiltrante."}},
[10412] = {"Sinetes dos Asardente", {"A Magistra Fyalenn, em Shattrath, quer que você leve 10 Sinetes Asardente para ela.","Completar missões para os Áugures reduzirá sua reputação com os Aldor."}},
[10413] = {"Os horrores da poluição", {"Leve a Ampola de Gosma de Horror Caótico para o Pesquisador Navuud, no Posto de Guarda do Protetorado, em Eternévoa."}},
[10414] = {"Sinete único dos Asardente", {"Requisitos:"}},
[10415] = {"Mais sinetes dos Asardente", {"Requisitos:"}},
[10416] = {"Síntese do poder", {"Voren'thal, o Vidente, em Shattrath, quer que você obtenha um Tomo Arcano.","Realizar missões para os Áugures fará sua reputação com os Aldor diminuir."}},
[10417] = {"Qual é o diagnóstico?!", {"Consiga o Diagnóstico Final e leve-o a Muhrdad, no Posto Terramédia."}},
[10418] = {"Uma raça de sabotadores", {"Muhrdad, no Posto Terramédia, quer que você mate 8 Crocoliscos Farpescamas."}},
[10419] = {"Tomos arcanos", {"Requisitos:"}},
[10420] = {"Luz purificante", {"Leve um Armamento Vil para Ishanah, em Shattrath.","Cumprir missões para os Aldor fará com que sua reputação com os Áugures diminua."}},
[10421] = {"Armamentos vis", {"Requisitos:"}},
[10422] = {"Capitão Tyralius", {"O Tratador da Carne Viridius, no Posto de Guarda do Protetorado, pediu para você encontrar e libertar o Capitão Tyralius."}},
[10423] = {"Ao Pináculo da Tempestade", {"Fale com Ghabar, no Pináculo da Tempestade, em Eternévoa."}},
[10424] = {"Diagnóstico: crítico", {"Use o aparelho diagnosticador próximo do gerador do Eco-domo Sutheron. Em seguida, leve o Diagnóstico Final para Ghabar, no Pináculo da Tempestade."}},
[10425] = {"Fuga dos bivaques", {"Escolte o Vanguarda do Protetorado Capturado de volta ao Posto de Guarda do Protetorado, em Eternévoa."}},
[10426] = {"A flora dos eco-domos", {"Use o Modulador de Campo de Energia nas açoitadeiras de Farahlon. Observe os resultados e relate-os a Aurine Fulgiluna, no Pináculo da Tempestade, quando tiver conduzido 10 testes."}},
[10427] = {"Criaturas dos eco-domos", {"Etiquete 12 Talbuques para Aurine Fulgiluna, no Pináculo da Tempestade. Você pode usar o Identificador de talbuque nos Garanhões Talbuques e nas Corças Talbuques."}},
[10428] = {"O pescador desparecido", {"Encontre Capúleo nas docas a sudeste da Ilha Nevoaprata que fica perto da costa de Névoa Lazúli."}},
[10429] = {"Quando a natureza vai longe demais", {"Aurine Fulgiluna, no Pináculo da Tempestade, quer que você lhe traga um Coração de Hidra Descomunal."}},
[10430] = {"Protótipo em teste", {"Leve o Aparato Experimental de Reparação para Tashar, no Eco-domo Campolonge, em Eternévoa."}},
[10431] = {"Ajuda vinda de fora", {"Vá ao Acampamento do Tuluman, ao norte da Área 52, e fale com Kaylaan.","Cumprir missões para os Aldor fará com que a sua reputação entre os Áugures diminua."}},
[10432] = {"Evidência maldita", {"Obtenha 8 Ordens de Kael'thas dos demônios em Manaforja Ara. Em seguida, leve-as ao Mestre Espião Thalodien, na Área 52.","Cumprir missões para os Áugures fará com que sua reputação entre os Aldor diminua."}},
[10433] = {"Manter uma boa aparência", {"Leve 10 Pelagens de Lince Rasgador a Marcísio Teira, no Posto Terramédia."}},
[10434] = {"A dupla dinâmica", {"Fale com Audi, a Encrenqueira, no Pináculo da Tempestade, em Eternévoa."}},
[10435] = {"Em busca de mercadoria", {"Audi, a Encrenqueira, no Pináculo da Tempestade, quer que você lhe traga 10 Segmentos de Gerador de Domo."}},
[10436] = {"Tudo pronto!", {"Tashar, no Eco-domo Campolonge, quer que você mate 12 Raptores Gumedente."}},
[10437] = {"Receita de destruição", {"Professor Dabiri, no Posto de Guarda do Protetorado, em Eternévoa, quer que você colete 8 Partículas de Dimensius."}},
[10438] = {"Asas etéreas", {"Professor Dabiri, no Posto de Guarda do Protetorado, em Eternévoa, quer que você use o Interruptor de Fases ao ter o Conduíte do Caos, no topo de Ultris, a seu alcance. Dispare o Interruptor de Fases quantas vezes puder, para garantir aniquilação total."}},
[10439] = {"Dimensius, O que Tudo Devora", {"Professor Dabiri, no Posto de Guarda do Protetorado, em Eternévoa, quer que você fale com o Capitão Said e que siga com seu exército para a Manaforja Ultris para matar Dimensius, que Tudo Devora!"}},
[10440] = {"Êxito!", {"Fale novamente com Ghabar, no Pináculo da Tempestade, em Eternévoa."}},
[10442] = {"O auxílio ao Posto Cenariano", {"A Falcoeira Drena Fluminéola quer que você ofereça seus serviços a Tiá Juba Rubra, no Posto Cenariano, que fica na Península Fogo do Inferno."}},
[10443] = {"O auxílio ao Posto Cenariano", {"Amaan, o Sábio, quer que você ofereça seus serviços a Tiá Juba Rubra, no Posto Cenariano, que fica na Península Fogo do Inferno."}},
[10444] = {"Relatório para o Posto Alleriano", {"Jenai Sussurrastra quer que você se apresente ao Tenente Meridian no Posto Alleriano, que fica na Mata Terokkar."}},
[10445] = {"As ampolas da eternidade", {"Soridormi, nas Cavernas do Tempo, quer que você recupere o Resíduo da Ampola de Vashj da Dama Vashj no Reservatório Presacurva e o Resíduo da Ampola de Kael do Kael'thas Andassol na Bastilha da Tormenta."}},
[10446] = {"O código final", {"O Tenente Meridian quer que você use o Código Final para ativar a Bomba de Mana. A seguir, fale novamente com Jenai Sussurrastra na Fortaleza Alleriana, que fica na Mata Terokkar."}},
[10447] = {"O código final", {"A Sargento Chawni quer que você use o Código Final para ativar a Bomba de Mana. A seguir, fale novamente com Tooki no Acampamento Quebrapedra, que fica na Mata Terokkar."}},
[10448] = {"Relatório para o Acampamento Quebrapedra", {"Tooki quer que você se apresente à Sargento Chawni. Você pode encontrá-la no Acampamento Quebrapedra, que fica na Mata Terokkar."}},
