[10507] = {"Virada", {"Use a Pedra Teleportadora de Socrethar no Ponto de Invasão: Lorde supremo, ao norte da Base-forja: Oblívio para levar seu grupo ao Porto de Socrethar. Uma vez lá, use a Presença de Voren'thal para derrotar Socrethar.","Cumprir missões para os Áugures fará com que a sua reputação entre os Aldor diminua."}},
[10508] = {"O presente de Voren'thal", {"O Mestre Espião Thalodien, na Área 52, quer que você obtenha a Primeira Metade da Pedra de Socrethar do Mestre Forjador Morug, na Base-forja: Oblívio e a Segunda Metade da Pedra de Socrethar de Silroth, na Base-forja: Geena.","Cumprir missões para os Áugures fará com que sua reputação entre os Aldor diminua."}},
[10509] = {"Destinado à glória", {"Leve a Pedra teleportadora de Socrethar a Voren'thal, o Vidente, no Terraço dos Áugures, em Shattrath.","Cumprir missões para os Áugures fará com que sua reputação entre os Aldor diminua."}},
[10510] = {"Para a Mina de Draenetista", {"Colete 5 Cristais da Mina de Draenetista."}},
[10511] = {"Banebruto é uma enorme cerveja!", {"Colete 11 Cervejas Banebruto dos Malho Sangrento."}},
