[10513] = {"Oronok Coração-partido", {"Procure Oronok Coração-partido na Plataforma Estilhaçada, ao norte da Cisterna Serpentálios."}},
[10514] = {"Eu fui muitas coisas...", {"Oronok Coração-partido na Fazenda de Oronok, que fica no Vale da Lua Negra, quer que você recolha 10 Tubérculos Lua Negra no Platô Despedaçado.","Ele também quer o Apito de Javali de Oronok de volta quando você terminar."}},
[10515] = {"Dê uma lição", {"Oronok Coração-partido na Fazenda de Oronok, no Vale da Lua Negra, quer que você destrua 10 Ovos de Esfola-pedra Vorasga no Platô Despedaçado."}},
[10516] = {"Paramentos de uma vindicante", {"Recupere a Lâmina da Vindicante Vuuleen e o Escudo da Vindicante Vuuleen."}},
[10517] = {"A Luz não gosta de Gorr'dim, não!", {"Mate Gorr'dim, líder dos ogros do Bastião Giralança."}},
[10518] = {"Bandeira da discórdia", {"Finque a bandeira dos Giralança no topo da Torre do Malho do Norte para atrair Gurn Rangalarva. Recolha a bandeira do clã Giralança e o capacete de Gurn Rangalarva."}},
[10519] = {"A Cifra da Danação: verdade e história", {"Oronok Coração-partido na Fazenda de Oronok, no Vale da Lua Negra, quer que você escute a história dele. Fale com Oronok para começar a ouvir a história."}},
[10521] = {"Grom'tor, filho de Oronok", {"Encontre Grom'tor, filho de Oronok, no Pontal dos Serpentálios, no Vale da Lua Negra."}},
[10522] = {"A Cifra da Danação: a carga de Grom'tor", {"Grom'tor, Filho de Oronok, no Pontal dos Serpentálios, no Vale da Lua Negra, quer que você recupere o Primeiro Fragmento da Cifra da Danação."}},
[10523] = {"A Cifra da Danação: primeiro fragmento recuperado", {"Leve a Caixa-forte de Grom'tor para Oronok Coração-partido na Fazenda de Oronok, no Vale da Lua Negra."}},
[10524] = {"Artefatos do clã Senhor do Trovão", {"Colete o Tambor do Clã Senhor do Trovão, a Flecha do Clã Senhor do Trovão e a Tabuleta do Clã Senhor do Trovão."}},
[10525] = {"Guia de visão", {"Use o Fragmento de Alma do Fogo para transformar seu espírito em um guia de visão para revelar a localização do último artefato dos Senhor do Trovão."}},
[10526] = {"A Lança do Trovão", {"Colete a Lança do Trovão."}},
[10527] = {"Ar'tor, filho de Oronok", {"Encontre Ar'tor, Filho de Oronok, no Pontal Illidari no Vale da Lua Negra."}},
[10528] = {"Prisões demoníacas de cristal", {"Procure e mate a Rainha da Dor Gabrissa no Pontal Illidari. Em seguida, volte até o cadáver de Ar'tor, Filho de Oronok, com a Chave Cristalina."}},
[10531] = {"The Battle for Arathi Basin!", {"Assault the mine, the lumber mill, the blacksmith and the farm, then return to Field Marshal Oslight in Refuge Pointe."}},
[10532] = {"Quebra da rota de suprimentos de Arathor", {"Vença a batalha pela Bacia Arathi, consiga uma Caixa de Recursos Arathi e fale novamente com o Sicário Mortis na Ruína do Martelo."}},
[10535] = {"Recursos da Bacia Arathi!", {"Vença a batalha pela Bacia Arathi, consiga uma Caixa de Recursos Arathi e fale novamente com a Sargento Martina na Ponta do Refúgio."}},
[10537] = {"Lohn'goron, arco dos Coração-partido", {"O Espírito de Ar'tor, no Pontal Illidari do Vale da Lua Negra, quer que você recupere Lohn'goron, Arco dos Coração-partido, com um dos demônios da área."}},
[10538] = {"Sangue quente", {"Use o Sangue de Olhos Sangrentos no Caldeirão Amaldiçoado para fazer Sangue Fervido. Leve 12 amostras de Sangue Fervido para o Boticário Alberto no Acampamento Quebra-espinha."}},
[10540] = {"A Cifra da Danação: a missão de Ar'tor", {"O Espírito de Ar'tor no Pontal Illidari, no Vale da Lua Negra, quer que você recupere o Segundo Fragmento da Cifra da Danação, que está em poder de Veneratus, o Muitos.","Criaturas atacadas ou danificadas pelo Espírito de Caçador não fornecerão saque nem experiência."}},
[10541] = {"A Cifra da Danação: segundo fragmento recuperado", {"Leve a Arca de Ar'tor para Oronok Coração-partido na Fazenda de Oronok, no Vale da Lua Negra."}},
[10542] = {"Meu narguilé! Minhas cervas!", {"Colete o Narguilé de T'chali e 10 Cervejas Banebruto dos Malho Sangrento."}},
[10543] = {"Grimnok e Korgaah, a batata tá assando!", {"Mate Grimnok Guerranato e Korgaah."}},
[10544] = {"Uma maldição para os dois clãs!", {"Use o Fetiche Sinistramente Forte para amaldiçoar 5 prédios ou torres no Bastião Giralança e 2 no Posto Avançado Malho Sangrento."}},
[10545] = {"Barril dos Giralança", {"Use o Barril de Cerveja Banebruto dos Malho Sangrento para embebedar 5 Ogros Giralança."}},
[10546] = {"Borak, filho de Oronok", {"Encontre Borak, Filho de Oronok, perto da Ponta do Eclipse, no Vale da Lua Negra."}},
[10547] = {"De cardólatras e ovos...", {"Borak, Filho de Oronok, junto à ponte ao norte da Ponta do Eclipse, quer que você encontre um Ovo Podre de Arakkoa. O ovo deve ser entregue a Tobias, o Come-lixo, em Shattrath, localizada no noroeste da Mata Terokkar."}},
[10548] = {"A triste verdade", {"Eralan, em Tranquillien, quer que você resgate a Cabeça Encolhida de Arqueólogo de um dos baús em Zeb'Tela ou Zeb'Nowa."}},
[10550] = {"O feixe de cardossangue", {"Leve o Feixe de Cardossangue a Borak, Filho de Oronok, que está na ponte perto da Ponta do Eclipse, no Vale da Lua Negra."}},
