[10750] = {"O Caminho da Conquista", {"Viaje até o Caminho da Conquista no Vale da Lua Negra."}},
[10751] = {"A invasão do caminho", {"Mate 20 Soldados Eclipsion, 10 Fidalgos Eclipsion e 10 Mesmerizadores Eclipsion."}},
[10752] = {"Rumo ao Vale Gris", {"Fale com Raene Correlobos em Astranaar."}},
[10753] = {"Livre-se dos selvagens", {"Mate 4 Devotos Vis Escamasas, 4 Devotos Vis Gorjadaga e 2 Corruptores Vis."}},
[10754] = {"Entre na Cidadela", {"Leve o Molde Pronto de Chave ao Comandante-chefe Danath Matatroll na Fortaleza da Honra, que fica na Península Fogo do Inferno."}},
[10755] = {"Entre na Cidadela", {"Entregue o Molde Pronto de Chave a Nazgrel de Thrallmar, na Península Fogo do Inferno."}},
[10757] = {"O pedido de Rohok", {"Leve 4 Barras de Ferrovil, 2 amostras de Pó Arcano e 4 Partículas de Fogo para Rohok, em Thrallmar, na Península Fogo do Inferno."}},
[10758] = {"Mais quente que o inferno", {"Destrua um Aníquilus na Península Fogo do Inferno e jogue o Molde não Aquecido de Chave nos destroços. Leve o Molde Queimado de Chave para Rohok, em Thrallmar."}},
[10759] = {"Encontre o desertor", {"Encontre Parshah nos Campos de Magma e fale com ele."}},
[10760] = {"As Ruínas de Sketh'lon", {"Consiga as Páginas 1, 2 e 3 do Diário do Comandante de Sketh'lon com os arakkoas do Conclave Negro nas Ruínas de Sketh'lon e as entregue ao Sargento Kargrul."}},
[10761] = {"Encontre o desertor", {"Encontre Parshah nos Campos de Magma e fale com ele."}},
[10762] = {"Grão-Mestre Rumberto", {"Leve o Molde Pronto de Chave para Rumberto na Fortaleza da Honra."}},
[10763] = {"O pedido de Rumberto", {"Leve 4 Barras de Ferrovil, 2 montinhos de Pó Arcano e 4 Partículas de Fogo para Rumberto, na Fortaleza da Honra, que fica na Península Fogo do Inferno."}},
[10764] = {"Mais quente que o inferno", {"Destrua um Aníquilus na Península Fogo do Inferno e jogue o Molde não Aquecido de Chave nos destroços. Leve o Molde Queimado de Chave para Rumberto na Fortaleza da Honra."}},
[10765] = {"Quando mundos colidem...", {"Assassine a Chanceler Sangfronde e Corok, o Possante, e recupere as Ordens de Illidan."}},
[10766] = {"Ponto de Invasão: Cataclismo", {"Apresente-se a Pléxis no Ponto de Invasão: Cataclismo."}},
[10767] = {"Ponto de Invasão: Cataclismo", {"Apresente-se a Nakansi no Ponto de Invasão: Cataclismo."}},
[10768] = {"Tabardos dos Illidari", {"Recolha 10 Tabardos dos Illidari."}},
[10769] = {"Discórdia nas fileiras...", {"Mate 5 Colossos Enlouquecidos enquanto estiver disfarçado como um agente de Illidan e fale novamente com o Suserano Or'barokh da Aldeia Lua Negra, no Vale da Lua Negra. Ele também vai querer a Pistola Sinalizadora de Kor'kron de volta."}},
[10770] = {"Pequenas brasas", {"Musgárvore, o Anciente, quer que você mate 8 Diabretes Escaldantes e fale novamente com ele no Bosque Ruuan."}},
[10771] = {"Das cinzas", {"Musgárvore, o Anciente, quer que você vá até Skald, plante Sementes de Radiferro em três morros de Solo Vulcânico Fértil diferentes, e fale novamente com ele no Bosque Ruuan."}},
[10772] = {"O Caminho da Conquista", {"Viaje até o Caminho da Conquista no Vale da Lua Negra."}},
[10773] = {"A invasão do caminho", {"Mate 20 Soldados Eclipsion, 10 Fidalgos Eclipsion e 5 Filhos de Corok."}},
[10774] = {"Elfo sangrento + gigante = ???", {"Assassine a Chanceler Sangfronde e Corok, o Possante, e recupere as Ordens de Illidan."}},
[10775] = {"Tabardos dos Illidari", {"Recolha 10 Tabardos dos Illidari."}},
[10776] = {"Discórdia nas fileiras...", {"Mate 5 Colossos Enloquecidos enquanto estiver disfarçado como um agente de Illidan, e fale novamente com o Thane Yoregar na Fortaleza Martelo Feroz, no Vale da Lua Negra. Ele também vai querer a pistola sinalizadora de volta."}},
[10777] = {"Totem de Asghar", {"Traga o Totem de Guerra de Sketh'lon para Parshah nos Campos de Magma."}},
[10778] = {"O bastão de Lianthe", {"Obtenha a Chave de Lianthe com qualquer um dos elfos sangrentos na Ponta do Eclipse e use-a para abrir o Cofre de Lianthe e recuperar o Bastão de Lianthe. Entregue o bastão a Parshah, nos Campos de Magma."}},
[10780] = {"Penas de Sketh'lon", {"Junte 8 Penas de Sketh'lon e as leve até Parshah, nos Campos de Magma."}},
[10781] = {"Batalha da Vigília Carmesim", {"Enfrente as forças de Illidan, na Vigília Carmesim e sobreviva!","Procure A'dal em Shattrath, caso seja vitorioso."}},
[10782] = {"Imbuir o castão", {"Vá até o Altar da Danação e use o Castão Incompleto próximo a Gul'dan. Quando o castão estiver energizado, procure Parshah, nos Campos de Magma."}},
[10784] = {"Acabe com a Base dos Malho Sangrento", {"Mate 10 Espancadores Malho Sangrento e 5 Bruxos Malho Sangrento."}},
[10786] = {"Matança em Rocha'mok", {"Mate 5 Brutamontes Rocha'mok e 3 Xamãs Rocha'mok. Depois, fale novamente com Tor'chunk Duasgarras na Cidadela do Senhor do Trovão, que fica nas Montanhas da Lâmina Afiada."}},
[10788] = {"Convocação de Talionia", {"Fale com Talionia em Luaprata."}},
[10789] = {"Retorno a Silvério Hidalgo", {"Fale com Silvério Hidalgo na Cidade Baixa."}},
[10790] = {"Retorno a Gan'rul Olho de Sangue", {"Fale com Gan'rul Olho de Sangue em Orgrimmar."}},
[10791] = {"Bem-vindo, espírito do lobo", {"Use o Incenso Cerimonial perto das velas na cabana de Malkaz e observe a cerimônia. Quando a cerimônia terminar, fale novamente com Malkaz."}},
[10792] = {"Zeth'Gor tem que queimar!", {"Mate os Peões dos Olhos Sangrentos, tome as tochas deles, acenda-as na forja de Zeth'Gor e jogue as tochas acesas nas Construções de Zeth'Gor."}},
[10793] = {"O diário de Val'zareq: presságios de guerra", {"Encontre Marcus Aurálion, Alto-general dos Sha'tar, na Vigília Carmesim.","Procure por ele dentro de uma prisão cristalina."}},
[10794] = {"Ladinos da Mão despedaçada", {"Fale com Shenthul em Orgrimmar."}},
[10795] = {"Um encontro com Dorgok", {"Mate Dorgok na Base dos Malho Sangrento."}},
[10796] = {"Acabe com a Base dos Malho Sangrento!", {"Mate 10 Espancadores Malho Sangrento e 5 Bruxos Malho Sangrento."}},
[10797] = {"Distintivo dos gronns", {"Entregue o Distintivo de Gorgrom ao Comandante Haephus Muro-de-pedra no Arvoredo Eterno, que fica nas Montanhas da Lâmina Afiada."}},
[10798] = {"Faça uma visita ao barão", {"Fale com Barão Zibecrina na torre do Círculo Sangrento, que fica nas Montanhas da Lâmina Afiada."}},
[10799] = {"Para a Ravina Trêmula", {"Colete 7 Glândulas Peçonhentas de Estoura-crosta."}},
[10802] = {"Gorgrom, o Devorador de Dragão", {"Colete 3 Totens Medonhos dos ogros de Rocha'mok. Depois use a armadilha do Zibecrina no altar de Gorgrom para atrair e matar Gorgrom, o Devorador de Dragão.","Quando ele morrer, coloque 3 Totens Medonhos perto do cadáver."}},
[10803] = {"Matança em Rocha'mok", {"Mate 5 Brutamontes Rocha'mok e 3 Xamãs Rocha'mok."}},
[10804] = {"Bondade", {"Mordenai, nos Campos da Asa Etérea, que fica no Vale da Lua Negra, quer que você alimente 8 Dracos Asa Etérea Adultos."}},
[10805] = {"Massacre no Covil de Gruul", {"Mate 10 Seguranças Giralança, 5 Magos de Batalha Giralança, 5 Assoladores Giralança e o líder deles, Fingrom."}},
[10807] = {"Os Degradados Grislíngua", {"Varen, o Recuperador, quer que você vá até as Ruínas de Baa'ri e mate 3 Tratadores Grislíngua, 6 Guerreiros Grislíngua e 4 Xamãs Grislíngua.","Completar missões para os Áugures reduzirá sua reputação junto aos Aldor."}},
[10808] = {"Frustrar o Conclave Negro", {"Mate os Ritualistas do Conclave Negro e use o Cajado de Parshah para encerrar o ritual de evocação do Conclave Negro. Então, fale novamente com Parshah nos Campos de Magma."}},
[10809] = {"Procura-se: Mestre de Worgs Esmaago", {"Mate o Mestre de Worgs Esmaago e leve sua cabeça para a Capitão Uivonegro no Acampamento Quebra-espinha."}},
[10810] = {"Máscara danificada", {"Entregue a Máscara Danificada a Anacletto Desastraz no Arvoredo Eterno, que fica nas Montanhas da Lâmina Afiada."}},
[10811] = {"Procure Neltarako", {"Procure Neltarako, patrono da Revoada Asa Etérea."}},
[10812] = {"Máscara misteriosa", {"Entregue a Máscara Misteriosa para o Lorde Selvagem Antelarion no Arvoredo Eterno, que fica nas Montanhas da Lâmina Afiada."}},
[10813] = {"Os olhos de Grillok", {"Use a Lasca de Zézzak para capturar um olho de Grillok, e se aproxime do caldeirão de Zézzak para extrai-lo. Depois, devolva-lhe a Lasca de Zézzak."}},
[10814] = {"A história de Neltarako", {"Fale com Neltarako e ouça a história dele."}},
[10816] = {"A reconquista do solo sagrado", {"O Vindicante Aluumen quer que você mate 8 Matadores Lua Negra, 8 Escolhidos Lua Negra e 4 Tecetrevas Lua Negra.","Completar missões para os Aldor reduzirá sua reputação junto aos Áugures."}},
[10817] = {"A grande retaliação", {"Varen, o Recuperador, quer que você mate 8 Matadores Lua Negra, 8 Escolhidos Lua Negra e 4 Tecetrevas Lua Negra.","Completar missões para os Áugures reduzirá sua reputação com os Aldor."}},
[10819] = {"Máscara de gás de devoto vil", {"Coloque a Máscara de Gás de Devoto Vil e use o Comunicador da Legião no Campo-forja: Fúria, nas Montanhas da Lâmina Afiada."}},
[10820] = {"Engana teu inimigo", {"Mate 4 Atendentes Forja da Perdição e 4 Engenheiros Forja da Perdição."}},
[10821] = {"Para o olho da rua!", {"Mate Malsinara."}},
[10822] = {"Sinete único dos Solfúria", {"Requisitos:"}},
[10823] = {"Mais sinetes dos Solfúria", {"Requisitos:"}},
[10824] = {"Sinetes dos Solfúria", {"A Maga de Batalha Vyara, no Sacrário Estelar, quer que você lhe traga 10 Sinetes dos Solfúria.","Completar missões para os Áugures reduzirá sua reputação com os Aldor."}},
[10825] = {"Os orbes da verdade", {"Entregue o orbe de Grishna ao Guardião das Árvores Chawn, no Arvoredo Eterno, que fica nas Montanhas da Lâmina Afiada."}},
[10826] = {"Marcas de Sargeras", {"Traga 10 Marcas de Sargeras para o Precursor Saronen no Vale da Lua Negra.","Completar missões para os Aldor reduzirá sua reputação junto aos Áugures."}},
