[11611] = {"Levados pelo Flagelo", {"O Encarregado Mortuus, na Fortaleza Brado Guerreiro, quer que você liberte 5 Peões do Brado Guerreiro."}},
[11612] = {"Retomada da pedreira", {"Etaruk, acampado perto da entrada da Pedreira da Rocha Fria, quer que você mate 12 Caçadores de Tesouro de Berília."}},
[11613] = {"O juramento de Karuk", {"Karuk quer que você mate 6 Saqueadores Skadir e 5 Barqueiros Skadir."}},
[11614] = {"Verdades não ditas", {"Encontre o Assombrante Luther na Pedreira do Megalito."}},
[11615] = {"Segredos de Nerub'ar", {"Leve o Diário do Luther para o Assombrante Barthus, na Fortaleza Brado Guerreiro."}},
[11616] = {"Mensagem para Grito Infernal", {"Leve o Bilhete do Barthus para Garrosh Grito Infernal, na Fortaleza Brado Guerreiro."}},
[11617] = {"Dificultando a fuga", {"Etaruk, fora da Pedreira da Rocha Fria, quer que você destrua a Plataforma Norte, a Plataforma Leste e a Plataforma Oeste."}},
[11618] = {"Reforços vindouros...", {"Procure o Assombrante Ickoris, no Acampamento das Fazendas Brado Guerreiro a oeste da Fortaleza Brado Guerreiro."}},
[11619] = {"Gamel, o Cruel", {"Karuk quer que você derrote Gamel, o Cruel, no Areal Quebramar."}},
[11620] = {"As palavras de um pai", {"Procure por Veehja no Altar das Escamas, no Areal Quebramar."}},
[11621] = {"A tabuleta de Leviroth", {"Fale com Karuk sobre a informação que você encontrou na tabuleta."}},
[11623] = {"Uma visita ao curador", {"Etaruk, nos limites da Pedreira da Rocha Fria, quer que você mate o Curador Insivius."}},
[11624] = {"Os céus saberão", {"Fale com Imperean nas Ruínas de Eldra'nath."}},
[11625] = {"O tridente de Naz'jan", {"Veehja, no Quebramar, quer que você vá até a extremidade nordeste das Ruínas Quebramar. Uma vez lá, mate Ragnar Drakkarlund e tome o Tridente de Naz'jan dele."}},
[11626] = {"O emissário", {"Encontre Leviroth nas águas abaixo do iceberg que flutua na extremidade norte das Ruínas Quebramar. Use o Tridente de Naz'jan para matá-lo e, depois, fale com Karuk no norte do Areal Quebramar."}},
[11627] = {"Ponto de ebulição", {"Imperean quer que você lute contra Fervus e Umius até se renderem. Depois, fale com ela nas Ruínas de Eldra'nath."}},
[11628] = {"Mortalhas do Flagelo", {"O Chefe Vento de Inverno, na Aldeia Taunka'le, quer que você lhe traga 5 Pelagens de Mamute Flagelado."}},
[11629] = {"O retorno à porta-voz espiritual", {"Entregue o Primevo de Imperean para a Porta-voz Espiritual Rosnapresas, no Posto Avançado Bor'gorok."}},
[11630] = {"A terra maculada", {"Leve 8 torrões de Terra Flagelada para o Chefe Vento de Inverno na Aldeia Taunka'le."}},
[11631] = {"Visão do ar", {"Use o Primevo de Imperean para realizar o ritual com o Totem de Rosnapresas no Posto Avançado Bor'gorok e, assim, obter a informação sobre o Clarividente Passo Duro.","Depois, fale com a Porta-voz Espiritual Rosnapresas."}},
[11632] = {"O que os ventos frios trazem...", {"Leve a Carapaça Endurecida de Ith'rix para o Lorde Supremo Saurfang na Fortaleza Brado Guerreiro."}},
[11633] = {"Infiltrando-se", {"O Chefe Vento de Inverno, na Aldeia Taunka'le, quer que você vista a Mortalha do Flagelo Imbuída dentro da Cidade-Templo de En'kilah e examine: a Torre Sangrenta, a Torre da Dor e a Torre da Decomposição."}},
[11634] = {"Mestre do Vento To'bor", {"Apresente-se ao Mestre do Vento To'bor na Fortaleza Brado Guerreiro."}},
[11635] = {"O Espírito do Clarividente Passo Duro", {"Fale com o Clarividente Passo Duro dentro de Magmute."}},
[11636] = {"Passeio de tapete mágico", {"Converse com Yanni, na Fortaleza Brado Guerreiro, para conseguir uma viagem segura para a costa. Fale com o Aniquilador Grek'lor quando chegar no Ancoradouro de Garrosh."}},
[11637] = {"Kaganishu", {"Recupere o Fetiche de Kaganishu e use-o nos Restos Mortais do Clarividente Passo Duro.","Você precisará entregar o fetiche ao Espírito do Clarividente Passo Duro dentro de Magmute."}},
[11638] = {"Devolva meus restos mortais", {"Entregue os Restos Mortais do Clarividente Passo Duro que se encontram abaixo de seu espírito para a Porta-voz Espiritual Rosnapresas no Posto Avançado Bor'gorok."}},
[11639] = {"Vingança contra Magmute", {"Mate 10 Xamãs de Magmute, 5 Colhedores de Magmute, 3 Esmagadores de Magmute e 3 Fêmeas de Magmutregar. Depois, fale com Ortrosh, no Posto Avançado Bor'gorok."}},
[11640] = {"Palavras de poder", {"Leve o Pergaminho do Sumo Sacerdote Naferset, o Pergaminho do Sumo Sacerdote Talet-Kha e o Pergaminho do Sumo Sacerdote Andorath para o Chefe Vento de Inverno, na Aldeia Taunka'le."}},
[11641] = {"Um ataque corajoso", {"Mate 15 Carniçais En'kilah e 5 Necromantes En'kilah e fale novamente com Durm Pelálgido na Aldeia Taunka'le."}},
[11642] = {"Tanques ainda não se consertam sozinhos", {"Fale com Mobu no Ancoradouro de Garrosh."}},
[11643] = {"O guéri-guéri hidráulico de Mobu", {"Mobu, na Costa do Eco da Tundra Boreana, quer que você recupere o Guéri-guéri Hidráulico."}},
[11644] = {"Placas de metal superfortes!", {"Mobu, na Costa do Eco da Tundra Boreana, quer que você consiga 10 Placas de Metal Superfortes."}},
[11645] = {"Nevolds sujos e fedorentos!", {"Recupere 10 caixas de Coisas da Abília e devolva-as para Abília Rodafrouxa, na Pista de Pouso do Biela."}},
[11646] = {"A inquisição boreana", {"Donathan ordenou que você ofereça ajuda ao Bibliotecário Normantis na torre dos magos, no Promontório Âmbar."}},
[11647] = {"Neutralizando os caldeirões", {"Sábia Aeire, na Aldeia Taunka'le, quer que você use o totem para neutralizar o Caldeirão Leste, o Caldeirão Central e o Caldeirão Oeste na Cidade-templo de En'kilah."}},
[11648] = {"A arte da persuasão", {"O Bibliotecário Normantis, no Promontório Âmbar, quer que você use o Aguilhão Neural no Feiticeiro de Berília Aprisionado até que ele revele o paradeiro da Lady Evanor."}},
[11649] = {"Partículas dos enfurecidos", {"Obtenha 5 Partículas da Tempestade e entregue-as a Imperean nas Ruínas de Eldra'nath."}},
[11650] = {"Só mais umas coisinhas...", {"Pegue as Ferramentas de Abília, um Capacitor Sobrecarregado e um Punhado de Grãos do Rocanar. Entregue-os para Abília Rodafrouxa, na Pista de Pouso do Biela."}},
[11651] = {"Com a barriga no tanque", {"Fale com o Aniquilador Grek'lor na Costa do Eco."}},
[11652] = {"As Planícies de Nasam", {"Use o Tanque de Cerco da Horda para destruir 100 Soldados do Flagelo e resgatar 3 Soldados do Brado Guerreiro Feridos nas Planícies de Nasam. Descubra também a identidade do líder do ataque do Flagelo.","Apresente-se a Garrosh Grito Infernal na Fortaleza Brado Grerreiro, caso tenha sucesso."}},
[11653] = {"Ahh... Quem é o grandalhão agora?", {"Teste o Canhão Redutor Proto-Típico Ultra-Avançado da Abília em 5 Esmagadores Magmute ou Fêmeas de Magmutregar. Após terminado, fale com ela na Pista de Pouso do Biela."}},
[11654] = {"A torre sangrenta", {"Procure na Torre Sangrenta por mais informações sobre a Ampola de Sangue Fresco."}},
[11655] = {"Bruma adentro", {"Waltor de Pal'ea na Costa do Eco, na Tundra Boreana, quer que você mate os Kvaldir e recupere 8 Relíquias Morsanas."}},
[11656] = {"Queime os barcos!", {"Waltor de Pal'ea na Costa do Eco da Tundra Boreana, quer que você use a Tocha Morsana para queimar os barcos kvaldir em Pal'ea e no Ancoradouro de Garrosh. Destrua os seguintes barcos: o Boca da Serpente, o Kur Drakkar, o Martelo de Bor e o Bigorna de Bor."}},
[11657] = {"Piromalabarismo", {"Pegue 4 tochas em sequência. Em seguida, fale com o Mestre Engolidor de Fogo."}},
[11658] = {"Plano B", {"Pegue 10 Trajes de Brado Guerreiro e um Estandarte do Brado Guerreiro. Leve-os de volta para Abília Rodafrouxa, na Pista de Pouso do Biela."}},
[11659] = {"Estilhace as orbes!", {"Destrua 5 Globos de Sangue e fale com Rastreador da Neve Junek, na Aldeia Taunka'le."}},
[11660] = {"Trompa do Velho Marinheiro", {"Waltor de Pal'ea, na Costa do Eco da Tundra Boreana, quer que você traga a Trompa do Velho Marinheiro."}},
[11661] = {"Orabus, o Timoneiro", {"Waltor de Pal'ea, na Costa do Eco da Tundra Boreana, quer que você use a Trompa do Velho Marinheiro para evocar Orabus, o Timoneiro. Quando Orabus, o Timoneiro, aparecer, mate-o."}},
[11662] = {"Procure Karuk!", {"Procure Karuk no Areal Quebramar."}},
[11663] = {"Compartilhando informações", {"Normantis pediu para que você se apresente para o Bibliotecário Donathan no Promontório Âmbar."}},
[11664] = {"Fuga das brumas", {"Siga Mutu, a Jovem, para fora das brumas. Fale com o Ancião Mutu após salvar Mutu, a Jovem."}},
[11665] = {"Crocoliscos na cidade", {"Traga um Crocolisco Bebê para o Velho Barlo. Você encontrará o Velho Barlo pescando a nordeste de Shattrath, no Lago Silmyr."}},
[11666] = {"Ladrões de Isca", {"Traga um Plati-pontanegra para o Velho Barlo. Você encontrará o Velho Barlo pescando a nordeste de Shattrath, no Lago Silmyr."}},
[11667] = {"O peixe fugitivo", {"Pesque o Maior Muçum do Mundo e traga-o para o Velho Barlo. Você encontrará o Velho Barlo pescando a nordeste de Shattrath, no Lago Silmyr."}},
[11668] = {"Camarão é a mãe!", {"Traga 10 Camarões Gigantes de Água Doce para o Velho Barlo. Você encontrará o Velho Barlo pescando a nordeste de Shattrath, no Lago Silmyr."}},
[11669] = {"Filé de sangrevil", {"Traga um Pargo Sangrevil Monstruoso para o Velho Barlo. Você encontrará o Velho Barlo pescando a nordeste de Shattrath, no Lago Silmyr."}},
[11670] = {"Foram os orcs, eu juro!", {"Enquanto disfarçado de orc do Brado Guerreiro, finque o Estandarte do Brado Guerreiro no cadáver de Magmutregar. Depois, fale novamente com Abília Rodafrouxa, na Pista de Pouso do Biela."}},
[11671] = {"Corrida contra o tempo", {"Donathan, no Promontório Âmbar, lhe pediu para usar o Detonador de Escudos de Berília para destruir o escudo no Penhasco de Berília. Logo em seguida, derrote Salrand e pegue a chave que ela guarda."}},
[11672] = {"Dia do alistamento", {"Encontre-se com o General Arlos, dentro do quartel da Bastilha Valentia."}},
[11673] = {"Tirem-me daquiii!", {"Escolte Quim Mudavolt e apresente-se a Biela Velotroll, na Pista de Pouso do Biela."}},
[11674] = {"A Sábia Chapadalta está desaparecida", {"Encontre a Sábia Chapadalta perto das Tocas da Morte."}},
[11675] = {"Uma morte adequada", {"Destrua 10 Magnatauros Pestilentos e fale novamente com a Sábia Chapadalta, ao sul das Tocas da Morte."}},
[11676] = {"Liberdade misericordiosa", {"Assombrante Canarius no Acampamento das Fazendas Brado Guerreiro quer que você liberte 5 Prisioneiros do Flagelo."}},
[11677] = {"Acabe com a peste", {"A Sábia Chapadalta, ao sul das Tocas da Morte, pediu para que você use as Sementes Purificadoras de Chapadalta em um Caldeirão Pestilento das Tocas da Morte para neutralizá-lo."}},
[11678] = {"Encontre Chifre Peludo", {"Encontre o Pisaléguas Chifre Peludo, em Talramas."}},
[11679] = {"Reforja da chave", {"Donathan quer que você fale com Surristrasz, no Promontório Âmbar."}},
[11680] = {"Asas emprestadas", {"Dê a Chave de Salrand para o Mago-de-guerra Anzim quando estiver pronto para libertar Evanor."}},
[11681] = {"O resgate de Evanor", {"Anzim, no Promontório Âmbar, quer que você voe com os magos-de-guerra para libertar Lady Evanor no Penhasco de Berília. Depois, apresente-se a ela na torre dos magos, no Promontório Âmbar."}},
[11682] = {"Fala dragônica", {"Lady Evanor pediu para que você fale com Surristrasz, no Promontório Âmbar."}},
[11683] = {"Necrópole caída", {"Destrua 20 Flagelos de qualquer tipo em Talramas e nos Poços Purulentos. Depois, fale novamente com Sábia Chapadalta, ao sul das Tocas da Morte."}},
[11684] = {"Patrulhando as fossas", {"O Grande Pai Mahan, na Aldeia Taunka'le, quer que você use o Mapa dos Campos de Gêiser para marcar a localidade da Fossa Sul, da Fossa Noroeste e da Fossa Nordeste."}},
[11685] = {"O coração dos elementos", {"Traga 5 Corações de Elemental para a Doma-ventos Barah, na Aldeia Taunka'le."}},
[11686] = {"As fazendas Brado Guerreiro", {"O Assombrante Ickoris, no Acampamento das Fazendas Brado Guerreiro, quer que você investigue o Celeiro Brado Guerreiro, a Fazenda dos Torp e o Matadouro Brado Guerreiro."}},
[11687] = {"O doutor e o lorde lich", {"Destrua o Doutor Bocafiada e o Lorde Lich Hibernus. Depois, fale novamente com a Sábia Chapadalta ao sul das Tocas da Morte."}},
[11688] = {"Malditos suínos imundos", {"O Fazendeiro Torp, no Acampamento das Fazendas Brado Guerreiro, quer que você mate 10 Suínos Vivos-mortos."}},
[11689] = {"Volte com as más notícias", {"Fale com a Grande Mãe Taiga, na Aldeia Taunka'le."}},
[11690] = {"Traga-os de volta vivos", {"O Fazendeiro Torp, no Acampamento das Fazendas Brado Guerreiro, quer que você resgate 8 Feras Kodo Infectadas."}},
[11691] = {"Evocar Ahune", {"Leve o Totem de Magma da Harmonia Telúrica até a Pedra de Gelo."}},
[11692] = {"Apresente-se à Tita", {"Otto Rodagiros pediu para que você encontre Tita Espetorque, ao sul das Tocas da Morte."}},
[11693] = {"Que maravilha... Magnatauros pestilentos!", {"Mate 10 Magnatauros Pestilentos e fale novamente com Tita Espetorque ao sul das Tocas da Morte."}},
[11694] = {"Algo se passa naquelas cavernas", {"Tita Espetorque, escondida ao sul das Tocas da Morte, pediu para que você use Pó de Inibição da Tita em um Caldeirão Pestilento das Tocas da Morte para neutralizá-lo."}},
[11695] = {"A Trompa da Fúria Elemental", {"Recupere a Metade Inferior da Trompa e a Metade Superior da Trompa do Chefe Gafamafo e do Chefe Mafagafo e leve-as para a Doma-ventos Barah, na Aldeia Taunka'le."}},
[11696] = {"Ahune está aqui!", {"Encontre a Luma Mãe dos Céus no Pátio dos Escravos."}},
[11697] = {"Raios! Tinqui foi pra necrópole!", {"Encontre Tinqui Apitocurto dentro de Talramas."}},
[11698] = {"Aproveita e elimina o Flagelo", {"Destrua 20 Flagelos de qualquer tipo em Talramas e nos Poços Purulentos. Depois, fale novamente com Tita Espetorque ao sul das Tocas da Morte."}},
[11699] = {"Tô presa nessa maldita jaula... mas não por muito tempo!", {"Consiga um Cristal de Núcleo-Motor, um Giroscópio Mágico e uma Pedra do Flagelo Timoneira. Entregue-os a Tinqui Apitocurto em Talramas."}},
[11700] = {"Conte a Tita", {"Fale com Tita Espetorque ao sul das Tocas da Morte."}},
[11701] = {"De volta à pista de pouso", {"Fale com Biela Velotroll na Pista de Pouso do Biela, que fica na Tundra Boreana."}},
[11702] = {"O rei Mrgl-Mrgl", {"Fale com Rei Mrgl-Mrgl no Retiro da Falésia Invernal."}},
[11703] = {"Fale com Getry", {"Encontre o Assombrante Getry nas Fazendas Brado Guerreiro."}},
[11704] = {"O rei Mrgl-Mrgl", {"Fale com Rei Mrgl-Mrgl no Retiro da Falésia Invernal."}},
[11705] = {"Esforços em vão", {"Ajude Getry a derrotar Varidus, o Esfolador, nas Fazendas Brado Guerreiro.","Apresente-se a Garrosh Grito Infernal caso seja bem-sucedido."}},
[11706] = {"O desmoronamento", {"A Doma-ventos Barah, na Aldeia Taunka'le, quer que você faça com que as fossas nerubianas desmoronem usando a Trompa da Fúria Elemental e derrote o Lorde Kryxix."}},
[11707] = {"Chamada de emergência", {"Fale com Biela Velotroll na Pista de Pouso do Biela."}},
[11708] = {"Os gnomecânicos", {"Ouça a história de Biela Velotroll e depois encontre Jinqui Porcassolta, na Pista de Pouso do Biela."}},
[11709] = {"Trabalho de Nork Frenessangue", {"Fale com o Carcereiro Nork Frenessangue na Fortaleza Brado Guerreiro, que fica na Tundra Boreana."}},
[11710] = {"Qual o problema com o transubstanciador?", {"Consiga 15 Peças Sobressalentes do Biela e entregue-as para Otto Rodagiros, na Pista de Pouso do Biela."}},
[11711] = {"Covarde em domicílio... Entrega em 30 minutos ou seu dinheiro de volta", {"Leve o Desertor da Aliança para a encruzilhada leste da Fortaleza Brado Guerreiro e dispare a Pistola Sinalizadora do Brado Guerreiro para sinalizar à Aliança.","Apresente-se ao Batedor Tungok assim que tiver entregado o Desertor da Aliança ao seu destino."}},
[11712] = {"Retransformar", {"Jinqui Porcassolta na Pista de Pouso do Biela pediu para que você use a Injeção de Maldição da Transformação em 6 Gnomecânicos do Biela."}},
[11713] = {"Patrulhando as fossas", {"Abner Torqueimado, na Pista de Pouso do Biela, quer que você use o Mapa dos Campos de Gêiser para marcar os locais da Fossa Sul, da Fossa Nordeste e da Fossa Noroeste."}},
[11714] = {"Exterminação perniciosa", {"O Batedor Tungok, nas Planícies Sanguesporo da Tundra Boreana, quer que você mate 8 Ceifadores Sanguesporo, 5 Incendiários Sanguesporo e 2 Assadores Sanguesporo."}},
[11715] = {"Abastecimento do projeto", {"Abner Torqueimado na Pista de Pouso do Biela quer que você consiga 8 Barris de Óleo usando a Coletora Portátil de Petróleo."}},
[11716] = {"O magnífico sanguesporo", {"A Maga Sangrenta Laurith, nas Planícies Sanguesporo da Tundra Boreana, quer que você consiga 10 Carpelos de Sanguesporo."}},
[11717] = {"Pólen da fonte", {"A Maga Sangrenta Laurith, nas Planícies Sanguesporo da Tundra Boreana, quer que você consiga 5 amostras de Pólen de Mariposa Sanguesporo."}},
[11718] = {"Robô na pele de um mamute", {"Abner Torqueimado, na Pista de Pouso do Biela, quer que você traga 6 Pelegos Grossos de Mamute."}},
[11719] = {"Uma cobaia adequada", {"A Maga Sangrenta Laurith, nas Planícies Sanguesporo da Tundra Boreana, pediu-lhe para usar a Flor de Sanguesporo Polinizada e deixá-la observar os resultados."}},
[11720] = {"A invasão de Gamute", {"Fale com o Primevo Chifre Poderoso nas Planícies Sanguesporo da Tundra Boreana."}},
[11721] = {"Gamutra, o Atormentador", {"Mate Gamutra e traga a Cabeça de Gamutra para o Primevo Chifre Poderoso nas Planícies Sanguesporo da Tundra Boreana."}},
[11722] = {"Troféus de Gamute", {"Leve a Cabeça de Gamutra para Garrosh Grito Infernal na Fortaleza Brado Guerreiro."}},
[11723] = {"Acione a Treme-treme!", {"Abner Torqueimado, na Pista de Pouso do Biela, quer que você use a Treme-treme 5000 para desmoronar os túneis nerubianos e derrotar o Lorde Kryxix."}},
[11724] = {"Omelete de mariposa tamanho família?", {"Leve o Ovo Reluzente Gigante para a Maga Sangrenta Laurith nas Planícies Sanguesporo da Tundra Boreana."}},
[11725] = {"Ache o piloto Fuzuê", {"Encontre Renê Fuzuê nos Campos de Gêiser."}},
[11726] = {"Um pouquinho de tempero", {"Nos Lagos Escaldantes, Renê Fuzuê quer que você consiga 4 Bolsas de Temperos de Gorloc."}},
[11727] = {"Tempos de heróis", {"Apresente-se ao Sargento Martelmonte, na Bastilha Valentia."}},
[11728] = {"Cocodolobo", {"Renê Fuzuê nos Lagos Escaldantes quer que você lhe traga 8 pedaços do Microfilme."}},
[11729] = {"A chave de fenda ultrassônica", {"Entregue a Chave de Fenda Ultrassônica para Abília Rodafrouxa, na Pista de Pouso do Biela."}},
[11730] = {"Patrão e empregado", {"Use a Chave de Fenda Ultrassônica para reprogramar 15 robôs e então fale novamente com Abília Rodafrouxa na Pista de Pouso do Biela.","Não esqueça de saquear os robôs mortos antes de reprogramá-los."}},
[11731] = {"Arremesso de tochas", {"Vença o jogo de arremessar tochas. Depois, fale com o Mestre Engolidor de Fogo."}},
[11732] = {"Profane o fogo!", nil},
[11733] = {"Travessia da fissura", {"Surristrasz quer que você viaje para Gelarra. Uma vez lá, fale com o Arquimago Berinand."}},
