[12465] = {"O diário de Barbapilha", {"Recupere a Página 4, a Página 5, a Página 6 e a Página 7 do Diário de Barbapilha e entregue-as para Amgo Cash no Mausoléu Invergarde, no Ermo das Serpes."}},
[12466] = {"À procura de Nevascal: Front da 7ª Legião", {"Apresente-se ao Comandante de Legião Tyralion, no Front da 7ª Legião, no Ermo das Serpes."}},
[12467] = {"À procura de Nevascal: Filactério de Thel'zan", {"Recupere o Filactério de Thel'zan de Nevascal e entregue-o para o Alto-comandante Haraldo Serpecida na Bastilha Invergarde, no Ermo das Serpes."}},
