[12520] = {"Maestria dos rinocerontes: o teste", {"Mate 15 Rinocerontes Estilhastes e fale novamente com Rosarães Guima, no Acampamento do Rosarães."}},
[12521] = {"Onde diabos está Rosarães Guima?", {"Fale com o Arquimago Pentarus quando você estiver pronto para partir para a Bacia Sholazar. Ao chegar lá, fale com Múncio Tirambaço no Mangue Verdejante da Bacia Sholazar."}},
[12522] = {"Precisa de um motor? Pega aí!", {"Veslex Chaveloz, no Acampamento do Rosarães, quer que você lhe traga o Motor de Máquina Voadora."}},
[12523] = {"Tem uma peça? Me dê uma!", {"Traga 7 Peças Sobressalentes da Empreendimentos S.A. para Veslex Chaveloz no Acampamento do Rosarães."}},
[12524] = {"Acidente da Empreendimentos S.A.", {"Debaar, no Acampamento do Rosarães, quer que você mate 15 membros da Empreendimentos S.A.. Você pode matar tanto Rufiões quanto Escavadores da Empreendimentos S.A."}},
[12525] = {"Tira aquele sorriso da cara dele", {"Mate o Encarregado Vigarício e o Bolofo e, em seguida, fale novamente com Debaar no Acampamento do Rosarães."}},
[12526] = {"Maestria dos rinocerontes: a caçada", {"Fale com o Oráculo Soo-rahm, nos Ossos de Nozronn."}},
[12527] = {"Tocaieiros glutões", {"Entregue 5 Cristais de Basilisco ao Alquimista Bacamarte no santuário, em Heb'Valok."}},
[12528] = {"Cooperando", {"Apresente-se ao Sumo Xamã Rakjak, no Morro dos Feralma, e diga que você é o novo \"escravo\"."}},
[12529] = {"O escravo do caçador de gorilas", {"Mate 8 Colhedores Falanjões e 6 Agressores Falanjões para o Sumo Xamã Rakjak, no Morro dos Feralma.","Se perder os Grilhões de Goregek, fale com o Ancião Harkek, no Morro dos Feralma."}},
[12530] = {"Atormentando os falanginhos", {"Goregek te deu um Cutucador de Falanginhos. Use-o para cutucar os Falanginhos e, assim, chamar a atenção da Matriarca dos Falanjões, tirando-a do esconderijo. Mate a Matriarca dos Falanjões e informe o Sumo Xamã Rakjak sobre a morte dela. Você pode encontrar Rakjak no Morro dos Feralma.","Se perder os Grilhões de Goregek, fale com o Ancião Harkek no Morro dos Feralma."}},
[12531] = {"A ameaça subterrânea", {"Encontre Serfex sob as areias do rio ao sul, mate-o e leve as garras dele para o Sumo Xamã Rakjak, no Morro dos Feralma.","Se perder o Giz Usado de Dajik, fale com o Ancião Harkek no Morro dos Feralma."}},
[12532] = {"A fuga das galinhas!", {"Capture 12 Galinhas Desgarradas para o Ancião Harkek.","Se perder o Giz Usado de Dajik, fale com o Ancião Harkek, no Morro dos Feralma."}},
[12533] = {"O aprendiz do caçador de vespas", {"Mate 6 Vespas Safiras e 9 Zangões Safiras para o Sumo Xamã Rakjak, no Morro dos Feralma.","Se perder o Giz Usado de Dajik, fale com o Ancião Harkek no Morro dos Feralma."}},
[12534] = {"A rainha safira", {"Traga o Aguilhão da Rainha Safira para o Sumo Xamã Rakjak, no Morro dos Feralma.","Se perder o Giz Usado de Dajik, fale com o Ancião Harkek no Morro dos Feralma."}},
[12535] = {"Preparando os danos", {"O Sumo Xamã Rakjak, no Morro dos Feralma, quer que você colete 8 Veios de Cristal do Pilar Beira-céu."}},
[12536] = {"Andar a crocolisco", {"Monte no crocolisco capturado e traga-o para o Refúgio Brumurmúria. A seguir, fale com Zepik para obter mais instruções.","Se perder a Trompa de Caça de Zepik, fale com o Ancião Harkek, no Morro dos Feralma."}},
[12537] = {"O raio cai duas vezes no mesmo lugar", {"Zepik quer que você esmague os Veios de Cristal do Pilar Beira-céu em frente ao santuário do tempo de Brumurmúria.","Se perder a Trompa de Caça de Zepik, fale com o Ancião Harkek, no Morro dos Feralma."}},
[12538] = {"A névoa não está escutando", {"Zepik quer que você mate 12 Gorlocs Brumurmúria.","Se perder a Trompa de Caça de Zepik, fale com o Ancião Harkek, no Morro dos Feralma."}},
