[12602] = {"The Alchemist's Apprentice", {"Alchemist Finklestein at Heb'valok wants you to assist him in the creation of a truth serum.","When you are ready to begin, you are to speak to him and follow his instructions."}},
[12603] = {"Afiando as garras", {"Mate 6 Dracos Primordiais e fale com Dorian, o Caçador de Dracos, no Posto Avançado de Dorian."}},
[12605] = {"Armando a isca", {"Traga 6 Filhotes Primordiais para Dorian, o Caçador de Dracos, no Posto Avançado de Dorian."}},
[12606] = {"Encasulados!", {"O Capitão Ruperto quer que você abra os Casulos Nerubianos, em Drak'Sotra, e fale novamente com ele após libertar 3 Soldados Rasos Cativos."}},
[12607] = {"Domando o mamute", {"Traga um Mamute Demolifante para Quebragulho no Posto Avançado de Dorian."}},
[12608] = {"Incursão dos sectários", {"Fale com o Avatar de Freya nas Cachoeiras de Voz-da-chuva."}},
[12609] = {"Abastecendo as prateleiras", {"O Capitão Arnath, em Heb'Valok, quer que você colete 7 porções de Linfa de Aranha Fresca das Viúvas-negras Rastejantes em Zul'Drak."}},
[12610] = {"Corte as asinhas", {"O Capitão Arnath, em Heb'Valok, pediu para você coletar 7 Asas de Morcego Imaculadas dos Morcegos de Zul'Drak."}},
[12611] = {"Devolver com poder sete vezes maior", {"O Avatar de Freya quer que você derrote Thalgran, o Arauto da Peste, usando a Proteção de Freya para refletir as Setas da Morte sobre ele com um poder sete vezes maior."}},
[12612] = {"O pilar caído", {"Vá ao Pilar Sangue da Vida e procure pistas de sua destruição."}},
[12613] = {"Energizando o pórtico: o mirante dos criadores", {"O Avatar de Freya quer que você vá ao Mirante dos Criadores, a leste da Bacia Sholazar, e use o Interruptor Teta."}},
[12614] = {"Agressão pós-parto", {"Mate a Prolemadre Slivina e fale novamente com Dorian, o Caçador de Dracos, no Posto Avançado de Dorian."}},
[12615] = {"A bênção de Zim'Torga", {"O Mandingueiro Khufu, na Fortaleza Argêntea, quer que você leve 10 Oferendas Drakkari à estátua de Zim'Torga."}},
[12616] = {"Tenris e a câmara secreta", {"O Emissário da Aurora Argêntea quer que você investigue a câmara do Príncipe Tenris Sanguebreu no Alojamento dos Empregados de Karazhan."}},
[12617] = {"Exterminando os invasores", {"O Avatar de Freya, nas Cachoeiras de Voz-da-chuva, quer que você vá ao Posto Avançado Mão da Morte, a noroeste, e mate 8 Sectários Infiltradores."}},
[12618] = {"Bênção de Zim'Torga", {"Requisitos:"}},
[12619] = {"A lâmina rúnica brasonada", {"Vasculhe cavaletes de armas na Fortaleza de Ébano para encontrar uma Espada Gasta pela Batalha. Em seguida, use-a em uma forja rúnica para moldar uma Espada de Lâmina Rúnica.","Leve a Espada de Lâmina Rúnica ao instrutor Razúvio na Fortaleza de Ébano."}},
[12620] = {"A fúria da guardiã da vida", {"O Avatar de Freya, nas Cachoeiras de Voz-da-chuva, quer que você use a Trompa de Freya no topo do Pilar Rutilante."}},
[12621] = {"O pacto de Freya", {"Escute o que o Avatar de Freya tem a dizer."}},
[12622] = {"Líderes de Jin'Alai", {"O Escalpelador Ahunae, em Zim'torga, pediu para você recuperar o Tesouro de Kutube'sa, o Tesouro de Gawanil e o Tesouro de Chalo, o Louco."}},
[12623] = {"Para o Mandingueiro", {"Fale com o Mandingueiro Khufu em Zim'Torga."}},
[12624] = {"Pode estar em qualquer lugar!", {"Procure um Anel de Noivado Dourado nas várias criaturas da Bacia Sholazar."}},
[12625] = {"Dominion Over Acherus", {"Report to Master Siegesmith Corvus on the garrison floor of Ebon Hold."}},
[12627] = {"Abrindo caminho em Jin'Alai", {"O Mandingueiro Khufu, em Zim'Torga, pediu para você perturbar o Caldeirão Azul, o Caldeirão Verde, o Caldeirão Roxo e o Caldeirão Vermelho."}},
[12628] = {"Falar com Har'koa", {"Fale com Har'koa no Altar de Har'koa."}},
[12629] = {"Você pode correr, mas não pode se esconder", {"Stefan, no Posto Ébano, quer que você vá ao Relicário da Agonia coletar 5 Tripas de Abominação Pútridas e 5 Babas Babosas de Carniçal."}},
[12630] = {"Chutando Nabunda", {"Stefan, no Posto Ébano, quer que você use o Nabunda para coletar 10 amostras de cabelo dos Trolls Definhados.","Se você perder Nabunda de vista, fale novamente com Stefan para encontrá-lo."}},
[12631] = {"Uma espécie de convite...", {"Leve a Gargantilha do Vivo-morto a Stefan no Posto Ébano."}},
[12632] = {"Meus filhos acima de tudo", {"Har'koa pediu que você use o bigode nos cadáveres dos filhos dela para libertá-los e ressuscitá-los. Fale com ela novamente no Altar de Har'koa quando terminar."}},
[12633] = {"O chamado das trevas", {"Leve a Gargantilha Repelente a Stefan no Posto Ébano."}},
[12634] = {"Alguns fazem limonada, outros fazem caipirinha", {"Traga 1 Laranja, 2 Cachos de Bananas e 1 Mamão para Canabraba Cervaforte, no Acampamento do Rosarães."}},
[12635] = {"Relíquias da Deusa dos Leopardos da Neve", {"O Cronista To'kini, em Zim'Torga, quer que você recupere 10 Relíquias Har'koanas."}},
[12636] = {"O olho de Áquerus", {"Apresente-se ao Lich Rei na Fortaleza de Ébano."}},
[12637] = {"Por um triz", {"Stefan, no Posto Ébano, quer que você mostre a Gargantilha do Vivo-morto a Datura Sanguerrosa."}},
[12638] = {"Decisão difícil", {"Stefan, no Posto Ébano, quer que você mostre a Gargantilha Repelente a Datura Sanguerrosa."}},
[12639] = {"Terra Congelada", {"A Domadora de Elementos Dagoda, em Zim'Torga, quer que você colete 7 Essências da Terra Congelada."}},
[12640] = {"Selando as fendas", {"O Escalpelador Ahunae, em Zim'Torga, quer que você sele 7 Fendas Elementais."}},
[12641] = {"A morte vem do alto", {"O Lich Rei ordena que você use o Olho de Áquerus para analisar o Castelo Escarlate, a Forja de Nova Avalon, a Prefeitura de Nova Avalon e a Capela da Chama Carmesim.","Controle o Olho de Áquerus usando o Mecanismo de Controle do Olho de Áquerus."}},
[12642] = {"Espírito de Rhunok", {"Fale com o Espírito de Rhunok no Altar de Rhunok."}},
[12643] = {"Esperança", {"Stefan, no Posto Ébano, quer que você vá ao Relicário da Agonia coletar 5 Tripas de Abominação Pútridas e 5 Babas Babosas de Carniçal."}},
[12644] = {"Alambicando o balde", {"Conclua o processo de destilação com êxito e leve o Ponche da Selva do Cervaforte pronto para Canabraba Cervaforte no Acampamento do Rosarães."}},
[12645] = {"A prova dos três", {"Ofereça uma amostra do Ponche da Selva do Cervaforte para Rosarães Guima, Hadrius Harlowe e Tamara Rodafrouxa. Em seguida, fale novamente com Canabraba Cervaforte, no Acampamento do Rosarães."}},
[12646] = {"Meu profeta, meu inimigo", {"O Espírito de Rhunok, no Altar de Rhunok, pediu que você recupere o Mojo do Deus Urso Ártico."}},
[12647] = {"Um fim para o sofrimento", {"O Espírito de Rhunok pediu para você usar o Incenso do Atormentador para despertar o corpo dele. Depois mate Rhunok com o poder que o espírito dele lhe deu.","Se, por algum motivo, você perder a aura do Mojo de Rhunok, basta retornar ao Espírito de Rhunok e ele lhe dará uma nova aura."}},
[12648] = {"Vestido a rigor", {"Stefan, no Posto Ébano, quer que você use a Gargantilha Enfeitiçada para ativar o disfarce de Flagelo.","Enquanto você estiver disfarçado de membro do Flagelo, compre o Plasma Amargo vendido por Cartilaginoso.","Se você perder a Gargantilha Enfeitiçada, fale com Stefan, no Posto Ébano, para obter outra."}},
[12649] = {"É hora do show!", {"Stefan, no Posto Ébano, quer que você use a Gargantilha Enfeitiçada para ativar o disfarce do Flagelo.","Enquanto você estiver disfarçado de membro do Flagelo, compre o Plasma Amargo vendido por Cartilaginoso.","Se você perder a Gargantilha Enfeitiçada, fale com Stefan, no Posto Ébano, para obter outra."}},
[12650] = {"Saqueando o próprio povo", {"O Cronista To'kini, em Zim'Torga, quer que você recupere 7 Artefatos Rhunokianos."}},
[12651] = {"Giroporto do Lago", {"Fale com Tamara Rodafrouxa no Giroporto do Lago."}},
[12652] = {"Comida pros \"carniçaus\"", {"Cartilaginoso, no Relicário da Agonia, quer que você use a Tigela de Bucho com Miolo perto de 10 Carniçais em Decomposição."}},
[12653] = {"De volta a Har'koa", {"Fale com Har'koa no Altar de Har'koa."}},
[12654] = {"Caçadora nas horas vagas", {"Leve os Restos Mortais do Suassuno para Tamara Rodafrouxa, no Giroporto do Lago."}},
[12655] = {"A bênção de Zim'Rhuk", {"O Mandingueiro Khufu, na Fortaleza Argêntea, quer que você leve 10 Oferendas Drakkari à estátua de Zim'Rhuk."}},
[12656] = {"Bênção de Zim'Rhuk", {"Requisitos:"}},
[12657] = {"O poder do Flagelo", {"O Lich Rei quer que você entregue as ordens para o grão-lorde Dárion Mograine na Fortaleza de Ébano.","Use o transportador iluminado atrás do Lich Rei para chegar ao grão-lorde Dárion Mograine, no Salão de Comando."}},
[12658] = {"Minha mascote é um galvo", {"Leve 7 Ovos de Galvo para Quebragulho no Posto Avançado de Dorian."}},
[12659] = {"Escalpos!", {"O Escalpelador Ahunae, em Zim'Torga, quer quer você use a Faca de Ahunae para cortar o escalpo de 10 trolls Heb'Drakkar.","O escalpelador o lembra que você deve saqueá-los primeiro."}},
[12660] = {"Armas da destruição", {"O Avatar de Freya quer que você vá ao Posto Avançado Mão da Morte, a noroeste, e destrua 4 Explosivos Instáveis."}},
[12661] = {"Infiltrando-se em Voltarus", {"Stefan quer que você use a Gargantilha Enfeitiçada para entrar em Voltarus e fazer o que o líder do Flagelo mandar.","Se você perder a Gargantilha Enfeitiçada, fale com Stefan, no Posto Ébano, para obter outra."}},
[12662] = {"Derrube Heb'Jin", {"A Domadora de Elementos Dagoda, em Zim'Torga, quer que você mate Heb'Jin."}},
[12663] = {"Reencontro", {"Drakuru, em Voltarus, quer que você fale com o Bolssang e faça um tour por Zul'Drak."}},
[12664] = {"Horizonte sombrio", {"Drakuru, em Voltarus, quer que você fale com o Bolssang e faça um tour por Zul'Drak."}},
[12665] = {"Sinto um distúrbio", {"Har'koa pediu para você descobrir o que aconteceu com Quetz'lun. Fale com ela para cavalgar sorrateiramente em um dos filhos dela até o Altar de Quetz'lun.","Depois disso, fale com ela novamente no Altar de Har'koa."}},
[12666] = {"Preparativos para o submundo", {"Har'koa quer que você colete 8 Adornos Sagrados. Entregue-os a ela no Altar de Har'koa."}},
[12667] = {"Encontre a deusa serpente do vento", {"Procure o Espírito de Quetz'lun no Altar de Quetz'lun."}},
[12668] = {"Base para a vingança", {"O Espírito de Quetz'lun lhe conferiu a tarefa de exterminar 12 trolls, Adoradores de Quetz'luz ou Berserkers Tocados pela Serpente, perto das Fontes de Almas.","Fale novamente com ela no Altar de Quetz'lun somente após cumprir a tarefa."}},
[12669] = {"Tudo péssimo até agora", {"Stefan, no Posto Ébano, quer que você fale novamente com Drakuru e cumpra quaisquer tarefas que ele ordenar.","Enquanto estiver no Relicário da Dor, use o Aditivo de Diluição em 5 Caldeirões de Praga.","Se você perder a Gargantilha Enfeitiçada, fale com Stefan, no Posto Ébano, para obter outra."}},
[12670] = {"Colheita escarlate", {"Apresente-se ao príncipe Valanar na Vanguarda da Morte."}},
[12671] = {"Voo de reconhecimento", {"Ajude o Piloto Vic a completar o Voo de Reconhecimento."}},
[12672] = {"Preparando o terreno", {"O Espírito de Quetz'lun exige que você colete 10 Fragmentos de Poder do Submundo. Fale com ela novamente no Altar de Quetz'lun somente após terminar."}},
[12673] = {"Agora é com você", {"O Suserano Drakuru, em Voltarus, quer que você use o Cetro de Sugestão nos Geists da Praga e depois mande-os coletar 7 Cristais da Praga."}},
[12674] = {"O inferno treme de fúria", {"O Espírito de Quetz'lun, no Altar de Quetz'lun, quer que você use a Vara Mandingada de Quetz'lun no Alto-sacerdote Mu'funu, na Alta-sacerdotisa Tua-Tua e no Alto-sacerdote Hawinni. Mate-os depois disso."}},
[12675] = {"Mais uma coisa", {"Entregue os Fragmentos de Poder do Submundo a Har'koa e suplique a ela um pouco de seu espírito. Ela ainda está no Altar de Har'koa."}},
[12676] = {"Sabotagem", {"Stefan, no Posto Ébano, quer que você cumpra outra tarefa para Drakuru.","Além disso, use 5 Cargas Explosivas em 5 Carroções do Flagelo no Relicário da Dor.","Se você perder a Gargantilha Enfeitiçada, fale com Stefan, no Posto Ébano, para obter outra."}},
[12677] = {"Materiais perigosos", {"Stefan, no Posto Ébano, quer que você vá a Voltarus e complete quaisquer tarefas que Drakuru ordenar. Enquanto você estiver lá, roube 5 Cristais da Praga Colhidos.","Se você perder a Gargantilha Enfeitiçada, fale com Stefan, no Posto Ébano, para obter outra."}},
[12678] = {"Se o caos dirige, que o sofrimento segure as rédeas", {"O príncipe Valanar, na Vanguarda da Morte, ordena você massacrar 10 Cruzados Escarlates ou Camponeses da Cruzada e 10 Cidadãos da Vila do Amparo."}},
[12679] = {"Colheita maldita", {"Orithos, o Anuviador Celeste, da Vanguarda da Morte, quer que você recupere 15 Flechas de Saronita."}},
[12680] = {"O grande roubo de cavalos", {"Salanar, o Cavalgante, quer que você roube um cavalo dos Estábulos da Vila do Amparo e o entregue para ele na Vanguarda da Morte."}},
[12681] = {"Agente dos reagentes", {"Traga 5 Gadanhos Retorcidos de Galvo para Colvin Norrington, no Posto Avançado de Dorian."}},
[12683] = {"Cuspido e escarrado", {"Colvin Norrington, no Posto Avançado de Dorian, quer que você obtenha 5 Amostras de Escarro da Hidra e mate 5 Hidras Ondamaras."}},
[12684] = {"Sangue de um deus morto", {"Har'koa pediu para você coletar 7 porções do Sangue de Mam'toth. Entregue-os a ela no Altar de Har'koa."}},
[12685] = {"Aqui se faz, aqui se paga", {"Mate o Profeta Drenado de Quetz'lun e depois se apresente ao Mandingueiro Khufu em Zim'Torga."}},
[12686] = {"Tolerância zero", {"Drakuru, em Voltarus, quer que você vá para o Relicário da Dor e use o Cetro de Potencialização em um Serviçal de Drakuru. Depois use as habilidades dele para derrotar Darmuk."}},
[12687] = {"No Reino das Sombras", {"Salanar, o Cavalgante, na Vanguarda da Morte, desafiou você a matar o Cavalgante Negro de Áquerus e a roubar o Corcel da Morte de Áquerus dele.","Se você tiver sucesso, leve o Corcel da Morte de Áquerus para a Vanguarda da Morte e use o Chamado do Cavaleiro para evocar Salanar."}},
[12688] = {"A engenharia de um desastre", {"Ajude a Engenheira Halice a escapar da Escavação do Caloteiro e fale com Rosarães Guima, no Acampamento do Rosarães."}},
[12689] = {"A mão dos oráculos", nil},
[12690] = {"Lenha para a fogueira", {"Drakuru, em Voltarus, quer que você use o Cetro do Comando nas Abominações Inchadas. Depois, use as habilidades delas no Pontal Frio para matar 60 Quebra-crânios Drakkari e atrair 3 Chefes Drakkari.","Os chefes aparecerão depois que você matar um certo número de Quebra-crânios."}},
[12691] = {"O cofre envelhecido", {"Obtenha uma Grande Chave de Pedra com os Guardiões de Sholazar e traga-a para o Cofre Envelhecido no Mirante dos Criadores."}},
[12692] = {"O retorno do caçador de lich", {"Volte ao Morro dos Feralma e fale com o Ancião Harkek."}},
[12695] = {"O retorno do amistoso pelesseca", {"Retorne às Frondes Voz-da-chuva e fale com o Sumo Oráculo Soo-say."}},
[12696] = {"Inspeção aérea", {"A Professora Calvert quer que você pegue o voo para o Giroporto do Lago e fale com o Piloto Vic quando chegar lá."}},
[12697] = {"Gothik, o Ceifador", {"Fale com Gothik, o Ceifador, na Vanguarda da Morte."}},
[12698] = {"O aspirador de almas", {"Gothik, o Ceifador, na Vanguarda da Morte, deu a você a Dádiva do Ceifador para usar nos Mineradores da Cruzada. Crie 5 Carniçais Escarlates com o dispositivo e entregue os carniçais a Gothik."}},
[12699] = {"Um incidente embaraçoso", {"Procure as Chaves do Vic dentro do lago do Coração do Rio."}},
[12700] = {"Um golpe de oportunismo", {"Fale com o príncipe Valanar na Vanguarda da Morte."}},
[12701] = {"Massacre no Pontal da Luz", {"O príncipe Valanar, na Vanguarda da Morte, quer que você massacre 100 Defensores Escarlates."}},
[12702] = {"Festa das galinhas!", {"Capture 12 Galinhas Desgarradas para o Ancião Harkek, no Morro dos Feralma."}},
[12703] = {"O alvoroço de Kartak", {"Traga uma amostra de Sangue de Oráculo para o Altar de Kartak para evocar Kartak. Com a ajuda dos poderes de Kartak, mate 50 Gorlocs de Piscabrilha para o Xamã Jakjek, na Aldeia de Kartak."}},
[12704] = {"Acalmando a grande pedra da chuva", {"O Sumo Oráculo Soo-say, nas Frondes Voz-da-chuva, quer que você leve um companheiro para cavar e coletar 6 Tesouros Brilhantes.","Não deixe de falar com o Sumo Oráculo Soo-say, nas Frondes Voz-da-chuva, sobre garantir a ajuda de um dos seus companheiros anteriores."}},
[12705] = {"Vontade dos titãs", {"Tome um Cristal Maculado dos guaxinantes perto do Refúgio Piscabrilha e leve-o para a Grande Pedra de Relâmpagos, para chamar Soo-holu. Com a ajuda dos poderes de Soo-holu, mate 50 guaxinantes Feralma e fale com o Traztrovão Soo-met, perto da Grande Pedra de Relâmpagos."}},
[12706] = {"Vitória na Vanguarda da Morte", {"Entregue o Relatório do Príncipe Valanar para o Grão-lorde Dárion Mograine em Áquerus: a Fortaleza de Ébano."}},
[12707] = {"Revolta dos cabeludos", {"Escalpelador Ahunae, em Zim'Torga, quer que você use o Medalhão de Mam'toth para acalmar um Mamute Enfurecido e cavalgar nele. Depois, você deve pisotear 12 Discípulos de Mam'toth até a morte."}},
[12708] = {"Guerreiros da Carranca Encantados", {"A Domadora de Elementos, em Zim'Torga, pediu para você matar 12 Guerreiros da Carranca Encantados."}},
[12709] = {"Baús bagateados", {"O Cronista To'kini, em Zim'Torga, pediu para você se apoderar de 12 Baús da Fortaleza de Zol'Maz."}},
[12710] = {"Revelação", {"Drakuru, em Voltarus, quer que você use o teleportador para a câmara superior."}},
[12711] = {"Cartas abandonadas", nil},
[12712] = {"A chave do Senhor da Guerra Zol'Maz", {"Har'koa, em Zim'Torga, pediu para você se apoderar da Chave do senhor da guerra Zol'Maz."}},
[12713] = {"Traição", {"Stefan, no Posto Ébano, quer que você derrote Drakuru.","Se você perder a Gargantilha Enfeitiçada, fale com Stefan, no Posto Ébano, para obter outra."}},
[12714] = {"A vontade do Lich Rei", {"Retorne para a Vanguarda da Morte e se apresente ao Príncipe Valanar."}},
[12715] = {"A Cripta da Memória", {"Apresente-se ao príncipe Keleseth na Cripta da Memória."}},
[12716] = {"O pedido do pestífero", {"Noth, o Pestífero, quer que você leve para ele 1 Caldeirão Vazio, 1 Corrente de Ferro e 10 Crânios de Cruzados."}},
[12717] = {"Beberagem Especial do Noth", {"Coloque a Pilha de Crânios de Cruzados dentro do Caldeirão Pestilento."}},
[12718] = {"Mais crânios para a mistura", {"Requisitos:"}},
[12719] = {"Sem escape e sem abrigo", {"O príncipe Keleseth, na Cripta da Memória, quer que você mate o prefeito Quimby e pegue os Arquivos de Nova Avalon."}},
[12720] = {"Como fazer amigos e influenciar inimigos", {"O príncipe Keleseth, na Cripta da Memória, quer que você descubra a verdade sobre a \"Aurora Escarlate\".","Remova os Persuasores de Keleseth da Caixa Adornada de Joias e use-os para \"persuadir\" os membros da Cruzada Escarlate a falar."}},
[12721] = {"Alvoroço", {"Har'koa, em Zim'Torga, pediu para libertar Akali e depois avisar o Mandingueiro Khufu em Zim'Torga."}},
[12722] = {"Como cordeiros no matadouro", {"O Barão Rivendare, na Cripta da Memória, quer que você mate 10 Soldados da Cruzada Escarlate e 15 Cidadãos de Nova Avalon."}},
[12723] = {"Atrás das linhas escarlates", {"Apresente-se a Orbaz Ruinassangue na Taberna Escarlate, que fica em Nova Avalon."}},
[12724] = {"O caminho do Cruzado justo", {"Orbaz Ruinassangue, na Taberna Escarlate, em Nova Avalon, quer que você roube o Itinerário das Patrulhas de Nova Avalon do Castelo Escarlate."}},
[12725] = {"Irmãos na morte", {"Encontre Koltirus Tecemorte no Castelo Escarlate."}},
[12726] = {"Canto de Vento e Água", {"Soo-nee quer que você use os Tambores da Tempestade na Prateleira do Criador da Tempestade e ajude Hufão a devorar 3 Elementais Vingativos da Tempestade e 3 Espíritos Aquosos."}},
[12727] = {"Fuga sangrenta", {"Mate o Alto-inquisidor Valroth e entregue a Cabeça de Valroth a Thassarian, que está na Taberna Escarlate, em Nova Avalon."}},
[12728] = {"Monitoramento da fissura: Caverna Falésia Invernal", {"O Bibliotecário Garren, no Promontório Âmbar, quer que você use o Arcanômetro na Caverna Falésia Invernal."}},
[12729] = {"Os deuses se pronunciaram", {"Entregue o Coração Imaculado do Guardião e a Essência Tempestuosa do Protetor a Har'koa em Zim'Torga."}},
[12730] = {"Convocação em Zol'Heb", {"Har'koa, em Zim'Torga, pediu para você matar o Profeta de Akali."}},
[12732] = {"A força cordissangue", {"Leve uma ampola de Cordissangue Tocado pelo Sol para Rejek, no Morro dos Feralma."}},
[12733] = {"O Desafio da Morte", {"Olrun, a Voz da Batalha, quer que você vença 5 duelos contra outros cavaleiros da morte.","Você pode escolher entre lutar contra cavaleiros da morte iniciados ou jogadores."}},
[12734] = {"Rejek: programado para matar", {"Ensanguentar a Lâmina de Rejek nos cadáveres de uma Vespa Safira, de um Agressor Falanjão e de 3 membros da Tribo Brumurmúria. É possível ensanguentar a lâmina tanto nos Oráculos Brumurmúrias quanto nos Guerreiros Brumurmúrias. Após completar a tarefa, fale com Rejek, no Morro dos Feralma."}},
