[12883] = {"Ordens de Drakuru", {"Entregue as Ordens de Drakuru ao Lorde Cruzado Lantinga no Campo da Luz, em Zul'Drak."}},
[12884] = {"Posto Ébano", {"Entregue as Ordens de Drakuru a Stefan Vadu no Posto Ébano, em Zul'Drak."}},
[12885] = {"Os exilados de Ulduar", {"Fale com Petrenedo, o Moldador de Rochas, no Refúgio do Petrenedo."}},
[12886] = {"O Drakkensryd", {"Use o Arpão das Hyldnir em um Ornamento de Coluna para sair do Drakkensryd. Fale com Thorim no Templo das Tempestades, que fica nos Picos Tempestuosos."}},
[12887] = {"Só diversão", {"Destrua o Oculário e, em seguida, apresente-se ao Barão Laska, na Abóbada das Sombras."}},
[12888] = {"METALL-E", {"Leve o Cartão de Acesso de METALL-E junto com 4 Punhados de Parafusos de Cobalto e 5 Capacitores Sobrecarregados para o METALL-E na Biblioteca do Inventor."}},
[12889] = {"O protótipo de console", {"Interaja com o protótipo de console na Biblioteca do Inventor."}},
[12891] = {"Tenho uma ideia, mas antes...", {"O Barão Laska, na Abóbada das Sombras, pediu que você coletasse um Bastão do Sectário, um Gancho da Abominação, uma Corda de Geist e 5 Essências do Flagelo."}},
[12892] = {"Só diversão", {"Destrua o Oculário e, em seguida, apresente-se ao Barão Laska, na Abóbada das Sombras."}},
[12893] = {"Liberar sua Mente", {"O Barão Laska, na Abóbada das Sombras, quer que você use o Bastão Soberano nos cadáveres do Torpe, da lady Noctibosque e do Saltador."}},
[12894] = {"Acampamento Avançado dos Cruzados", {"Procure o Cruzado MacKellar no Campo Avançado dos Cruzados, em Zul'Drak."}},
[12895] = {"O Barbabronze perdido", {"Encontre o Bilhete Embrulhado em Lona no acampamento de Brann e entregue-o a Boktar Furissangue no Local da Queda de Grom'arsh."}},
[12896] = {"Se não conseguir convertê-lo...", {"Derrote o General Halonegro e, em seguida, informe o seu sucesso a Thassarian, a bordo do Rompe-céus."}},
[12897] = {"Se não conseguir convertê-lo...", {"Derrote o general Halonegro e, em seguida, informe o seu sucesso a Koltirus Tecemorte, a bordo da belonave Martelo de Orgrim."}},
[12898] = {"A Abóbada das Sombras", {"Apresente-se ao Barão Laska na Abóbada das Sombras."}},
[12899] = {"A Abóbada das Sombras", {"Apresente-se ao Barão Laska na Abóbada das Sombras."}},
[12900] = {"Confeccionando um arnês", {"Astrid Bjornrittar na Aldeia Brunnhildar quer que você consiga 3 Peles de Yeti Jubálgida."}},
[12901] = {"A arte de criar algo do nada", {"O Engenheiro Reed, no Campo Avançado dos Cruzados, em Zul'Drak, quer que você recupere 10 pedaços de Sucata do Flagelo."}},
[12902] = {"Em busca de respostas", {"Procure, no Covil Patafúria, uma pista de quem ou o que iniciou os ataques contra os Guaxinantes Patafúria."}},
[12903] = {"É para isso que servem os amigos...", {"O Cruzado MacKellar, no Campo Avançado dos Cruzados, quer que você encontre o Cruzado Dargath, Gerk e Burr."}},
[12904] = {"A Luz não me concederá vingança", {"Gerk, na Ruína de Thrym, em Zul'Drak, quer que você mate 15 varguls."}},
[12905] = {"Mildred, a Cruel", {"Fale com Mildred, a Cruel, no interior da Mina Esquecida."}},
[12906] = {"Disciplina", {"Mildred, a Cruel, na Mina Esquecida, quer que você use o Bastão Disciplinador em 6 Vraikalen Exaustos."}},
[12907] = {"Um exemplo a ser dado", {"Mildred, a Cruel, na Mina Esquecida, quer que você mate Garhal."}},
[12908] = {"Uma certa prisioneira", {"Leve a chave de Mildred para Lok'lira, a Velha, na Mina Esquecida."}},
[12909] = {"O nariz sabe", {"Fale com Khaliisi aos pés da montanha a leste do Local da Queda de Grom'arsh."}},
[12910] = {"Farejando o criminoso", {"Monte em Mandíbulas de Gelo e cavalgue-a até a origem do cheiro do bilhete de Brann. Em seguida, recupere o Comunicador de Brann e use-o para fazer contato com Brann Barbabronze."}},
[12911] = {"Kill Credit Test", {"/wave at Kill Credit Test"}},
[12912] = {"Uma grande tempestade se aproxima", {"Gymer, Rei dos Gigantes da Tempestade, quer que você encontre um modo de libertá-lo da jaula. Fale com o Engenheiro Reed no Campo Avançado dos Cruzados."}},
[12913] = {"Fale órquico, homem!", {"Fale com Moteha Filho do Vento, no Local da Queda de Grom'arsh."}},
[12914] = {"Resgate de Gymer", {"O Engenheiro Reed, no Campo Avançado dos Cruzados, em Zul'Drak, quer que você leve 6 Essências de Banshee e 6 amostras de Terra Diatomácea para ele."}},
[12915] = {"Fazendo as pazes", {"Thorim, no Templo das Tempestades, quer que você mate Fjorn e 5 Gigantes Forjados em Tempestade na Bigorna de Fjorn, a leste de Dun Niffelem.","O Amuleto da Terra de Thorim, quando usado, evoca os Terranos para lutar ao seu lado. O amuleto requer um Pedregulho de Granito da Bigorna de Fjorn, próxima dali."}},
[12916] = {"Nossa única esperança", {"Fale com Gymer, na Ruína de Thrym, em Zul'Drak, após usar a Mega Carga de Cequatrum para abrir a Jaula do Flagelo que o mantém preso."}},
[12917] = {"Falando com a voz do vento", {"Traga 5 Vozes do Vento para Moteha Filho do Vento, no Local da Queda de Grom'arsh."}},
[12918] = {"Gema Perfeita", {"Leve dois Citrinos Grandes, dois Jades-Escuros e dois Cristais Sombras para Ounolo, em Valgarde."}},
[12919] = {"Vingança do Rei da Tempestade", {"Fale com Gymer para começar.","Enquanto estiver em cima de Gymer, destrua 100 lacaios do Flagelo e mate Algar, o Escolhido, Thrym e o Príncipe Navarius. Apresente-se ao Cruzado MacKellar no Campo Avançado dos Cruzados ao cumprir a missão."}},
[12920] = {"Compreendendo Brann", {"Use o Comunicador de Brann para fazer contato e falar com ele. Em seguida, fale com Boktar Furissangue no Local da Queda de Grom'arsh.","Se perder o Comunicador de Brann, fale com Moteha Filho do Vento no Local da Queda de Grom'arsh."}},
[12921] = {"Mudança de cenário", {"Reúna-se novamente com Lok'lira, a Velha, na Aldeia Brunnhildar."}},
[12922] = {"O fogo do refinador", {"Colete 10 Centelhas Furiosas dos Elementais Vingativos Exasperados no Lago Congelado e use os na Bigorna de Fjorn."}},
[12924] = {"Forjando uma aliança", {"Vá a Dun Niffelem e peça ao Rei Iokkum para permitir que a armadura de Thorim seja reforjada. Após concluir a tarefa de Iokkum, fale com Njormeld em Dun Niffelem."}},
[12925] = {"Aberrações", {"Thyra Kvinnshal, na Aldeia Brunnhildar, quer que você vá a Valkyrion e tome Ampolas de Óleo Gélido das Aspirantes Valkyrion. Use as Ampolas de Óleo Gélido para destruir 30 Ovos de Protodraco Pestilento."}},
[12926] = {"As peças do quebra-cabeça", {"Use o Comunicador para fazer contato com Brann Barbabronze e descobrir o que fazer em seguida."}},
[12927] = {"Prospecção de dados", {"Use o Disco do Inventor para obter 7 fragmentos de Dados Ocultos nos Bancos de Dados."}},
[12928] = {"Invólucro de Norgannon", {"Obtenha o Invólucro de Norgannon com o Arquivista Mecaton."}},
[12929] = {"Os Terranos de Ulduar", {"Fale com Petrenedo, o Moldador de Rochas, no Refúgio do Petrenedo."}},
[12930] = {"Terra Rara", {"Use a Picareta de Mineração Terrana para escavar 7 torrões de Terra Encantada. Leve a terra e 5 Friotramas para Petrenedo, o Moldador de Rochas, no Refúgio do Petrenedo."}},
[12931] = {"Revidando o ataque", {"Petrenedo, o Moldador de Rochas, quer que você mate 10 Atacantes Forjados em Tempestade e fale novamente com ele no Refúgio do Petrenedo. Você pode matar Saqueadores Forjados em Tempestade ou Aniquiladores Forjados em Tempestade."}},
[12932] = {"Anfiteatro da Angústia: Yggdras!", {"Entre na arena do Anfiteatro da Angústia e derrote Yggdras! Fale com Wodin, o Serviçal Troll, para resgatar o seu prêmio."}},
[12933] = {"Anfiteatro da Angústia: Magnatauro!", {"Entre na arena do Anfiteatro da Angústia e derrote Barbodum! Fale com Wodin, o Serviçal Troll, para resgatar o seu prêmio."}},
[12934] = {"Anfiteatro da Angústia: Do além!", {"Entre na arena do Anfiteatro da Angústia e derrote um dos lordes elementais! Fale com Wodin, o Serviçal Troll, para resgatar o seu prêmio."}},
[12935] = {"Anfiteatro da Angústia: Morsarmagedom!", {"Entre na arena do Anfiteatro da Angústia e derrote Orinoko Quebrapresa! Fale com Wodin, o Serviçal Troll, para resgatar o seu prêmio."}},
[12936] = {"Anfiteatro da Angústia: Korrak, o Sanguinário!", {"Entre na arena do Anfiteatro da Angústia e derrote Korrak, o Sanguinário! Fale com Wodin, o Serviçal Troll, para resgatar o seu prêmio."}},
[12937] = {"Alívio aos que tombaram", {"Use o Cataplasma Terrano para curar 8 Defensores Terranos Caídos e fale novamente com Petrenedo, o Moldador de Rochas, no Refúgio do Petrenedo."}},
[12938] = {"O duque", {"Fale com o Duque Lankral na Abóbada das Sombras."}},
[12939] = {"Desafio de honra", {"O Duque Lankral, na Abóbada das Sombras, quer que você use a Bandeira de Desafio para desafiar e derrotar 6 Combatentes Mjordin."}},
[12940] = {"Balde de balas", nil},
[12941] = {"Balde de balas", nil},
[12942] = {"Fora com as asas negras", {"Iva, a Vingativa, na Aldeia Brunnhildar, quer que você vá a Valkyrion e mate 12 Val'kyr Nascentes."}},
[12943] = {"O decreto da Abóbada das Sombras", {"O Duque Lankral, na Abóbada das Sombras, ordenou que você leia o Decreto da Abóbada das Sombras na presença de Thane Ufrang, o Potente, e, em seguida, mate-o."}},
