[12949] = {"Em busca da chave", {"Vaelen, o Esfolado, no Salão de Ufrang, pediu que você recupere a Chave das Correntes de Vaelen."}},
[12950] = {"Balde de balas", nil},
[12951] = {"Deixa o barão saber disso...", {"Fale com o Barão Laska na Abóbada das Sombras."}},
[12952] = {"Gema Perfeita", {"Leve dois Citrinos Grandes, dois Jades-escuros e dois Cristais Sombras para Horácio Stern, em Porto Vendeta."}},
[12953] = {"Queimem Valkyrion", {"Use os Lança-arpões Valkyrion para queimar 6 Montes de Feno Seco."}},
[12954] = {"Anfiteatro da Angústia: Yggdras!", {"Entre na arena do Anfiteatro da Angústia e derrote Yggdras! Fale com Wodin, o Serviçal Troll, para resgatar o seu prêmio."}},
[12955] = {"Eliminando a concorrência", {"O Saltador, na Abóbada das Sombras, quer que você desafie e derrote Sigrid Gelonata; Efrem, o Fiel; Onu'zun e Tinqui Apitocurto."}},
[12956] = {"Uma centelha de esperança", {"Leve o Minério Rebrilhante Refinado para Thorim no Templo das Tempestades."}},
[12957] = {"Escravos dos forjados em tempestade", {"Petrenedo, o Moldador de Rochas, no Refúgio do Petrenedo, quer que você liberte 6 Gnomecânicos Cativos e mate 3 Capatazes Forjados em Tempestade."}},
[12958] = {"Remessa: amuleto de jade sangrento", {"Faça um Amuleto de Jade Sangrento a partir de um Amuleto Vraikal, um Jade-escuro e uma Pedra-sangrenta e entregue-o para Falcão Joiado em Dalaran.","Você pode retirar Amuletos Vraikalen de qualquer Vraikal de Nortúndria."}},
[12959] = {"Remessa: estatueta de mármore faiscante", {"Faça uma Estatueta de Mármore Faiscante a partir de um Marfim do Norte, uma Calcedônia e um Cristal Sombrio e entregue-a para Falcão Joiado, em Dalaran.","Você pode retirar Marfim do Norte de qualquer Cavacorne ou Mamute de Nortúndria."}},
[12960] = {"Remessa: broche solar perverso", {"Faça um Broche Solar Perverso a partir de um Broche do Anão de Ferro, um Citrino Grande e um Cristal do Sol e entregue-o para Falcão Joiado em Dalaran.","Você pode retirar Broches do Anão de Ferro de qualquer Anão de Ferro de Nortúndria."}},
[12961] = {"Remessa: estatueta de osso intrincada", {"Faça uma Estatueta de Osso Intrincada a partir de um Osso de Protodragão, um Cristal do Sol e um Jade-escuro e entregue-a para Falcão Joiado, em Dalaran.","Você pode retirar o Osso de Protodragão de qualquer Protodragão de Nortúndria."}},
[12962] = {"Remessa: relíquia de armadura brilhante", {"Faça uma Relíquia de Armadura Brilhante a partir de um Pedaço de Armadura Elemental, uma Pedra-sangrenta e um Citrino Grande e entregue-a para Falcão Joiado, em Dalaran.","Você pode retirar um Pedaço de Armadura Elemental de qualquer Elemental Vingativo de Nortúndria."}},
[12963] = {"Remessa: antiguidade solar cambiante", {"Faça uma Antiguidade Solar Cambiante a partir de uma Raridade do Flagelo, um Cristal do Sol e um Cristal Sombrio e entregue-a para Falcão Joiado, em Dalaran.","Você pode retirar uma Raridade do Flagelo de qualquer membro do Flagelo em Nortúndria."}},
[12964] = {"Minério negro", {"Leve 5 Amostras de Minério para Petrenedo, o Moldador de Rochas, no Refúgio do Petrenedo."}},
[12965] = {"As dádivas de Loken", {"Destrua a Fúria de Loken, o Poder de Loken e a Proteção de Loken e fale novamente com Petrenedo, o Moldador de Rochas, no Refúgio do Petrenedo."}},
[12966] = {"Não tem como errar", {"O Rei Iokkum em Dun Niffelem quer que você encontre Njormeld na Bigorna de Fjorn."}},
[12967] = {"Enfrentando os elementos", {"Njormeld quer que você acompanhe Snorri até a bigorna de Fjorn e ajude-o a matar 10 Elementais Vingativos Exasperados."}},
[12968] = {"O desatino de Yulda", {"Iva, a Vingativa, na Aldeia Brunnhildar, quer que você mate Yulda, a Voz da Tempestade, em Valkyrion."}},
[12969] = {"É aquele Goblin ali?", {"Desafie Agnetta Tyrsdottar para salvar Zeev Saifaísca. Fale novamente com Lok'lira, a Velha, na Aldeia Brunnhildar, após sua vitória."}},
[12970] = {"O Tornehylde", {"Ouça a proposta de Lok'lira, a Velha."}},
[12971] = {"Encarando todas as desafiantes", {"Lok'lira, a Velha, na Aldeia Brunnhildar, quer que você derrote 6 Desafiantes Vitoriosas."}},
[12972] = {"Você vai precisar de um urso", {"Fale com Brijana na entrada da Aldeia Brunnhildar."}},
[12973] = {"Os irmãos Barbabronze", {"Siga na máquina voadora com Brann Barbabronze até a Fortaleza Gelada."}},
[12974] = {"O chamado dos campeões!", {"Procure Gurgthock no Anfiteatro da Angústia, em Zul'Drak."}},
[12975] = {"In memoriam", {"O Rei Iokkum em Dun Niffelem quer que você reúna 8 Fragmentos de Trompa em Tormentério."}},
[12976] = {"Um monumento aos que tombaram", {"O Rei Iokkum quer que você leve os Fragmentos da Trompa de Hodir a Njormeld, em Dun Niffelem."}},
[12977] = {"Chamado de Hodir", {"Derrote 5 Antepassados Niffelems e 5 Gelonatos Inquietos em Tormentério. Sopre a Trompa de Hodir sobre os corpos e volte à Trompa de Hodir em Dun Niffelem."}},
[12978] = {"Enfrentando a tempestade", {"Bruor Ferruína no Refúgio do Petrenedo quer que você mate 10 Forjados em Tempestade de Nidavelir. Você pode matar Campeões Forjados em Tempestade, Magos Forjados em Tempestade ou Golens de Guerra Forjados em Tempestade."}},
[12979] = {"A armadura das trevas", {"Leve 4 Amostras de Armadura das Trevas para Bruor Ferruína no Refúgio do Petrenedo."}},
[12980] = {"O segredo da armadura", {"Leve a Placa de Armadura das Trevas à Oficina do Mimir e peça ao Assistente Tock para examiná-la. Quando terminar, fale novamente com Bruor Ferruína no Refúgio do Petrenedo."}},
[12981] = {"Quente e frio", {"Vá ao Lago Congelado e mate Elementais Vingativos Quebradiços para obter Essências de Gelo. Depois vá até a antiga localização da Bigorna de Fjorn, mate Elementais Vingativos Exasperados para obter sobras de metal e combine-as com as essências geladas. Em seguida, reúna 6 das Sobras de Ferro Congelado obtidas no processo e volte à nova localização da Bigorna de Fjorn em Dun Niffelem."}},
[12982] = {"Prisioneiros da Lâmina de Ébano", {"Vaelen, o Esfolado, na Abóbada das Sombras, quer que você liberte 8 Prisioneiros da Lâmina de Ébano das jaulas em Jotunheim e arredores."}},
[12983] = {"A última da espécie", {"Astrid Bjornrittar na Aldeia Brunnhildar quer que você resgate uma Matriarca Rosnagelo na Caverna Hibérnia."}},
[12984] = {"Valduran, o Filho da Tempestade", {"Use a Trompa de Guerra de Petrenedo quando encontrar Valduran, o Filho da Tempestade. Derrote Valduran, o Filho da Tempestade, e fale novamente com Petrenedo, o Moldador de Rochas, no Refúgio do Petrenedo."}},
[12985] = {"Fazendo a cabeça", {"Njormeld, em Dun Niffelem, precisa que você use a Picareta com Ponta de Diamante nos Gigantes de Ferro Mortos, no Lago Congelado, e fale novamente com ele quando tiver coletado 8 Olhos Forjados em Tempestade."}},
[12986] = {"O destino dos titãs", {"Creteus nas Estepes do Sopé pediu para você ir ao Templo da Invenção, ao Templo do Inverno, ao Templo da Vida e ao Templo da Ordem e utilizar o banco de dados para vasculhar cada área."}},
[12987] = {"No devido lugar", {"Njormeld quer que você voe até o topo no aguilhão de gelo norte em Dun Niffelem e use as Tabuletas do Pronunciamento."}},
[12988] = {"Destrua as forjas!", {"Destrua a Forja Norte do Relâmpago, a Forja Central do Relâmpago e a Forja Sul do Relâmpago com as Bombas de Petrenedo e fale novamente com Petrenedo, o Moldador de Rochas, no Refúgio do Petrenedo."}},
[12989] = {"A escuridão serpenteante", {"Astrid Bjornrittar quer que você mate 8 Yormungares Vorazes na Caverna Hibérnia a leste da Aldeia Brunnhildar."}},
[12991] = {"Atinja o ponto fraco", {"Mate 10 Artífices Forjados em Tempestade e fale novamente com Bruor Ferruína no Refúgio do Petrenedo."}},
[12992] = {"Esmagar Vraikalen!", {"O Torpe, na Abóbada das Sombras, quer que você mate 15 vraikalen em Jotunheim e arredores."}},
[12993] = {"Uma ameaça colossal", {"Reúna as Especificações de Ataque e de Defesa do Colosso e leve-as para Petrenedo, o Moldador de Rochas, no Refúgio do Petrenedo."}},
[12994] = {"Caçador de espiões", {"Derrote 3 Espiões Forjados em Tempestade no Vale dos Antigos Invernos.","Use o Canino do Worg Etéreo no Cadáver do Worg Caído para evocar um Geloworg Etéreo, que expõe inimigos ocultos. Volte à Grande Mãe Geloworg, em Dun Niffelem, quando o vale estiver livre de espiões."}},
[12995] = {"Deixando nossa marca", {"O Barão Laska, na Abóbada das Sombras, quer que você coloque 15 Estandartes da Lâmina de Ébano perto dos cadáveres dos vraikalen encontrados em Jotunheim e arredores."}},
[12996] = {"O aquecimento", {"Astrid Bjornrittar na Aldeia Brunnhildar quer que você use as Rédeas da Matriarca Ursa de Guerra para derrotar Kirgaraak."}},
[12997] = {"Para o fosso", {"Astrid Bjornrittar na Aldeia Brunnhildar quer que você use as Rédeas da Matriarca Ursa de Guerra no Fosso da Presa e derrote 6 Ursos de Guerra do Tornehylde."}},
[12998] = {"O coração da tempestade", {"Petrenedo, o Moldador de Rochas, no Refúgio do Petrenedo, quer que você vá a Uldis e tente impedir que o Feitor Narvir consiga o Coração da Tempestade."}},
[12999] = {"A Bruxa dos Ossos", {"Fale com a Bruxa dos Ossos dentro da caverna na Vila Njorndar."}},
[13000] = {"Medidas de emergência", {"A Guarda-de-Sangue Lorga quer que você vá ao Covil de Gimorak a leste e consiga 6 Peles de Worg."}},
[13001] = {"Forja da lança de Hodir", {"O Erudito Randvir em Dun Niffelem quer que você colete 3 Estilhaços de Gelo Eterno das profundezas da Caverna Hibérnia e 3 Peles de Mamute Estoico no Vale dos Antigos Invernos."}},
[13002] = {"Gema Perfeita", {"Traga dois Citrinos Grandes, dois Jades-escuros e dois Cristais Sombrios para Geba'li na Fortaleza Brado Guerreiro."}},
[13003] = {"Como matar seu dragão", {"Use a Lança de Hodir para desafiar a Serpe Selvagem no Vale dos Antigos Invernos. Uma vez iniciado o combate, vocês lutarão até a morte."}},
[13004] = {"Gema Perfeita", {"Traga dois Citrinos Grandes, dois Jades-escuros e dois Cristais Sombrios para Alestros na Bastilha Valentia."}},
[13005] = {"Juramento Terrano", {"Thorim, no Terraço dos Criadores, quer que você use a Trompa dos Picos e convoque os Terranos para derrotar 20 Anões de Ferro e 7 Sentinelas de Ferro."}},
[13006] = {"Limpeza viscosa", {"Entre na Caverna Hibérnia a oeste de Dun Niffelem e colete 5 unidades de Óleo Viscoso ali.","Volte em seguida para Dun Niffelem e aplique o óleo no Elmo de Hodir."}},
[13007] = {"O colosso de ferro", {"Use um Yormungar Domado para derrotar o Colosso de Ferro e fale novamente com Petrenedo, o Moldador de Rochas, no Refúgio do Petrenedo."}},
[13008] = {"Táticas do Flagelo", {"O Comandante Cruzado Entari, na Vanguarda Argêntea, que fica na Coroa de Gelo, quer que você liberte 8 Cruzados Enredados."}},
[13009] = {"Um novo começo", {"Njormeld quer que você leve a armadura Reforjada para Thorim no Templo das Tempestades."}},
[13010] = {"Krolmir, o Martelo das Tempestades", {"Thorim quer que você fale com o Rei Iokkum em Dun Niffelem e descubra o que ele sabe sobre Krolmir."}},
[13011] = {"Abate do Yorcuttar", {"O Rei Iokkum, de Dun Niffelem, quer que você mate Yorcuttar na Caverna Hibérnia."}},
