[13068] = {"Uma história de coragem", {"O Grão-lorde Tirion Fordring, no Pináculo dos Cruzados, quer que você localize o Cruzado Brindeiro na Vigia Silente, no nordeste da Coroa de Gelo. A melhor forma de encontrá-lo é sobrevoar a área procurando pela fogueira dele."}},
[13069] = {"Derrube-os!", {"O Saltador, na Abóbada das Sombras, quer que você use um Arpão de Fogo Rápido de Jotunheim para derrubar 15 Protodracos de Jotunheim e seus cavaleiros."}},
[13070] = {"Uma frente fria se aproxima", {"Apresente-se ao Mestre de Ataque Fezzik na Vanguarda Argêntea, que fica na Coroa de Gelo."}},
[13071] = {"Torpe brincar com fogo!", {"O Torpe, na Abóbada das Sombras, quer que você roube um Protodraco de Njorndar e use-o para incendiar 8 prédios dos vraikalen na região de Jotunheim.","A Fortaleza Balargarde, devido à sua natureza, não pode ser incendiada."}},
[13072] = {"Os resquícios de um herói", {"Leve as notícias sobre o destino do Cruzado Brindeiro para o Grão-lorde Tirion Fordring, no Pináculo dos Cruzados."}},
[13073] = {"A ajuda do Guardião", {"O Grão-lorde Tirion Fordring, na Pináculo dos Cruzados, quer que você peça passagem para a Clareira da Lua à Arquidruida Lilliandra e, chegando lá, fale com o Guardião Remulos."}},
[13074] = {"Esperança no Pesadelo Esmeralda", {"O Guardião Remulos, na Clareira da Lua, instruiu você a entrar no Sonho Esmeralda e coletar 3 Bolotas Esmeralda.","Para retornar a Azeroth, basta se afastar do Santuário de Remulos."}},
[13075] = {"A dádiva de Remulos", {"Retorne ao Cruzado Brindeiro com a Bolota Esmeralda. Você o encontrará na Vigia Silente, que fica na Coroa de Gelo."}},
[13076] = {"Ainda há tempo", {"Retorne ao Grão-lorde Tirion Fordring, no Pináculo dos Cruzados, com as notícias sobre o efeito da Bolota Esmeralda."}},
[13077] = {"O toque de um aspecto", {"Solicite a ajuda de Alexstrasza, a Mãe da Vida, no Templo do Repouso das Serpes."}},
[13078] = {"Lágrimas de Dália", {"Alexstrasza, a Mãe da Vida, no Templo do Repouso das Serpes, pediu que você colhesse Lágrimas de Dália nas áreas recentemente renovadas pelas chamas dos Protetores Rubis."}},
[13079] = {"A dádiva de Alexstrasza", {"Leve o Sopro de Alexstrasza para o Cruzado Brindeiro na Vigia Silente, que fica na Coroa de Gelo."}},
[13080] = {"Ainda há esperanças", {"Retorne ao Grão-lorde Tirion Fordring, no Pináculo dos Cruzados, com as notícias sobre o efeito do Sopro de Alexstrasza."}},
[13081] = {"A vontade dos Naarus", {"Solicite a ajuda de A'dal em Shattrath, que fica na Mata Terokkar."}},
[13082] = {"A dádiva de A'dal", {"Retorne ao Cruzado Brindeiro na Vigia Silente, que fica na Coroa de Gelo."}},
[13083] = {"Há luz na escuridão", {"Entregue o Tabardo do Cruzado Brindeiro para o Grão-lorde Tirion Fordring no Pináculo dos Cruzados."}},
[13084] = {"Jotunheim destruída", {"O Duque Lankral, na Abóbada das Sombras, quer que você queime 10 Estandartes Vraikalen."}},
[13085] = {"O retorno de Vaelen", {"Fale novamente com Vaelen, o Esfolado, na Abóbada das Sombras, que fica na Coroa de Gelo."}},
[13086] = {"A última linha de defesa", {"Use um Canhão Argênteo para destruir 100 membros do Flagelo. Além disso, mate 3 Devastadores Ninhálgida.","Após cumprir sua missão, fale com o Mestre de Ataque Fezzik."}},
[13087] = {"Culinária do norte", {"Leve quatro pedaços de Carne Gelada para Brom Cervejarte em Valgarde. Você consegue retirar Carne Gelada de qualquer fera de Nortúndria."}},
[13088] = {"Culinária do norte", {"Traga quatro Carnes Geladas para Roliço MacKreel na Bastilha Valentia. Você consegue retirar Carne Gelada de qualquer fera de Nortúndria."}},
[13089] = {"Culinária do norte", {"Leve quatro pedaços de Carne Gelada para Tomás Colíquio em Porto Vendeta. Você consegue retirar Carne Gelada de qualquer fera de Nortúndria."}},
[13090] = {"Culinária do norte", {"Traga quatro Carnes Geladas para Orn Casco Tenro na Fortaleza Brado Guerreiro. Você consegue retirar Carne Gelada de qualquer fera de Nortúndria."}},
[13091] = {"A arte de ser um terror d'água", {"A Bruxa dos Ossos, na Vila Njorndar, quer que você controle um Terror d'Água e use-o para matar 10 vraikalen na região de Jotunheim.","Você pode matar Guarda-sonos de Jotunheim, Guerreiros de Jotunheim, Aquemagos Mjordin, Irmãs de Armas Njordar e Vargulen de Valhalas."}},
[13092] = {"Lendo os ossos", {"A Bruxa dos Ossos, na Vila Njorndar, quer que você colete 15 Ossos de Vraikal."}},
[13093] = {"Lendo os ossos", {"Requisitos:"}},
[13094] = {"Não se envergonham?", {"A Imagem da Maga-de-guerra Kaitlyn quer que você recupere a Pesquisa de Berinand."}},
[13095] = {"Não se envergonham?", {"A Imagem da Maga-de-guerra Kaitlyn quer que você recupere a Pesquisa de Berinand."}},
[13096] = {"Gal'darah tem que pagar", {"Tol'mar, em Dubra'Jin, quer que você assassine Gal'darah em Gundrak."}},
[13097] = {"Negócios inacabados", {"Har'koa quer que você fale com Tol'mar em Dubra'Jin."}},
[13098] = {"Para a posteridade", {"A Cronista Bah'Kini, em Dubra'Jin, quer que você entre em Gundrak e colete 6 Tabuletas Históricas dos Drakkari."}},
[13099] = {"Só dando uma olhada", {"To'kini quer que você fale com Bah'kini em Dubra'Jin."}},
[13100] = {"Bolo de Carne de Cogumelo Infuso", {"Use a Fôrma de Bolo de Carne para cozinhar 4 Cogumelos Infusos e 2 porções de Carne Gelada. Em seguida, entregue o Bolo de Carne para Otávio Adeleiro na Casa dos Bricabraques, que fica na Praça Fiarruna, em Dalaran."}},
[13101] = {"Convenção no Saguão da Destreza", {"Entregue 4 Ensopados do Norte preparados por você, com sua perícia em culinária, e uma Jarra de Vinho da queijaria de Dalaran para Arila Verlazúli no Saguão da Destreza, que fica na Praça Fiarruna, em Dalaran."}},
[13102] = {"Ensopado esgotado", {"Cozinhe 4 Cenouras da Floresta do Canto Cristalino e 4 porções de Carne Gelada na Panela de Ensopado.","Quando o Cozido Vegetariano ficar pronto, entregue-o para o estalajadeiro dos Esgotos de Dalaran, Hilton Pestana."}},
[13103] = {"Queijo para Furiouro", {"Sirva um Roquefort de Dalaran Curado da queijaria Desce a Saideira e 6 Taças de Vinho de Dalaran Meio Cheias tiradas das mesas da cidade na Bandeja de Queijo Vazia.","Entregue a Bandeja de Queijo e Vinho para Rânido Furiouro, na Loja dos Coureiros, na Área Comercial Magus."}},
[13104] = {"Mais uma vez, para a Brecha!", {"Apresente-se ao Vigia de Ébano no Vale dos Ecos, em frente à Brecha."}},
[13105] = {"Mais uma vez, para a Brecha!", {"Apresente-se ao Vigia de Ébano no Vale dos Ecos, em frente à Brecha."}},
[13106] = {"Guarda Negra", {"Fale com a Cavaleira das Trevas Arly na Guarda Negra, que fica na Coroa de Gelo."}},
[13107] = {"Mostardos-quentes!", {"Abasteça a Cesta de Piquenique Vazia com 4 Rinocerontes-quentes, preparados por você com sua perícia em culinária, e 4 Mostardas Selvagens colhidas dos gramados de Dalaran.","Entregue a Cesta de Piquenique ao Arquimago Pentarus na Plataforma de Krasus, em Dalaran."}},
[13108] = {"Custe o que custar!", {"O Rei Iokkum, em Dun Niffelem, quer que você adentre os Salões Relampejantes e derrote Loken. Depois, de posse da Língua de Loken, fale com o Rei Iokkum."}},
[13109] = {"Diametralmente opostos", {"O Rei Iokkum, em Dun Niffelem, quer que você penetre nos Salões Relampejantes e derrote Volkhan."}},
[13110] = {"Os mortos inquietos", {"O Padre Gustav, no Vale dos Ecos, que fica na Coroa de Gelo, quer que você use Água Benta nos cadáveres dos Cruzados Reanimados para libertar 10 Almas Inquietas."}},
[13111] = {"Um tipo único", {"A Cronista Bah'Kini, em Dubra'Jin, quer que você entre em Gundrak para obter uma parte do Colosso Drakkari."}},
[13112] = {"Bolo de Carne de Cogumelo Infuso", {"Use a Fôrma de Bolo de Carne para cozinhar 4 Cogumelos Infusos e 2 porções de Carne Gelada. Em seguida, entregue o Bolo de Carne para Otávio Adeleiro, na Casa dos Bricabraques, que fica na Praça Fiarruna, em Dalaran."}},
[13113] = {"Convenção no Saguão da Destreza", {"Entregue 4 Ensopados do Norte e uma Jarra de Vinho da queijaria de Dalaran para Arila Verlazúli no Saguão da Destreza, que fica na Praça Fiarruna, em Dalaran."}},
[13114] = {"Ensopado esgotado", {"Cozinhe 4 Cenouras do Canto Cristalino da Floresta do Canto Cristalino com 4 porções de Carne Gelada na Panela de Ensopado.","Quando o Cozido Vegetariano ficar pronto, entregue-o para o estalajadeiro dos Esgotos de Dalaran, Hilton Pestana."}},
[13115] = {"Queijo para Furiouro", {"Sirva um Roquefort de Dalaran Curado da queijaria Desce a Saideira e 6 Taças de Vinho de Dalaran Meio Cheias tiradas das mesas da cidade na Bandeja de Queijo Vazia.","Entregue a Bandeja de Queijo e Vinho para Rânido Furiouro, na Loja dos Coureiros, na Área Comercial Magus."}},
[13116] = {"Mostardos-quentes!", {"Abasteça a Cesta de Piquenique Vazia com 4 Rinocerontes-quentes, preparados por você com sua perícia em culinária, e 4 Mostardas Selvagens colhidas dos gramados de Dalaran.","Entregue a Cesta de Piquenique para o Arquimago Pentarus na Plataforma de Krasus, em Dalaran."}},
[13117] = {"De onde vêm eles?", {"Investigue um Altar de Evocação na parte leste da Usina de Carne e, em seguida, apresente-se novamente à Cavaleira das Trevas Arly, na Guarda Negra."}},
[13118] = {"A purgação do Forte do Flagelo", {"O Vigia de Ébano, no Vale dos Ecos, que fica na Coroa de Gelo, quer que você mate 3 Sumos Sacerdotes das Profundezas Abandonadas, 3 Hipomonarcas das Profundezas Abandonadas e 8 Cruzados Reanimados."}},
[13119] = {"A destruição dos altares", {"Mate o Mestre-evocador Zarod e use o Cajado do Evocador Mestre para destruir o Primeiro, o Segundo, o Terceiro e o Quarto Altares de Evocação. Quando terminar a tarefa, apresente-se à Cavaleira das Trevas Arly, na Guarda Negra."}},
[13120] = {"O olhar da morte", {"Use o Orbe Olhar da Morte para colocar orbes no Apoio de Orbe do Laboratório de Abominação, no Apoio de Orbe do Laboratório de Gigante de Carne e no Apoio de Orbe da Área do Caldeirão. Em seguida, fale novamente com a Cavaleira das Trevas Arly, na Guarda Negra."}},
[13121] = {"Através do olho", {"A Bruxa dos Ossos, na Vila Njorndar, quer que você use o Olho do Lich Rei para obter informações."}},
[13122] = {"A Pedra do Flagelo", {"O Vigia de Ébano, no Vale dos Ecos, que fica na Coroa de Gelo, quer que você lhe traga 15 Pedras do Flagelo."}},
[13124] = {"O esforço perdura", {"Raelorasz quer que você entre no Óculus e resgate Belgaristrasz e seus companheiros."}},
[13125] = {"Silêncio no forte", {"O Vigia de Ébano, no Vale dos Ecos, que fica na Coroa de Gelo, quer que você mate Salranax, o Lacerador de Carnes, o Hipomonarca Talonox e o Sumo Sacerdote Yath'amon."}},
[13126] = {"Uma frente unificada", {"Belgaristrasz quer que você destrua 10 Constructos Centrífugos e derrube o escudo de Varos. Então, derrote Varos Galganuvens."}},
[13127] = {"Lorde-mago Urom", {"Belgaristrasz quer que você derrote o Lorde-mago Urom dentro de Óculus."}},
[13128] = {"Asas e preces", {"Belgaristrasz quer que você mate Éregos dentro do Óculus."}},
[13129] = {"Brincando de morto", {"Kurzel quer que você use o Trapo da Blusa de Kurzel no cadáver de Novos, o Evocador, e traga o Pano Manchado de Icor de volta para ela na Bastilha Drak'Tharon."}},
[13130] = {"A rocha que iniciou uma revolução", {"O Cruzado-arquiteto Silas, no Vale dos Ecos, que fica na Coroa de Gelo, quer que você recupere 10 troncos de Cerne Cristalino e 10 peças de Maçonaria Élfica Antiga."}},
[13131] = {"Sucata no meu baú", {"Brigg, no Pináculo Utgarde, quer que você encontre 5 Barras de Prata Imaculadas, 3 Miçangas Brilhosas, 2 Cálices Dourados e uma Estátua de Jade."}},
[13132] = {"Que a vingança seja minha!", {"Brigg, no Pináculo Utgarde, quer que você mate o Rei Ymiron."}},
[13133] = {"Encontre o herói ancestral", {"A Bruxa dos Ossos, na Vila Njorndar, quer que você traga Iskalder até ela."}},
[13134] = {"Derramando o sangue inimigo", {"A Cavaleira das Trevas Arly, na Guarda Negra, quer que você destrua 5 tanques de Fluido Embalsamador e 5 Orbes de Sangue."}},
[13135] = {"Perigo iminente", {"O Cruzado-engenheiro Patricuspe, no Vale dos Ecos, que fica na Coroa de Gelo, quer que você recupere 8 Energias Cristalizadas."}},
[13136] = {"Fragmentos serrilhados", {"Entregue 10 Fragmentos Serrilhados para o Cruzado Olakin Sainrith, na Guarda Negra."}},
[13137] = {"Combate não muito justo", {"A Bruxa dos Ossos, na Vila Njorndar, quer que você derrote Iskalder no Pináculo Chaga de Guerra."}},
[13138] = {"Estou trabalhando... trabalhando!", {"Use o Saco de Fragmentos Serrilhados em uma das fornalhas do nível inferior de Malykriss. Em seguida, entregue a Barra de Metal Fundido resultante para o Cruzado Olakin Sainrith, na Guarda Negra."}},
[13139] = {"No coração congelado de Nortúndria", {"Entregue o Relatório do Padre Gustav ao Grão-lorde Tirion Fordring na Vanguarda Argêntea, que fica na Coroa de Gelo."}},
[13140] = {"Os ferreiros rúnicos de Malykriss", {"Entregue 5 Placas Rúnicas de Saronita para o Cruzado Olakin Sainrith, na Guarda Negra."}},
[13141] = {"A batalha pelo Pináculo dos Cruzados", {"Vá para o Pináculo dos Cruzados e finque o Estandarte Abençoado da Cruzada em cima da Pilha de Crânios de Cruzados. Defenda o estandarte contra os ataques do Flagelo até que a terra no pináculo tenha sido santificada. Em seguida, fale com o Padre Gustav no Vale dos Ecos, que fica na Coroa de Gelo."}},
[13142] = {"A vingança da banshee", {"A Bruxa dos Ossos, na Vila Njorndar, quer que você a vingue desafiando e derrotando o Sumo Thane Balargarde."}},
[13143] = {"Novo recruta", {"Vereth, o Astuto, na Beira do Sofrimento, quer que você leve um Espreitador Ágil Subjugado ao topo das rochas a nordeste da Beira do Sofrimento."}},
[13144] = {"Matar dois membros de flagelo com um esqueleto só", {"A Cavaleira das Trevas Arly, na Guarda Negra, quer que você destrua 3 Abominações Acorrentadas usando os Esqueletos Incandescentes."}},
[13145] = {"O forte torpe", {"Vereth, o Astuto, na Beira do Sofrimento, quer que você use um Espreitador Ágil para inspecionar Malykriss."}},
[13146] = {"Quanta generosidade", {"Vereth, o Astuto, quer que você use a Corrente de Ferro do Espreitador Ágil para arrastar Bombas do Flagelo para perto de 4 Cruentos Machadeiros em Malykriss."}},
[13147] = {"O mediador", {"Vereth, o Astuto, na Beira do Sofrimento, quer que você use um Espreitador Ágil para arremessar pedras em Bárbaros Umbralinos quando eles estiverem perto de mortos-vivos inferiores em Malykriss. Fale novamente com Vereth depois de provocar 5 brigas."}},
[13148] = {"Conserto de colar", {"Conserte o colar com uma Calcedônia e devolva-o para Falcão Joiado na Loja dos Joalheiros, na Área Comercial Magus, em Dalaran."}},
[13149] = {"Dissipando ilusões", {"Crona quer que você use o Interruptor Arcano nos caixotes suspeitos em Stratholme do passado e, então, fale com ela perto da entrada de Stratholme."}},
[13151] = {"Uma escolta real", {"Crona pediu a você que acompanhe Arthas no Expurgo de Stratholme. Você deve falar com ela novamente quando Mal'Ganis for derrotado."}},
[13152] = {"Criatura versus criador", {"A Cavaleira das Trevas Arly, na Guarda Negra, quer que você destrua a Estaca de Metal que prende o Remendeiro, no Sacrário da Reanimação, e, em seguida, ajude-o a se vingar do Doutor Sabnok."}},
[13153] = {"Proteção para os guerreiros", {"A Feiticeira Kaylana na Fortaleza Invérnia quer que você tome 10 Armaduras da Horda Imbuídas de jogadores da Horda na Cachoeira Glacial."}},
[13154] = {"Ossos e flechas", {"O Arqueiro Ranulfo na Fortaleza Invérnia quer que você tome 10 Aljavas de Flechas de Osso de Dragão de jogadores da Horda na Floresta das Sombras."}},
[13155] = {"Vereth, o Astuto", {"Apresente-se a Vereth, o Astuto, na Beira do Sofrimento, que fica na Coroa de Gelo."}},
[13156] = {"Uma erva extraordinária", {"A Anacoreta Tessa na Fortaleza Invérnia quer que você tome 10 Bolsas de Herborismo da Horda de jogadores da Horda na Estepe da Vida."}},
[13157] = {"O Pináculo dos Cruzados", {"Procure o Grão-lorde Tirion Fordring no Pináculo dos Cruzados, que fica na Coroa de Gelo."}},
[13158] = {"Discrição é tudo", {"Rhonin quer que você vá ao Castelo Violeta em Dalaran e fale com o Carcereiro Alturas."}},
[13159] = {"Contenção", {"O carcereiro Alturas quer que você entre no Castelo Violeta e dê cabo da força de invasão dos dragões azuis. Você deve se apresentar a ele novamente uma vez que Cyanigosa esteja morta."}},
[13160] = {"Vista estonteante", {"Vereth, o Astuto, na Beira do Sofrimento, quer que você use a habilidade Arremessar de um Espreitador Ágil para destruir 12 Sentinelas Criodermas adormecidas."}},
