[14196] = {"Incendiário", {"Você deve criar um Vórtice Furioso derrotando 7 Discípulos de Eluna na presença da sua tempestade de fogo.","Caso você perca sua Gema do Vórtice, outra poderá ser obtida da Pedra Rúnica do Vórtice Ancestral."}},
[14197] = {"Ferro fundido (e mal pago)", {"Colete 20 lingotes de Ferro do Sopé na Mina do Sopé em Azshara."}},
[14198] = {"Cavalgante da tempestade", {"Use a Gema do Vórtice Furioso no Posto do Nijel. Equanto você estiver comandando sua tempestade, mate 40 Defensores do Posto do Nijel e queime 15 Comerciantes do Posto do Nijel.","Caso você perca sua Gema do Vórtice Furioso, outra poderá ser obtida da Pedra Rúnica do Vórtice Ancestral."}},
[14201] = {"Mil histórias na areia", {"Colete 5 Artefatos Recuperados nas ruínas ao redor do Lago Mennar, em Azshara."}},
[14202] = {"A marcha do progresso", {"Escolte uma Topógrafa Goblina até os três Equipamentos de Topógrafo ao redor do Lago Mennar, e defenda-a enquanto ela faz leituras com o equipamento. Outra Topógrafa sempre pode ser convocada com o seu Sinalizador de Topógrafos."}},
[14204] = {"Escondidos na escuridão", {"Derrote 6 Tocaieiros Dentessangue."}},
