[2240] = {"Потайной чертог", nil, {"Прочитайте журнал Балеога, исследуйте, потайной чертог и потом доложите об увиденном геологу Грозовой Вершине."}},
[2242] = {"Зов судьбы", nil, {"Найдите Сетира Древнего и принесите все, что сумеете найти, Сиарне."}},
[2258] = {"Пробежка по Бесплодным землям", nil, {"Принесите 5 вторых желудков канюка, 10 клыков скального койота и 5 осколков каменных элементалей Джаркалу Смешанному Лишайнику в Каргат."}},
[2278] = {"Платиновые диски", nil, {"Поговорите с каменным стражем и запомните все, что он расскажем вам. Когда он закончит свой пересказ событий древности, активируйте диски Норганнона."}},
[2279] = {"Платиновые диски", nil, {"Отнесите миниатюрную копию дисков Норганнона в Лигу исследователей в Стальгорне."}},
[2280] = {"Платиновые диски", nil, {"Отнесите миниатюрную копию дисков Норганнона тому, кому это будет весьма интересно."}},
[2281] = {"Встречи в Красногорье", nil, {"Пойдите к Приозерью в Красногорье и поговорите с Люцием."}},
[2282] = {"Лесопилка Альтера", nil, {"Откройте сундук Люция, достаньте руну воровства и вернитесь к Люцию в Приозерье."}},
[2283] = {"Пропавшее ожерелье", nil, {"Найдите на раскопках Ульдамана драгоценное ожерелье (возможно, поврежденное) и принесите его в Оргриммар Драну Дрофферсу."}},
[2284] = {"Пропавшее ожерелье, этап 2", nil, {"Разыщите ключ к местонахождению самоцветов в глубинах Ульдамана."}},
[2298] = {"Королевская облава", nil, {"Отправляйтесь в Штормград и найдите Рензика Ножа."}},
[2299] = {"К Хальфдану!", nil, {"Отправляйтесь в Стальгорн и поговорите с Хальфданом Чернобородом."}},
[2318] = {"Трудности перевода", nil, {"Найдите кого-нибудь, кто сможет перевести для вас дневник паладина. Ближайший к вам такой умелец, скорее всего, найдется в Каргате, форпосте в Бесплодных землях."}},
[2339] = {"Найти самоцветы и источник энергии", nil, {"Добудьте все три самоцвета и источник энергии для ожерелья из Ульдамана и принесите их Джаркалу Замшелому Клыку в Каргат. Джаркал считает, что источник энергии можно найти у самой сильной твари в Ульдамане. \n\nИз дневника следует... \n* Рубин спрятан на огражденной завалами территории Тенегорна. \n*Топаз спрятан в урне на территорях троггов под боком у дворфов Альянса.\n*Сапфир забрал Гримлок, старейшина троггов."}},
[2342] = {"Возвращенные сокровища", nil, {"Принесите Патрику Гарретту в Подгород фамильное сокровище из сундука в Южном зале Ульдамана."}},
[2358] = {"Рога Незры", nil, {"Верните рога Незры Арантиру в Приозерье."}},
[2359] = {"Башня Клейвена", nil, {"Добудьте ключ от башни Братства Справедливости, проберитесь в башню и выясните, каково содержимое сундука из Сумеречного леса. Отнесите найденные вещи Матиасу Шоу в Штормград. Почитайте дневник Кирнен, чтобы больше узнать о башне."}},
[2360] = {"Матиас и Братство Справедливости", nil, {"Найдите агента Кеарнен в Западном Крае и поговорите с ней."}},
[2378] = {"Поиски Изувеченной Длани", nil, {"Отправляйтесь в Оргриммар и поговорите с Шентулом в Расселине Теней."}},
[2379] = {"Зандо'зан", nil, {"Поговорите с Зандо'заном в Расселине Теней."}},
[2381] = {"Грабь награбленное", nil, {"Принесите Рениксу Презренному из Кабестана сокровище Южного моря. Не забудьте взять из гизмотронного аппарата Реникса ХОЧУ и отмычки. Они понадобятся для выполнения задания.\n\nЕсли на вас нападут необычайно враждебные попугаи, воспользуйтесь ХОЧУ!"}},
[2382] = {"Реникс из Кабестана", nil, {"Поговорите с Рениксом Презренным в Кабестане, что в Степях."}},
[2383] = {"Записка на пергаменте", nil, {"Прочтите пергамент и поговорите с Франгом в долине Испытаний."}},
[2398] = {"Потерянные дворфы", nil, {"Найдите Бейлога в Ульдамане."}},
[2399] = {"Росток папоротника", nil, nil},
[2418] = {"Силовые камни", nil, {"Принесите 8 дентриевых силовых камней и 8 энелиевых силовых камней Ригглфаззу в Бесплодные земли."}},
[2438] = {"Изумрудный ловец снов", nil, {"Добудьте изумрудного ловца снов."}},
[2439] = {"Платиновые диски", nil, {"Отнесите наградной сертификат Дините Каменной Мантии в хранилище Стальгорна."}},
[2440] = {"Платиновые диски", nil, {"Отнесите наградной сертификат Бене Заиндевевшее Копыто в Громовой Утес."}},
[2458] = {"Надежное прикрытие", nil, {"Отыщите башню Торговой компании к северу от Нефтяного болота и вступите в контакт с тайным агентом Изувеченной Длани, надсмотрщиком Физзулом.\n\nВозле башни выстрелите из сигнального ружья ДВА раза, потом подойдите к надсмотрщику Физзулу и продемонстрируйте приветствие Изувеченной Длани (команда /честь).\n\nВсе нужно делать строго по порядку – Физзул убивает всех, кто кажется ему подозрительным."}},
[2459] = {"Фероситас Пожиратель Снов", nil, {"Убейте 7 мистиков из племени Кривой Сосны и найдите пропавший самоцвет."}},
[2460] = {"Приветствие Изувеченной Длани", nil, {"Повторите приветствие Изувеченной Длани вслед за Шентулом."}},
[2478] = {"Миссия: выполнима, но с трудом", nil, {"Украдите ключ от башни у штейгера Силиксиза, убейте двух мертвяков-мутантов Торговой Компании, двух патрульных Торговой Компании и двух смотрителей Торговой Компании.\n\nЛиквидируйте старшего мастера Галливикса, взломайте замок на его сейфе и украдите запас измененной смеси Занзила. Отнесите запас смеси и голову Галливикса Шентулу в Оргриммар и получите следующие указания."}},
[2479] = {"Помощь Хинотта", nil, {"Отправляйтесь в Мельницу Таррен в предгорьях Хилсбрада и доставьте образчик смеси Занзила Сержу Хинотту.\n\nСядьте на дирижабль, идущий до Подгорода, и летите вдоль дороги на юг, через Серебряный бор в сторону Хилсбрада. Следите за знаками."}},
[2498] = {"Возвращение к Деналану", nil, {"Релиан Зеленый Костер хочет, чтобы вы поговорили с Деналаном у озера Аль'Амет."}},
[2499] = {"Дубохмур", nil, {"Снимите гигантскую опухоль с Дубохмура."}},
[2500] = {"Пробежка по Бесплодным землям", nil, {"Добудьте в Бесплодных землях реагенты для Гхака Целителя и отнесите их ему в Телcамар."}},
[2518] = {"Слезы Луны", nil, {"Добудьте серебристые паутинные железы леди Сатры."}},
[2521] = {"Служба для Кум'иши", nil, {"Если в своем путешествии по Выжженным землям вы найдете безупречную сферу из дренетиста, отнесите ее Кум'ише Коллекционеру. \n\nБудьте внимательны – этот камень – один из самых редких кристаллов во всем Азероте. Любое существо в этих землях может владеть безупречной сферой из дренетиста. \n\nВы будете вознаграждены за каждую безупречную сферу из дренетиста, которую доставите Кум-ише."}},
[2522] = {"Для коллекции Кум'иши", nil, {"Найдите безупречную сферу из дренетиста и верните ее Кум'ише Коллекционеру."}},
[2523] = {"Оскверненный песнецвет", nil, {"Требования:"}},
[2541] = {"Спящий друид", nil, {"Принесите шаманский оберег вуду Обену Яростному Когтю в обитель Бен'этиль."}},
[2561] = {"Друид-медведь", nil, {"Уничтожьте лишенное души тело Обена Яростного Когтя, а затем воспользуйтесь оберегом вуду."}},
[2581] = {"Челюсти гиены-хохотуна", nil, {"Принесите три челюсти гиены, два обожженных легких вепря и клешню скорпока магу крови Дразиалу."}},
[2582] = {"Ярость веков", nil, {"Принесите три челюсти гиены, два обожженных легких вепря и клешню скорпока магу крови Дразиалу."}},
[2583] = {"Жизненная сила вепря", nil, {"Принесите три обожженных легких вепря, две клешни скорпока и мозг василиска магу крови Дразиалу."}},
[2585] = {"Решительный удар", nil, {"Принесите три клешни скорпока, две порции потрохов стервятника и обожженное легкое вепря магу крови Дразиалу."}},
[2601] = {"Укус василиска", nil, {"Принесите десять мозгов василиска и потроха двух стервятников волшебнице крови Линоре."}},
[2603] = {"Стойкость стервятника", nil, {"Принесите потроха десяти стервятников и две челюсти гиены волшебнице крови Линоре."}},
[2605] = {"Жаждущий гоблин", nil, {"Добудьте насыщенную влагой железу и принесите ее Синю Гогельмогелю в Прибамбасск."}},
[2606] = {"Хороший вкус", nil, {"Поговорите с Поливалкой в Прибамбасске по поручению Синя Гогельмогеля."}},
[2607] = {"Прикосновение Занзила", nil, {"Поговорите с Доком Миксиль-Пиксилем в подвале казарм."}},
[2609] = {"Прикосновение Занзила", nil, {"Принесите Доку Миксиль-Пиксилю один букетик простых полевых цветов, одну освинцованную колбу, одну бронзовую трубку и одну катушку светлого зеленовато-желтого шелка. Чтотит, знаете ли, сам собой не проходит."}},
[2621] = {"Опозоренный", nil, {"Поговорите с командиром разведотряда Руагом в Каменоре, что в Болоте Печали."}},
[2623] = {"Болотный говорун", nil, {"Заберите приказы вождя и верните их павшему герою Орды."}},
[2641] = {"Маленький секрет Поливалки", nil, {"Найдите фиалковый траган и вернитесь к Поливалке в Прибамбасск."}},
[2681] = {"Камни, что связывают нас", nil, {"Освободите девять слуг Разелиха, трех слуг Севины, трех слуг Аллистария и трех слуг Грола. Когда ваша задача будет выполнена, возвращайтесь к павшему герою. Вы должны оставаться поблизости от камней, иначе процесс не будет завершен."}},
[2744] = {"Охотник на демонов", nil, {"Поговорите в Азшаре с Лорамом Многоногом."}},
[2745] = {"Проникновение в крепость", nil, {"Поговорите с Тирионом в крепости Штормграда."}},
[2746] = {"Все по порядку", nil, {"Принесите Тириону в Штормград 3 отреза шелка и 2 свежих яблока Клары."}},
[2751] = {"Варварские доспехи", nil, {"Принесите 2 украшенных узором бронзовых наруча, 2 бронзовых длинных меча и 2 острых когтя Орокку Омошу в Оргриммар."}},
[2752] = {"За железным наплечьем", nil, {"Принесите Орокку Омошу в Оргриммар 4 бронзовых боевых топора и 4 бронзовых боевых молота."}},
[2753] = {"Тяжелая поступь", nil, {"Принесите 4 зеленых железных шлема, 4 пары зеленых железных наручей и 2 пары зеленых железных поножей Орокку Омошу в Оргриммар."}},
[2754] = {"Рога безумия", nil, {"Принесите Орокку Омошу в Оргриммар 2 прочных железных кувалды, 2 пары посеребренных бронзовых сапог и 2 пары посеребренных бронзовых рукавиц."}},
[2755] = {"Радости Омоша", nil, {"Научитесь танцу Радости Омошей."}},
[2756] = {"Старые обычаи", nil, {"Принесите 4 стальные кирасы и 4 стальных латных шлема Атурку Наковальне в Оргриммар."}},
[2757] = {"Вперед, в Пиратскую Бухту!", nil, {"Поговорите с Макгеваном в Пиратской Бухте."}},
[2758] = {"Происхождение кузнечного искусства", nil, {"Принесите 6 пар золотых чешуйчатых наручей Хэнку Молоту в Штормград."}},
[2761] = {"Плавим, не плавим, плавим, не плавим…", nil, {"Принесите сорок мифриловых слитков и сорок железных слитков Гальвану Древнему из Тернистой долины."}},
[2762] = {"Обманное серебро", nil, {"Принесите сорок мифриловых слитков и пять слитков истинного серебра Гальвану Древнему из Тернистой долины."}},
[2763] = {"Могущество камней", nil, {"Принесите сорок мифриловых слитков и 4 цитрина Гальвану Древнему из Тернистой долины."}},
[2764] = {"Лучший ученик Гальвана", nil, {"Поговорите с Трентоном Молотом Света из Прибамбасска."}},
[2766] = {"Найти КПХ-22/FE!", nil, {"Отнесите аварийный маяк к автономному роботу Карлса, в пещеру рядом с Долиной Свирепого Утеса."}},
[2767] = {"Спасти КПХ-22/FE!", nil, {"Проводите КПX-22/FE в безопасное место, затем сообщите об этом Карлсу Пропеллерзу в Пиратской Бухте."}},
[2768] = {"Изыскательский жезл", nil, {"Принесите изыскательский жезл главному инженеру Чепухастеру в Прибамбасск."}},
[2769] = {"Братья Медноштиф", nil, {"Поговорите с Виззлом Медноштифом на Мерцающей равнине."}},
[2770] = {"Газ'рилла", nil, {"Принесите искрящую чешую Газ'риллы Виззлу Медноштифу на Мерцающую равнину."}},
[2771] = {"Голова на плечах", nil, {"Принесите два мифриловых капюшона и изысканный мифриловый наплечник Трентону Молоту Света."}},
[2772] = {"Мир у твоих ног", nil, {"Принесите две пары тяжелых мифриловых сапог и изысканные мифриловые штаны Трентону Молоту Света."}},
[2773] = {"Мифриловый парень", nil, {"Принесите две тяжелых мифриловых кирасы и изысканные мифриловые перчатки Трентону Молоту Света."}},
[2781] = {"Разыскивается: Калиф Жало Скорпида", nil, {"Принесите голову Калифа Жало Скорпида главному инженеру Чепухастеру в Прибамбасск."}},
[2782] = {"Секрет Рин'джи", nil, {"Отнесите табличку Оран Змеиные Кольца в Квартал Магов Подгорода. Оран – известная ученая, изучающая троллей из племени Сухокожих, и она наверняка сумеет прочесть надписи на табличке."}},
[2783] = {"Незначительные разногласия", nil, {"Поговорите с павшим героем Орды на границе Выжженных земель и болот."}},
[2821] = {"Знак качества", nil, {"Принесите 10 толстых шкур йети Пратту Макгруббену в Крепость Оперенной Луны."}},
[2822] = {"Знак качества", nil, {"Принесите 10 толстых шкур йети Джангору Быстрому Страннику в Лагерь Мохаче."}},
[2841] = {"Техновойны", nil, {"Добудьте в Гномрегане чертежи боевой машины и код от сейфа Термоштепселя в Гномрегане и принесите их Ноггу в Оргриммар."}},
[2842] = {"Главный инженер Скути", nil, {"Поговорите со Скути в Пиратской Бухте."}},
[2846] = {"Тиара Глубин", nil, {"Принесите Тиару Глубин Табете в Пылевых топях."}},
[2854] = {"Доспехи из дикой кожи", nil, {"Принесите 10 кусков плотной кожи Джангору Быстрому Страннику в Лагерь Мохаче."}},
[2855] = {"Наплечники из дикой кожи", nil, {"Принесите 6 накладок из толстой кожи и дикую лозу Джангору Быстрому Страннику в лагерь Мохаче."}},
[2856] = {"Жилет из дикой кожи", nil, {"Принесите 2 черепашьих чешуйчатых кирасы, 2 пары черепашьих чешуйчатых перчаток и дикую лозу Джангору Быстрому Страннику в лагерь Мохаче."}},
[2857] = {"Шлем из дикой кожи", nil, {"Принесите 2 ночных мундира, 2 ночных головных повязки и дикую лозу Джангору Быстрому Страннику в лагерь Мохаче."}},
[2861] = {"Миссия Табеты", nil, {"Поговорите с Табетой в Пылевых топях."}},
[2862] = {"Война с племенем Древолапов", nil, {"Принесите 10 грив гноллов-древолапов Хадокену Быстрому Страннику в Лагере Мохаче."}},
[2863] = {"Истребление вожаков", nil, {"Убейте в течение часа 5 вожаков стаи Древолапов и вернитесь к Хадокену Быстрому Страннику."}},
[2864] = {"Тран'рек", nil, {"Поговорите с Тран'реком в Прибамбасске."}},
[2865] = {"Панцири скарабеев", nil, {"Принести 5 целых панцирей скарабея Тран'реку в Прибамбасск."}},
[2870] = {"Борьба с лордом Шалзару", nil, {"Убейте лорда Шалзару и принесите обнаруженную им таинственную реликвию Латроникусу Лунному Копью в Крепость Оперенной Луны."}},
[2872] = {"Долг Столи", nil, {"Поговорите со Столи из Порта Хитрой Шестеренки."}},
[2873] = {"Посылка Столи", nil, {"Отыщите партию товара Столи и возвратите ее хозяину в Порт Хитрой Шестеренки."}},
[2874] = {"Ром для Маккинли", nil, {"Отнесите бутылку Столи \"Морскому волку\" МакКинли в Пиратскую Бухту."}},
