[3542] = {"Доставка Андрону Ганту", nil, {"Отнесите табличку Маркри Андрону Ганту в Подгород."}},
[3561] = {"Доставка верховному магу Ксилему", nil, {"Отнесите табличку Саэль'Хай верховному магу Ксилему в Азшару."}},
[3563] = {"Плата Джес'римона Джедиге", nil, {"Доставьте записку Джес'римона Джедиге в Азшару."}},
[3564] = {"Плата Андрона Джедиге", nil, {"Доставьте записку Андрона Джедиге в Азшару."}},
[3565] = {"Плата Ксилема Джедиге", nil, {"Доставь записку Ксилема Джедиге в Азшару."}},
[3568] = {"Разносчик порчи", nil, {"Наполните все 4 пустых фиала из приливных заводей в руинах Эльдарата и возвращайтесь к химику Карабосу."}},
