[3626] = {"Возвращение в Выжженные земли", nil, {"Вернитесь к Павшему герою Орды на Развилку Расщепленного Копья, что на Болоте Печали."}},
[3627] = {"Воссоединить расколотый амулет", nil, {"Убейте Грола Разрушителя, леди Севину и верховного мага Аллистария. С их трупов заберите Амулет Грола, Амулет Севины и Амулет Аллистария.\n\nПомните о том, что нужно использовать зачарованное азшаритовое оружие, отравленное Скверной, чтобы ослабить троицу! Их нельзя убить иначе – но используйте оружие разумно..."}},
[3629] = {"Гоблинское инженерное дело", nil, {"Если вы хотите начать обучение гоблинскому инженерному делу, отнесите cправочник по инженерным дисциплинам Никсу Шепружину в Прибамбасск."}},
[3630] = {"Гномское инженерное дело", nil, {"Если вы хотите узнать больше о гномском инженерном деле, отнесите справочник по инженерным дисциплинам мехмастеру Замыкальцу в Стальгорн."}},
[3631] = {"Призыв коня Скверны", nil, {"Поговорите в Кабестане со Страхадом Фарсаном."}},
[3632] = {"Гномское инженерное дело", nil, {"Если вы хотите узнать больше о гномском инженерном деле, отнесите справочник по инженерным дисциплинам мехмастеру Замыкальцу в Стальгорн."}},
[3633] = {"Гоблинское инженерное дело", nil, {"Если вы хотите узнать о гоблинском инженерном деле, отнесите cправочник по инженерным дисциплинам Никсу Шепружину в Прибамбасск, что в Танарисе."}},
[3635] = {"Гномское инженерное дело", nil, {"Если вы хотите узнать побольше о гномском инженерном деле, возьмите справочник по инженерным дисциплинам и отнесите его Карлсу Пропеллерзу в Пиратскую Бухту."}},
[3636] = {"Нести свет", nil, {"Убейте Амненнара Хладовея в Курганах Иглошкурых."}},
[3639] = {"Покажи свою работу", nil, {"Принесите 20 больших железных бомб, 20 шашек плотного динамита и 5 взрывоопасных овец Никсу Шепружину в Прибамбасск."}},
[3641] = {"Покажи свою работу", nil, {"Принесите 6 мифриловых трубок, точный прицел и 2 усовершенствованных манекена-мишени мехмастеру Замыкальцу в Стальгорн."}},
[3643] = {"Покажи свою работу", nil, {"Принесите 6 мифриловых трубок, точный прицел и 2 усовершенствованных манекена-мишени Карлсу Пропеллерзу в Пиратскую Бухту."}},
[3661] = {"Любимцы Элуны?", nil, {"Собрерите во Внутренних землях 15 перьев диких совухов и отнесите их Эреласу Янтарному Небу в деревню Рут'теран."}},
[3701] = {"Дымящиеся руины Тауриссана", nil, {"Пойдите в руины Тауриссана в Пылающих степях и добудьте информацию из таурисанских реликвий. Вернитесь к королевскому историку Аркессон."}},
[3721] = {"Собственный ККХ", nil, nil},
[3741] = {"Ожерелье Хилари", nil, {"Найдите ожерелье Хилари и верните его Хилари в Приозерье."}},
[3761] = {"Земля Ун'Горо", nil, {"Принесите 20 образцов почвы из Кратера Ун'Горо Геду на Вершину Старейшин Громового Утеса."}},
[3762] = {"Помощь верховному друиду Руническому Тотему", nil, {"Поговорите с верховным друидом Хамуулом Руническим Тотемом на Уступе Древних Громового Утеса."}},
[3763] = {"Помощь верховному друиду Оленьему Шлему", nil, {"Поговорите с верховным друидом Фэндралом Оленьим Шлемом в Дарнасе."}},
[3764] = {"Земля Ун'Горо", nil, {"Принесите 20 образцов земли из кратера Ун'Горо Дженалю в дарнасский анклав Кенария."}},
[3765] = {"Проблема за морем", nil, {"Отправляйтесь в Аубердин к Гершалу Шепоту Ночи."}},
[3783] = {"Йети где-то рядом…", nil, {"Соберите 10 толстых кусков меха йети для Уми Смехотреп из Круговзора."}},
[3784] = {"Помощь верховному друиду Руническому Тотему", nil, {"Поговорите с верховным друидом Хамуулом Руническим Тотемом на Уступе Древних Громового Утеса."}},
[3785] = {"Изучение рассветницы", nil, {"При помощи сумы вечного цветения, пакетика семян Тарлендриса и двух порций почвы Ун'Горо попытайтесь прорастить рассветницу.\n\nПринесите 10 рассветниц Матренгилу Медвежьей Лапе в Дарнас."}},
[3786] = {"Изучение рассветницы", nil, {"При помощи сумы вечного цветения, пакетика семян Тарлендриса и двух порций земли Ун'Горо попытайтесь прорастить рассветницу.\n\nПринесите 10 рассветниц Башане Рунический Тотем в Громовой Утес."}},
[3789] = {"Помощь верховному друиду Оленьему Шлему", nil, {"Поговорите с верховным друидом Фэндралом Оленьим Шлемом в Дарнасе."}},
[3790] = {"Помощь верховному друиду Оленьему Шлему", nil, {"Поговорите с верховным друидом Фэндралом Оленьим Шлемом в Дарнасе."}},
[3791] = {"Тайна рассветницы", nil, {"Принесите 10 рассветниц друиду по имени Квинтис Ночной Костер в Крепости Оперенной Луны."}},
[3801] = {"Наследие Черного Железа", nil, {"Чтобы получить ключ от города, поговорите с Франклорном Искусником у гробницы Искусника на Черной горе."}},
[3802] = {"Наследие Черного Железа", nil, {"Убейте Финия Темностроя и добудьте великий молот, Железный Друг. Отнесите Железного Друга в святилище Тауриссана и вложите его в руки статуи Франклорна Искусника."}},
[3821] = {"Скала Молота Ужаса", nil, {"Отправляйтесь в Пылающие степи и выясните, что сталось с Ша'ни Гордый Клык."}},
[3822] = {"Кром'Грул", nil, {"Заберите ноздревое кольцо Ша'ни у Кром'Грула и верните его Тал'траку из Каргата. Обыщите различные пещеры и рудники скалы Молота Ужаса."}},
[3823] = {"Угасание Огненного Чрева", nil, {"Убейте 15 огров-магов, 7 огров и 7 громил из клана Огненного Чрева."}},
[3824] = {"Гор'теш Жестокий", nil, {"Завладейте головой Гор'теша."}},
[3845] = {"Тайна, покрытая мраком", nil, {"Изучите найденное в сумке и верните владельцу."}},
[3861] = {"Куд-кудаааа!", nil, {"Найдите немного цыплячьего корма и вернитесь к вашей курочке-подружке. Когда найдете курицу, покажите, как вы рады ее видеть (эмоция \"/рад\"), а потом накормите специальным цыплячьим кормом."}},
[3881] = {"Спасение экспедиции", nil, {"Принесите ящик с провизией и исследовательское оборудование Виллидену Маршаллу в Кратер Ун'Горо."}},
[3882] = {"Игра в кости", nil, {"Принесите 8 костей динозавра Искру Пустокопу в Кратер Ун'Горо."}},
[3883] = {"Экология чужих", nil, {"Воспользуйтесь фиалом для образцов, чтобы взять образец материала со стены улья из одного из инкубаторов Гориши в Кратере Ун'Горо. Ищите в ульях комнаты со свисающими гроздьями личинок. \n\nПринесите образец материала стены улья Холании Маршалл в Кратер Ун'Горо."}},
[3884] = {"Дневник Вилидена", nil, {"Верните дневник Виллидену Маршаллу, который находится где-то в Кратере Ун'Горо."}},
[3901] = {"Игрушки-погремушки", nil, {"Kill 8 Rattlecage Skeletons."}},
[3902] = {"Поиски старой одежды", nil, {"Обыщите Могильник и его окрестности и найдите 6 ящиков с добычей и передайте их стражу смерти Салтену."}},
[3903] = {"Милли Осворт", nil, {"Поговорите с Милли Осворт."}},
[3904] = {"Урожай Милли", nil, {"Принесите 8 корзин с виноградом Милли Осворт в Аббатство Североземья."}},
[3905] = {"Извещение о получении партии винограда", nil, {"Отнесите извещение о получении партии винограда брату Нилзу в Аббатство Североземья."}},
[3906] = {"Дисгармония пламени", nil, {"Отправьтесь в карьер у Черной горы и уничтожьте Подчинителя Пирона. По выполнению вернитесь к Громосерду."}},
[3907] = {"Дисгармония пламени", nil, {"Ступайте в глубины Черной горы и выследите лорда Опалителя. Убейте его и принесите Громосерду все, что может дать какую-то информацию."}},
[3908] = {"Тайна, покрытая мраком", nil, {"Отнесите меч Линкена Донове Снегоноре."}},
