[8304] = {"Дражайшая Наталия", nil, {"Допросите Франкала и Рутгара в лагере Бронзоборода о местонахождении Наталии Мар'алит."}},
[8305] = {"Давно забытые воспоминания", nil, {"Отыщите в Силитусе хрустальную слезу и загляните в ее глубины."}},
[8306] = {"В бездне безумия", nil, {"Найдите возлюбленную командора Мар'алита из крепости Кенария в Силитусе. По имеющим сведениям, госпожу Наталию Мар'алит можно найти в улье Регал.\n\nПрежде чем отправляться в улей, не забудьте поговорить с дворфами из лагеря Бронзоборода. Они могут дать пару советов и подкинуть дополнительную работу.\n\nСледует помнить слова командора: \"Сделай, что должно...\""}},
[8307] = {"Блюдо из червей", nil, {"Поищите в лагере Сумеречного Молота на западе от крепости Кенария сведения о приготовлении мяса песчаных червей."}},
[8308] = {"Потерянное письмо Бранна Бронзоборода", nil, {"Отнесите письмо Бранна Бронзоборода в лагерь Бронзоборода в южной части Силитуса."}},
[8309] = {"Поиски рун", nil, {"При помощи копирующего устройства геолога получите оттиск покрытого письменами кристалла из Улья Регал."}},
[8310] = {"Расшифровка", nil, {"Добудьте мозг силитида из улья Регал."}},
[8311] = {"Тыквовинские вкусности для Джеспера!", nil, {"Поговорите с хозяевами таверн в Штормграде, Стальгорне и Дарнасе, а также с Талвашем дель Кисселем в Стальгорне. Выполните то, что они потребуют за предлагамые ими сладости.\n\nВернитесь к Джесперу в сиротский приют Штормграда и принесите ему марципан Дарнаса, леденец Гномрегана, нугу из Штормграда и мятную конфету Стальгорна."}},
[8312] = {"Тыквовинские вкусности для Крохи!", nil, {"Поговорите с хозяевами таверн в Оргриммаре, Подгороде и Громовом Утесе, а также с Кали Ремик в Аллее Духов Оргриммара. Выполните то, что они потребуют, за предлагамые ими сладости.\n\nВернитесь к Крохе в сиротский приют Оргриммара и принесите ей марципан Громового Утеса, леденец троллей Черного Копья, нугу из Оргриммара и мятную конфету Подгорода."}},
[8313] = {"Наделение знаниями", nil, {"Принесите вырванную страницу с рецептом Каландрате в таверну крепости Кенария."}},
[8314] = {"Разгадка тайны", nil, {"Принесите покрытую письменами хрустальную призму геологу Ехидной в Крепость Кенария, что в Силитусе."}},
[8317] = {"Помощь по кухне", nil, {"Каландрате из крепости Кенария нужны 10 копченых пустынных клецок."}},
[8318] = {"Тайные послания", nil, {"Принесите 10 зашифрованных сумеречных текстов Бору Буйногривому в крепость Кенария."}},
[8319] = {"Зашифрованные Сумеречные тексты", nil, {"Требования:"}},
[8320] = {"Геолорды из культа Сумеречного Молота", nil, {"Убейте 10 геолордов из культа Сумеречного Молота и вернитесь к Хууму Буйногривому в крепость Кенария."}},
[8321] = {"Вайрал Подлый", nil, {"Убейте Вайрала Подлого и заберите его перстень-печатку."}},
[8322] = {"Тухлые яйца", nil, {"Бросьте тухлые яйца в главную бочку с пивом в таверне Южнобережья."}},
[8323] = {"Истинно верующие", nil, {"Принесите 10 зашифрованных Сумеречных текстов отшельнику Ортеллу."}},
[8324] = {"Не теряя веры", nil, {"Требования:"}},
[8325] = {"Очищение Острова Солнечного Скитальца", nil, {"Убейте 6 маназмеев – они находятся у пламенеющих кристаллов, недалеко от башни и беседки."}},
[8326] = {"Вынужденные меры", nil, {"Соберите 8 ошейников с убитых рысей-прыголапов и детенышей прыголапов и возвращайтесь к магистру Эроне на острове Солнечного Скитальца."}},
[8327] = {"Визит к Лантану Перилону", nil, {"Поговорите с Лантаном Перилоном на острове Солнечного Скитальца – он стоит рядом с дорогой, отходящей на запад от Солнечного Шпиля."}},
[8328] = {"Обучение мага", nil, {"Поговорите с наставницей магов Джулией Солнцесвет в Солнечном Шпиле на Острове Солнечного Скитальца."}},
[8329] = {"Обучение воина", nil, {"Поговорите с Делиосом – наставником воинов – в Солнечном Шпиле на Острове Солнечного Скитальца."}},
[8330] = {"Вещи Соланиана", nil, {"Верните Соланиану его гадальный шар, дневник и свиток магии Плети. Их можно найти на острове Солнечного Скитальца у пруда, у одного из пламенеющих кристаллов и у фонтана. Вернитесь с вещами к хранителю Колодца в Солнечный шпиль на острове Солнечного Скитальца."}},
[8332] = {"Герцоги Совета", nil, {"Принесите Аурель Золотой Лист в крепость Кенария 1 большой сверкающий осколок и 3 талисмана бездны."}},
[8334] = {"Агрессия", nil, {"Убейте 7 древунов и 7 одичавших древунов, затем вернитесь к Лантану Перилону на Острове Солнечного Скитальца."}},
[8335] = {"Фелендрен Изгой", nil, {"Убейте 8 чародейских призраков, 2 нечистых чародейских призрака и Фелендрена Изгоя в академии Фалтриена. Принесите голову Фелендрена Лантану Перилону на остров Солнечного Скитальца."}},
[8336] = {"Пригоршня осколков", nil, {"Соберите 6 магических осколков, убивая любых существ, обладающих магическими способностями, а затем вернитесь к чародею Итанасу."}},
[8337] = {"<UNUSED>", nil, {"Требования:"}},
[8338] = {"Оскверненный магический осколок", nil, {"Принесите оскверненный магический осколок чародею Хелиону, который стоит возле беседки у Солнечного Шпиля на острове Солнечного Скитальца."}},
[8341] = {"Лорды Совета", nil, {"Принесите Аурель Золотой Лист в крепость Кенария 5 больших сияющих осколков и 3 перстня бездны."}},
[8345] = {"Святилище Дат'Ремара", nil, {"Прочтите надпись на святилище Дат'Ремара и вернитесь к хранителю Колодца Соланиану в Солнечный шпиль на острове Солнечного Скитальца."}},
[8346] = {"Неутолимая жажда", nil, {"Используйте вашу расовую способность \"Волшебный поток\" против маназмея, затем вернитесь к чародею Хелиону."}},
[8347] = {"Помощь курьерам", nil, {"Поговорите с курьером Аларион у моста к югу от острова Солнечного Скитальца."}},
[8348] = {"Перстень герцогов", nil, {"Принесите перстень бездны Бору Буйногривому из крепости Кенария."}},
[8349] = {"Бор Буйная Грива", nil, {"Поговорите с Бором Буйногривым из крепости Кенария."}},
[8350] = {"Доставка посылки", nil, {"Доставьте посылку Эроны хозяйке таверны Деланиель на площадь Соколиных Крыльев в лесах Вечной Песни."}},
[8352] = {"Скипетр Совета", nil, {"Принесите скипетр бездны Бору Буйногривому из крепости Кенария."}},
[8354] = {"Курочка с мятой", nil, {"Изобразите курицу для хозяина таверны Нормана и получите за это конфету Подгорода!"}},
[8356] = {"Поиграй мускулами!", nil, {"Поиграйте мускулами перед хозяйкой таверны Аллисон и получите от нее за это нугу из Штормграда."}},
[8358] = {"Плата за леденцы", nil, {"Изобразите паровозик перед Кали Ремик (команда /train) и получите в награду леденец племени Черного Копья!"}},
[8359] = {"Поиграй мускулами!", nil, {"Поиграйте мускулами перед хозяйкой таверны Гришкой и получите от нее за это нугу из Оргриммара."}},
[8360] = {"Танец за марципан", nil, {"Потанцуйте для хозяйки таверны Палы, и за это вы получите марципан Громового Утеса!"}},
[8361] = {"Связь с Бездной", nil, {"Убейте храмовника бездны по поручению Бора Буйногривого из крепости Кенария, что в Силитусе, и принесите в доказательство талисман бездны."}},
[8365] = {"Пиратские шляпы", nil, {"Добудьте 20 шляп пиратов Южных Морей и отнесите их Отти Кутюри в Порт Хитрой Шестеренки в Танарисе."}},
[8366] = {"Покажи этим пиратам", nil, {"Начальник отдела безопасности Чепухастер из Порта Хитрой Шестеренки в Танарисе просит убить 10 пиратов, 10 флибустьеров, 10 рабочих и 10 сорвиголов Южных морей."}},
[8367] = {"Великая честь", nil, {"Принесите почетные знаки каждого поля боя ордынскому вестнику войны в любую столицу Орды или в Шаттрат."}},
[8369] = {"Вторжение в Альтеракскую долину", nil, {"Принесите 3 почетных знака Альтеракской долины ордынскому вестнику войны рядом со входом на поле боя."}},
[8370] = {"Завоевание Низины Арати", nil, {"Принесите 3 почетных знака Низины Арати ордынскому вестнику войны рядом со входом на поля боя."}},
[8371] = {"В едином порыве", nil, {"Принесите почетные знаки каждого поля боя бригадному генералу Альянса в любую столицу Альянса или в Шаттрат."}},
[8372] = {"Битва за ущелье Песни Войны", nil, {"Принесите 3 почетных знака ущелья Песни Войны бригадному генералу Альянса рядом со входом на поля боя."}},
[8373] = {"Сила сосен", nil, {"Уберите любую бомбу-вонючку Отрекшихся, брошенную в Южнобережье, при помощи очистителя бомб-вонючек. По выполнении задания возвращайтесь к сержанту Хартману."}},
[8374] = {"Завоевание Низины Арати", nil, {"Принесите 3 почетных знака Низины Арати бригадному генералу Альянса рядом со входом на поля боя."}},
[8375] = {"Помни Альтеракскую долину!", nil, {"Принесите 3 почетных знака Альтеракской долины бригадному генералу Альянса рядом со входом на поле боя."}},
[8393] = {"Завоевание Низины Арати", nil, {"Принесите 3 почетных знака Низины Арати бригадному генералу Альянса рядом со входом на поля боя."}},
[8394] = {"Завоевание Низины Арати", nil, {"Принесите 3 почетных знака Низины Арати бригадному генералу Альянса рядом со входом на поля боя."}},
[8395] = {"Завоевание Низины Арати", nil, {"Принесите 3 почетных знака Низины Арати бригадному генералу Альянса рядом со входом на поля боя."}},
[8396] = {"Завоевание Низины Арати", nil, {"Принесите 3 почетных знака Низины Арати бригадному генералу Альянса рядом со входом на поля боя."}},
[8399] = {"Битва за ущелье Песни Войны", nil, {"Принесите 3 почетных знака ущелья Песни Войны бригадному генералу Альянса рядом со входом на поля боя."}},
[8400] = {"Битва за ущелье Песни Войны", nil, {"Принесите 3 почетных знака ущелья Песни Войны бригадному генералу Альянса рядом со входом на поля боя."}},
[8401] = {"Битва за ущелье Песни Войны", nil, {"Принесите 3 почетных знака ущелья Песни Войны бригадному генералу Альянса рядом со входом на поля боя."}},
[8402] = {"Битва за ущелье Песни Войны", nil, {"Принесите 3 почетных знака ущелья Песни Войны бригадному генералу Альянса рядом со входом на поля боя."}},
[8403] = {"Битва за ущелье Песни Войны", nil, {"Принесите 3 почетных знака ущелья Песни Войны бригадному генералу Альянса рядом со входом на поля боя."}},
[8410] = {"Покорение стихий", nil, {"Соберите образцы воздуха, огня, земли и воды по поручению Бат'ры Стража Ветра."}},
[8412] = {"Тотем духов", nil, {"Принесите части тотема Духа Бат'ре Стражу Ветра."}},
[8413] = {"Вуду", nil, {"Принесите вудуистские перья Бат'ре Стражу Ветра."}},
[8414] = {"Борьба со злом", nil, {"Принесите 20 камней Плети приспешников верховному жрецу Тель'данису в Западные Чумные земли."}},
[8415] = {"Лагерь Промозглого Ветра", nil, {"Обратитесь к командиру Ашламу Отважному Кулаку в Лагерь Промозглого Ветра."}},
[8417] = {"Неупокоенный дух", nil, {"Поговорите с павшим героем Орды у входа в Выжженные земли."}},
[8418] = {"Создание камня силы", nil, {"Добудьте янтарные, синие и зеленые вудуистские перья у троллей из Затонувшего храма."}},
[8419] = {"Просьба беса", nil, {"Принесите отрез ткани Скверны Бесенку в Оскверненный лес."}},
[8420] = {"Пробирает!", nil, {"Принесите отрез ткани Скверны Бесенку в Оскверненный лес."}},
[8421] = {"Бросовый материал", nil, {"Принесите 10 кусков гнилой древесины и 4 порции экстракта Отравленной Крови Бесенку в Оскверненный лес."}},
[8422] = {"Тролли Пера", nil, {"Принесите 6 вудуистских перьев троллей из затонувшего храма."}},
[8423] = {"Воинское испытание", nil, {"Убейте 7 зловепрей в Выжженных землях и вернитесь к павшему герою Орды."}},
[8424] = {"Война против Приверженцев Тени", nil, {"Убейте приверженцев тени в Выжженных землях и вернитесь к павшему герою Орды."}},
[8425] = {"Вудуистские перья", nil, {"Принесите вудуистские перья павшему герою Орды, забрав их у троллей в Затонувшем храме."}},
[8430] = {"Битва за ущелье Песни Войны", nil, {"Принесите 3 почетных знака ущелья Песни Войны ордынскому вестнику войны рядом со входом на поля боя."}},
[8436] = {"Завоевание Низины Арати", nil, {"Принесите 3 почетных знака Низины Арати ордынскому вестнику войны рядом со входом на поля боя."}},
[8437] = {"Завоевание Низины Арати", nil, {"Принесите 3 почетных знака Низины Арати ордынскому вестнику войны рядом со входом на поля боя."}},
[8438] = {"Завоевание Низины Арати", nil, {"Принесите 3 почетных знака Низины Арати ордынскому вестнику войны рядом со входом на поля боя."}},
[8439] = {"Завоевание Низины Арати", nil, {"Принесите 3 почетных знака Низины Арати ордынскому вестнику войны рядом со входом на поля боя."}},
[8446] = {"Под покровом кошмара", nil, {"Найдите того, кто сможет осознать значение поглощенного кошмарами предмета.\n\nМожет быть, вам сможет помочь могущественный друид."}},
[8447] = {"Пробуждение легенд", nil, {"Дождитесь, пока хранитель Ремул с Лунной поляны очистит поглощенный кошмарами предмет."}},
