[9340] = {"Глубокий Разлом", nil, {"Ступайте на восток от Соколиного дозора, к Глубокому разлому, и убейте 8 детенышей камнеруба и 3 камнерубов-вожаков по приказу капитана следопытов Венн'рена."}},
[9345] = {"Изготовление мази", nil, {"Соберите 12 листьев пламенного шиполиста и возвращайтесь к Грелагу на Заставу Хребтолома."}},
[9349] = {"Сбор яиц опустошителей", nil, {"Принесите 12 яиц опустошителей Легасси на место крушения дирижабля на полуострове Адского Пламени."}},
[9351] = {"Дикие демоны Бездны", nil, {"Принесите 10 конденсированных сущностей демона Бездны \"Крикуну\" Скриду Многослову на место крушения дирижабля."}},
[9352] = {"Вторжение из Дарнаса", nil, {"Отправляйтесь в Западное святилище, к юго-западу от площади Соколиных Крыльев, и уничтожьте всех чужаков, которых вы там встретите. Затем сообщите о результатах хранительнице энергии Велании."}},
[9353] = {"Подарочный купон АйКока", nil, {"Требования:"}},
[9355] = {"Работа для сообразительных", nil, {"Убейте 15 хищных землероев и возвращайтесь к штейгеру Большие Десны в Оплот Чести."}},
[9356] = {"Летать как птица", nil, {"Принесите 12 мясистых крыльев канюка Легасси на место крушения дирижабля."}},
[9358] = {"Следопыт Сарейн", nil, {"Поговорите со следопытом Сарейн у деревни Легкий Ветерок, что в лесах Вечной Песни."}},
[9359] = {"Обитель Странников", nil, {"Поговорите с лейтенантом Вестницей Зари в обители Странников, что в лесах Вечной Песни."}},
[9360] = {"Вторжение племени Амани", nil, {"Доставьте планы вторжения племени Амани лейтенанту Вестнице Зари в обитель Странников."}},
[9361] = {"Зловепрь – тоже мясо", nil, {"Легасси требует принести ему 8 кусков мяса зловепря на место крушения дирижабля. Убейте зловепря, добудьте порченое мясо зловепря, а затем очистите его с помощью микстуры очищения."}},
[9362] = {"Полководец Креллиан", nil, {"Принесите радужную раковину верховному магу Ксилему в башню в утесах Азшары."}},
[9363] = {"Предупреждение деревне Легкий Ветерок", nil, {"Отправляйтесь в деревню Легкий Ветерок в лесах Вечной Песни и поговорите со следопытом Деголиеном."}},
[9364] = {"Частицы магии", nil, {"Наложите заклинание превращения на тварей Бича Злобы в Азшаре и убейте клонов, которые появятся несколько мгновений спустя. Убейте 50 клонов и возвращайтесь к верховному магу Ксилему в Азшару."}},
[9366] = {"Оскверненная кровь", nil, {"Принесите 6 образцов оскверненной крови исчадий ужаса, обитающих у прудов Аггонара, Риатену Мрачному из Соколиного дозора на полуострове Адского Пламени."}},
[9367] = {"Праздник Огня", nil, {"Прикоснитесь к пламени праздничных костров в Штормграде, Стальгорне и Дарнасе, затем поговорите с хранителем мудрости праздника в столичных городах."}},
[9368] = {"Праздник Огня", nil, {"Прикоснитесь к пламени праздничных костров в Оргриммаре, Громовом Утесе и Подгороде, затем поговорите с Праздничным сказителем в столичных городах."}},
[9369] = {"Исцеляющие кристаллы", nil, {"Добудьте 6 фиалов с кровью мотылька из ближайших долинных мотыльков и принесите их Проэнтиусу на место крушения в долине Аммен."}},
[9370] = {"Остановить очищение", nil, {"Поставьте сигнальный самоцвет на алтарь Аггонара, рядом с прудами Аггонара, и уничтожьте дренейского анахорета и всех, кто явится на сигнал. После выполнения задания заберите самоцвет и возвращайтесь к Риатену Мрачному в Соколиный дозор."}},
[9371] = {"Ботаник Таэрикс", nil, {"Поговорите с ботаником Таэрикс на месте крушения в долине Аммен."}},
[9372] = {"Распространение демонической скверны", nil, {"Соберите 6 образцов крови зловепря у крупных зловепрей по просьбе Тии Рыжей Гривы с Кенарийской заставы на полуострове Адского Пламени."}},
[9373] = {"Потерянное послание", nil, {"Доставьте подпорченный кожаный чехол Тие Рыжей Гриве, которая стоит лагерем рядом с руинами Ша'наара на западе полуострова Адского Пламени."}},
[9374] = {"Записи Арелиона", nil, {"Отправляйтесь к прудам Аггонара и найдите записи Арелиона по просьбе магистра Каринды из Соколиного дозора на полуострове Адского Пламени."}},
[9375] = {"Дорога к Соколиному дозору", nil, {"Помогите израненной эльфийке крови – пилигриму добраться до Соколиного дозора, затем поговорите с Талерисом Созерцателем Рассвета."}},
[9376] = {"Скарб пилигрима", nil, {"Перозубые опустошители напали на израненную эльфийку крови – пилигрима, когда она остановилась в Лощине Пыльного Пера. Разыщите ее стоянку и потерянные вещи."}},
[9377] = {"Жажда магической силы", nil, {"Извлеките мощную сущность демона Бездны из мстительных демонов Бездны и возвращайтесь к Талерису Созерцателю Рассвета в Соколиный дозор."}},
[9381] = {"Стрелы, бьющие без промаха", nil, {"Принесите 8 хвостовых перьев стервятника-костеклюя сокольничему Дренне Речной Ветер в Соколиный дозор."}},
[9383] = {"Честолюбивый план", nil, {"Используйте освященный кристалл против раненого неконтролируемого демона Бездны, затем возвращайтесь к Элсаане в храм Телхамат."}},
[9385] = {"Разъяренные Опустошители", nil, {"Убейте 10 перозубых опустошителей и возвращайтесь к Гремни Длиннобороду на полуострове Адского Пламени."}},
[9386] = {"Свет в темных местах", nil, {"Найдите костры, размещенные в пике Черной горы, Забытом Городе, Некроситете и Стратхольме и коснитесь их; затем поговорите с хранителем праздничного пламени."}},
[9387] = {"Источник порчи", nil, {"Отправляйтесь в руины Ша'наара на полуострове Адского Пламени и добудьте из иллидари-надсмотрщиков 5 демонических сущностей. После завершения задания возвращайтесь к аптекарю Азетену в Соколиный дозор."}},
[9388] = {"Мерцающие огни Калимдора", nil, {"Посетите праздничные костры в Ясеневом лесу, Степях, на Темных берегах и в Когтистых горах, затем поговорите с хранителем праздничного пламени."}},
[9389] = {"Мерцающие огни Восточных королевств", nil, {"Посетите праздничные костры в предгорьях Хилсбрада, в Серебряном бору, Западном Крае и Болотине, затем поговорите с хранителем праздничного пламени."}},
[9390] = {"Найти Седаи", nil, {"Разыщите Седаи, брата анахорета Обадия. Седаи может находиться где-то поблизости, на востоке, на полуострове Адского Пламени."}},
[9391] = {"Осветить путь", nil, {"Отправляйтесь к Глубокому разлому на полуострове Адского Пламени и зажгите южный маяк, западный маяк и центральный маяк. После выполнения задания возвращайтесь к капитану следопытов Венн'рену из Соколиного дозора вместе с зажженным факелом."}},
[9392] = {"Обучение разбойника", nil, {"Поговорите с наставником разбойников налетчиком Авокором в Солнечном Шпиле на острове Солнечного Скитальца."}},
[9393] = {"Обучение охотника", nil, {"Поговорите с следопытом Саллиной в Солнечном Шпиле на Острове Солнечного Скитальца."}},
[9394] = {"Куда подевался Виллитен?", nil, {"Найдите смотрителя Виллитена в землях Блеклых Сумерек, что в лесах Вечной Песни."}},
[9395] = {"Вилла Салтерила", nil, {"Поговорите с лордом Салтерилом на вилле Салтерила в лесах Вечной Песни."}},
[9396] = {"Магия араккоа", nil, {"Принесите 4 свитка племени Хаал'эш чародею Калестрису Утренней Звезде в Соколиный дозор на полуострове Адского Пламени."}},
[9397] = {"Пух и перья", nil, {"Отправляйтесь в логово Хаал'эш на полуострове Адского Пламени и найдите молодую самку калири в гнезде калири. Посадите птенца в пустую клетку и принесите сокольничему Дренне Речной Ветер в Соколиный дозор."}},
[9398] = {"Смертельно опасные хищники", nil, {"Убейте 8 детенышей камнеруба и 4 камнерубов-вожаков по просьбе разведчицы Вануры из храма Телхамат на полуострове Адского Пламени."}},
[9399] = {"Безжалостные десятники", nil, {"Убейте 4 иллидари-надсмотрщиков на руинах Ша'наара и возвращайтесь к Икану в храм Телхамат на полуострове Адского Пламени."}},
[9400] = {"Убийца", nil, {"Ступайте по дороге к северу от цитадели Адского Пламени и разыщите убийцу по имени Крун Хребтолом по просьбе Назгрела."}},
[9401] = {"Странное оружие", nil, {"Принесите тяжелый каменный топор Назгрелу в Траллмар на полуострове Адского Пламени."}},
[9402] = {"Апорт!", nil, {"Нырните на дно пруда Тихого Шелеста и добудьте лазурный фиал для инструктора Антеола."}},
[9403] = {"Чистейшая вода", nil, {"Наполните лазурный флакон водой из водопада Элрендар для Антеола."}},
[9404] = {"Живая ветка", nil, {"Убивайте хранителей лесов Вечной Песни, пока не найдете животворную ветвь для инструктора Антеола."}},
[9405] = {"Поручение Вождя", nil, {"Принесите тяжелый каменный топор провозвестнице Регулкут в Траллмар на полуострове Адского Пламени."}},
[9406] = {"Маг'хары", nil, {"Доставьте письмо маг'харов Назгрелу в Траллмар на полуострове Адского Пламени."}},
[9407] = {"Через Темный портал", nil, {"Пройдите через Темный портал и найдите генерал-лейтенанта Ориона. Он ждет вашего прибытия на другой стороне, в Запределье."}},
[9409] = {"Срочная доставка", nil, {"Отнесите сверток с фиалами Залдууну на место крушения в долине Аммен."}},
[9410] = {"Хранитель душ", nil, {"Установите тотем духа волка на том месте, где вы нашли тело Круна Хребтолома. Когда появится дух древнего волка, следуйте за ним."}},
[9415] = {"Маршал Синяя Синестен", nil, {"Поговорите с маршалом Синестеном в лагере Альянса возле Улья Зора."}},
[9416] = {"Генерал Кирика", nil, {"Поговорите с генералом Кирикой в лагере Орды рядом с Ульем Регал."}},
[9417] = {"Угроза Араккоа", nil, {"Убейте 4 ветроступов и 6 коготарей из племени Хаал'эш по просьбе Гремни Длинноборода с полустрова Адского Пламени."}},
[9418] = {"Сфера Авруу", nil, {"Отнесите сферу Авруу на алтарь племени Хаал'эш, который находится на юго-востоке от логова Хаал'эш на полуострове Адского Пламени. После того как Аэранас обретет свободу, отдайте ему сферу."}},
[9419] = {"Истощение пустыни", nil, {"Найдите и принесите силитист к экстрактору силитистов и поговорите с маршалом Синестеном в лагере Альянса возле Улья Зора."}},
[9420] = {"Прекрасное оперение", nil, {"Принесите Миррену Длиннобороду на полуострове Адского Пламени 8 перьев калири от различных калири из логова Хаал'эш."}},
[9421] = {"Обучение шамана", nil, nil},
[9422] = {"Истощение пустыни", nil, {"Найдите и принесите силитист к экстрактору силитистов и поговорите с генералом Кирикой в лагере Орды возле Улья Регал."}},
[9423] = {"Возвращение к Обадию", nil, {"Возвращайтесь к анахорету Обадию в храм Телхамат на полуострове Адского Пламени и расскажите о смерти Седаи."}},
[9424] = {"Месть Макуру", nil, {"Принесите 10 ожерелий предков маг'харов Макуру в храм Телхамат на полуострове Адского Пламени."}},
[9425] = {"Возвращение в Мельницу Таррен", nil, {"Отправляйтесь в Мельницу Таррен в предгорьях Хилсбрада и поговорите с советником Вечерней Зарей."}},
[9426] = {"Пруды Аггонара", nil, {"Отправляйтесь к прудам Аггонара и убейте 6 исчадий ужаса и 6 волдырных гнилостней по просьбе Амаана Мудрого."}},
[9427] = {"Очищение вод", nil, {"Отправляйтесь к прудам Аггонара и используйте фиал очищения над телом Аггонара по просьбе Амаана Мудрого. После того как вода будет очищена от сущности Аггонара, возвращайтесь обратно к Амаану."}},
[9428] = {"На заставу Расщепленного Дерева", nil, {"Поговорите с советником Верным Солнцу на Заставе Расщепленного Дерева в Ясеневом лесу."}},
[9429] = {"Путешествие в Темнолесье", nil, {"Поговорите с анахоретом Деланом в городе Темнолесье в Сумеречном лесу."}},
[9430] = {"Реликвии Ша'наара", nil, {"Отправляйтесь в руины Ша'наара и соберите 10 реликвий Ша'наара по просьбе Амаана Мудрого из храма Телхамат на полуострове Адского Пламени."}},
[9431] = {"Разный подход", nil, {"Соберите 2 горсти очищающей земли с грохочущих булыжней и отнесите Визло Фингалету на заставу Белого Плеса."}},
[9433] = {"Проба из Лунного колодца", nil, {"На каменной игле, ближайшей к границе с Таланааром, в кустах спрятано устройство управления Роботроном 3000. Найдите ее и включите робота при помощи устройства, которое вам дал Визло. Подведите робота к западному краю Тысячи Игл, где находится застава Альянса под названием Таланаар, и прикажите ему набрать немного воды из Лунного колодца, после чего вернитесь к Визло Фингалету на заставу Белого Плеса."}},
[9434] = {"Испытание тоника", nil, {"Доставьте ящик с реагентами магистра Элозай на Заставу Вольного Ветра."}},
[9435] = {"Пропавшие кристаллы", nil, {"Отберите у нежити посылку с редкими кристаллами и верните ее Ураану из Южнобережья."}},
[9436] = {"Изучение Кровавого Скальпа", nil, {"Немет Остроглаз из лагеря Гром'гол попросил вас принести ему тотем Кровавого Скальпа, добыв его у шамана из племени Кровавого Скальпа."}},
[9437] = {"Сумерки \"Гонца Рассвета\"", nil, {"Верните груз с \"Гонца Рассвета\" Балиндару Ясной Звезде в деревню Гиблотопь и спасите его первого помощника Итанию из рук тераморцев, из Северной башни."}},
[9438] = {"Донесение Траллу", nil, {"Отправляйтесь в Оргриммар и доставьте Траллу письмо Назгрела."}},
[9439] = {"Потерянный багаж", nil, {"Верните советнику Сарофасу в Каргат вещи советника и рапиру советника из крепости Ангор."}},
[9440] = {"Маленькие кусочки", nil, {"Накормите испорченными кусочками мяса домашний скот Заблудших. Потом верните остатки Серсее Певице Заката в Каменор на Болото Печали."}},
[9441] = {"Посол к маг'харам", nil, {"Вернитесь к Горкану Кровопалу на заставу маг'харов на полуострове Адского Пламени и предложите ему свои услуги."}},
[9442] = {"Изнуряющая слабость", nil, {"Принесите гриб-сухошляпку заклинательнице земли Риге с заставы маг'харов на полуострове Адского Пламени. Сухошляпки растут только в пещере внутри Глубокого разлома."}},
[9443] = {"Так называемый знак Светоносного", nil, {"Добудьте знак Светоносного и принесите его Мелару Клинку Рассвета в Бастион, что в Западных Чумных землях."}},
[9444] = {"Осквернение гробницы Утера", nil, {"Оскверните гробницу Утера и возвращайтесь к Мелару Клинку Рассвета в Бастион, что в Западных Чумных землях."}},
[9449] = {"Зов земли", nil, {"Поговорите с духом долины в Священной роще в долине Аммен."}},
[9452] = {"Деликатесный красный луциан", nil, {"Поймайте 10 красных луцианов рыболовной сетью дренеев. Отдайте рыбу и сеть Диктинне на острове Лазурной Дымки."}},
[9453] = {"Поиски Актеона", nil, {"Отнесите ящик красного луциана Актеону на Лазурную заставу."}},
[9454] = {"Великая охота на лунных оленей", nil, {"Принесите 6 кусков вырезки лунного оленя Актеону на Лазурную заставу."}},
[9455] = {"Странные находки", nil, {"Отнесите слабо мерцающий кристалл экзарху Менелаусу на Лазурную заставу."}},
[9456] = {"Истребление ночных ловцов, остров 2...", nil, {"Уничтожьте 8 зараженных ночных ловцов – недоростков и вернитесь на Лазурную заставу."}},
[9457] = {"Необычный посетитель", nil, {"Воспользуйтесь даром Найаса неподалеку от алтаря Найаса и призовите этого элементаля воды. Одолейте его и принесите сердце Найаса Немету Остроглазу в лагерь Гром'гол."}},
[9461] = {"Зов Огня", nil, {"Найдите пророка Велена в Чертоге Света Экзодара."}},
[9463] = {"Ради науки", nil, {"Принесите 8 побегов корнехватов анахоретке Фатиме на Лазурную заставу."}},
[9464] = {"Зов Огня", nil, {"Поговорите с Пламенравом на Тлеющей поляне на острове Лазурной Дымки."}},
[9465] = {"Зов Огня", nil, {"Верните ритуальный факел Пламенраву на Тлеющую поляну на острове Лазурной Дымки."}},
[9466] = {"Разыскивается: Свирепая Черноскребка", nil, {"Принесите когти Черноскребки капитану следопытов Венн'рену в Соколиный дозор на полуострове Адского Пламени."}},
[9470] = {"Жест доброй воли", nil, {"Убейте знахаря Май'джина и его питомца-паука Чаказа по поручению посла Руалета с Заоблачного пика."}},
[9471] = {"Охота на хищников", nil, {"Убейте 10 облезлых среброгривов и 5 среброгривых волков по поручению посла Руалета с Заоблачного пика."}},
[9472] = {"Любовница Арелиона", nil, {"Выманите Виеру Шепот Солнца из поселения и примените к ней свиток воздаяния Каринды по приказу магистра Каринды из Соколиного дозора."}},
[9473] = {"Другое решение", nil, {"Принесите 5 луковиц лазурного львиного зева Дедалу на Лазурную заставу."}},
[9474] = {"Знак Светоносного", nil, {"Добудьте знак Светоносного и принесите его анахорету Трууну в Лагерь Промозглого Ветра, что Западных Чумных землях."}},
[9475] = {"Похищенные яйца", nil, {"Соберите 5 яиц грифона и принесите их послу Руалету на Заоблачный пик."}},
[9476] = {"В поисках Пероборода", nil, {"Отправляйтесь в Шадра'Алор и поищите следы Пероборода."}},
[9483] = {"Изысканные удовольствия жизни", nil, {"Требования:"}},
[9484] = {"Приручение зверя", nil, {"Приручите безумного дракондора с помощью хлыста приручения, потом вернитесь к лейтенанту Вестнице Зари."}},
[9485] = {"Приручение зверя", nil, {"Приручите мрачницу с помощью хлыста приручения, потом вернитесь к лейтенанту Вестнице Зари."}},
[9487] = {"Волшебные разорители", nil, {"Чародей Вандрил хочет, чтобы вы принесли ему 10 осколков тайной магии."}},
[9488] = {"Простое одеяние", nil, {"Чародей Вандрил хочет, чтобы вы принесли ему 15 кусков льняной ткани и паутинную железу паука-прядильщика."}},
[9489] = {"Поднять боевой дух", nil, {"Примените \"Слово Силы: Стойкость\" к 6 следопытам из лесов Вечной Песни на Тропе Мертвых."}},
[9490] = {"Матриарх камнедеров", nil, {"Добудьте когти Свирепой Черноскребки, которая обитает в Глубоком Разломе и принесите их Икану в храм Телхамат на полуострове Адского Пламени."}},
[9491] = {"Не откажусь", nil, {"Эраланна из Транквиллиона хочет, чтобы вы добыли исцарапанное золотое кольцо из катакомб Амани."}},
[9492] = {"Новый поворот событий", nil, {"Принесите кулак вождя Каргата командиру армии Данату Троллебою в Оплот Чести."}},
[9493] = {"Элита Орды Скверны", nil, {"Убейте 8 легионеров клана Изувеченной Длани, 4 центуриона клана Изувеченной Длани и 4 воителей клана Изувеченной Длани по приказу боевого командира Ромуса из Оплота Чести."}},
[9494] = {"Угли Скверны", nil, {"Принесите уголь Скверны волхву Забраксис из Оплота Чести."}},
[9495] = {"Воля Вождя", nil, {"Принесите кулак вождя Каргата Назгрелу в Траллмар."}},
[9496] = {"Элита Орды Скверны", nil, {"Убейте 8 легионеров клана Изувеченной Длани, 4 центуриона клана Изувеченной Длани и 4 воителей из клана Изувеченной Длани по приказу темного охотника Туджина из Траллмара."}},
[9497] = {"Знамя Орды Скверны", nil, {"Убейте О'мрогга Завоевателя и захватите ордынское знамя Скверны. После этого возвращайтесь к властителю Хану Изуверу в Траллмар."}},
[9498] = {"Соколиный дозор", nil, {"Доложитесь капитану следопытов Венн'рену в Соколином дозоре на полуострове Адского Пламени."}},
[9499] = {"Соколиный дозор", nil, {"Отправляйтесь в Соколиный дозор, на юго-запад полуострова Адского Пламени, и поговорите с капитаном следопытов Венн'реном."}},
[9501] = {"Зов Воды", nil, {"Найдите Водяницу у Подводного Рифа возле острова Кровавой Дымки и поговорите с ней."}},
[9505] = {"Пророчество Велена", nil, {"Отправляйтесь на юг и отыщите народ, о котором говорило раненое существо."}},
[9506] = {"Начало положено", nil, {"Верните адмиралу Одиссию морской компас и морскую карту, украденные Торговой компанией."}},
[9512] = {"Крабовый супчик поваренка", nil, {"Принесите 6 кусков мяса шустрых крабов \"Поваренку\" Максоусу в лагерь Одиссия."}},
[9513] = {"Возвращение руин", nil, {"Убейте 5 мирмидонов, 5 наг и 5 сирен из клана Зловещей Чешуи для жрицы Кайлин Ил'динар."}},
[9514] = {"Покрытая рунами табличка", nil, {"Отнеcите покрытую рунами табличку жрице Кайлин Ил'динар в лагерь Одиссия на острове Лазурной Дымки."}},
[9515] = {"Полководец Шрисс'тиз", nil, {"Жрица Кайлин Ил'динар из лагеря Одиссия попросила вас убить полководца Шрисс'тиза."}},
[9516] = {"Уничтожить Легион!", nil, {"Убейте 15 демонов на холме Демонического Огня, в каньоне Гибели Демона и гряде Гибели Демона."}},
[9517] = {"Напрасные усилия", nil, {"Добудьте 5 связок оскверненной древесины Сатирнаара."}},
[9518] = {"Пособники разрушения", nil, {"Убейте надзирателя Гортака и 12 других сторонников Орды на лесозаготовках Песни Войны."}},
[9519] = {"Потерянная чаша", nil, {"Добудьте чашу Элуны."}},
[9520] = {"Демонические планы", nil, {"Доставьте демонические планы воздаятелю Ведаару на лесозаготовки Песни Войны в Ясеневый лес."}},
[9521] = {"Донесение с северного фронта", nil, {"Доставьте донесение часового Мелирии часовому Дальней Песне в рощу Среброкрылых, что в Ясеневом лесу."}},
[9522] = {"Больше никогда!", nil, {"Добудьте пламенеющее лезвие Горганнона и желчное копье Ищейки."}},
[9523] = {"Хрупкие предметы. Не кантовать!", nil, {"Принесите 8 древних реликвий археологу Адаманту Железному Сердцу в лагерь Одиссия на острове Лазурной Дымки."}},
[9526] = {"Возрождение Холма Демонического Огня", nil, {"Посадите 8 древесных саженцев в кучи плодородной земли."}},
[9527] = {"Все, что осталось", nil, {"Верните Коулену останки его семьи."}},
[9528] = {"Крик о помощи", nil, {"Проводите Магвин через остров до дома."}},
[9529] = {"Камень", nil, {"Добудьте камень Бездны с Тропы Мертвых."}},
[9530] = {"Созревший план", nil, {"Принесите выдолбленный ствол и 5 охапок листьев адмиралу Одиссию в лагерь Одиссия на острове Лазурной Дымки."}},
[9531] = {"У деревьев есть уши", nil, {"Проследите за встречей Гизла с предателем.\n\nРасскажите обо всем увиденном адмиралу Одиссию."}},
[9532] = {"Найти Келтуса Темного Листа", nil, {"Найдите Келтуса Темного Листа."}},
[9533] = {"Рука помощи", nil, {"Отправляйтесь в Лесную Песнь и поговорите с архитектором Немосом.\n\nОбратитесь к воздаятельнице Паланааре, если вам требуется транспорт."}},
[9534] = {"Уничтожить Легион!", nil, {"Уничтожьте 15 любых демонов на холме Демонического Огня, в Каньоне Гибели Демона и Гряде Гибели Демона, а затем вернитесь к Валуше на Заставу Расщепленного Дерева в Ясеневом лесу."}},
[9536] = {"Больше никогда!", nil, {"Добудьте пламенеющее лезвие Горганнона и желчное копье Ищейки."}},
[9537] = {"Не ведая пощады", nil, {"Добудьте для адмирала Одиссия переписку предателя у инженера \"Искра\" Перемолоттера"}},
[9538] = {"Изучение языка", nil, {"Изучите пергамент \"Язык фурболгов из племени Тихвой для начинающих\", полученный от Ауррена, а затем изучите тотемом Акиды на Лазурной заставе."}},
[9539] = {"Тотем Ку", nil, {"Найдите тотем Ку с помощью проводника."}},
[9540] = {"Тотем Тикти", nil, {"Найдите тотем Тикти с помощью проводника."}},
[9541] = {"Тотем Йора", nil, {"Найдите тотем Йора с помощью проводника."}},
[9542] = {"Тотем Варк", nil, {"Найдите тотем Варк с помощью проводника."}},
[9543] = {"Искупление", nil, {"Поговорите с Амааном Мудрым в храме Телхамат на полуострове Адского Пламени."}},
[9544] = {"Пророчество Акиды", nil, {"Освободите 8 пленников из племени Тихвой из клеток в деревне Косолапов. Вернитесь к Аругу из племени Тихвой на Лазурную заставу."}},
[9545] = {"Реликвия ясновидца", nil, {"Вернитесь к телу Седаи, которое находится к северо-востоку от храма Телхамат, и используйте там реликвию ясновидца по просьбе Амаана Мудрого из храма Телхамат на полуострове Адского Пламени. После выполнения задания возвращайтесь к Амаану."}},
[9546] = {"Исполнение пророчества", nil, {"Освободите 8 пленников из племени Тихвой из клеток в деревне Косолапов по просьбе Аругу из племени Тихвой."}},
[9548] = {"Украденное снаряжение", nil, {"Верните Клопперу Бацбаху его оборудование. Клоппера можно найти в старом черепашьем панцире на берегу."}},
[9549] = {"Артефакты племени Черного Ила", nil, {"Принесите 3 варварских идола и 6 варварских ножа мурлоков Клопперу Бацбаху, который прячется в старом черепашьем панцире на берегу."}},
[9550] = {"Загадочная карта", nil, {"Найдите павильон, изображенный на карте, и выясните, что в нем необычного."}},
[9557] = {"Расшифровка книги", nil, {"Отнесите древние записи на Кровавую заставу и покажите анахорету Пайтеусу."}},
[9558] = {"Длиннобородые", nil, {"Найдите Гремни Длинноборода на северо-западе от логова Хаал'эш на полуострове Адского Пламени."}},
[9559] = {"Логово племени Тихвой", nil, {"Поговорите с верховным вождем в логове племени Тихвой."}},
[9560] = {"Звери Судного дня!", nil, {"Принесите 8 шкур опустошителей Мурдо в логово племени Тихвой."}},
[9561] = {"Записи Нолкаи", nil, {"Найдите место, описанное в дневнике Нолкаи, и отыщите его тайник."}},
[9562] = {"Мурлоки... За что нам эта напасть?", nil, {"Верните 5 мешков зерна племени Тихвой."}},
[9563] = {"Доверие Миррена", nil, {"Принесите Миррену Длиннобороду 1 кружку горькой настойки Стражей Пустоты. Для выполнения этого задания необходимо иметь дружелюбное отношение Оплота Чести."}},
[9564] = {"Клок шерсти Гурфа", nil, {"Верните Гурфу клок его шерсти в логово племени Тихвой на острове Лазурной Дымки."}},
[9565] = {"Поиски в логове племени Тихвой", nil, {"Найдите в логове племени Тихвой причины предательства совухов."}},
[9566] = {"Кровавые кристаллы", nil, {"Поговорите с верховным вождем в логове племени Тихвой."}},
[9567] = {"Лицо врага", nil, {"Найдите в Наззивиане монумент Наззивуса, возьмите один из иероглифов с монумента и вернитесь к воздаятельнице Аалезии на Кровавую заставу."}},
[9569] = {"Противостояние", nil, {"Убейте Зевракса, 5 аксариенских тенеловов и 5 аксариенских призывателей огня, соберите 5 оскверненных кристаллов и отнесите их на Кровавую заставу воздаятельнице Аалезии."}},
[9570] = {"Куркен-жмуркен", nil, {"Убейте Куркена и принесите его шкуру Курцу Прорицателю в логово племени Тихвой на острове Лазурной Дымки."}},
[9571] = {"Шкура Куркена", nil, {"Отнесите шкуру Куркена Мурдо в логово племени Тихвой."}},
[9572] = {"Ослабить оборону валов", nil, {"Убейте начальника стражи Гарголмара, Омора Неодолимого и дракона Назана. Принесите руку Гарголмара, копыто Омора и голову Назана Каса'ресу в Траллмар на Полуострове Адского Пламени."}},
[9573] = {"Вождь Умуру", nil, {"Убейте вождя Умуру и 9 безумных диких совухов."}},
[9574] = {"Отравленные скверной", nil, {"Принесите на Кровавую заставу 6 кусков коры, покрытой кристаллами, и отдайте Морэ."}},
[9575] = {"Ослабить оборону валов", nil, {"Убейте начальника стражи Гарголмара, Омора Неодолимого и дракона Назана. Принесите руку Гарголмара, копыто Омора и голову Назана Пушкаренку в Оплот Чести на полуострове Адского Пламени."}},
[9576] = {"Ожерелье Жестокого Плавника", nil, {"Покажите подвеску красного кристалла Морэ на Кровавой заставе."}},
[9577] = {"DAILY TEST QUEST (PVP)", nil, {"Поговорите с <QUESTGIVER> в <AREA>."}},
[9578] = {"В поисках Галена", nil, {"Найдите Галена, мужа Морэ."}},
[9579] = {"Судьба Галена", nil, {"Принесите на Кровавую заставу амулет Галена и отдайте его Морэ."}},
[9580] = {"Вскормленные медвежатиной", nil, {"Принесите зверолову Лисеону на Кровавую заставу 8 кусков мяса с боков старых бурых медведей."}},
[9581] = {"Изучение кристаллов", nil, {"Добудьте образец кристалла с места крушения, отколов фрагмент кайлом для добычи кристаллов. Принесите образец на Кровавую заставу и отдайте предвестнику Миколаасу."}},
[9582] = {"Силой единой", nil, {"Откройте клетку смертоносного опустошителя, убейте его и вернитесь к Руаде на Лазурную заставу на острове Лазурной Дымки."}},
[9583] = {"Omar's Test Quest", nil, {"Принесите Омару ткани."}},
[9584] = {"Второй образец", nil, {"Добудьте образец кристалла с места столкновения, отколов фрагмент кайлом для добычи кристаллов. Принесите образец на Кровавую заставу и отдайте его предвестнику Миколаасу."}},
[9585] = {"Последний образчик", nil, {"Добудьте образец кристалла с места столкновения, отколов фрагмент кайлом для добычи кристаллов. Принесите образец на Кровавую заставу и отдайте предвестнику Миколаасу."}},
[9587] = {"Предвестье беды", nil, {"Доставьте угрожающее письмо боевому командиру Данату Троллебою в Оплот Чести на полуострове Адского Пламени."}},
[9588] = {"Предвестье беды", nil, {"Доставьте угрожающее письмо Назгрелу в Траллмар на Полуострове Адского Пламени."}},
[9589] = {"Кровь есть жизнь", nil, {"Принесите 10 фиалов крови орков Скверны Пушкаренку в Оплот Чести на полуострове Адского Пламени."}},
[9590] = {"Кровь есть жизнь", nil, {"Принесите 10 фиалов крови орков Скверны центуриону Каса'ресу в Траллмар на Полуострове Адского Пламени."}},
[9594] = {"Знаки Легиона", nil, {"Убейте 8 сатиров из племени Наззивус и 8 скверноподданных из племени Наззивус, а затем отнесите латный доспех Тзерака воздаятельнице Аалезии на Кровавую заставу."}},
[9595] = {"Контроль", nil, {"Убивайте мурлоков с Илистого Берега до тех пор, пока не появится призрак кель'дорайского мага, затем уничтожьте его. После этого поговорите с Бати в Чертоге Света Экзодара."}},
[9600] = {"Искупление", nil, {"Воскресите молодого фурболга-шамана из племени Тихвой при помощи знака Жизни, а затем вернитесь к Джоль."}},
[9601] = {"В Бастион", nil, {"Поговорите с Меларом Клинком Рассвета из Бастиона."}},
[9602] = {"Избавь их от лукавого...", nil, {"Отнесите переписку предателя экзарху Менелаусу на Лазурную заставу."}},
[9603] = {"Лежанки, лекарства и прочая ерунда", nil, {"Отвезите список Челланы на Лазурную заставу укротителю гиппогрифов Залдаану."}},
[9604] = {"На крыльях гиппогрифа", nil, {"Возьмите у Залдаана гиппогрифа и летите в Экзодар. В Экзодаре отдайте Нургуни список Челланы. Нургуни можно найти у Трона Наару в Экзодаре."}},
[9605] = {"Укротитель гиппогрифов Стефанос", nil, {"Найдите Стефаноса у трона наару, что в Экзодаре, и наймите гиппогрифа для возвращения на Лазурную заставу."}},
[9606] = {"Возвращение к корчмарю Челлане", nil, {"Договоритесь со Стефаносом и наймите у него гиппогрифа для путешествия на Лазурную заставу. На заставе доставьте припасы Нургуни корчмарю Тоферу Челлане."}},
[9607] = {"В сердце ярости", nil, {"Детально изучите Кузню Крови и доложите об увиденном командиру армии Данату Троллебою в Оплот Чести на полуострове Адского Пламени."}},
[9608] = {"В сердце ярости", nil, {"Детально изучите Кузню Крови и доложите об увиденном Назгрелу из Траллмара на Полуострове Адского Пламени."}},
[9610] = {"Озеро Слез", nil, {"Добудьте 5 артефактов Атал'ай и вернитесь с ними к Холаару в Убежище на Болото Печали."}},
[9612] = {"Большое спасибо!", nil, {"Поговорите с экзархом Менелаусом на Лазурной заставе."}},
[9616] = {"Бандиты!", nil, {"Отнесите сообщение эльфов крови экзарху Менелаусу на Лазурную заставу."}},
[9617] = {"На поиски Странников", nil, {"Поговорите с лейтенантом Вестницей Рассвета в обители Странников."}},
[9620] = {"Пропавшие картографы", nil, {"Предвестник Миколаас просит разыскать группу разведчиков, которую он послал к развалинам Лорет'Арана, и помочь группе чем только возможно."}},
[9621] = {"Посол к Орде", nil, {"Отнесите письмо от Лор'темара Терона леди Сильване Ветрокрылой в Подгород, столицу Отрекшихся в Тирисфальских лесах."}},
[9622] = {"Предупреди свой народ", nil, {"Поговорите с экзархом Менелаусом на Лазурной заставе."}},
[9623] = {"Время пришло", nil, {"Передайте распоряжения экзарха Тораллиусу Пастуху. Он находится у Экзодара, недалеко от входа."}},
[9624] = {"Любимое блюдо", nil, {"Принесите 10 песчаных груш Аонару в лагерь Кессела."}},
[9625] = {"Элекки – это серьезно", nil, {"Поговорите с Ворканом Пастухом Элекков на острове Кровавой Дымки."}},
[9626] = {"Встреча с вождем", nil, {"Отнесите письмо с печатью Леди Сильваны Ветрокрылой вождю в Оргриммар, столицу Дуротара."}},
[9627] = {"Верность Орде", nil, {"Возвращайтесь в Луносвет и поговорите с Лор'темаром Тероном."}},
[9628] = {"Цена знания – жизнь", nil, {"Неподалеку от того места, где были убиты картографы, обитают наги из клана Зловещей Чешуи. Отберите у них кристалл с результатами наблюдений и доставьте его на Кровавую заставу предвестнику Миколаасу."}},
[9629] = {"Поймать и отпустить", nil, {"Морэ с Кровавой заставы просит вас \"пометить\" 6 разведчиков из племени Черного Ила при помощи маркировщика мурлоков."}},
[9630] = {"Дневник Медива", nil, {"По поручению верховного мага Альтура на Перевале Мертвого Ветра войдите в Каражан и поговорите с Вравьеном."}},
[9631] = {"Взаимовыручка", nil, {"Доставьте фрагмент обугленной кости Калинне Латред в Зону 52 в Пустоверти."}},
[9632] = {"Новые союзники", nil, {"Доставьте рекомендательное письмо охотнице Келле Тетиве Ночи на остров Лазурной Дымки."}},
[9633] = {"Дорога к Аубердину", nil, {"Сядьте на корабль, идущий в Аубердин. Пристань находится неподалеку от дома охотницы Келлы Тетивы Ночи. В Аубердине отдайте рекомендательное письмо Тандрису Ветропряду."}},
[9634] = {"Чужие-хищники", nil, {"Убейте 10 детенышей опустошителя с острова Кровавой Дымки и вернитесь к Воркану Пастуху Элекков. Воркана можно найти на острове Кровавой Дымки в лагере Кессела."}},
[9635] = {"Удаленный конденсатор выхлопов!", nil, {"Принесите 15 деталей парового насоса Маку Дайверу в Забра'джин в Зангартопи."}},
[9636] = {"Удаленный конденсатор выхлопов!", nil, {"Принесите 15 деталей парового насоса К. Ли Мелюзге в Телредор в Зангартопи."}},
[9637] = {"Требование Калинны", nil, {"Калинна Латред требует отыскать и принести ей фолиант сумерек и книгу забытых имен. Первой владеет главный чернокнижник Пустоклят, его можно найти в Разрушенных залах цитадели Адского Пламени. Вторая принадлежит заклинателю тьмы Ситу, который находится в Сетеккских залах в Аукиндоне.\n\nЭто задание должно быть выполнено на героическом уровне сложности."}},
[9638] = {"В хороших руках", nil, {"Поговорите с Градавом в Библиотеке стража в Каражане."}},
[9639] = {"Камсис", nil, {"Поговорите с Камсис в Библиотеке Стража в Каражане."}},
[9640] = {"Тень Арана", nil, {"Добудьте дневник Медива и верните его Камсис в Библиотеку Стража в Каражане."}},
[9641] = {"Осколки облученного кристалла", nil, {"Принесите 10 осколков облученного кристалла воздаятелю Боросу на Кровавую заставу."}},
[9642] = {"И снова облученные кристаллы", nil, {"Требования:"}},
[9643] = {"Удушающая лоза", nil, {"Принесите зверолову Лисеону 6 побегов колючей удушающей лозы."}},
[9644] = {"Ночная Погибель", nil, {"Ступайте на террасу Мастера в Каражане и прикоснитесь к урне Калинны, чтобы призвать Ночную Погибель. Извлеките из тела Ночной Погибели слабую чародейскую сущность и доставьте ее верховному магу Альтуру."}},
[9645] = {"Терраса Мастера", nil, {"Поднимитесь на террасу Мастера в Каражане и прочитайте дневник Медива."}},
[9646] = {"РАЗЫСКИВАЕТСЯ: Коготь Смерти", nil, {"Принесите лапу Когтя Смерти предвестнику Миколаасу на Кровавую заставу."}},
[9647] = {"Ловля бражников", nil, {"Зверолов Лисеон просит убить 10 царственных синих бражников."}},
[9648] = {"Грибная похлебочка Матпарма", nil, {"Матпарм просит принести ему на Кровавую заставу по одному экземпляру водянистого смрадорога, кровавого гриба, губительной полиспоры и конусовидной поганки."}},
[9649] = {"Слеза Изеры", nil, {"Матпарм просит принести ему на Кровавую заставу 2 экземпляра Слезы Изеры."}},
[9663] = {"Дуга Кессела", nil, {"Сообщите верховному вождю племени Тихвой, экзарху Менелаусу и адмиралу Одиссию о готовящемся вторжении эльфов крови, а затем возвратитесь к Кесселу. У вас есть 15 минут. Элекка следует держать подальше от воды."}},
[9664] = {"Новые форпосты", nil, {"Захватите башню Королевской Стражи, Восточную башню, Башню Северного перевала и Башню Чумного леса в Восточных Чумных землях и вернитесь к посланнику Белобороду в Часовню Последней Надежды."}},
[9665] = {"Укрепим оборону", nil, {"Захватите башню Королевской Стражи, Восточную башню, Башню Северного перевала и Башню Чумного леса в Восточных Чумных землях и возвращайтесь к посланнику Гормоку в Часовню Последней Надежды."}},
[9666] = {"Демонстрация силы", nil, {"Проникните в логово клана Зловещей Чешуи, убейте лорда Ксиза и пронзите его тело древком дренейского знамени."}},
[9667] = {"Спасение принцессы Тихвой", nil, {"Выпустите принцессу Тихвою из клетки, а затем найдите на Кровавой заставе посла племени Тихвой Олорга и поговорите с ним.\n\nЧтобы раздобыть ключ от клетки, убейте верховного вождя племени Косолапов."}},
[9668] = {"Доклад экзарху Адметиусу", nil, {"Поговорите с экзархом Адметиусом на Кровавой заставе."}},
[9669] = {"Пропавшая экспедиция", nil, {"Убейте 8 пауков-ткачих, 8 паучих-кровопийц и матриарха пауков – Зарак. По завершении задания вернитесь на Кровавую заставу к Акелусу."}},
[9670] = {"Они живы! Может быть...", nil, {"Освободите 5 ученых экспедиции по просьбе ученого Корнелиуса."}},
[9671] = {"Срочная доставка", nil, nil},
[9672] = {"Наследие Проклятой Крови", nil, {"Адмирал Одиссий просит разыскать капитана Эдварда Хейнса и поговорить с ним."}},
[9674] = {"Проклятые наги", nil, {"Капитан Эдвард Хейнс с острова Драконьей Скорби просит убить 10 наг с острова Проклятой Крови."}},
[9676] = {"Наставник паладинов", nil, {"Поговорите с наставником паладинов Джестенисом Солнцесветом в Солнечном Шпиле на Острове Солнечного Скитальца."}},
[9677] = {"Призыв предводителя рыцарей Доблестной Крови", nil, {"Поговорите с предводителем рыцарей Доблестной Кровью на площади Странников в Луносвете."}},
[9678] = {"Первое испытание", nil, {"Отправляйтесь на остров, описанный предводителем рыцарей Доблестной Кровью, найдите пещеру и разожгите огонь в жаровне. Пройдя первое испытание, возвращайтесь к предводителю рыцарей Доблестной Крови в Луносвет."}},
[9680] = {"Тайны прошлого", nil, {"Верховный маг Альтур желает, чтобы вы нашли в горах к югу от Каражана на Перевале Мертвого Ветра фрагмент обугленной кости и доставили ему."}},
[9681] = {"Источник силы", nil, {"Поговорите с магистром Асталором Кровавой Клятвой в тайной зале, расположенной под штабом рыцарей крови."}},
[9682] = {"Отчаявшиеся", nil, {"Капитан Эдвард Хейнс с острова Драконьей Скорби просит освободить 4 души с острова Проклятой Крови."}},
[9683] = {"Избавление от Проклятой Крови", nil, {"Капитан Эдвард Хейнс с острова Драконьей Скорби просит уничтожить статую королевы Азшары и убить Атофа Проклятую Кровь."}},
[9684] = {"Получение силы Света", nil, {"Используйте мерцающий сосуд на эльфах крови-магистрах, чтобы наполнить его магической силой, и возвращайтесь к предводителю рыцарей Доблестной Крови в Луносвет."}},
[9685] = {"Воскрешение", nil, {"Отправляйтесь в таверну Луносвета и воскресите Сангриаса Безмолвного Клинка с помощью силы, заключенной в наполненном мерцающем сосуде."}},
[9686] = {"Второе испытание", nil, {"Пройдите второе испытание, победив воителя Ярокрова, воителя Светореза, воительницу Быстромеч и воителя Солнцесвета в павильоне у наставника Келерана Алого Взмаха."}},
[9687] = {"Святотатство", nil, {"Принц Торет с острова Драконьей Скорби просит отыскать 8 костей драконов."}},
[9688] = {"Дорога в Сон", nil, {"Принц Торет с острова Драконьей Скорби просит убить 5 темно-зеленых дракончиков и 5 детенышей зеленого дракона."}},
[9689] = {"Острозуб", nil, {"Принц Торет с острова Драконьей Скорби просит убить Острозуба."}},
[9690] = {"Второе испытание", nil, {"Поговорите с наставником Келераном Алым Взмахом."}},
[9692] = {"Путь адепта", nil, {"Принесите оскверненный самоцвет Кора, ящик со слитками, закаленными в крови, нужное количество крови разгневанных и знак отличия рыцаря крови предводителю рыцарей Доблестной Крови в Луносвет."}},
[9693] = {"Что для тебя Аргус?..", nil, {"Экзарх Адметиус с Кровавой заставы просит поговорить с воздаятелем Боросом."}},
[9694] = {"Кровавая застава", nil, {"Убейте 10 шпионов Солнечных Ястребов и сообщите об этом на Кровавую заставу воздаятелю Боросу."}},
[9695] = {"Повеление Солнечного короля", nil, {"Доставьте повеление Солнечного короля воздаятелю Боросу на Кровавую заставу."}},
[9696] = {"Переводы...", nil, {"Доставьте послание Солнечных Ястребов дознавательнице Элизии на Кровавую заставу."}},
[9697] = {"Наблюдательница Лиса'о", nil, {"Поговорите с наблюдательницей Лиса'о у кенарийского караульного поста в Зангартопи."}},
[9698] = {"Аудиенция у Пророка", nil, {"Доставьте переведенное послание Солнечных Ястребов Пророку Велену в Экзодар."}},
[9699] = {"Правда или вымысел", nil, {"Доставьте распоряжения Велена воздаятелю Боросу на Кровавую заставу."}},
[9700] = {"Магия против Тьмы", nil, {"Воздаятель Борос с Кровавой заставы просит найти солнечный портал и убить 5 аномалий Бездны."}},
[9701] = {"Наблюдение за спорлингами", nil, {"Исследуйте Грибную поляну в Зангартопи, затем вернитесь к наблюдательнице Лиса'о к кенарийскому караульному посту в Зангартопи и расскажите об увиденном."}},
[9702] = {"Вопросы питания", nil, {"Соберите 10 образцов выброшенного корма и вернитесь к наблюдательнице Лиса'о на кенарийский караульный пост в Зангартопи."}},
[9703] = {"Криогенный блок", nil, {"Воздаятель Курос хочет, чтобы вы доставили на Кровавую заставу 12 коробок с медицинскими припасами."}},
[9704] = {"Смерть от рук Презренных", nil, {"Отправляйтесь на юг, в руины Луносвета, и отыщите убитого курьера на Рассветной улице."}},
[9705] = {"Возвращение посылки", nil, {"Вернитесь к курьеру Аларион с посылкой Эроны."}},
[9706] = {"Дневник Галена: судьба воздаятеля Саруана", nil, {"Доберитесь до Кровавой заставы и отдайте дневник Галена воздаятелю Куросу."}},
[9708] = {"Знакомые грибы", nil, {"Соберите 15 образцов грибов и принесите их наблюдательнице Лиса'о на караульный пост Кенария в Зангартопи."}},
[9709] = {"Ворованные грибы", nil, {"Соберите 10 ящиков грибов и вернитесь с ними к наблюдательнице Лиса'о на караульный пост Кенария в Зангартопи."}},
[9711] = {"Матис Жестокий", nil, {"Воздаятель Курос с Кровавой заставы требует, чтобы Матис Жестокий был пойман."}},
[9715] = {"Принеси мне клумбу!", nil, {"Добудьте 5 растений кровавого гибискуса и отнесите их Гзхун'тту в Спореггар в Зангартопи."}},
[9716] = {"Неприятности на озере Тенетопь", nil, {"Выясните причину убывания воды в озере Тенетопь. Затем вернитесь к Исиэль Песни Ветра в Кенарийский приют в Зангартопи."}},
[9717] = {"Удачный ход", nil, {"Добудьте ветку подспорника и вернитесь с ней к Т'шу в Спореггар в Зангартопи."}},
[9718] = {"С высоты птичьего полета", nil, {"Используйте амулет буревестника, чтобы исследовать основные озера Зангартопи по просьбе Исиэли Песни Ветра."}},
[9719] = {"Охота на Охотницу", nil, {"Принесите мозг Черной Охотницы Кхн'иксу в Спореггар в Зангартопи."}},
[9720] = {"Необходимо поддерживать равновесие!", nil, {"Осыпьте семенами железной лозы пульты управления паровым насосом в Змеином озере, озере Тенетопь, озере Болотных Огоньков и в Лагуне. Затем вернитесь к Исиэль Песни Ветра в Кенарийский приют в Зангартопи и отдайте ей оставшиеся семена."}},
[9721] = {"Призыв леди Лиадрин", nil, {"Поговорите с Лордом Соланаром в Луносвете."}},
[9723] = {"Жест преданности", nil, {"Принесите лорду Соланару в Луносвет 40 отрезов рунической ткани, 6 арканитовых слитков, 10 солнечников, 5 темных рун и 150 золотых."}},
[9724] = {"Предупреждение Кругу Кенария", nil, {"Поговорите с Амитиэль Странницей Туманов на Заставе Кенария на полуострове Адского Пламени."}},
[9725] = {"Доказательство лояльности", nil, {"Лорд Соланар Кровогнев из Луносвета просит уничтожить 3 мясных фургона и убить 15 инженеров Плети."}},
[9726] = {"Теперь, когда мы друзья...", nil, {"Убейте 12 надсмотрщиков за рабами из клана Кровавой Чешуи, 6 чародеек из клана Кровавой Чешуи и возвращайтесь к Гзхун'тту в Спореггар в Зангартопи."}},
[9727] = {"Раз уж мы по-прежнему друзья...", nil, {"Убейте 12 надсмотрщиков за рабами из клана Кровавой Чешуи, 6 чародеек из клана Кровавой Чешуи и возвращайтесь к Гзхун'тту в Спореггар в Зангартопи."}},
[9728] = {"Теплый прием", nil, {"Принесите стражу Хамуту из Кенарийского приюта 30 когтей наг."}},
[9729] = {"Фхвур крушить!", nil, {"Проводите Фхвура к паровому насосу на озере Болотных Огоньков, чтобы добыть ковчег Ссслита. Потом доставьте обратно Фхвура и ковчег в целости и сохранности, и вернитесь к Гзхун'тту в Спореггар в Зангартопи."}},
[9730] = {"Предводитель Темного Гребня", nil, {"Убейте раджу Хагазеда в Лагуне и вернитесь к стражу Хамуту из Кенарийской экспедиции в Зангартопи, чтобы получить награду."}},
[9731] = {"Схема стоков", nil, {"Найдите в Змеином озере признаки утечки воды. Вернитесь к Исиэли Песни Ветра в Кенарийский оплот и сообщите о своем открытии."}},
[9732] = {"Назад в болото", nil, {"Вернитесь к Исиэль Песни Ветра в Кенарийском приюте в Зангартопи."}},
[9735] = {"Истинные владыки Света", nil, {"Принесите фиал святой воды Длани Тира Лорду Соланару Кровогневу в Луносвет."}},
[9736] = {"Истинные владыки Света", nil, {"Принесите Лорду Соланару Кровогневу в Луносвет 1 чародейский катализатор, 1 меру сумеречного порошка, 1 азеротский алмаз и 1 безупречный черный алмаз."}},
[9737] = {"Истинные владыки Света", nil, {"Потушите неугасимое пламя часовни Алонсия с помощью огнетушительной смеси и уничтожьте всех, кто попробует защитить часовню."}},
[9738] = {"Пропавшие без вести", nil, {"Выясните, что стало с заклинателем земли Гневвсом, натуралистом Кусом, культиватором Зеленопалом и призывателем ветров Когтем. Затем возвращайтесь к дозорному Джанг в резервуар Кривого Клыка в Зангартопь."}},
[9739] = {"Проблемы спорлингов", nil, {"Соберите 10 мешочков зрелых спор по просьбе Фассна с Грибной поляны."}},
[9740] = {"Солнечные Ворота", nil, {"Воздаятель Эсом с Кровавой заставы хочет, чтобы солнечные врата были уничтожены."}},
[9741] = {"Твари из Бездны", nil, {"Убейте 12 тварей Бездны и сообщите об этом на Кровавую заставу воздаятелю Эсому."}},
